INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1, 1, 'Α', '{\"def\":{\"short\":\"the first letter of the alphabet; figuratively, only (from its use as a numeral) the first\",\"long\":[\"first letter of Greek alphabet\",\"Christ is the Alpha to indicate that he is the beginning and the end\"]},\"deriv\":\"of Hebrew origin\",\"pronun\":{\"ipa\":\"ˈɑl.fɑ\",\"ipa_mod\":\"ˈɑl.fɑ\",\"sbl\":\"alpha\",\"dic\":\"AL-fa\",\"dic_mod\":\"AL-fa\"},\"see\":[\"G0260\",\"G0427\"],\"comment\":\"Often used (usually ἄν, before a vowel) also in composition (as a contraction from G0427) in the sense of privation; so, in many words, beginning with this letter; occasionally in the sense of union (as a contraction of G0260).\"}', 'Alpha'),
(2, 2, 'Ἀαρών', '{\"def\":{\"lit\":\"light-bringer\",\"short\":\"Aaron, the brother of Moses\",\"long\":[\"the brother of Moses, the first high priest of Israel and head of the whole priestly order\"]},\"deriv\":\"of Hebrew origin (H0175)\",\"pronun\":{\"ipa\":\"ɑ.ɑˈron\",\"ipa_mod\":\"ɑ.ɑˈrown\",\"sbl\":\"aarōn\",\"dic\":\"ah-ah-RONE\",\"dic_mod\":\"ah-ah-RONE\"},\"see\":[\"H0175\"]}', 'Aaron'),
(3, 3, 'Ἀβαδδών', '{\"def\":{\"lit\":\"destruction\",\"short\":\"a destroying angel\",\"long\":[\"ruin\",\"destruction\",\"the place of destruction\",\"the name of the angel-prince of the infernal regions, the minister of death and the author of havoc on the earth\"]},\"deriv\":\"of Hebrew origin (H0011)\",\"pronun\":{\"ipa\":\"ɑ.βɑðˈðon\",\"ipa_mod\":\"ɑ.vɑðˈðown\",\"sbl\":\"abaddōn\",\"dic\":\"ah-vahth-THONE\",\"dic_mod\":\"ah-vahth-THONE\"},\"see\":[\"H0011\"]}', 'Abaddon'),
(4, 4, 'ἀβαρής', '{\"def\":{\"short\":\"weightless, i.e., (figuratively) not burdensome\",\"long\":[\"not burdensome\",\"not heavy\",\"light without weight\"]},\"deriv\":\"from G0001 (as a negative particle) and G0922\",\"pronun\":{\"ipa\":\"ɑ.βɑˈres\",\"ipa_mod\":\"ɑ.vɑˈre̞s\",\"sbl\":\"abarēs\",\"dic\":\"ah-va-RASE\",\"dic_mod\":\"ah-va-RASE\"},\"see\":[\"G0001\",\"G0922\"]}', 'from being burdensome'),
(5, 5, 'Ἀββᾶ', '{\"def\":{\"lit\":\"father\",\"short\":\"father as a vocative\",\"long\":[\"father, customary title used of God in prayer\"]},\"deriv\":\"of Chaldee origin (H0002)\",\"pronun\":{\"ipa\":\"ɑβˈβɑ\",\"ipa_mod\":\"ɑvˈvɑ\",\"sbl\":\"abba\",\"dic\":\"av-VA\",\"dic_mod\":\"av-VA\"},\"see\":[\"H0002\"],\"comment\":\"Whenever it occurs in the New Testament it has the Greek interpretation joined to it, that is apparently to be explained by the fact that the original Chaldee word through frequent use in prayer, gradually acquired the nature of a most sacred proper name, to which the Greek speaking Jews added the name from their own tongue.\"}', 'Abba'),
(6, 6, 'Ἄβελ', '{\"def\":{\"lit\":\"vanity (i.e., transitory)\",\"short\":\"Abel, the son of Adam\",\"long\":[\"the second son of Adam, murdered by his brother Cain\"]},\"deriv\":\"of Hebrew origin (H1893)\",\"pronun\":{\"ipa\":\"ˈɑ.βɛl\",\"ipa_mod\":\"ˈɑ.ve̞l\",\"sbl\":\"abel\",\"dic\":\"AH-vel\",\"dic_mod\":\"AH-vale\"},\"see\":[\"H1893\"]}', 'Abel'),
(7, 7, 'Ἀβιά', '{\"def\":{\"lit\":\"my father is Jah (Jehovah)\",\"short\":\"Abijah, the name of two Israelites\",\"long\":[\"a priest, the head of a priestly family from whom when David divided the priests into 24 classes, Abia was the 8th order\",\"son and successor to Rehoboam on the throne of Judah\"]},\"deriv\":\"of Hebrew origin (H0029)\",\"pronun\":{\"ipa\":\"ɑ.βiˈɑ\",\"ipa_mod\":\"ɑ.viˈɑ\",\"sbl\":\"abia\",\"dic\":\"ah-vee-AH\",\"dic_mod\":\"ah-vee-AH\"},\"see\":[\"H0029\"]}', 'Abia'),
(8, 8, 'Ἀβιαθάρ', '{\"def\":{\"lit\":\"father of abundance\",\"short\":\"Abiathar, an Israelite\",\"long\":[\"the name of a high priest\"]},\"deriv\":\"of Hebrew origin (H0054)\",\"pronun\":{\"ipa\":\"ɑ.βi.ɑˈθɑr\",\"ipa_mod\":\"ɑ.vi.ɑˈθɑr\",\"sbl\":\"abiathar\",\"dic\":\"ah-vee-ah-THAHR\",\"dic_mod\":\"ah-vee-ah-THAHR\"},\"see\":[\"H0054\"]}', 'Abiathar'),
(9, 9, 'Ἀβιληνή', '{\"def\":{\"lit\":\"grassy meadow\",\"short\":\"Abilene, a region of Syria\",\"long\":[\"a region of Syria between Lebanon and Hermon towards Phoenicia, 18 miles (29 km) from Damascus and Heliopolis in a remarkable gorge called \\\"Suk Wadi Barada\\\"\"]},\"deriv\":\"of foreign origin (compare H0058)\",\"pronun\":{\"ipa\":\"ɑ.βi.leˈne\",\"ipa_mod\":\"ɑ.vi.le̞ˈne̞\",\"sbl\":\"abilēnē\",\"dic\":\"ah-vee-lay-NAY\",\"dic_mod\":\"ah-vee-lay-NAY\"},\"see\":[\"H0058\"]}', 'Abilene'),
(10, 10, 'Ἀβιούδ', '{\"def\":{\"lit\":\"my father is majesty\",\"short\":\"Abihud, an Israelite\",\"long\":[\"the son of Bela and grandson of Benjamin (1 Chronicles 8:3)\"]},\"deriv\":\"of Hebrew origin (H0031)\",\"pronun\":{\"ipa\":\"ɑ.βiˈuð\",\"ipa_mod\":\"ɑ.viˈuð\",\"sbl\":\"abioud\",\"dic\":\"ah-vee-OOTH\",\"dic_mod\":\"ah-vee-OOTH\"},\"see\":[\"H0031\"]}', 'Abiud'),
(11, 11, 'Ἀβραάμ', '{\"def\":{\"lit\":\"father of a multitude\",\"short\":\"Abraham, the Hebrew patriarch\",\"long\":[\"the son of Terah and the founder of the Jewish nation\"]},\"deriv\":\"of Hebrew origin (H0085)\",\"pronun\":{\"ipa\":\"ɑ.βrɑˈɑm\",\"ipa_mod\":\"ɑ.vrɑˈɑm\",\"sbl\":\"abraam\",\"dic\":\"ah-vra-AM\",\"dic_mod\":\"ah-vra-AM\"},\"see\":[\"H0085\"]}', 'Abraham'),
(12, 12, 'ἄβυσσος', '{\"def\":{\"short\":\"depthless, i.e., (specially) (infernal) \\\"abyss\\\"\",\"long\":[\"bottomless\",\"unbounded\",\"the abyss\",[\"the pit\",\"the immeasurable depth\",\"of Orcus, a very deep gulf or chasm in the lowest parts of the earth used as the common receptacle of the dead and especially as the abode of demons\"]]},\"deriv\":\"from G0001 (as a negative particle) and a variation of G1037\",\"pronun\":{\"ipa\":\"ˈɑ.βys.sos\",\"ipa_mod\":\"ˈɑ.vjus.sows\",\"sbl\":\"abyssos\",\"dic\":\"AH-voos-sose\",\"dic_mod\":\"AH-vyoos-sose\"},\"see\":[\"G0001\",\"G1037\"]}', 'deep, (bottomless) pit'),
(13, 13, 'Ἄγαβος', '{\"def\":{\"lit\":\"locust\",\"short\":\"Agabus, an Israelite\",\"long\":[\"a Christian prophet\"]},\"deriv\":\"of Hebrew origin (compare H2285)\",\"pronun\":{\"ipa\":\"ˈɑ.ɣɑ.βos\",\"ipa_mod\":\"ˈɑ.ɣɑ.vows\",\"sbl\":\"agabos\",\"dic\":\"AH-ga-vose\",\"dic_mod\":\"AH-ga-vose\"},\"see\":[\"H2285\"]}', 'Agabus'),
(14, 14, 'ἀγαθοεργέω', '{\"def\":{\"short\":\"to work good\",\"long\":[\"to work good, to do good, to do well, act rightly\"]},\"deriv\":\"from G0018 and G2041\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.θo.ɛrˈɣɛ.o\",\"ipa_mod\":\"ɑ.ɣɑ.θow.e̞rˈɣe̞.ow\",\"sbl\":\"agathoergeō\",\"dic\":\"ah-ga-thoh-er-GEH-oh\",\"dic_mod\":\"ah-ga-thoh-are-GAY-oh\"},\"see\":[\"G0018\",\"G2041\"]}', 'do good'),
(15, 15, 'ἀγαθοποιέω', '{\"def\":{\"short\":\"to be a well-doer (as a favor or a duty)\",\"long\":[\"to do good, do something which profits others\",[\"to be a good help to someone\",\"to do someone a favor\",\"to benefit\"],\"to do well, do right\"]},\"deriv\":\"from G0017\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.θo.pyˈɛ.o\",\"ipa_mod\":\"ɑ.ɣɑ.θow.pyˈe̞.ow\",\"sbl\":\"agathopoieō\",\"dic\":\"ah-ga-thoh-poo-EH-oh\",\"dic_mod\":\"ah-ga-thoh-poo-A-oh\"},\"see\":[\"G0017\"]}', '(when) do good (well)'),
(16, 16, 'ἀγαθοποιΐα', '{\"def\":{\"short\":\"well-doing, i.e., virtue\",\"long\":[\"a course of right action, well doing, virtue\"]},\"deriv\":\"from G0017\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.θo.pyˈi.ɑ\",\"ipa_mod\":\"ɑ.ɣɑ.θow.pyˈi.ɑ\",\"sbl\":\"agathopoiia\",\"dic\":\"ah-ga-thoh-poo-EE-ah\",\"dic_mod\":\"ah-ga-thoh-poo-EE-ah\"},\"see\":[\"G0017\"]}', 'well-doing'),
(17, 17, 'ἀγαθοποιός', '{\"def\":{\"short\":\"a well-doer, i.e., virtuous\",\"long\":[\"acting rightly, doing well, virtuous\"]},\"deriv\":\"from G0018 and G4160\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.θo.pyˈos\",\"ipa_mod\":\"ɑ.ɣɑ.θow.pyˈows\",\"sbl\":\"agathopoios\",\"dic\":\"ah-ga-thoh-poo-OSE\",\"dic_mod\":\"ah-ga-thoh-poo-OSE\"},\"see\":[\"G0018\",\"G4160\"]}', 'them that do well'),
(18, 18, 'ἀγαθός', '{\"def\":{\"short\":\"\\\"good\\\" (in any sense, often as noun)\",\"long\":[\"of good constitution or nature\",\"useful, salutary\",\"good, pleasant, agreeable, joyful, happy\",\"excellent, distinguished\",\"upright, honorable\"]},\"deriv\":\"a primary word\",\"pronun\":{\"ipa\":\"ɑ.ɣɑˈθos\",\"ipa_mod\":\"ɑ.ɣɑˈθows\",\"sbl\":\"agathos\",\"dic\":\"ah-ga-THOSE\",\"dic_mod\":\"ah-ga-THOSE\"},\"see\":[\"G2570\"],\"comment\":\"Compare G2570.\"}', 'benefit, good(-s) (things), well'),
(19, 19, 'ἀγαθωσύνη', '{\"def\":{\"short\":\"goodness, i.e., virtue or beneficence\",\"long\":[\"uprightness of heart and life, goodness, kindness\"]},\"deriv\":\"from G0018\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.θoˈsy.ne\",\"ipa_mod\":\"ɑ.ɣɑ.θowˈsju.ne̞\",\"sbl\":\"agathōsynē\",\"dic\":\"ah-ga-thoh-SOO-nay\",\"dic_mod\":\"ah-ga-thoh-SYOO-nay\"},\"see\":[\"G0018\"]}', 'goodness'),
(20, 20, 'ἀγαλλίασις', '{\"def\":{\"short\":\"exultation; specially, welcome\",\"long\":[\"exultation, extreme joy, gladness\"]},\"deriv\":\"from G0021\",\"pronun\":{\"ipa\":\"ɑ.ɣɑlˈli.ɑ.sis\",\"ipa_mod\":\"ɑ.ɣɑlˈli.ɑ.sis\",\"sbl\":\"agalliasis\",\"dic\":\"ah-gahl-LEE-ah-sees\",\"dic_mod\":\"ah-gahl-LEE-ah-sees\"},\"see\":[\"G0021\"],\"comment\":\"At feasts, people were anointed with the \\\"oil of gladness.\\\" Paul, in Hebrews 1:9, is alluding to this inaugural ceremony of anointing, and uses it as an emblem of the divine power and majesty to which to which the Son of God has been exalted.\"}', 'gladness, (exceeding) joy'),
(21, 21, 'ἀγαλλιάω', '{\"def\":{\"short\":\"properly, to jump for joy, i.e., exult\",\"long\":[\"to exult, rejoice exceedingly, be exceeding glad\"]},\"deriv\":\"from agan (much) and G0242\",\"pronun\":{\"ipa\":\"ɑ.ɣɑl.liˈɑ.o\",\"ipa_mod\":\"ɑ.ɣɑl.liˈɑ.ow\",\"sbl\":\"agalliaō\",\"dic\":\"ah-gahl-lee-AH-oh\",\"dic_mod\":\"ah-gahl-lee-AH-oh\"},\"see\":[\"G0242\"]}', 'be (exceeding) glad, with exceeding joy, rejoice (greatly)'),
(22, 22, 'ἄγαμος', '{\"def\":{\"short\":\"unmarried\",\"long\":[\"unmarried, unwedded, single\"]},\"deriv\":\"from G0001 (as a negative particle) and G1062\",\"pronun\":{\"ipa\":\"ˈɑ.ɣɑ.mos\",\"ipa_mod\":\"ˈɑ.ɣɑ.mows\",\"sbl\":\"agamos\",\"dic\":\"AH-ga-mose\",\"dic_mod\":\"AH-ga-mose\"},\"see\":[\"G0001\",\"G1062\"]}', 'unmarried'),
(23, 23, 'ἀγανακτέω', '{\"def\":{\"short\":\"to be greatly afflicted, i.e., (figuratively) indignant\",\"long\":[\"to be indignant, moved with indignation, be very displeased\"]},\"deriv\":\"from ἄγαν (much) and ἄχθος (grief; akin to the base of G0043)\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.nɑkˈtɛ.o\",\"ipa_mod\":\"ɑ.ɣɑ.nɑkˈte̞.ow\",\"sbl\":\"aganakteō\",\"dic\":\"ah-ga-nahk-TEH-oh\",\"dic_mod\":\"ah-ga-nahk-TAY-oh\"},\"see\":[\"G0043\"]}', 'be much (sore) displeased, have (be moved with, with) indignation'),
(24, 24, 'ἀγανάκτησις', '{\"def\":{\"short\":\"indignation\",\"long\":[\"indignation, irritation, vexation\"]},\"deriv\":\"from G0023\",\"pronun\":{\"ipa\":\"ɑ.ɣɑˈnɑk.te.sis\",\"ipa_mod\":\"ɑ.ɣɑˈnɑk.te̞.sis\",\"sbl\":\"aganaktēsis\",\"dic\":\"ah-ga-NAHK-tay-sees\",\"dic_mod\":\"ah-ga-NAHK-tay-sees\"},\"see\":[\"G0023\"]}', 'indignation'),
(25, 25, 'ἀγαπάω', '{\"def\":{\"short\":\"to love (in a social or moral sense)\",\"long\":[\"of persons\",[\"to welcome, to entertain, to be fond of, to love dearly\"],\"of things\",[\"to be well pleased, to be contented at or with a thing\"]]},\"deriv\":\"perhaps from ἄγαν (much) (or compare H5689)\",\"pronun\":{\"ipa\":\"ɑ.ɣɑˈpɑ.o\",\"ipa_mod\":\"ɑ.ɣɑˈpɑ.ow\",\"sbl\":\"agapaō\",\"dic\":\"ah-ga-PA-oh\",\"dic_mod\":\"ah-ga-PA-oh\"},\"see\":[\"H5689\",\"G5368\"],\"comment\":\"Compare G5368.\"}', '(be-)love(-ed)'),
(26, 26, 'ἀγάπη', '{\"def\":{\"short\":\"love, i.e., affection or benevolence; specially (plural) a love-feast\",\"long\":[\"brotherly love, affection, good will, love, benevolence\",\"love feasts\"]},\"deriv\":\"from G0025\",\"pronun\":{\"ipa\":\"ɑˈɣɑ.pe\",\"ipa_mod\":\"ɑˈɣɑ.pe̞\",\"sbl\":\"agapē\",\"dic\":\"ah-GA-pay\",\"dic_mod\":\"ah-GA-pay\"},\"see\":[\"G0025\"]}', '(feast of) charity(-ably), dear, love'),
(27, 27, 'ἀγαπητός', '{\"def\":{\"short\":\"beloved\",\"long\":[\"beloved, esteemed, dear, favorite, worthy of love\"]},\"deriv\":\"from G0025\",\"pronun\":{\"ipa\":\"ɑ.ɣɑ.peˈtos\",\"ipa_mod\":\"ɑ.ɣɑ.pe̞ˈtows\",\"sbl\":\"agapētos\",\"dic\":\"ah-ga-pay-TOSE\",\"dic_mod\":\"ah-ga-pay-TOSE\"},\"see\":[\"G0025\"]}', '(dearly, well) beloved, dear'),
(28, 28, 'Ἄγαρ', '{\"def\":{\"lit\":\"flight\",\"short\":\"Hagar, the concubine of Abraham\",\"long\":[\"Abraham\'s concubine and mother to Ishmael\"]},\"deriv\":\"of Hebrew origin (H1904)\",\"pronun\":{\"ipa\":\"ˈɑ.ɣɑr\",\"ipa_mod\":\"ˈɑ.ɣɑr\",\"sbl\":\"agar\",\"dic\":\"AH-gahr\",\"dic_mod\":\"AH-gahr\"},\"see\":[\"H1904\"]}', 'Hagar'),
(29, 29, 'ἀγγαρεύω', '{\"def\":{\"short\":\"properly, to be a courier, i.e., (by implication) to press into public service\",\"long\":[\"to employ a courier, dispatch a mounted messenger, press into public service, compel to go\"]},\"deriv\":\"of foreign origin (compare H0104)\",\"pronun\":{\"ipa\":\"ɑŋ.ɣɑˈrɛβ.o\",\"ipa_mod\":\"ɑŋ.ɣɑˈrev.ow\",\"sbl\":\"angareuō\",\"dic\":\"ang-ga-REV-oh\",\"dic_mod\":\"ang-ga-RAVE-oh\"},\"see\":[\"H0104\"],\"comment\":\"In Persia, mounted couriers were kept at regular intervals throughout Persia for carrying the royal dispatches.\"}', 'compel (to go)'),
(30, 30, 'ἀγγεῖον', '{\"def\":{\"short\":\"a receptacle\",\"long\":[\"a vessel, receptacle, a pail, a reservoir\"]},\"deriv\":\"from ἄγγος (a pail, perhaps as bent; compare the base of G0043)\",\"pronun\":{\"ipa\":\"ɑŋˈɣi.on\",\"ipa_mod\":\"ɑŋˈʝi.own\",\"sbl\":\"angeion\",\"dic\":\"ang-GEE-one\",\"dic_mod\":\"ang-GEE-one\"},\"see\":[\"G0043\"]}', 'vessel'),
(31, 31, 'ἀγγελία', '{\"def\":{\"short\":\"an announcement, i.e., (by implication) precept\",\"long\":[\"message, announcement, news\",\"a proclamation, command, order\"]},\"deriv\":\"from G0032\",\"pronun\":{\"ipa\":\"ɑŋ.ɣɛˈli.ɑ\",\"ipa_mod\":\"ɑŋ.ʝe̞ˈli.ɑ\",\"sbl\":\"angelia\",\"dic\":\"ang-geh-LEE-ah\",\"dic_mod\":\"ang-gay-LEE-ah\"},\"see\":[\"G0032\"]}', 'message'),
(32, 32, 'ἄγγελος', '{\"def\":{\"short\":\"compare G0034) (to bring tidings); a messenger; especially an \\\"angel\\\"; by implication, a pastor\",\"long\":[\"a messenger, envoy, one who is sent, an angel, a messenger from God\"]},\"deriv\":\"from ἀγγέλλω (probably derived from G0071\",\"pronun\":{\"ipa\":\"ˈɑŋ.ɣɛ.los\",\"ipa_mod\":\"ˈɑŋ.ʝe̞.lows\",\"sbl\":\"angelos\",\"dic\":\"ANG-geh-lose\",\"dic_mod\":\"ANG-gay-lose\"},\"see\":[\"G0034\",\"G0071\"]}', 'angel, messenger'),
(33, 33, 'ἄγε', '{\"def\":{\"short\":\"properly, lead, i.e., come on\",\"long\":[\"go to, come!, come now!\"]},\"deriv\":\"imperative of G0071\",\"pronun\":{\"ipa\":\"ˈɑ.ɣɛ\",\"ipa_mod\":\"ˈɑ.ʝe̞\",\"sbl\":\"age\",\"dic\":\"AH-geh\",\"dic_mod\":\"AH-gay\"},\"see\":[\"G0071\"]}', 'go to'),
(34, 34, 'ἀγέλη', '{\"def\":{\"short\":\"a drove\",\"long\":[\"herd or oxen or cattle, a herd or company\"]},\"deriv\":\"from G0071 (compare G0032)\",\"pronun\":{\"ipa\":\"ɑˈɣɛ.le\",\"ipa_mod\":\"ɑˈɣe̞.le̞\",\"sbl\":\"agelē\",\"dic\":\"ah-GEH-lay\",\"dic_mod\":\"ah-GAY-lay\"},\"see\":[\"G0032\",\"G0071\"]}', 'herd'),
(35, 35, 'ἀγενεαλόγητος', '{\"def\":{\"short\":\"unregistered as to birth\",\"long\":[\"one whose descent there is no record of, without genealogy\"]},\"deriv\":\"from G0001 (as negative particle) and G1075\",\"pronun\":{\"ipa\":\"ɑ.ɣɛ.nɛ.ɑˈlo.ɣe.tos\",\"ipa_mod\":\"ɑ.ʝe̞.ne̞.ɑˈlow.ɣe̞.tows\",\"sbl\":\"agenealogētos\",\"dic\":\"ah-geh-neh-ah-LOH-gay-tose\",\"dic_mod\":\"ah-gay-nay-ah-LOH-gay-tose\"},\"see\":[\"G0001\",\"G1075\"]}', 'without descent'),
(36, 36, 'ἀγενής', '{\"def\":{\"short\":\"properly, without kin, i.e., (of unknown descent, and by implication) ignoble\",\"long\":[\"ignoble, coward, mean, base, of no family, that is: low born\"]},\"deriv\":\"from G0001 (as negative particle) and G1085\",\"pronun\":{\"ipa\":\"ɑ.ɣɛˈnes\",\"ipa_mod\":\"ɑ.ʝe̞ˈne̞s\",\"sbl\":\"agenēs\",\"dic\":\"ah-geh-NASE\",\"dic_mod\":\"ah-gay-NASE\"},\"see\":[\"G0001\",\"G1085\"]}', 'base things'),
(37, 37, 'ἁγιάζω', '{\"def\":{\"short\":\"to make holy, i.e., (ceremonially) purify or consecrate; (mentally) to venerate\",\"long\":[\"to render or acknowledge, or to be venerable or hallow\",\"to separate from profane things and dedicate to God\",[\"consecrate things to God\",\"dedicate people to God\"],\"to purify\",[\"to cleanse externally\",\"to purify by expiation: free from the guilt of sin\",\"to purify internally by renewing of the soul\"]]},\"deriv\":\"from G0040\",\"pronun\":{\"ipa\":\"hɑ.ɣiˈɑ.zo\",\"ipa_mod\":\"ɑ.ʝiˈɑ.zow\",\"sbl\":\"hagiazō\",\"dic\":\"ha-gee-AH-zoh\",\"dic_mod\":\"a-gee-AH-zoh\"},\"see\":[\"G0040\"]}', 'hallow, be holy, sanctify'),
(38, 38, 'ἁγιασμός', '{\"def\":{\"short\":\"properly, purification, i.e., (the state) purity; concretely (by Hebraism) a purifier\",\"long\":[\"consecration, purification\",\"the effect of consecration\",[\"sanctification of heart and life\"]]},\"deriv\":\"from G0037\",\"pronun\":{\"ipa\":\"hɑ.ɣi.ɑˈsmos\",\"ipa_mod\":\"ɑ.ʝi.ɑˈsmows\",\"sbl\":\"hagiasmos\",\"dic\":\"ha-gee-ah-SMOSE\",\"dic_mod\":\"a-gee-ah-SMOSE\"},\"see\":[\"G0037\"]}', 'holiness, sanctification'),
(39, 39, 'ἅγιον', '{\"def\":{\"short\":\"a sacred thing (i.e., spot)\",\"long\":[\"reverend, worthy of veneration\",[\"of things which on account of some connection with God possess a certain distinction and claim to reverence, as places sacred to God which are not to be profaned\",\"of persons whose services God employs, for example, apostles\"],\"set apart for God, to be as it were, exclusively his\",\"services and offerings\",[\"prepared for God with solemn rite, pure, clean\"],\"in a moral sense, pure sinless upright holy\"]},\"deriv\":\"neuter of G0040\",\"pronun\":{\"ipa\":\"ˈhɑ.ɣi.on\",\"ipa_mod\":\"ˈɑ.ʝi.own\",\"sbl\":\"hagion\",\"dic\":\"HA-gee-one\",\"dic_mod\":\"A-gee-one\"},\"see\":[\"G0040\"]}', 'holiest (of all), holy place, sanctuary'),
(40, 40, 'ἅγιος', '{\"def\":{\"short\":\"sacred (physically, pure, morally blameless or religious, ceremonially, consecrated)\",\"long\":[\"most holy thing, a saint\"]},\"deriv\":\"from ἅγος (an awful thing) (compare G0053, G2282)\",\"pronun\":{\"ipa\":\"ˈhɑ.ɣi.os\",\"ipa_mod\":\"ˈɑ.ʝi.ows\",\"sbl\":\"hagios\",\"dic\":\"HA-gee-ose\",\"dic_mod\":\"A-gee-ose\"},\"see\":[\"G0053\",\"G2282\"]}', '(most) holy (one, thing), saint'),
(41, 41, 'ἁγιότης', '{\"def\":{\"short\":\"sanctity (i.e., properly, the state)\",\"long\":[\"sanctity\",\"in a moral sense: holiness\"]},\"deriv\":\"from G0040\",\"pronun\":{\"ipa\":\"hɑ.ɣiˈo.tes\",\"ipa_mod\":\"ɑ.ʝiˈow.te̞s\",\"sbl\":\"hagiotēs\",\"dic\":\"ha-gee-OH-tase\",\"dic_mod\":\"a-gee-OH-tase\"},\"see\":[\"G0040\"]}', 'holiness'),
(42, 42, 'ἁγιωσύνη', '{\"def\":{\"short\":\"sacredness (i.e., properly, the quality)\",\"long\":[\"majesty, holiness\",\"moral purity\"]},\"deriv\":\"from G0040\",\"pronun\":{\"ipa\":\"hɑ.ɣi.oˈsy.ne\",\"ipa_mod\":\"ɑ.ʝi.owˈsju.ne̞\",\"sbl\":\"hagiōsynē\",\"dic\":\"ha-gee-oh-SOO-nay\",\"dic_mod\":\"a-gee-oh-SYOO-nay\"},\"see\":[\"G0040\"]}', 'holiness'),
(43, 43, 'ἀγκάλη', '{\"def\":{\"short\":\"an arm (as curved)\",\"long\":[\"the curve or inner angle of the arm, the bent arm\",\"anything closely enfolding, as the arms of the sea, etc.\"]},\"deriv\":\"from ἄγκος (a bend, \\\"ache\\\")\",\"pronun\":{\"ipa\":\"ɑŋˈkɑ.le\",\"ipa_mod\":\"ɑŋˈkɑ.le̞\",\"sbl\":\"ankalē\",\"dic\":\"ang-KA-lay\",\"dic_mod\":\"ang-KA-lay\"}}', 'arm'),
(44, 44, 'ἄγκιστρον', '{\"def\":{\"short\":\"a hook (as bent)\",\"long\":[\"a fishing hook, a small hook\"]},\"deriv\":\"from the same as G0043\",\"pronun\":{\"ipa\":\"ˈɑŋ.ki.stron\",\"ipa_mod\":\"ˈɑŋ.ci.strown\",\"sbl\":\"ankistron\",\"dic\":\"ANG-kee-strone\",\"dic_mod\":\"ANG-kee-strone\"},\"see\":[\"G0043\"]}', 'hook'),
(45, 45, 'ἄγκυρα', '{\"def\":{\"short\":\"an \\\"anchor\\\" (as crooked)\",\"long\":[\"an anchor\",\"metaphorically any stay or safeguard\"]},\"deriv\":\"from the same as G0043\",\"pronun\":{\"ipa\":\"ˈɑŋ.ky.rɑ\",\"ipa_mod\":\"ˈɑŋ.cju.rɑ\",\"sbl\":\"ankyra\",\"dic\":\"ANG-koo-ra\",\"dic_mod\":\"ANG-kyoo-ra\"},\"see\":[\"G0043\"],\"comment\":\"Ancient anchors resembled modern ones in form: they were of iron, provided with a stock, and with two teeth-like extremities often but by no means without flukes.\"}', 'anchor'),
(46, 46, 'ἄγναφος', '{\"def\":{\"short\":\"properly, unfulled, i.e., (by implication) new (cloth)\",\"long\":[\"unmilled, unfulled, undressed, unprocessed, new\"]},\"deriv\":\"from G0001 (as a negative particle) and the same as G1102\",\"pronun\":{\"ipa\":\"ˈɑ.ɣnɑ.fos\",\"ipa_mod\":\"ˈɑ.ɣnɑ.fows\",\"sbl\":\"agnaphos\",\"dic\":\"AH-gna-fose\",\"dic_mod\":\"AH-gna-fose\"},\"see\":[\"G0001\",\"G1102\"]}', 'new'),
(47, 47, 'ἁγνεία', '{\"def\":{\"short\":\"cleanliness (the quality), i.e., (specially) chastity\",\"long\":[\"purity, sinlessness of life\"]},\"deriv\":\"from G0053\",\"pronun\":{\"ipa\":\"hɑˈɣni.ɑ\",\"ipa_mod\":\"ɑˈɣni.ɑ\",\"sbl\":\"hagneia\",\"dic\":\"ha-GNEE-ah\",\"dic_mod\":\"a-GNEE-ah\"},\"see\":[\"G0053\"]}', 'purity'),
(48, 48, 'ἁγνίζω', '{\"def\":{\"short\":\"to make clean, i.e., (figuratively) sanctify (ceremonially or morally)\",\"long\":[\"ceremonially\",[\"to make pure, purify, cleanse\"],\"morally\"]},\"deriv\":\"from G0053\",\"pronun\":{\"ipa\":\"hɑˈɣni.zo\",\"ipa_mod\":\"ɑˈɣni.zow\",\"sbl\":\"hagnizō\",\"dic\":\"ha-GNEE-zoh\",\"dic_mod\":\"a-GNEE-zoh\"},\"see\":[\"G0053\"]}', 'purify (self)'),
(49, 49, 'ἁγνισμός', '{\"def\":{\"short\":\"a cleansing (the act), i.e., (ceremonially) lustration\",\"long\":[\"purification, expiation\"]},\"deriv\":\"from G0048\",\"pronun\":{\"ipa\":\"hɑ.ɣniˈsmos\",\"ipa_mod\":\"ɑ.ɣniˈsmows\",\"sbl\":\"hagnismos\",\"dic\":\"ha-gnee-SMOSE\",\"dic_mod\":\"a-gnee-SMOSE\"},\"see\":[\"G0048\"]}', 'purification'),
(50, 50, 'ἀγνοέω', '{\"def\":{\"short\":\"not to know (through lack of information or intelligence); by implication, to ignore (through disinclination)\",\"long\":[\"to be ignorant, not to know\",\"not to understand, unknown\",\"to err or sin through mistake, to be wrong\"]},\"deriv\":\"from G0001 (as a negative particle) and G3539\",\"pronun\":{\"ipa\":\"ɑ.ɣnoˈɛ.o\",\"ipa_mod\":\"ɑ.ɣnowˈe̞.ow\",\"sbl\":\"agnoeō\",\"dic\":\"ah-gnoh-EH-oh\",\"dic_mod\":\"ah-gnoh-A-oh\"},\"see\":[\"G0001\",\"G3539\"]}', '(be) ignorant(-ly), not know, not understand, unknown'),
(51, 51, 'ἀγνόημα', '{\"def\":{\"short\":\"a thing ignored, i.e., shortcoming\",\"long\":[\"a sin committed through ignorance or thoughtlessness\"]},\"deriv\":\"from G0050\",\"pronun\":{\"ipa\":\"ɑˈɣno.e.mɑ\",\"ipa_mod\":\"ɑˈɣnow.e̞.mɑ\",\"sbl\":\"agnoēma\",\"dic\":\"ah-GNOH-ay-ma\",\"dic_mod\":\"ah-GNOH-ay-ma\"},\"see\":[\"G0050\"]}', 'error'),
(52, 52, 'ἄγνοια', '{\"def\":{\"short\":\"ignorance (properly, the quality)\",\"long\":[\"lack of knowledge, ignorance\",[\"especially of divine things\",\"of moral blindness\"]]},\"deriv\":\"from G0050\",\"pronun\":{\"ipa\":\"ˈɑ.ɣny.ɑ\",\"ipa_mod\":\"ˈɑ.ɣny.ɑ\",\"sbl\":\"agnoia\",\"dic\":\"AH-gnoo-ah\",\"dic_mod\":\"AH-gnoo-ah\"},\"see\":[\"G0050\"]}', 'ignorance'),
(53, 53, 'ἁγνός', '{\"def\":{\"short\":\"properly, clean, i.e., (figuratively) innocent, modest, perfect\",\"long\":[\"exciting reverence, venerable, sacred\",\"pure\",[\"pure from carnality, chaste, modest\",\"pure from every fault, immaculate\",\"clean\"]]},\"deriv\":\"from the same as G0040\",\"pronun\":{\"ipa\":\"hɑˈɣnos\",\"ipa_mod\":\"ɑˈɣnows\",\"sbl\":\"hagnos\",\"dic\":\"ha-GNOSE\",\"dic_mod\":\"a-GNOSE\"},\"see\":[\"G0040\"]}', 'chaste, clean, pure'),
(54, 54, 'ἁγνότης', '{\"def\":{\"short\":\"cleanness (the state), i.e., (figuratively) blamelessness\",\"long\":[\"purity, chastity, uprightness of life\"]},\"deriv\":\"from G0053\",\"pronun\":{\"ipa\":\"hɑˈɣno.tes\",\"ipa_mod\":\"ɑˈɣnow.te̞s\",\"sbl\":\"hagnotēs\",\"dic\":\"ha-GNOH-tase\",\"dic_mod\":\"a-GNOH-tase\"},\"see\":[\"G0053\"]}', 'pureness'),
(55, 55, 'ἁγνῶς', '{\"def\":{\"short\":\"purely, i.e., honestly\",\"long\":[\"chaste, clean, pure, with sincerity\"]},\"deriv\":\"adverb from G0053\",\"pronun\":{\"ipa\":\"hɑˈɣnos\",\"ipa_mod\":\"ɑˈɣnows\",\"sbl\":\"hagnōs\",\"dic\":\"ha-GNOSE\",\"dic_mod\":\"a-GNOSE\"},\"see\":[\"G0053\"]}', 'sincerely'),
(56, 56, 'ἀγνωσία', '{\"def\":{\"short\":\"ignorance (properly, the state)\",\"long\":[\"not knowing, ignorance\"]},\"deriv\":\"from G0001 (as negative particle) and G1108\",\"pronun\":{\"ipa\":\"ɑ.ɣnosˈi.ɑ\",\"ipa_mod\":\"ɑ.ɣnowsˈi.ɑ\",\"sbl\":\"agnōsia\",\"dic\":\"ah-gnose-EE-ah\",\"dic_mod\":\"ah-gnose-EE-ah\"},\"see\":[\"G0001\",\"G1108\"]}', 'ignorance, not the knowledge'),
(57, 57, 'ἄγνωστος', '{\"def\":{\"short\":\"unknown\",\"long\":[\"unknown, forgotten\"]},\"deriv\":\"from G0001 (as negative particle) and G1110\",\"pronun\":{\"ipa\":\"ˈɑ.ɣnos.tos\",\"ipa_mod\":\"ˈɑ.ɣnows.tows\",\"sbl\":\"agnōstos\",\"dic\":\"AH-gnose-tose\",\"dic_mod\":\"AH-gnose-tose\"},\"see\":[\"G0001\",\"G1110\"]}', 'unknown'),
(58, 58, 'ἀγορά', '{\"def\":{\"short\":\"properly, the town-square (as a place of public resort); by implication, a market or thoroughfare\",\"long\":[\"any assembly, especially of the people\",\"the place of assembly\",[\"for public debating,\",\"for elections\",\"for trials\",\"for buying and selling\",\"for all kinds of business\"],\"market place, street\"]},\"deriv\":\"from ἀγείρω (to gather; probably akin to G1453)\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈrɑ\",\"ipa_mod\":\"ɑ.ɣowˈrɑ\",\"sbl\":\"agora\",\"dic\":\"ah-goh-RA\",\"dic_mod\":\"ah-goh-RA\"},\"see\":[\"G1453\"]}', 'market(-place), street'),
(59, 59, 'ἀγοράζω', '{\"def\":{\"short\":\"properly, to go to market, i.e., (by implication) to purchase; specially, to redeem\",\"long\":[\"to be in the market place, to attend it\",\"to do business there, buy or sell\",\"of idle people: to haunt the market place, lounge there\"]},\"deriv\":\"from G0058\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈrɑ.zo\",\"ipa_mod\":\"ɑ.ɣowˈrɑ.zow\",\"sbl\":\"agorazō\",\"dic\":\"ah-goh-RA-zoh\",\"dic_mod\":\"ah-goh-RA-zoh\"},\"see\":[\"G0058\"]}', 'buy, redeem'),
(60, 60, 'ἀγοραῖος', '{\"def\":{\"short\":\"relating to the market-place, i.e., forensic (times); by implication, vulgar\",\"long\":[\"in, of or belonging to the market place\",\"frequenting the market place\",[\"hucksters, petty traffickers, retail dealers\",\"idlers, loungers, the common sort, low, mean vulgar\"],\"generally, proper to the assembly, suited to forensic speaking, business-like transactions\"]},\"deriv\":\"from G0058\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈrɛ.os\",\"ipa_mod\":\"ɑ.ɣowˈre.ows\",\"sbl\":\"agoraios\",\"dic\":\"ah-goh-REH-ose\",\"dic_mod\":\"ah-goh-RAY-ose\"},\"see\":[\"G0058\"]}', 'baser sort, low'),
(61, 61, 'ἄγρα', '{\"def\":{\"short\":\"(abstractly) a catching (of fish); also (concretely) a haul (of fish)\",\"long\":[\"a catching, hunting\",\"that which is taken in hunting, the booty, prey\",[\"of birds or beasts, game\",\"of fish, a draught or haul\"]]},\"deriv\":\"from G0071\",\"pronun\":{\"ipa\":\"ˈɑ.ɣrɑ\",\"ipa_mod\":\"ˈɑ.ɣrɑ\",\"sbl\":\"agra\",\"dic\":\"AH-gra\",\"dic_mod\":\"AH-gra\"},\"see\":[\"G0071\"]}', 'draught'),
(62, 62, 'ἀγράμματος', '{\"def\":{\"short\":\"unlettered, i.e., illiterate\",\"long\":[\"illiterate, unlearned\"]},\"deriv\":\"from G0001 (as negative particle) and G1121\",\"pronun\":{\"ipa\":\"ɑˈɣrɑm.mɑ.tos\",\"ipa_mod\":\"ɑˈɣrɑm.mɑ.tows\",\"sbl\":\"agrammatos\",\"dic\":\"ah-GRAHM-ma-tose\",\"dic_mod\":\"ah-GRAHM-ma-tose\"},\"see\":[\"G0001\",\"G1121\"]}', 'unlearned'),
(63, 63, 'ἀγραυλέω', '{\"def\":{\"short\":\"to camp out\",\"long\":[\"to live in the fields, be under the open sky, even at night\"]},\"deriv\":\"from G0068 and G0832 (in the sense of G0833)\",\"pronun\":{\"ipa\":\"ɑ.ɣrɛβˈlɛ.o\",\"ipa_mod\":\"ɑ.ɣraˈle̞.ow\",\"sbl\":\"agrauleō\",\"dic\":\"ah-grev-LEH-oh\",\"dic_mod\":\"ah-gra-LAY-oh\"},\"see\":[\"G0068\",\"G0832\",\"G0833\"]}', 'abide in the field'),
(64, 64, 'ἀγρεύω', '{\"def\":{\"short\":\"to hunt, i.e., (figuratively) to entrap\",\"long\":[\"to hunt, to take by hunting, catch\",\"metaphorically to hunt after, pursue eagerly\"]},\"deriv\":\"from G0061\",\"pronun\":{\"ipa\":\"ɑˈɣrɛβ.o\",\"ipa_mod\":\"ɑˈɣrev.ow\",\"sbl\":\"agreuō\",\"dic\":\"ah-GREV-oh\",\"dic_mod\":\"ah-GRAVE-oh\"},\"see\":[\"G0061\"]}', 'catch'),
(65, 65, 'ἀγριέλαιος', '{\"def\":{\"short\":\"an oleaster\",\"long\":[\"of or belonging to the oleaster or wild olive\",\"the oleaster, wild olive tree\"]},\"deriv\":\"from G0066 and G1636\",\"pronun\":{\"ipa\":\"ɑ.ɣriˈɛ.lɛ.os\",\"ipa_mod\":\"ɑ.ɣriˈe̞.le.ows\",\"sbl\":\"agrielaios\",\"dic\":\"ah-gree-EH-leh-ose\",\"dic_mod\":\"ah-gree-A-lay-ose\"},\"see\":[\"G0066\",\"G1636\"]}', 'olive tree (which is) wild'),
(66, 66, 'ἄγριος', '{\"def\":{\"short\":\"wild (as pertaining to the country), literally (natural) or figuratively (fierce)\",\"long\":[\"living or growing in the fields or woods\",[\"of animals, wild, savage\",\"of countries, wild, uncultivated, unreclaimed\"],\"of men and animals in a moral sense, wild savage, fierce\",[\"boorish, rude\",\"of any violent passion, vehement, furious\"]]},\"deriv\":\"from G0068\",\"pronun\":{\"ipa\":\"ˈɑ.ɣri.os\",\"ipa_mod\":\"ˈɑ.ɣri.ows\",\"sbl\":\"agrios\",\"dic\":\"AH-gree-ose\",\"dic_mod\":\"AH-gree-ose\"},\"see\":[\"G0068\"]}', 'wild, raging'),
(67, 67, 'Ἀγρίππας', '{\"def\":{\"lit\":\"hero-like\",\"short\":\"wild-horse tamer; Agrippas, one of the Herods\",\"long\":[\"Name of a ruling family in Israel at the time of Christ\"]},\"deriv\":\"apparently from G0066 and G2462\",\"pronun\":{\"ipa\":\"ɑˈɣrip.pɑs\",\"ipa_mod\":\"ɑˈɣrip.pɑs\",\"sbl\":\"agrippas\",\"dic\":\"ah-GREEP-pahs\",\"dic_mod\":\"ah-GREEP-pahs\"},\"see\":[\"G0066\",\"G2462\"]}', 'Agrippa'),
(68, 68, 'ἀγρός', '{\"def\":{\"short\":\"a field (as a drive for cattle); genitive case, the country; specially, a farm, i.e., hamlet\",\"long\":[\"land\",[\"the field, the country\",\"a piece of land, bit of tillage\",\"the farms, country seats, neighbouring hamlets\"]]},\"deriv\":\"from G0071\",\"pronun\":{\"ipa\":\"ɑˈɣros\",\"ipa_mod\":\"ɑˈɣrows\",\"sbl\":\"agros\",\"dic\":\"ah-GROSE\",\"dic_mod\":\"ah-GROSE\"},\"see\":[\"G0071\"]}', 'country, farm, piece of ground, land'),
(69, 69, 'ἀγρυπνέω', '{\"def\":{\"short\":\"to be sleepless, i.e., keep awake\",\"long\":[\"to be sleepless, keep awake, watch\",\"to be circumspect, attentive, ready\"]},\"deriv\":\"ultimately from G0001 (as negative particle) and G5258\",\"pronun\":{\"ipa\":\"ɑ.ɣryˈpnɛ.o\",\"ipa_mod\":\"ɑ.ɣrjuˈpne̞.ow\",\"sbl\":\"agrypneō\",\"dic\":\"ah-groo-PNEH-oh\",\"dic_mod\":\"ah-gryoo-PNAY-oh\"},\"see\":[\"G0001\",\"G5258\"]}', 'watch'),
(70, 70, 'ἀγρυπνία', '{\"def\":{\"short\":\"sleeplessness, i.e., a keeping awake\",\"long\":[\"sleeplessness, watching\"]},\"deriv\":\"from G0069\",\"pronun\":{\"ipa\":\"ɑ.ɣryˈpni.ɑ\",\"ipa_mod\":\"ɑ.ɣrjuˈpni.ɑ\",\"sbl\":\"agrypnia\",\"dic\":\"ah-groo-PNEE-ah\",\"dic_mod\":\"ah-gryoo-PNEE-ah\"},\"see\":[\"G0069\"]}', 'watch'),
(71, 71, 'ἄγω', '{\"def\":{\"short\":\"properly, to lead; by implication, to bring, drive, (reflexively) go, (specially) pass (time), or (figuratively) induce\",\"long\":[\"to lead, take with one\",[\"to lead by laying hold of, and this way to bring to the point of destination: of an animal\",\"to lead by accompanying to (into) a place\",\"to lead with one\'s self, attach to one\'s self as an attendant\",\"to conduct, bring\",\"to lead away, to a court of justice, magistrate, etc.\"],\"to lead,\",[\"to lead, guide, direct\",\"to lead through, conduct to: to something\",\"to move, impel: of forces and influences on the mind\"],\"to pass a day, keep or celebrate a feast, etc.\",\"to go, depart\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈɑ.ɣo\",\"ipa_mod\":\"ˈɑ.ɣow\",\"sbl\":\"agō\",\"dic\":\"AH-goh\",\"dic_mod\":\"AH-goh\"}}', 'be, bring (forth), carry, (let) go, keep, lead away, be open'),
(72, 72, 'ἀγωγή', '{\"def\":{\"short\":\"a bringing up, i.e., mode of living\",\"long\":[\"a leading\",\"metaphorically: conduct\",[\"a conducting, training, education, discipline\",\"the life led, way or course of life\"]]},\"deriv\":\"reduplicated from G0071\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈɣe\",\"ipa_mod\":\"ɑ.ɣowˈɣe̞\",\"sbl\":\"agōgē\",\"dic\":\"ah-goh-GAY\",\"dic_mod\":\"ah-goh-GAY\"},\"see\":[\"G0071\"]}', 'manner of life'),
(73, 73, 'ἀγών', '{\"def\":{\"short\":\"properly, a place of assembly (as if led), i.e., (by implication) a contest (held there); figuratively, an effort or anxiety\",\"long\":[\"an assembly,\",[\"a place of assembly: especially an assembly met to see games\",\"the place of contest, the arena or stadium\"],\"the assembly of the Greeks at their national games\",[\"hence the contest for a prize at their games\",\"generally, any struggle or contest\",\"a battle\",\"an action at law, trial\"]]},\"deriv\":\"from G0071\",\"pronun\":{\"ipa\":\"ɑˈɣon\",\"ipa_mod\":\"ɑˈɣown\",\"sbl\":\"agōn\",\"dic\":\"ah-GONE\",\"dic_mod\":\"ah-GONE\"},\"see\":[\"G0071\"]}', 'conflict, contention, fight, race'),
(74, 74, 'ἀγωνία', '{\"def\":{\"short\":\"a struggle (properly, the state), i.e., (figuratively) anguish\",\"long\":[\"a struggle for victory\",[\"gymnastic exercise, wrestling\"],\"of severe mental struggles and emotions, agony, anguish\"]},\"deriv\":\"from G0073\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈni.ɑ\",\"ipa_mod\":\"ɑ.ɣowˈni.ɑ\",\"sbl\":\"agōnia\",\"dic\":\"ah-goh-NEE-ah\",\"dic_mod\":\"ah-goh-NEE-ah\"},\"see\":[\"G0073\"]}', 'agony'),
(75, 75, 'ἀγωνίζομαι', '{\"def\":{\"short\":\"to struggle, literally (to compete for a prize), figuratively (to contend with an adversary), or genitive case (to endeavor to accomplish something)\",\"long\":[\"to enter a contest: contend in the gymnastic games\",\"to contend with adversaries, fight\",\"metaphorically to contend, struggle, with difficulties and dangers\",\"to endeavor with strenuous zeal, strive: to obtain something\"]},\"deriv\":\"from G0073\",\"pronun\":{\"ipa\":\"ɑ.ɣoˈni.zo.mɛ\",\"ipa_mod\":\"ɑ.ɣowˈni.zow.me\",\"sbl\":\"agōnizomai\",\"dic\":\"ah-goh-NEE-zoh-meh\",\"dic_mod\":\"ah-goh-NEE-zoh-may\"},\"see\":[\"G0073\"]}', 'fight, labor fervently, strive'),
(76, 76, 'Ἀδάμ', '{\"def\":{\"lit\":\"the red earth\",\"short\":\"Adam, the first man; typically (of Jesus) man (as his representative)\",\"long\":[\"Adam, the first man, the parent of the whole human race\"]},\"deriv\":\"of Hebrew origin (H0121)\",\"pronun\":{\"ipa\":\"ɑˈðɑm\",\"ipa_mod\":\"ɑˈðɑm\",\"sbl\":\"adam\",\"dic\":\"ah-THAHM\",\"dic_mod\":\"ah-THAHM\"},\"see\":[\"H0121\"]}', 'Adam'),
(77, 77, 'ἀδάπανος', '{\"def\":{\"short\":\"and G1160; costless, i.e., gratuitous\",\"long\":[\"without expense, requiring no outlay, costing nothing\"]},\"deriv\":\"from G0001 (as negative particle)\",\"pronun\":{\"ipa\":\"ɑˈðɑ.pɑ.nos\",\"ipa_mod\":\"ɑˈðɑ.pɑ.nows\",\"sbl\":\"adapanos\",\"dic\":\"ah-THA-pa-nose\",\"dic_mod\":\"ah-THA-pa-nose\"},\"see\":[\"G0001\",\"G1160\"]}', 'without expense'),
(78, 78, 'Ἀδδί', '{\"def\":{\"lit\":\"ornament\",\"short\":\"Addi, an Israelite\",\"long\":[\"son of Cosam, and father of Melchi in our Lord\'s genealogy\"]},\"deriv\":\"probably of Hebrew origin (compare H5716)\",\"pronun\":{\"ipa\":\"ɑðˈði\",\"ipa_mod\":\"ɑðˈði\",\"sbl\":\"addi\",\"dic\":\"ath-THEE\",\"dic_mod\":\"ath-THEE\"},\"see\":[\"H5716\"]}', 'Addi'),
(79, 79, 'ἀδελφή', '{\"def\":{\"short\":\"a sister (naturally or ecclesiastically)\",\"long\":[\"a full, own sister\",\"one connected by the tie of the Christian religion\"]},\"deriv\":\"feminine of G0080\",\"pronun\":{\"ipa\":\"ɑ.ðɛlˈfe\",\"ipa_mod\":\"ɑ.ðe̞lˈfe̞\",\"sbl\":\"adelphē\",\"dic\":\"ah-thel-FAY\",\"dic_mod\":\"ah-thale-FAY\"},\"see\":[\"G0080\"]}', 'sister'),
(80, 80, 'ἀδελφός', '{\"def\":{\"short\":\"a brother (literally or figuratively) near or remote (much like G0001)\",\"long\":[\"a brother, whether born of the same two parents or only of the same father or mother\",\"having the same national ancestor, belonging to the same people, or countryman\",\"any fellow or man\",\"a fellow believer, united to another by the bond of affection\",\"an associate in employment or office\",\"brethren in Christ\",[\"his brothers by blood\",\"all men\",\"apostles\",\"Christians, as those who are exalted to the same heavenly place\"]]},\"deriv\":\"from G0001 (as a connective particle) and δελφύς (the womb)\",\"pronun\":{\"ipa\":\"ɑ.ðɛlˈfos\",\"ipa_mod\":\"ɑ.ðe̞lˈfows\",\"sbl\":\"adelphos\",\"dic\":\"ah-thel-FOSE\",\"dic_mod\":\"ah-thale-FOSE\"},\"see\":[\"G0001\",\"G0001\"]}', 'brother'),
(81, 81, 'ἀδελφότης', '{\"def\":{\"short\":\"brotherhood (properly, the feeling of brotherliness), i.e., the (Christian) fraternity\",\"long\":[\"brotherhood, brotherly kindness\",\"a family of brothers, the brotherhood\"]},\"deriv\":\"from G0080\",\"pronun\":{\"ipa\":\"ɑ.ðɛlˈfo.tes\",\"ipa_mod\":\"ɑ.ðe̞lˈfow.te̞s\",\"sbl\":\"adelphotēs\",\"dic\":\"ah-thel-FOH-tase\",\"dic_mod\":\"ah-thale-FOH-tase\"},\"see\":[\"G0080\"]}', 'brethren, brotherhood'),
(82, 82, 'ἄδηλος', '{\"def\":{\"short\":\"hidden, figuratively, indistinct\",\"long\":[\"not manifest, indistinct, uncertain, obscure\"]},\"deriv\":\"from G0001 (as a negative particle) and G1212\",\"pronun\":{\"ipa\":\"ˈɑ.ðe.los\",\"ipa_mod\":\"ˈɑ.ðe̞.lows\",\"sbl\":\"adēlos\",\"dic\":\"AH-thay-lose\",\"dic_mod\":\"AH-thay-lose\"},\"see\":[\"G0001\",\"G1212\"]}', 'appear not, uncertain'),
(83, 83, 'ἀδηλότης', '{\"def\":{\"short\":\"uncertainty\",\"long\":[\"uncertainty\"]},\"deriv\":\"from G0082\",\"pronun\":{\"ipa\":\"ɑ.ðeˈlo.tes\",\"ipa_mod\":\"ɑ.ðe̞ˈlow.te̞s\",\"sbl\":\"adēlotēs\",\"dic\":\"ah-thay-LOH-tase\",\"dic_mod\":\"ah-thay-LOH-tase\"},\"see\":[\"G0082\"]}', 'X uncertain'),
(84, 84, 'ἀδήλως', '{\"def\":{\"short\":\"uncertainly\",\"long\":[\"uncertainly\"]},\"deriv\":\"adverb from G0082\",\"pronun\":{\"ipa\":\"ɑˈðe.los\",\"ipa_mod\":\"ɑˈðe̞.lows\",\"sbl\":\"adēlōs\",\"dic\":\"ah-THAY-lose\",\"dic_mod\":\"ah-THAY-lose\"},\"see\":[\"G0082\"]}', 'uncertainly'),
(85, 85, 'ἀδημονέω', '{\"def\":{\"short\":\"to be in distress (of mind)\",\"long\":[\"to be troubled, great distress or anguish, depressed\"]},\"deriv\":\"from a derivative of ἀδέω (to be sated to loathing)\",\"pronun\":{\"ipa\":\"ɑ.ðe.moˈnɛ.o\",\"ipa_mod\":\"ɑ.ðe̞.mowˈne̞.ow\",\"sbl\":\"adēmoneō\",\"dic\":\"ah-thay-moh-NEH-oh\",\"dic_mod\":\"ah-thay-moh-NAY-oh\"}}', 'be full of heaviness, be very heavy'),
(86, 86, 'ᾅδης', '{\"def\":{\"short\":\"properly, unseen, i.e., \\\"Hades\\\" or the place (state) of departed souls\",\"long\":[\"name Hades or Pluto, the god of the lower regions\",\"Orcus, the nether world, the realm of the dead\",\"the grave, death, hell\"]},\"deriv\":\"from G0001 (as negative particle) and G1492\",\"pronun\":{\"ipa\":\"ˈhɑ.ðes\",\"ipa_mod\":\"ˈɑ.ðe̞s\",\"sbl\":\"hadēs\",\"dic\":\"HA-thase\",\"dic_mod\":\"A-thase\"},\"see\":[\"G0001\",\"G1492\"]}', 'grave, hell'),
(87, 87, 'ἀδιάκριτος', '{\"def\":{\"short\":\"properly, undistinguished, i.e., (actively) impartial\",\"long\":[\"undistinguished, unintelligible\",\"without dubiousness, ambiguity or uncertainty\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G1252\",\"pronun\":{\"ipa\":\"ɑ.ðiˈɑ.kri.tos\",\"ipa_mod\":\"ɑ.ðiˈɑ.kri.tows\",\"sbl\":\"adiakritos\",\"dic\":\"ah-thee-AH-kree-tose\",\"dic_mod\":\"ah-thee-AH-kree-tose\"},\"see\":[\"G0001\",\"G1252\"]}', 'without partiality'),
(88, 88, 'ἀδιάλειπτος', '{\"def\":{\"short\":\"unintermitted, i.e., permanent\",\"long\":[\"unintermitted, unceasing, continual\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of a compound of G1223 and G3007\",\"pronun\":{\"ipa\":\"ɑ.ðiˈɑ.li.ptos\",\"ipa_mod\":\"ɑ.ðiˈɑ.li.ptows\",\"sbl\":\"adialeiptos\",\"dic\":\"ah-thee-AH-lee-ptose\",\"dic_mod\":\"ah-thee-AH-lee-ptose\"},\"see\":[\"G0001\",\"G1223\",\"G3007\"]}', 'without ceasing, continual'),
(89, 89, 'ἀδιαλείπτως', '{\"def\":{\"short\":\"uninterruptedly, i.e., without omission (on an appropriate occasion)\",\"long\":[\"without intermission, incessantly, without ceasing\"]},\"deriv\":\"adverb from G0088\",\"pronun\":{\"ipa\":\"ɑ.ði.ɑˈli.ptos\",\"ipa_mod\":\"ɑ.ði.ɑˈli.ptows\",\"sbl\":\"adialeiptōs\",\"dic\":\"ah-thee-ah-LEE-ptose\",\"dic_mod\":\"ah-thee-ah-LEE-ptose\"},\"see\":[\"G0088\"]}', 'without ceasing'),
(90, 90, 'ἀδιαφθορία', '{\"def\":{\"short\":\"incorruptibleness, i.e., (figuratively) purity (of doctrine)\",\"long\":[\"incorruptibility, soundness, integrity\",[\"of mind\"]]},\"deriv\":\"from a derivative of a compound of G0001 (as a negative particle) and a derivative of G1311\",\"pronun\":{\"ipa\":\"ɑ.ði.ɑ.fθoˈri.ɑ\",\"ipa_mod\":\"ɑ.ði.ɑ.fθowˈri.ɑ\",\"sbl\":\"adiaphthoria\",\"dic\":\"ah-thee-ah-fthoh-REE-ah\",\"dic_mod\":\"ah-thee-ah-fthoh-REE-ah\"},\"see\":[\"G0001\",\"G1311\"]}', 'uncorruptness'),
(91, 91, 'ἀδικέω', '{\"def\":{\"short\":\"to be unjust, i.e., (actively) do wrong (morally, socially or physically)\",\"long\":[\"absolutely\",[\"to act unjustly or wickedly, to sin,\",\"to be a criminal, to have violated the laws in some way\",\"to do wrong\",\"to do hurt\"],\"transitively\",[\"to do some wrong or sin in some respect\",\"to wrong some one, act wickedly towards him\",\"to hurt, damage, harm\"]]},\"deriv\":\"from G0094\",\"pronun\":{\"ipa\":\"ɑ.ðiˈkɛ.o\",\"ipa_mod\":\"ɑ.ðiˈke̞.ow\",\"sbl\":\"adikeō\",\"dic\":\"ah-thee-KEH-oh\",\"dic_mod\":\"ah-thee-KAY-oh\"},\"see\":[\"G0094\"]}', 'hurt, injure, be an offender, be unjust, (do, suffer, take) wrong'),
(92, 92, 'ἀδίκημα', '{\"def\":{\"short\":\"a wrong done\",\"long\":[\"a misdeed, evil doing, iniquity\"]},\"deriv\":\"from G0091\",\"pronun\":{\"ipa\":\"ɑˈði.ke.mɑ\",\"ipa_mod\":\"ɑˈði.ke̞.mɑ\",\"sbl\":\"adikēma\",\"dic\":\"ah-THEE-kay-ma\",\"dic_mod\":\"ah-THEE-kay-ma\"},\"see\":[\"G0091\"]}', 'evil doing, iniquity, matter of wrong'),
(93, 93, 'ἀδικία', '{\"def\":{\"short\":\"(legal) injustice (properly, the quality, by implication, the act); morally, wrongfulness (of character, life or act)\",\"long\":[\"injustice, of a judge\",\"unrighteousness of heart and life\",\"a deed violating law and justice, act of unrighteousness\"]},\"deriv\":\"from G0094\",\"pronun\":{\"ipa\":\"ɑ.ðiˈki.ɑ\",\"ipa_mod\":\"ɑ.ðiˈki.ɑ\",\"sbl\":\"adikia\",\"dic\":\"ah-thee-KEE-ah\",\"dic_mod\":\"ah-thee-KEE-ah\"},\"see\":[\"G0094\"]}', 'iniquity, unjust, unrighteousness, wrong'),
(94, 94, 'ἄδικος', '{\"def\":{\"short\":\"unjust; by extension wicked; by implication, treacherous; specially, heathen\",\"long\":[\"descriptive of one who violates or has violated justice\",[\"unjust\",\"unrighteous, sinful\",\"of one who deals fraudulently with others, deceitful\"]]},\"deriv\":\"from G0001 (as a negative particle) and G1349\",\"pronun\":{\"ipa\":\"ˈɑ.ði.kos\",\"ipa_mod\":\"ˈɑ.ði.kows\",\"sbl\":\"adikos\",\"dic\":\"AH-thee-kose\",\"dic_mod\":\"AH-thee-kose\"},\"see\":[\"G0001\",\"G1349\"]}', 'unjust, unrighteous'),
(95, 95, 'ἀδίκως', '{\"def\":{\"short\":\"unjustly\",\"long\":[\"unjustly, undeserved, without fault\"]},\"deriv\":\"adverb from G0094\",\"pronun\":{\"ipa\":\"ɑˈði.kos\",\"ipa_mod\":\"ɑˈði.kows\",\"sbl\":\"adikōs\",\"dic\":\"ah-THEE-kose\",\"dic_mod\":\"ah-THEE-kose\"},\"see\":[\"G0094\"]}', 'wrongfully'),
(96, 96, 'ἀδόκιμος', '{\"def\":{\"short\":\"unapproved, i.e., rejected; by implication, worthless (literally or morally)\",\"long\":[\"not standing the test, not approved\",[\"properly used of metals and coins\"],\"that which does not prove itself such as it ought\",[\"unfit for, unproved, spurious, reprobate\"]]},\"deriv\":\"from G0001 (as a negative particle) and G1384\",\"pronun\":{\"ipa\":\"ɑˈðo.ki.mos\",\"ipa_mod\":\"ɑˈðow.ci.mows\",\"sbl\":\"adokimos\",\"dic\":\"ah-THOH-kee-mose\",\"dic_mod\":\"ah-THOH-kee-mose\"},\"see\":[\"G0001\",\"G1384\"]}', 'castaway, rejected, reprobate'),
(97, 97, 'ἄδολος', '{\"def\":{\"short\":\"and G1388; undeceitful, i.e., (figuratively) unadulterated\",\"long\":[\"guileless\",[\"in things: unmixed, unadulterated, pure\",\"in persons: without dishonest intent, guileless\"]]},\"deriv\":\"from G0001 (as a negative particle)\",\"pronun\":{\"ipa\":\"ˈɑ.ðo.los\",\"ipa_mod\":\"ˈɑ.ðow.lows\",\"sbl\":\"adolos\",\"dic\":\"AH-thoh-lose\",\"dic_mod\":\"AH-thoh-lose\"},\"see\":[\"G0001\",\"G1388\"]}', 'sincere'),
(98, 98, 'Ἀδραμυττηνός', '{\"def\":{\"lit\":\"I shall abide in death\",\"short\":\"Adramyttene or belonging to Adramyttium\",\"long\":[\"Adramyttium, a seaport of Mysia\"]},\"deriv\":\"from Ἀδραμύττειον (a place in Asia Minor)\",\"pronun\":{\"ipa\":\"ɑ.ðrɑ.myt.teˈnos\",\"ipa_mod\":\"ɑ.ðrɑ.mjut.te̞ˈnows\",\"sbl\":\"adramyttēnos\",\"dic\":\"ah-thra-moot-tay-NOSE\",\"dic_mod\":\"ah-thra-myoot-tay-NOSE\"}}', 'of Adramyttium'),
(99, 99, 'Ἀδρίας', '{\"def\":{\"lit\":\"without wood\",\"short\":\"the Adriatic sea (including the Ionian)\",\"long\":[\"Adriatic Sea, Adrias, the sea between Greece and Italy\"]},\"deriv\":\"from Ἀδρία (a place near its shore)\",\"pronun\":{\"ipa\":\"ɑˈðri.ɑs\",\"ipa_mod\":\"ɑˈðri.ɑs\",\"sbl\":\"adrias\",\"dic\":\"ah-THREE-as\",\"dic_mod\":\"ah-THREE-as\"}}', 'Adria'),
(100, 100, 'ἁδρότης', '{\"def\":{\"short\":\"plumpness, i.e., (figuratively) liberality\",\"long\":[\"bountiful collection, great abundance\",\"thickness, ripeness, fullness, especially of the body\"]},\"deriv\":\"from ἁδρός (stout)\",\"pronun\":{\"ipa\":\"hɑˈðro.tes\",\"ipa_mod\":\"ɑˈðrow.te̞s\",\"sbl\":\"hadrotēs\",\"dic\":\"ha-THROH-tase\",\"dic_mod\":\"a-THROH-tase\"}}', 'abundance'),
(101, 101, 'ἀδυνατέω', '{\"def\":{\"short\":\"to be unable, i.e., (passively) impossible\",\"long\":[\"impossible\",[\"not to have strength, power, or ability, to be weak\",\"can not be done, to be impossible\"]]},\"deriv\":\"from G0102\",\"pronun\":{\"ipa\":\"ɑ.ðy.nɑˈtɛ.o\",\"ipa_mod\":\"ɑ.ðju.nɑˈte̞.ow\",\"sbl\":\"adynateō\",\"dic\":\"ah-thoo-na-TEH-oh\",\"dic_mod\":\"ah-thyoo-na-TAY-oh\"},\"see\":[\"G0102\"]}', 'be impossible'),
(102, 102, 'ἀδύνατος', '{\"def\":{\"short\":\"unable, i.e., weak (literally or figuratively); passively, impossible\",\"long\":[\"without strength, impotent, powerless, weakly, disabled\",\"unable to be done, impossible\"]},\"deriv\":\"from G0001 (as a negative particle) and G1415\",\"pronun\":{\"ipa\":\"ɑˈðy.nɑ.tos\",\"ipa_mod\":\"ɑˈðju.nɑ.tows\",\"sbl\":\"adynatos\",\"dic\":\"ah-THOO-na-tose\",\"dic_mod\":\"ah-THYOO-na-tose\"},\"see\":[\"G0001\",\"G1415\"]}', 'could not do, impossible, impotent, not possible, weak'),
(103, 103, 'ᾄδω', '{\"def\":{\"short\":\"to sing\",\"long\":[\"to the praise of anyone, to sing\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈɑ.ðo\",\"ipa_mod\":\"ˈɑ.ðow\",\"sbl\":\"adō\",\"dic\":\"AH-thoh\",\"dic_mod\":\"AH-thoh\"}}', 'sing'),
(104, 104, 'ἀεί', '{\"def\":{\"short\":\"\\\"ever,\\\" by qualification regularly; by implication, earnestly\",\"long\":[\"perpetually, incessantly\",\"invariably, at any and every time: when according to the circumstances something is or ought to be done again\"]},\"deriv\":\"from an obsolete primary noun (apparently meaning continued duration)\",\"pronun\":{\"ipa\":\"ɑˈi\",\"ipa_mod\":\"ɑˈi\",\"sbl\":\"aei\",\"dic\":\"ah-EE\",\"dic_mod\":\"ah-EE\"}}', 'always, ever'),
(105, 105, 'ἀετός', '{\"def\":{\"short\":\"an eagle (from its wind-like flight)\",\"long\":[\"an eagle: since eagles do not usually go in quest of carrion, this may to a vulture that resembles an eagle\",\"an eagle as a standard (Roman Military)\"]},\"deriv\":\"from the same as G0109\",\"pronun\":{\"ipa\":\"ɑ.ɛˈtos\",\"ipa_mod\":\"ɑ.e̞ˈtows\",\"sbl\":\"aetos\",\"dic\":\"ah-eh-TOSE\",\"dic_mod\":\"ah-ay-TOSE\"},\"see\":[\"G0109\"]}', 'eagle'),
(106, 106, 'ἄζυμος', '{\"def\":{\"short\":\"unleavened, i.e., (figuratively) uncorrupted; (in the neutral plural) specially (by implication) the Passover week\",\"long\":[\"unfermented, free from leaven or yeast\",[\"of the unleavened loaves used in the paschal feast of the Jews\",\"metaphorically free from faults or the \\\"leaven of iniquity\\\"\"]]},\"deriv\":\"from G0001 (as a negative particle) and G2219\",\"pronun\":{\"ipa\":\"ˈɑ.zy.mos\",\"ipa_mod\":\"ˈɑ.zju.mows\",\"sbl\":\"azymos\",\"dic\":\"AH-zoo-mose\",\"dic_mod\":\"AH-zyoo-mose\"},\"see\":[\"G0001\",\"G2219\"]}', 'unleavened (bread)'),
(107, 107, 'Ἀζώρ', '{\"def\":{\"lit\":\"helper\",\"short\":\"Azor, an Israelite\",\"long\":[\"A son of Eliakim, in the line of Jesus Christ\"]},\"deriv\":\"of Hebrew origin (compare H5809)\",\"pronun\":{\"ipa\":\"ɑˈzor\",\"ipa_mod\":\"ɑˈzowr\",\"sbl\":\"azōr\",\"dic\":\"ah-ZORE\",\"dic_mod\":\"ah-ZORE\"},\"see\":[\"H5809\"]}', 'Azor'),
(108, 108, 'Ἄζωτος', '{\"def\":{\"lit\":\"a stronghold\",\"short\":\"Azotus (i.e., Ashdod), a place in Palestine\",\"long\":[\"one of the five chief cities of the Philistines, lying between Ashkelon and Jamnia and near the Mediterranean\"]},\"deriv\":\"of Hebrew origin (H0795)\",\"pronun\":{\"ipa\":\"ˈɑ.zo.tos\",\"ipa_mod\":\"ˈɑ.zow.tows\",\"sbl\":\"azōtos\",\"dic\":\"AH-zoh-tose\",\"dic_mod\":\"AH-zoh-tose\"},\"see\":[\"H0795\"]}', 'Azotus'),
(109, 109, 'ἀήρ', '{\"def\":{\"short\":\"\\\"air\\\" (as naturally circumambient)\",\"long\":[\"the air, particularly the lower and denser air as distinguished from the higher and rarer air\",\"the atmospheric region\"]},\"deriv\":\"from ἄημι (to breathe unconsciously, i.e. respire; by analogy, to blow)\",\"pronun\":{\"ipa\":\"ɑˈer\",\"ipa_mod\":\"ɑˈe̞r\",\"sbl\":\"aēr\",\"dic\":\"ah-ARE\",\"dic_mod\":\"ah-ARE\"},\"see\":[\"G5594\"],\"comment\":\"Compare G5594.\"}', 'air'),
(110, 110, 'ἀθανασία', '{\"def\":{\"short\":\"deathlessness\",\"long\":[\"undying, immortality, everlasting\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and G2288\",\"pronun\":{\"ipa\":\"ɑ.θɑ.nɑˈsi.ɑ\",\"ipa_mod\":\"ɑ.θɑ.nɑˈsi.ɑ\",\"sbl\":\"athanasia\",\"dic\":\"ah-tha-na-SEE-ah\",\"dic_mod\":\"ah-tha-na-SEE-ah\"},\"see\":[\"G0001\",\"G2288\"]}', 'immortality'),
(111, 111, 'ἀθέμιτος', '{\"def\":{\"short\":\"illegal; by implication, flagitious\",\"long\":[\"contrary to law and justice, prohibited by law, illicit, criminal\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of θέμις (statute; from the base of G5087)\",\"pronun\":{\"ipa\":\"ɑˈθɛ.mi.tos\",\"ipa_mod\":\"ɑˈθe̞.mi.tows\",\"sbl\":\"athemitos\",\"dic\":\"ah-THEH-mee-tose\",\"dic_mod\":\"ah-THAY-mee-tose\"},\"see\":[\"G0001\",\"G5087\"]}', 'abominable, unlawful thing');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(112, 112, 'ἄθεος', '{\"def\":{\"short\":\"godless\",\"long\":[\"without God, knowing and worshipping no God\",\"denying the gods, especially the recognized gods of the state\",\"godless, ungodly\",\"abandoned by the gods\"]},\"deriv\":\"from G0001 (as a negative particle) and G2316\",\"pronun\":{\"ipa\":\"ˈɑ.θɛ.os\",\"ipa_mod\":\"ˈɑ.θe̞.ows\",\"sbl\":\"atheos\",\"dic\":\"AH-theh-ose\",\"dic_mod\":\"AH-thay-ose\"},\"see\":[\"G0001\",\"G2316\"]}', 'without God'),
(113, 113, 'ἄθεσμος', '{\"def\":{\"short\":\"lawless, i.e., (by implication) criminal\",\"long\":[\"one who breaks through the restraint of law and gratifies his lusts\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G5087 (in the sense of enacting)\",\"pronun\":{\"ipa\":\"ˈɑ.θɛ.smos\",\"ipa_mod\":\"ˈɑ.θe̞.smows\",\"sbl\":\"athesmos\",\"dic\":\"AH-theh-smose\",\"dic_mod\":\"AH-thay-smose\"},\"see\":[\"G0001\",\"G5087\"]}', 'wicked'),
(114, 114, 'ἀθετέω', '{\"def\":{\"short\":\"to set aside, i.e., (by implication) to disesteem, neutralize or violate\",\"long\":[\"to do away with, to set aside, disregard\",\"to thwart the efficacy of anything, nullify, make void, frustrate\",\"to reject, to refuse, to slight\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and a derivative of G5087\",\"pronun\":{\"ipa\":\"ɑ.θɛˈtɛ.o\",\"ipa_mod\":\"ɑ.θe̞ˈte̞.ow\",\"sbl\":\"atheteō\",\"dic\":\"ah-theh-TEH-oh\",\"dic_mod\":\"ah-thay-TAY-oh\"},\"see\":[\"G0001\",\"G5087\"]}', 'cast off, despise, disannul, frustrate, bring to nought, reject'),
(115, 115, 'ἀθέτησις', '{\"def\":{\"short\":\"cancellation (literally or figuratively)\",\"long\":[\"abolition, disannulling, put away, rejection\"]},\"deriv\":\"from G0114\",\"pronun\":{\"ipa\":\"ɑˈθɛ.te.sis\",\"ipa_mod\":\"ɑˈθe̞.te̞.sis\",\"sbl\":\"athetēsis\",\"dic\":\"ah-THEH-tay-sees\",\"dic_mod\":\"ah-THAY-tay-sees\"},\"see\":[\"G0114\"]}', 'disannulling, put away'),
(116, 116, 'Ἀθῆναι', '{\"def\":{\"lit\":\"uncertainty\",\"short\":\"Athenae, the capitol of Greece\",\"long\":[\"A famous city in Greece, the capital of Attica, and the chief seat of learning and civilisation during the golden period of the history of Greece\"]},\"deriv\":\"plural of Ἀθήνη (the goddess of wisdom, who was reputed to have founded the city)\",\"pronun\":{\"ipa\":\"ɑˈθe.nɛ\",\"ipa_mod\":\"ɑˈθe̞.ne\",\"sbl\":\"athēnai\",\"dic\":\"ah-THAY-neh\",\"dic_mod\":\"ah-THAY-nay\"}}', 'Athens'),
(117, 117, 'Ἀθηναῖος', '{\"def\":{\"short\":\"an Athenaean or inhabitant of Athenae\",\"long\":[\"a person who lives in Athens, an Athenian\"]},\"deriv\":\"from G0116\",\"pronun\":{\"ipa\":\"ɑ.θeˈnɛ.os\",\"ipa_mod\":\"ɑ.θe̞ˈne.ows\",\"sbl\":\"athēnaios\",\"dic\":\"ah-thay-NEH-ose\",\"dic_mod\":\"ah-thay-NAY-ose\"},\"see\":[\"G0116\"]}', 'Athenian'),
(118, 118, 'ἀθλέω', '{\"def\":{\"short\":\"to contend in the competitive games\",\"long\":[\"to engage in a contest, contend in public games, contend for a prize\",\"to endure, suffer\"]},\"deriv\":\"from ἄθλος (a contest in the public lists)\",\"pronun\":{\"ipa\":\"ɑˈθlɛ.o\",\"ipa_mod\":\"ɑˈθle̞.ow\",\"sbl\":\"athleō\",\"dic\":\"ah-THLEH-oh\",\"dic_mod\":\"ah-THLAY-oh\"}}', 'strive'),
(119, 119, 'ἄθλησις', '{\"def\":{\"short\":\"a struggle (figuratively)\",\"long\":[\"to contest, to combat, to strive, struggle, hard trial\"]},\"deriv\":\"from G0118\",\"pronun\":{\"ipa\":\"ˈɑ.θle.sis\",\"ipa_mod\":\"ˈɑ.θle̞.sis\",\"sbl\":\"athlēsis\",\"dic\":\"AH-thlay-sees\",\"dic_mod\":\"AH-thlay-sees\"},\"see\":[\"G0118\"]}', 'fight'),
(120, 120, 'ἀθυμέω', '{\"def\":{\"short\":\"to be spiritless, i.e., disheartened\",\"long\":[\"to be disheartened, dispirited, broken in spirit\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and G2372\",\"pronun\":{\"ipa\":\"ɑ.θyˈmɛ.o\",\"ipa_mod\":\"ɑ.θjuˈme̞.ow\",\"sbl\":\"athymeō\",\"dic\":\"ah-thoo-MEH-oh\",\"dic_mod\":\"ah-thyoo-MAY-oh\"},\"see\":[\"G0001\",\"G2372\"]}', 'be dismayed'),
(121, 121, 'ἄθωος', '{\"def\":{\"short\":\"not guilty\",\"long\":[\"not guilty, innocent, unpunished\"]},\"deriv\":\"from G0001 (as a negative particle) and probably a derivative of G5087 (meaning a penalty)\",\"pronun\":{\"ipa\":\"ˈɑ.θo.os\",\"ipa_mod\":\"ˈɑ.θow.ows\",\"sbl\":\"athōos\",\"dic\":\"AH-thoh-ose\",\"dic_mod\":\"AH-thoh-ose\"},\"see\":[\"G0001\",\"G5087\"]}', 'innocent'),
