(202, 202, 'ἀκροατής', '{\"def\":{\"short\":\"a hearer (merely)\",\"long\":[\"a hearer\"]},\"deriv\":\"from ἀκροάομαι (to listen; apparently an intensive of G0191)\",\"pronun\":{\"ipa\":\"ɑ.kro.ɑˈtes\",\"ipa_mod\":\"ɑ.krow.ɑˈte̞s\",\"sbl\":\"akroatēs\",\"dic\":\"ah-kroh-ah-TASE\",\"dic_mod\":\"ah-kroh-ah-TASE\"},\"see\":[\"G0191\"]}', 'hearer'),
(203, 203, 'ἀκροβυστία', '{\"def\":{\"short\":\"the prepuce; by implication, an uncircumcised (i.e., gentile, figuratively, unregenerate) state or person\",\"long\":[\"having the foreskin, uncircumcised\",\"a Gentile\",\"a condition in which the corrupt desires rooted in the flesh were not yet extinct\"]},\"deriv\":\"from G0206 and probably a modified form of πόσθη (the penis or male sexual organ)\",\"pronun\":{\"ipa\":\"ɑ.kro.βyˈsti.ɑ\",\"ipa_mod\":\"ɑ.krow.vjuˈsti.ɑ\",\"sbl\":\"akrobystia\",\"dic\":\"ah-kroh-voo-STEE-ah\",\"dic_mod\":\"ah-kroh-vyoo-STEE-ah\"},\"see\":[\"G0206\",\"G2192\"]}', 'not circumcised, uncircumcised (with G2192), uncircumcision'),
(204, 204, 'ἀκρογωνιαῖος', '{\"def\":{\"short\":\"belonging to the extreme corner\",\"long\":[\"placed at an extreme corner, the corner foundation stone\"]},\"deriv\":\"from G0206 and G1137\",\"pronun\":{\"ipa\":\"ɑ.kro.ɣo.niˈɛ.os\",\"ipa_mod\":\"ɑ.krow.ɣow.niˈe.ows\",\"sbl\":\"akrogōniaios\",\"dic\":\"ah-kroh-goh-nee-EH-ose\",\"dic_mod\":\"ah-kroh-goh-nee-A-ose\"},\"see\":[\"G0206\",\"G1137\"]}', 'chief corner'),
(205, 205, 'ἀκροθίνιον', '{\"def\":{\"short\":\"properly (in the plural) the top of the heap, i.e., (by implication) best of the booty\",\"long\":[\"top of the heap, the first fruits\",[\"best of the spoils or crops\"],\"the Greeks customarily selected from the topmost part of the heaps and offered this to the gods\"]},\"deriv\":\"from G0206 and θίς (a heap)\",\"pronun\":{\"ipa\":\"ɑ.kroˈθi.ni.on\",\"ipa_mod\":\"ɑ.krowˈθi.ni.own\",\"sbl\":\"akrothinion\",\"dic\":\"ah-kroh-THEE-nee-one\",\"dic_mod\":\"ah-kroh-THEE-nee-one\"},\"see\":[\"G0206\"]}', 'spoils'),
(206, 206, 'ἄκρον', '{\"def\":{\"short\":\"the extremity\",\"long\":[\"the farthest bounds, uttermost parts, end, highest, extreme\",[\"of the earth\",\"of heaven\"]]},\"deriv\":\"neuter of an adjective probably akin to the base of G0188\",\"pronun\":{\"ipa\":\"ˈɑ.kron\",\"ipa_mod\":\"ˈɑ.krown\",\"sbl\":\"akron\",\"dic\":\"AH-krone\",\"dic_mod\":\"AH-krone\"},\"see\":[\"G0188\"]}', 'one end... other, tip, top, uttermost participle'),
(207, 207, 'Ἀκύλας', '{\"def\":{\"lit\":\"an eagle\",\"short\":\"Akulas, an Israelite\",\"long\":[\"a Jew of Pontus, a tent maker convert to Christ, companion and ally of Paul in propagating Christianity\"]},\"deriv\":\"probably for Latin <latin>aquila</latin> (an eagle)\",\"pronun\":{\"ipa\":\"ɑˈky.lɑs\",\"ipa_mod\":\"ɑˈkju.lɑs\",\"sbl\":\"akylas\",\"dic\":\"ah-KOO-lahs\",\"dic_mod\":\"ah-KYOO-lahs\"}}', 'Aquila'),
(208, 208, 'ἀκυρόω', '{\"def\":{\"short\":\"to invalidate\",\"long\":[\"to render void, deprive of force and authority\"]},\"deriv\":\"from G0001 (as a negative particle) and G2964\",\"pronun\":{\"ipa\":\"ɑ.kyˈro.o\",\"ipa_mod\":\"ɑ.cjuˈrow.ow\",\"sbl\":\"akyroō\",\"dic\":\"ah-koo-ROH-oh\",\"dic_mod\":\"ah-kyoo-ROH-oh\"},\"see\":[\"G0001\",\"G2964\"]}', 'disannul, make of none effect'),
(209, 209, 'ἀκωλύτως', '{\"def\":{\"short\":\"in an unhindered manner, i.e., freely\",\"long\":[\"without hindrances, freely\"]},\"deriv\":\"adverb from a compound of G0001 (as a negative particle) and a derivative of G2967\",\"pronun\":{\"ipa\":\"ɑ.koˈly.tos\",\"ipa_mod\":\"ɑ.kowˈlju.tows\",\"sbl\":\"akōlytōs\",\"dic\":\"ah-koh-LOO-tose\",\"dic_mod\":\"ah-koh-LYOO-tose\"},\"see\":[\"G0001\",\"G2967\"]}', 'no man forbidding him'),
(210, 210, 'ἄκων', '{\"def\":{\"short\":\"unwilling\",\"long\":[\"not of one\'s own will, unwillingly, against the will\"]},\"deriv\":\"from G0001 (as a negative particle) and G1635\",\"pronun\":{\"ipa\":\"ˈɑ.kon\",\"ipa_mod\":\"ˈɑ.kown\",\"sbl\":\"akōn\",\"dic\":\"AH-kone\",\"dic_mod\":\"AH-kone\"},\"see\":[\"G0001\",\"G1635\"]}', 'against the will'),
(211, 211, 'ἀλάβαστρον', '{\"def\":{\"short\":\"properly, an \\\"alabaster\\\" box, i.e., (by extension) a perfume vase (of any material)\",\"long\":[\"a box made of alabaster in which unguents are preserved\"]},\"deriv\":\"neuter of ἀλάβαστρος (of uncertain derivation), the name of a stone\",\"pronun\":{\"ipa\":\"ɑˈlɑ.βɑ.stron\",\"ipa_mod\":\"ɑˈlɑ.vɑ.strown\",\"sbl\":\"alabastron\",\"dic\":\"ah-LA-va-strone\",\"dic_mod\":\"ah-LA-va-strone\"}}', '(alabaster) box'),
(212, 212, 'ἀλαζονεία', '{\"def\":{\"short\":\"braggadocio, i.e., (by implication) self-confidence\",\"long\":[\"empty, braggart talk\",\"an insolent and empty assurance, which trusts in its own power and resources and shamefully despises and violates divine laws and human rights\",\"an impious and empty presumption which trusts in the stability of earthy things\"]},\"deriv\":\"from G0213\",\"pronun\":{\"ipa\":\"ɑ.lɑ.zoˈni.ɑ\",\"ipa_mod\":\"ɑ.lɑ.zowˈni.ɑ\",\"sbl\":\"alazoneia\",\"dic\":\"ah-la-zoh-NEE-ah\",\"dic_mod\":\"ah-la-zoh-NEE-ah\"},\"see\":[\"G0213\"]}', 'boasting, pride'),
(213, 213, 'ἀλαζών', '{\"def\":{\"short\":\"braggart\",\"long\":[\"an empty pretender, a boaster\"]},\"deriv\":\"from ἄλη (vagrancy)\",\"pronun\":{\"ipa\":\"ɑ.lɑˈzon\",\"ipa_mod\":\"ɑ.lɑˈzown\",\"sbl\":\"alazōn\",\"dic\":\"ah-la-ZONE\",\"dic_mod\":\"ah-la-ZONE\"}}', 'boaster'),
(214, 214, 'ἀλαλάζω', '{\"def\":{\"short\":\"to vociferate, i.e., (by implication) to wail; figuratively, to clang\",\"long\":[\"to repeat frequently the cry \\\"alala\\\" as soldiers used to do on\\r entering into battle\",\"to utter a joyful sound\",\"to wail, lament\",\"to ring loudly, to clang\"]},\"deriv\":\"from ἀλαλή ( a shout, \\\"halloo\\\")\",\"pronun\":{\"ipa\":\"ɑ.lɑˈlɑ.zo\",\"ipa_mod\":\"ɑ.lɑˈlɑ.zow\",\"sbl\":\"alalazō\",\"dic\":\"ah-la-LA-zoh\",\"dic_mod\":\"ah-la-LA-zoh\"}}', 'tinkle, wail'),
(215, 215, 'ἀλάλητος', '{\"def\":{\"short\":\"unspeakable\",\"long\":[\"not to be uttered, not expressed in words\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G2980\",\"pronun\":{\"ipa\":\"ɑˈlɑ.le.tos\",\"ipa_mod\":\"ɑˈlɑ.le̞.tows\",\"sbl\":\"alalētos\",\"dic\":\"ah-LA-lay-tose\",\"dic_mod\":\"ah-LA-lay-tose\"},\"see\":[\"G0001\",\"G2980\"]}', 'unutterable, which cannot be uttered'),
(216, 216, 'ἄλαλος', '{\"def\":{\"short\":\"mute\",\"long\":[\"speechless, dumb, wanting the faculty of speech\"]},\"deriv\":\"from G0001 (as a negative particle) and G2980\",\"pronun\":{\"ipa\":\"ˈɑ.lɑ.los\",\"ipa_mod\":\"ˈɑ.lɑ.lows\",\"sbl\":\"alalos\",\"dic\":\"AH-la-lose\",\"dic_mod\":\"AH-la-lose\"},\"see\":[\"G0001\",\"G2980\"]}', 'dumb'),
(217, 217, 'ἅλας', '{\"def\":{\"short\":\"salt; figuratively, prudence\",\"long\":[\"salt with which food is seasoned and sacrifices are sprinkled\",\"those kinds of saline matter used to fertilize arable land\",\"salt is a symbol of lasting concord\",\"wisdom and grace exhibited in speech\"]},\"deriv\":\"from G0251\",\"pronun\":{\"ipa\":\"ˈhɑ.lɑs\",\"ipa_mod\":\"ˈɑ.lɑs\",\"sbl\":\"halas\",\"dic\":\"HA-lahs\",\"dic_mod\":\"A-lahs\"},\"see\":[\"G0251\"],\"comment\":\"Because salt protects food from putrefaction and preserves it unchanged it is a symbol of a lasting agreement. Accordingly, in the solemn ratification of compacts, Middle Easterners were, and are to this day, accustomed to partake of salt together.\"}', 'salt'),
(218, 218, 'ἀλείφω', '{\"def\":{\"short\":\"to oil (with perfume)\",\"long\":[\"to anoint\"]},\"deriv\":\"from G0001 (as particle of union) and the base of G3045\",\"pronun\":{\"ipa\":\"ɑˈli.fo\",\"ipa_mod\":\"ɑˈli.fow\",\"sbl\":\"aleiphō\",\"dic\":\"ah-LEE-foh\",\"dic_mod\":\"ah-LEE-foh\"},\"see\":[\"G0001\",\"G3045\"]}', 'anoint'),
(219, 219, 'ἀλεκτοροφωνία', '{\"def\":{\"short\":\"cock-crow, i.e., the third night-watch\",\"long\":[\"the crowing of a cock or rooster, cock-crowing\",[\"used of the third watch of the night\"]]},\"deriv\":\"from G0220 and G5456\",\"pronun\":{\"ipa\":\"ɑ.lɛk.to.ro.foˈni.ɑ\",\"ipa_mod\":\"ɑ.le̞k.tow.row.fowˈni.ɑ\",\"sbl\":\"alektorophōnia\",\"dic\":\"ah-lek-toh-roh-foh-NEE-ah\",\"dic_mod\":\"ah-lake-toh-roh-foh-NEE-ah\"},\"see\":[\"G0220\",\"G5456\"]}', 'cockcrowing'),
(220, 220, 'ἀλέκτωρ', '{\"def\":{\"short\":\"a cock or male fowl\",\"long\":[\"a cock, or male of any bird, a rooster\"]},\"deriv\":\"from ἀλέκω (to ward off)\",\"pronun\":{\"ipa\":\"ɑˈlɛk.tor\",\"ipa_mod\":\"ɑˈle̞k.towr\",\"sbl\":\"alektōr\",\"dic\":\"ah-LEK-tore\",\"dic_mod\":\"ah-LAKE-tore\"}}', 'cock'),
(221, 221, 'Ἀλεξανδρεύς', '{\"def\":{\"short\":\"an Alexandreian or inhabitant of Alexandria\",\"long\":[\"a native or resident of Alexandria in Egypt\"]},\"deriv\":\"from Ἀλεξάνδρεια (the city so called)\",\"pronun\":{\"ipa\":\"ɑ.lɛ.k͡sɑnˈðrɛβs\",\"ipa_mod\":\"ɑ.le̞.k͡sɑn̪ˈðrefs\",\"sbl\":\"alexandreus\",\"dic\":\"ah-leh-ksahn-THREVS\",\"dic_mod\":\"ah-lay-ksahn-THRAYFS\"}}', 'of Alexandria, Alexandrian'),
(222, 222, 'Ἀλεξανδρῖνος', '{\"def\":{\"short\":\"Alexandrine, or belonging to Alexandria\",\"long\":[\"a native or resident of Alexandria in Egypt\",\"of Alexandria or belonging to Alexandria\"]},\"deriv\":\"from the same as G0221\",\"pronun\":{\"ipa\":\"ɑ.lɛ.k͡sɑnˈðri.nos\",\"ipa_mod\":\"ɑ.le̞.k͡sɑn̪ˈðri.nows\",\"sbl\":\"alexandrinos\",\"dic\":\"ah-leh-ksahn-THREE-nose\",\"dic_mod\":\"ah-lay-ksahn-THREE-nose\"},\"see\":[\"G0221\"]}', 'of Alexandria'),
(223, 223, 'Ἀλέξανδρος', '{\"def\":{\"lit\":\"the defender of man\",\"short\":\"Alexander, the name of three Israelites and one other man\",\"long\":[\"son of Simon of Cyrene who carried Jesus\'s cross, Mark 15:21\",\"a certain man of the kindred of the high priest, Acts 4:6\",\"a certain Jew, Acts 19:33\",\"a certain coppersmith who opposed the Apostle Paul, 1 Timothy 1:20\",\"man-defender, the protector of men\"]},\"deriv\":\"from the same as (the first part of) G0220 and G0435\",\"pronun\":{\"ipa\":\"ɑˈlɛ.k͡sɑn.ðros\",\"ipa_mod\":\"ɑˈle̞.k͡sɑn̪.ðrows\",\"sbl\":\"alexandros\",\"dic\":\"ah-LEH-ksahn-throse\",\"dic_mod\":\"ah-LAY-ksahn-throse\"},\"see\":[\"G0220\",\"G0435\"]}', 'Alexander'),
(224, 224, 'ἄλευρον', '{\"def\":{\"short\":\"flour\",\"long\":[\"wheat flour, meal\"]},\"deriv\":\"from ἀλέω(to grind)\",\"pronun\":{\"ipa\":\"ˈɑ.lɛβ.ron\",\"ipa_mod\":\"ˈɑ.lev.rown\",\"sbl\":\"aleuron\",\"dic\":\"AH-lev-rone\",\"dic_mod\":\"AH-lave-rone\"}}', 'meal'),
(225, 225, 'ἀλήθεια', '{\"def\":{\"short\":\"truth\",\"long\":[\"objectively\",[\"what is true in any matter under consideration\",[\"truly, in truth, according to truth\",\"of a truth, in reality, in fact, certainly\"],\"what is true in things appertaining to God and the duties of man, moral and religious truth\",[\"in the greatest latitude\",\"the true notions of God which are open to human reason without his supernatural intervention\"],\"the truth as taught in the Christian religion, respecting God and the execution of his purposes through Christ, and respecting the duties of man, opposing alike to the superstitions of the Gentiles and the inventions of the Jews, and the corrupt opinions and precepts of false teachers even among Christians\"],\"subjectively\",[\"truth as a personal excellence\",[\"that candour of mind which is free from affection, pretence, simulation, falsehood, deceit\"]]]},\"deriv\":\"from G0227\",\"pronun\":{\"ipa\":\"ɑˈle.θi.ɑ\",\"ipa_mod\":\"ɑˈle̞.θi.ɑ\",\"sbl\":\"alētheia\",\"dic\":\"ah-LAY-thee-ah\",\"dic_mod\":\"ah-LAY-thee-ah\"},\"see\":[\"G0227\"]}', 'true, X truly, truth, verity');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(226, 226, 'ἀληθεύω', '{\"def\":{\"short\":\"to be true (in doctrine and profession)\",\"long\":[\"to speak or tell the truth\",[\"to teach the truth\",\"to profess the truth\"]]},\"deriv\":\"from G0227\",\"pronun\":{\"ipa\":\"ɑ.leˈθɛβ.o\",\"ipa_mod\":\"ɑ.le̞ˈθev.ow\",\"sbl\":\"alētheuō\",\"dic\":\"ah-lay-THEV-oh\",\"dic_mod\":\"ah-lay-THAVE-oh\"},\"see\":[\"G0227\"]}', 'speak (tell) the truth'),
(227, 227, 'ἀληθής', '{\"def\":{\"short\":\"true (as not concealing)\",\"long\":[\"true\",\"loving the truth, speaking the truth, truthful\"]},\"deriv\":\"from G0001 (as a negative particle) and G2990\",\"pronun\":{\"ipa\":\"ɑ.leˈθes\",\"ipa_mod\":\"ɑ.le̞ˈθe̞s\",\"sbl\":\"alēthēs\",\"dic\":\"ah-lay-THASE\",\"dic_mod\":\"ah-lay-THASE\"},\"see\":[\"G0001\",\"G2990\"]}', 'true, truly, truth'),
(228, 228, 'ἀληθινός', '{\"def\":{\"short\":\"truthful\",\"long\":[\"that which has not only the name and resemblance, but the real nature corresponding to the name, in every respect corresponding to the idea signified by the name, real, true genuine\",[\"opposite to what is fictitious, counterfeit, imaginary, simulated or pretended\",\"it contrasts realities with their semblances\",\"opposite to what is imperfect defective, frail, uncertain\"],\"true, veracious, sincere\"]},\"deriv\":\"from G0227\",\"pronun\":{\"ipa\":\"ɑ.le.θiˈnos\",\"ipa_mod\":\"ɑ.le̞.θiˈnows\",\"sbl\":\"alēthinos\",\"dic\":\"ah-lay-thee-NOSE\",\"dic_mod\":\"ah-lay-thee-NOSE\"},\"see\":[\"G0227\"]}', 'true'),
(229, 229, 'ἀλήθω', '{\"def\":{\"short\":\"to grind\",\"long\":[\"to grind\"]},\"deriv\":\"from the same as G0224\",\"pronun\":{\"ipa\":\"ɑˈle.θo\",\"ipa_mod\":\"ɑˈle̞.θow\",\"sbl\":\"alēthō\",\"dic\":\"ah-LAY-thoh\",\"dic_mod\":\"ah-LAY-thoh\"},\"see\":[\"G0224\"],\"comment\":\"It was the custom to send women and female slaves to the mill houses to turn the hand mills.\"}', 'grind'),
(230, 230, 'ἀληθῶς', '{\"def\":{\"short\":\"truly\",\"long\":[\"truly, of a truth, in reality, most certainly\"]},\"deriv\":\"adverb from G0227\",\"pronun\":{\"ipa\":\"ɑ.leˈθos\",\"ipa_mod\":\"ɑ.le̞ˈθows\",\"sbl\":\"alēthōs\",\"dic\":\"ah-lay-THOSE\",\"dic_mod\":\"ah-lay-THOSE\"},\"see\":[\"G0227\"]}', 'indeed, surely, of a surety, truly, of a (in) truth, verily, very'),
(231, 231, 'ἁλιεύς', '{\"def\":{\"short\":\"a sailor (as engaged on the salt water), i.e., (by implication) a fisher\",\"long\":[\"a fisherman, fisher\"]},\"deriv\":\"from G0251\",\"pronun\":{\"ipa\":\"hɑ.liˈɛβs\",\"ipa_mod\":\"ɑ.liˈefs\",\"sbl\":\"halieus\",\"dic\":\"ha-lee-EVS\",\"dic_mod\":\"a-lee-AYFS\"},\"see\":[\"G0251\"]}', 'fisher(-man)'),
(232, 232, 'ἁλιεύω', '{\"def\":{\"short\":\"to be a fisher, i.e., (by implication) to fish\",\"long\":[\"to fish\"]},\"deriv\":\"from G0231\",\"pronun\":{\"ipa\":\"hɑ.liˈɛβ.o\",\"ipa_mod\":\"ɑ.liˈev.ow\",\"sbl\":\"halieuō\",\"dic\":\"ha-lee-EV-oh\",\"dic_mod\":\"a-lee-AVE-oh\"},\"see\":[\"G0231\"]}', 'go a-fishing'),
(233, 233, 'ἁλίζω', '{\"def\":{\"short\":\"to salt\",\"long\":[\"to salt, season with salt, sprinkle with salt\"]},\"deriv\":\"from G0251\",\"pronun\":{\"ipa\":\"hɑˈli.zo\",\"ipa_mod\":\"ɑˈli.zow\",\"sbl\":\"halizō\",\"dic\":\"ha-LEE-zoh\",\"dic_mod\":\"a-LEE-zoh\"},\"see\":[\"G0251\"]}', 'salt'),
(234, 234, 'ἀλίσγεμα', '{\"def\":{\"short\":\"(ceremonially) defilement\",\"long\":[\"pollution, condemnation\"]},\"deriv\":\"from ἀλισγέω (to soil)\",\"pronun\":{\"ipa\":\"ɑˈlis.ɣɛ.mɑ\",\"ipa_mod\":\"ɑˈlis.ʝe̞.mɑ\",\"sbl\":\"alisgema\",\"dic\":\"ah-LEES-geh-ma\",\"dic_mod\":\"ah-LEES-gay-ma\"}}', 'pollution'),
(235, 235, 'ἀλλά', '{\"def\":{\"short\":\"properly, other things, i.e., (adverbially) contrariwise (in many relations)\",\"long\":[\"but\",[\"nevertheless, notwithstanding\",\"an objection\",\"an exception\",\"a restriction\",\"nay, rather, yea, moreover\",\"forms a transition to the cardinal matter\"]]},\"deriv\":\"neuter plural of G0243\",\"pronun\":{\"ipa\":\"ɑlˈlɑ\",\"ipa_mod\":\"ɑlˈlɑ\",\"sbl\":\"alla\",\"dic\":\"al-LA\",\"dic_mod\":\"al-LA\"},\"see\":[\"G0243\"]}', 'and, but (even), howbeit, indeed, nay, nevertheless, no, notwithstanding, save, therefore, yea, yet'),
(236, 236, 'ἀλλάσσω', '{\"def\":{\"short\":\"to make different\",\"long\":[\"to change, to exchange one thing for another, to transform\"]},\"deriv\":\"from G0243\",\"pronun\":{\"ipa\":\"ɑlˈlɑs.so\",\"ipa_mod\":\"ɑlˈlɑs.sow\",\"sbl\":\"allassō\",\"dic\":\"al-LAHS-soh\",\"dic_mod\":\"al-LAHS-soh\"},\"see\":[\"G0243\"]}', 'change'),
(237, 237, 'ἀλλαχόθεν', '{\"def\":{\"short\":\"from elsewhere\",\"long\":[\"from another place\"]},\"deriv\":\"from G0243\",\"pronun\":{\"ipa\":\"ɑl.lɑˈxo.θɛn\",\"ipa_mod\":\"ɑl.lɑˈxow.θe̞n\",\"sbl\":\"allachothen\",\"dic\":\"al-la-HOH-then\",\"dic_mod\":\"al-la-HOH-thane\"},\"see\":[\"G0243\"]}', 'some other way'),
(238, 238, 'ἀλληγορέω', '{\"def\":{\"short\":\"to allegorize\",\"long\":[\"to speak allegorically or in a figure\"]},\"deriv\":\"from G0243 and ἀγορέω (to harangue (compare G0058))\",\"pronun\":{\"ipa\":\"ɑl.le.ɣoˈrɛ.o\",\"ipa_mod\":\"ɑl.le̞.ɣowˈre̞.ow\",\"sbl\":\"allēgoreō\",\"dic\":\"al-lay-goh-REH-oh\",\"dic_mod\":\"al-lay-goh-RAY-oh\"},\"see\":[\"G0058\",\"G0243\"]}', 'be an allegory (the Greek word itself)'),
(239, 239, 'ἀλληλουϊα', '{\"def\":{\"short\":\"praise ye Jah!, an adoring exclamation\",\"long\":[\"praise ye the Lord, Hallelujah\"]},\"deriv\":\"of Hebrew origin (imperative of H1984 and H3050)\",\"pronun\":{\"ipa\":\"ɑl.le.lu.i.ɑ\",\"ipa_mod\":\"ɑl.le̞.lu.i.ɑ\",\"sbl\":\"allēlouia\",\"dic\":\"al-lay-loo-ee-ah\",\"dic_mod\":\"al-lay-loo-ee-ah\"},\"see\":[\"H1984\",\"H3050\"]}', 'alleluiah'),
(240, 240, 'ἀλλήλων', '{\"def\":{\"short\":\"one another\",\"long\":[\"one another, reciprocally, mutually\"]},\"deriv\":\"Genitive plural from G0243 reduplicated\",\"pronun\":{\"ipa\":\"ɑlˈle.lon\",\"ipa_mod\":\"ɑlˈle̞.lown\",\"sbl\":\"allēlōn\",\"dic\":\"al-LAY-lone\",\"dic_mod\":\"al-LAY-lone\"},\"see\":[\"G0243\",\"G3326\",\"G4314\"]}', 'each other, mutual, one another, (the other), (them-, your-)selves, (selves) together (sometimes with G3326 or G4314)'),
(241, 241, 'ἀλλογενής', '{\"def\":{\"short\":\"foreign, i.e., not a Jew\",\"long\":[\"sprung from another race, a foreigner, alien\"]},\"deriv\":\"from G0243 and G1085\",\"pronun\":{\"ipa\":\"ɑl.lo.ɣɛˈnes\",\"ipa_mod\":\"ɑl.low.ʝe̞ˈne̞s\",\"sbl\":\"allogenēs\",\"dic\":\"al-loh-geh-NASE\",\"dic_mod\":\"al-loh-gay-NASE\"},\"see\":[\"G0243\",\"G1085\"]}', 'stranger'),
(242, 242, 'ἅλλομαι', '{\"def\":{\"short\":\"to jump; figuratively, to gush\",\"long\":[\"to leap\",\"to spring up, gush up: of water\"]},\"deriv\":\"middle voice of apparently a primary verb\",\"pronun\":{\"ipa\":\"ˈhɑl.lo.mɛ\",\"ipa_mod\":\"ˈɑl.low.me\",\"sbl\":\"hallomai\",\"dic\":\"HAHL-loh-meh\",\"dic_mod\":\"AHL-loh-may\"}}', 'leap, spring up'),
(243, 243, 'ἄλλος', '{\"def\":{\"short\":\"\\\"else,\\\" i.e., different (in many applications)\",\"long\":[\"another, other\"]},\"deriv\":\"a primary word\",\"pronun\":{\"ipa\":\"ˈɑl.los\",\"ipa_mod\":\"ˈɑl.lows\",\"sbl\":\"allos\",\"dic\":\"AL-lose\",\"dic_mod\":\"AL-lose\"}}', 'more, one (another), (an-, some an-)other(-s, -wise)'),
