(251, 251, 'ἅλς', '{\"def\":{\"short\":\"\\\"salt\\\"\",\"long\":[\"salt\"]},\"deriv\":\"a primary word\",\"pronun\":{\"ipa\":\"ˈhɑl.s\",\"ipa_mod\":\"ˈɑl.s\",\"sbl\":\"hals\",\"dic\":\"HAHL-s\",\"dic_mod\":\"AHL-s\"}}', 'salt'),
