(281, 281, 'ἀμήν', '{\"def\":{\"short\":\"properly, firm, i.e., (figuratively) trustworthy; adverbially, surely (often as interjection, so be it)\",\"long\":[\"firm\",[\"metaphorically faithful\"],\"verily, amen\",[\"at the beginning of a discourse: surely, truly, of a truth\",\"at the end: so it is, so be it, may it be fulfilled\"]]},\"deriv\":\"of Hebrew origin (H0543)\",\"pronun\":{\"ipa\":\"ɑˈmen\",\"ipa_mod\":\"ɑˈme̞n\",\"sbl\":\"amēn\",\"dic\":\"ah-MANE\",\"dic_mod\":\"ah-MANE\"},\"see\":[\"H0543\"],\"comment\":\"It was a custom, which passed over from the synagogues to the Christian assemblies, that when he who had read or discoursed, had offered up solemn prayer to God, the others responded \\\"Amen,\\\" and thus made the substance of what was uttered their own.\"}', 'amen, verily'),
(282, 282, 'ἀμήτωρ', '{\"def\":{\"short\":\"motherless, i.e., of unknown maternity\",\"long\":[\"born without a mother\",\"bereft of a mother\",\"born of a base or unknown mother\",\"unmotherly, not worthy of the name of mother\"]},\"deriv\":\"from G0001 (as a negative particle) and G3384\",\"pronun\":{\"ipa\":\"ɑˈme.tor\",\"ipa_mod\":\"ɑˈme̞.towr\",\"sbl\":\"amētōr\",\"dic\":\"ah-MAY-tore\",\"dic_mod\":\"ah-MAY-tore\"},\"see\":[\"G0001\",\"G3384\"]}', 'without mother'),
(283, 283, 'ἀμίαντος', '{\"def\":{\"short\":\"unsoiled, i.e., (figuratively) pure\",\"long\":[\"not defiled, unsoiled\",[\"free from that by which the nature of a thing is deformed and debased, or its force and vigour impaired\"]]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G3392\",\"pronun\":{\"ipa\":\"ɑˈmi.ɑn.tos\",\"ipa_mod\":\"ɑˈmi.ɑn.tows\",\"sbl\":\"amiantos\",\"dic\":\"ah-MEE-an-tose\",\"dic_mod\":\"ah-MEE-an-tose\"},\"see\":[\"G0001\",\"G3392\"]}', 'undefiled'),
