(322, 322, 'ἀναδείκνυμι', '{\"def\":{\"short\":\"to exhibit, i.e., (by implication) to indicate, appoint\",\"long\":[\"to proclaim any one as elected to office\",\"to announce as appointed a king, general, etc.\",\"to lift up anything on high and exhibit it for all to behold\"]},\"deriv\":\"from G0303 and G1166\",\"pronun\":{\"ipa\":\"ɑ.nɑˈði.kny.mi\",\"ipa_mod\":\"ɑ.nɑˈði.knju.mi\",\"sbl\":\"anadeiknymi\",\"dic\":\"ah-na-THEE-knoo-mee\",\"dic_mod\":\"ah-na-THEE-knyoo-mee\"},\"see\":[\"G0303\",\"G1166\"]}', 'appoint, shew'),
