(489, 489, 'ἀντιμισθία', '{\"def\":{\"short\":\"requital, correspondence\",\"long\":[\"a reward given in compensation, requital, recompence\"]},\"deriv\":\"from a compound of G0473 and G3408\",\"pronun\":{\"ipa\":\"ɑn.ti.miˈsθi.ɑ\",\"ipa_mod\":\"ɑn.ti.miˈsθi.ɑ\",\"sbl\":\"antimisthia\",\"dic\":\"an-tee-mee-STHEE-ah\",\"dic_mod\":\"an-tee-mee-STHEE-ah\"},\"see\":[\"G0473\",\"G3408\"]}', 'recompense'),
(490, 490, 'Ἀντιόχεια', '{\"def\":{\"lit\":\"driven against\",\"short\":\"Antioch (Antiochia), a place in Syria\",\"long\":[\"Antioch, the capital of Syria and center of the early Christianity\",\"Antioch in Pisidia, a city currently located in Turkey where Paul often visited on his missionary journeys\"]},\"deriv\":\"from Ἀντίοχυς (a Syrian king)\",\"pronun\":{\"ipa\":\"ɑn.tiˈo.xi.ɑ\",\"ipa_mod\":\"ɑn.tiˈow.çi.ɑ\",\"sbl\":\"antiocheia\",\"dic\":\"an-tee-OH-hee-ah\",\"dic_mod\":\"an-tee-OH-hee-ah\"},\"comment\":\"Antioch in Syria is situated on the borders on the Orontes river, founded by Seleucus Nicanor in 300 BC and named in honor of his father, Antiochus. Many Greek-Jews lived there and it was here that the followers of Christ were first called Christians.\"}', 'Antioch'),
(491, 491, 'Ἀντιοχεύς', '{\"def\":{\"short\":\"an Antiochian or inhabitant of Antiochia\",\"long\":[\"an Antiochian, a native of Antioch\"]},\"deriv\":\"from G0490\",\"pronun\":{\"ipa\":\"ɑn.ti.oˈxɛβs\",\"ipa_mod\":\"ɑn.ti.owˈçefs\",\"sbl\":\"antiocheus\",\"dic\":\"an-tee-oh-HEVS\",\"dic_mod\":\"an-tee-oh-HAYFS\"},\"see\":[\"G0490\"]}', 'of Antioch'),
(492, 492, 'ἀντιπαρέρχομαι', '{\"def\":{\"short\":\"to go along opposite\",\"long\":[\"to pass by opposite to\"]},\"deriv\":\"from G0473 and G3928\",\"pronun\":{\"ipa\":\"ɑn.ti.pɑˈrɛr.xo.mɛ\",\"ipa_mod\":\"ɑn.ti.pɑˈre̞r.xow.me\",\"sbl\":\"antiparerchomai\",\"dic\":\"an-tee-pa-RER-hoh-meh\",\"dic_mod\":\"an-tee-pa-RARE-hoh-may\"},\"see\":[\"G0473\",\"G3928\"]}', 'pass by on the other side'),
(493, 493, 'Ἀντίπας', '{\"def\":{\"lit\":\"like the father\",\"short\":\"Antipas, a Christian\",\"long\":[\"a Christian of Pergamos who suffered martyrdom\"]},\"deriv\":\"contracted for a compound of G0473 and a derivative of G3962\",\"pronun\":{\"ipa\":\"ɑnˈti.pɑs\",\"ipa_mod\":\"ɑnˈti.pɑs\",\"sbl\":\"antipas\",\"dic\":\"an-TEE-pahs\",\"dic_mod\":\"an-TEE-pahs\"},\"see\":[\"G0473\",\"G3962\"]}', 'Antipas'),
(494, 494, 'Ἀντιπατρίς', '{\"def\":{\"lit\":\"for forefather\",\"short\":\"Antipatris, a place in Palestine\",\"long\":[\"a city situated between Joppa and Caesarea, in a very fertile region, not far from the coast; rebuilt by Herod the Great and named Antipatris in honor of his father Antipater\"]},\"deriv\":\"from the same as G0493\",\"pronun\":{\"ipa\":\"ɑn.ti.pɑˈtris\",\"ipa_mod\":\"ɑn.ti.pɑˈtris\",\"sbl\":\"antipatris\",\"dic\":\"an-tee-pa-TREES\",\"dic_mod\":\"an-tee-pa-TREES\"},\"see\":[\"G0493\"]}', 'Antipatris'),
