(496, 496, 'ἀντιπίπτω', '{\"def\":{\"short\":\"to oppose\",\"long\":[\"to fall upon, run against\",\"to be adverse, oppose, strive against\"]},\"deriv\":\"from G0473 and G4098 (including its alternate)\",\"pronun\":{\"ipa\":\"ɑn.tiˈpi.pto\",\"ipa_mod\":\"ɑn.tiˈpi.ptow\",\"sbl\":\"antipiptō\",\"dic\":\"an-tee-PEE-ptoh\",\"dic_mod\":\"an-tee-PEE-ptoh\"},\"see\":[\"G0473\",\"G4098\"]}', 'resist'),
(497, 497, 'ἀντιστρατεύομαι', '{\"def\":{\"short\":\"(figuratively) to attack, i.e., (by implication) destroy\",\"long\":[\"to make a military expedition, or take the field, against anyone\",\"to oppose, war against\"]},\"deriv\":\"from G0473 and G4754\",\"pronun\":{\"ipa\":\"ɑn.ti.strɑˈtɛβ.o.mɛ\",\"ipa_mod\":\"ɑn.ti.strɑˈtev.ow.me\",\"sbl\":\"antistrateuomai\",\"dic\":\"an-tee-stra-TEV-oh-meh\",\"dic_mod\":\"an-tee-stra-TAVE-oh-may\"},\"see\":[\"G0473\",\"G4754\"]}', 'war against'),
(498, 498, 'ἀντιτάσσομαι', '{\"def\":{\"short\":\"to range oneself against, i.e., oppose\",\"long\":[\"to range in battle against\",\"to oppose one\'s self, resist\"]},\"deriv\":\"from G0473 and the middle voice of G5021\",\"pronun\":{\"ipa\":\"ɑn.tiˈtɑs.so.mɛ\",\"ipa_mod\":\"ɑn.tiˈtɑs.sow.me\",\"sbl\":\"antitassomai\",\"dic\":\"an-tee-TAHS-soh-meh\",\"dic_mod\":\"an-tee-TAHS-soh-may\"},\"see\":[\"G0473\",\"G5021\"]}', 'oppose themselves, resist'),
(499, 499, 'ἀντίτυπον', '{\"def\":{\"short\":\"corresponding (\\\"antitype\\\"), i.e., a representative, counterpart\",\"long\":[\"a thing formed after some pattern\",\"a thing resembling another, its counterpart\",[\"something in the Messianic times which answers to the type, as baptism corresponds to the deluge (1 Peter 3:21)\"]]},\"deriv\":\"neuter of a compound of G0473 and G5179\",\"pronun\":{\"ipa\":\"ɑnˈti.ty.pon\",\"ipa_mod\":\"ɑnˈti.tju.pown\",\"sbl\":\"antitypon\",\"dic\":\"an-TEE-too-pone\",\"dic_mod\":\"an-TEE-tyoo-pone\"},\"see\":[\"G0473\",\"G5179\"]}', '(like) figure (whereunto)'),
(500, 500, 'ἀντίχριστος', '{\"def\":{\"short\":\"an opponent of the Messiah\",\"long\":[\"the adversary of the Messiah\"]},\"deriv\":\"from G0473 and G5547\",\"pronun\":{\"ipa\":\"ɑnˈti.xri.stos\",\"ipa_mod\":\"ɑnˈti.xri.stows\",\"sbl\":\"antichristos\",\"dic\":\"an-TEE-hree-stose\",\"dic_mod\":\"an-TEE-hree-stose\"},\"see\":[\"G0473\",\"G5547\"]}', 'antichrist'),
(501, 501, 'ἀντλέω', '{\"def\":{\"short\":\"to bale up (properly, bilge water), i.e., dip water (with a bucket, pitcher, etc.)\",\"long\":[\"to draw out of a ship\'s bilge-water, to bale or pump out\",\"to draw water\"]},\"deriv\":\"from ἄντλος (the hold of a ship)\",\"pronun\":{\"ipa\":\"ɑn.tˈlɛ.o\",\"ipa_mod\":\"ɑn.tˈle̞.ow\",\"sbl\":\"antleō\",\"dic\":\"an-t-LEH-oh\",\"dic_mod\":\"an-t-LAY-oh\"}}', 'draw (out)'),
(502, 502, 'ἄντλημα', '{\"def\":{\"short\":\"a baling-vessel\",\"long\":[\"what is drawn\",\"the act of drawing water\",\"a thing to draw with, e.g., bucket and rope let down into a well\"]},\"deriv\":\"from G0501\",\"pronun\":{\"ipa\":\"ˈɑn.t.le.mɑ\",\"ipa_mod\":\"ˈɑn.t.le̞.mɑ\",\"sbl\":\"antlēma\",\"dic\":\"AN-t-lay-ma\",\"dic_mod\":\"AN-t-lay-ma\"},\"see\":[\"G0501\"]}', 'thing to draw with'),
(503, 503, 'ἀντοφθαλμέω', '{\"def\":{\"short\":\"to face\",\"long\":[\"to look against or straight at\",\"metaphorically to bear up against, withstand\"]},\"deriv\":\"from a compound of G0473 and G3788\",\"pronun\":{\"ipa\":\"ɑn.to.fθɑlˈmɛ.o\",\"ipa_mod\":\"ɑn.tow.fθɑlˈme̞.ow\",\"sbl\":\"antophthalmeō\",\"dic\":\"an-toh-fthahl-MEH-oh\",\"dic_mod\":\"an-toh-fthahl-MAY-oh\"},\"see\":[\"G0473\",\"G3788\"]}', 'bear up into'),
(504, 504, 'ἄνυδρος', '{\"def\":{\"short\":\"waterless, i.e., dry\",\"long\":[\"without water\"]},\"deriv\":\"from G0001 (as a negative particle) and G5204\",\"pronun\":{\"ipa\":\"ˈɑ.ny.ðros\",\"ipa_mod\":\"ˈɑ.nju.ðrows\",\"sbl\":\"anydros\",\"dic\":\"AH-noo-throse\",\"dic_mod\":\"AH-nyoo-throse\"},\"see\":[\"G0001\",\"G5204\"]}', 'dry, without water'),
(505, 505, 'ἀνυπόκριτος', '{\"def\":{\"short\":\"undissembled, i.e., sincere\",\"long\":[\"unfeigned, undisguised, sincere\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G5271\",\"pronun\":{\"ipa\":\"ɑ.nyˈpo.kri.tos\",\"ipa_mod\":\"ɑ.njuˈpow.kri.tows\",\"sbl\":\"anypokritos\",\"dic\":\"ah-noo-POH-kree-tose\",\"dic_mod\":\"ah-nyoo-POH-kree-tose\"},\"see\":[\"G0001\",\"G5271\"]}', 'without dissimulation (hypocrisy), unfeigned'),
(506, 506, 'ἀνυπότακτος', '{\"def\":{\"short\":\"unsubdued, i.e., insubordinate (in fact or temper)\",\"long\":[\"not made subject, unsubjected\",\"that cannot be subjected to control, disobedient, unruly, refractory\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G5293\",\"pronun\":{\"ipa\":\"ɑ.nyˈpo.tɑk.tos\",\"ipa_mod\":\"ɑ.njuˈpow.tɑk.tows\",\"sbl\":\"anypotaktos\",\"dic\":\"ah-noo-POH-tahk-tose\",\"dic_mod\":\"ah-nyoo-POH-tahk-tose\"},\"see\":[\"G0001\",\"G5293\"]}', 'disobedient, that is not put under, unruly'),
(507, 507, 'ἄνω', '{\"def\":{\"short\":\"upward or on the top\",\"long\":[\"up, upwards, above, on high\",\"of the quarters of the heaven, northward\",\"of countries, inland, up from the coast\",\"of time, formerly\"]},\"deriv\":\"adverb from G0473\",\"pronun\":{\"ipa\":\"ˈɑ.no\",\"ipa_mod\":\"ˈɑ.now\",\"sbl\":\"anō\",\"dic\":\"AH-noh\",\"dic_mod\":\"AH-noh\"},\"see\":[\"G0473\"]}', 'above, brim, high, up'),
(508, 508, 'ἀνώγεον', '{\"def\":{\"short\":\"above the ground, i.e., (properly) the second floor of a building; used for a dome or a balcony on the upper story\",\"long\":[\"anything above the ground\",\"a room in the upper part of a house\"]},\"deriv\":\"from G0507 and G1093\",\"pronun\":{\"ipa\":\"ɑˈno.ɣɛ.on\",\"ipa_mod\":\"ɑˈnow.ʝe̞.own\",\"sbl\":\"anōgeon\",\"dic\":\"ah-NOH-geh-one\",\"dic_mod\":\"ah-NOH-gay-one\"},\"see\":[\"G0507\",\"G1093\"]}', 'upper room'),
(509, 509, 'ἄνωθεν', '{\"def\":{\"short\":\"from above; by analogy, from the first; by implication, anew\",\"long\":[\"from above, from a higher place\",[\"of things which come from heaven or God\"],\"from the first, from the beginning, from the very first\",\"anew, over again\"]},\"deriv\":\"from G0507\",\"pronun\":{\"ipa\":\"ˈɑ.no.θɛn\",\"ipa_mod\":\"ˈɑ.now.θe̞n\",\"sbl\":\"anōthen\",\"dic\":\"AH-noh-then\",\"dic_mod\":\"AH-noh-thane\"},\"see\":[\"G0507\"]}', 'from above, again, from the beginning (very first), the top'),
(510, 510, 'ἀνωτερικός', '{\"def\":{\"short\":\"superior, i.e., (locally) more remote\",\"long\":[\"upper\"]},\"deriv\":\"from G0511\",\"pronun\":{\"ipa\":\"ɑ.no.tɛ.riˈkos\",\"ipa_mod\":\"ɑ.now.te̞.riˈkows\",\"sbl\":\"anōterikos\",\"dic\":\"ah-noh-teh-ree-KOSE\",\"dic_mod\":\"ah-noh-tay-ree-KOSE\"},\"see\":[\"G0511\"]}', 'upper'),
(511, 511, 'ἀνώτερος', '{\"def\":{\"short\":\"upper, i.e., (neuter as adverb) to a more conspicuous place, in a former part of the book\",\"long\":[\"higher\",[\"of motion: to a higher place, up higher\",\"of rest: in a higher place, above\"]]},\"deriv\":\"comparative degree of G0507\",\"pronun\":{\"ipa\":\"ɑˈno.tɛ.ros\",\"ipa_mod\":\"ɑˈnow.te̞.rows\",\"sbl\":\"anōteros\",\"dic\":\"ah-NOH-teh-rose\",\"dic_mod\":\"ah-NOH-tay-rose\"},\"see\":[\"G0507\"]}', 'above, higher'),
(512, 512, 'ἀνωφελής', '{\"def\":{\"short\":\"useless or (neuter) inutility\",\"long\":[\"unprofitable, useless\"]},\"deriv\":\"from G0001 (as a negative particle) and the base of G5624\",\"pronun\":{\"ipa\":\"ɑ.no.fɛˈles\",\"ipa_mod\":\"ɑ.now.fe̞ˈle̞s\",\"sbl\":\"anōphelēs\",\"dic\":\"ah-noh-feh-LASE\",\"dic_mod\":\"ah-noh-fay-LASE\"},\"see\":[\"G0001\",\"G5624\"]}', 'unprofitable(-ness)'),
(513, 513, 'ἀξίνη', '{\"def\":{\"short\":\"compare G4486); an axe\",\"long\":[\"an axe\"]},\"deriv\":\"probably from ἄγνυμι (to break\",\"pronun\":{\"ipa\":\"ɑˈk͡si.ne\",\"ipa_mod\":\"ɑˈk͡si.ne̞\",\"sbl\":\"axinē\",\"dic\":\"ah-KSEE-nay\",\"dic_mod\":\"ah-KSEE-nay\"},\"see\":[\"G4486\"]}', 'axe'),
(514, 514, 'ἄξιος', '{\"def\":{\"short\":\"deserving, comparable or suitable (as if drawing praise)\",\"long\":[\"weighing, having weight, having the weight of another thing of like value, worth as much\",\"befitting, congruous, corresponding to a thing\",\"of one who has merited anything worthy\",[\"both in a good and a bad sense\"]]},\"deriv\":\"probably from G0071\",\"pronun\":{\"ipa\":\"ˈɑ.k͡si.os\",\"ipa_mod\":\"ˈɑ.k͡si.ows\",\"sbl\":\"axios\",\"dic\":\"AH-ksee-ose\",\"dic_mod\":\"AH-ksee-ose\"},\"see\":[\"G0071\"]}', 'due reward, meet, (un-)worthy'),
(515, 515, 'ἀξιόω', '{\"def\":{\"short\":\"to deem entitled or fit\",\"long\":[\"to think meet, fit, right\",\"to judge worthy, deem, deserving\"]},\"deriv\":\"from G0514\",\"pronun\":{\"ipa\":\"ɑ.k͡siˈo.o\",\"ipa_mod\":\"ɑ.k͡siˈow.ow\",\"sbl\":\"axioō\",\"dic\":\"ah-ksee-OH-oh\",\"dic_mod\":\"ah-ksee-OH-oh\"},\"see\":[\"G0514\"]}', 'desire, think good, count (think) worthy'),
(516, 516, 'ἀξίως', '{\"def\":{\"short\":\"appropriately\",\"long\":[\"suitably, worthily, in a manner worthy of\"]},\"deriv\":\"adverb from G0514\",\"pronun\":{\"ipa\":\"ɑˈk͡si.os\",\"ipa_mod\":\"ɑˈk͡si.ows\",\"sbl\":\"axiōs\",\"dic\":\"ah-KSEE-ose\",\"dic_mod\":\"ah-KSEE-ose\"},\"see\":[\"G0514\"]}', 'as becometh, after a godly sort, worthily(-thy)'),
(517, 517, 'ἀόρατος', '{\"def\":{\"short\":\"invisible\",\"long\":[\"unseen, or that which can not be seen, e.g., invisible\"]},\"deriv\":\"from G0001 (as a negative particle) and G3707\",\"pronun\":{\"ipa\":\"ɑˈo.rɑ.tos\",\"ipa_mod\":\"ɑˈow.rɑ.tows\",\"sbl\":\"aoratos\",\"dic\":\"ah-OH-ra-tose\",\"dic_mod\":\"ah-OH-ra-tose\"},\"see\":[\"G0001\",\"G3707\"]}', 'invisible (thing)'),
(518, 518, 'ἀπαγγέλλω', '{\"def\":{\"short\":\"to announce\",\"long\":[\"to bring tidings (from a person or a thing), bring word, report\",\"to proclaim, to make known openly, declare\"]},\"deriv\":\"from G0575 and the base of G0032\",\"pronun\":{\"ipa\":\"ɑ.pɑŋˈɣɛl.lo\",\"ipa_mod\":\"ɑ.pɑŋˈɣe̞l.low\",\"sbl\":\"apangellō\",\"dic\":\"ah-pahng-GEL-loh\",\"dic_mod\":\"ah-pahng-GALE-loh\"},\"see\":[\"G0032\",\"G0575\"]}', 'bring word (again), declare, report, shew (again), tell'),
(519, 519, 'ἀπάγχομαι', '{\"def\":{\"short\":\"to strangle oneself off (i.e., to death)\",\"long\":[\"to throttle, strangle, in order to put out of the way or kill\",\"to hang one\'s self, to end one\'s life by hanging\"]},\"deriv\":\"from G0575 and ἄγχω (to choke; akin to the base of G0043)\",\"pronun\":{\"ipa\":\"ɑˈpɑŋ.xo.mɛ\",\"ipa_mod\":\"ɑˈpɑŋ.xow.me\",\"sbl\":\"apanchomai\",\"dic\":\"ah-PAHNG-hoh-meh\",\"dic_mod\":\"ah-PAHNG-hoh-may\"},\"see\":[\"G0043\",\"G0575\"]}', 'hang himself'),
(520, 520, 'ἀπάγω', '{\"def\":{\"short\":\"to take off (in various senses)\",\"long\":[\"to lead away\",[\"especially of those who are led off to trial, prison, or punishment\"]]},\"deriv\":\"from G0575 and G0071\",\"pronun\":{\"ipa\":\"ɑˈpɑ.ɣo\",\"ipa_mod\":\"ɑˈpɑ.ɣow\",\"sbl\":\"apagō\",\"dic\":\"ah-PA-goh\",\"dic_mod\":\"ah-PA-goh\"},\"see\":[\"G0071\",\"G0575\"]}', 'bring, carry away, lead (away), put to death, take away'),
