(524, 524, 'ἀπαλγέω', '{\"def\":{\"short\":\"to grieve out, i.e., become apathetic\",\"long\":[\"to cease to feel pain or grief\",[\"to bear troubles with greater equanimity, cease to feel pain at\",\"to become callous, insensible to pain, apathetic\"]]},\"deriv\":\"from G0575 and ἀλγέω (to smart)\",\"pronun\":{\"ipa\":\"ɑ.pɑlˈɣɛ.o\",\"ipa_mod\":\"ɑ.pɑlˈɣe̞.ow\",\"sbl\":\"apalgeō\",\"dic\":\"ah-pahl-GEH-oh\",\"dic_mod\":\"ah-pahl-GAY-oh\"},\"see\":[\"G0575\"]}', 'be past feeling'),
(525, 525, 'ἀπαλλάσσω', '{\"def\":{\"short\":\"to change away, i.e., release, (reflexively) remove\",\"long\":[\"to remove, release, to be removed, to depart\",\"to set free, deliver\"]},\"deriv\":\"from G0575 and G0236\",\"pronun\":{\"ipa\":\"ɑ.pɑlˈlɑs.so\",\"ipa_mod\":\"ɑ.pɑlˈlɑs.sow\",\"sbl\":\"apallassō\",\"dic\":\"ah-pahl-LAHS-soh\",\"dic_mod\":\"ah-pahl-LAHS-soh\"},\"see\":[\"G0236\",\"G0575\"]}', 'deliver, depart'),
(526, 526, 'ἀπαλλοτριόω', '{\"def\":{\"short\":\"to estrange away, i.e., (passively and figuratively) to be non-participant\",\"long\":[\"to alienate, estrange\",\"to be shut out from one\'s fellowship and intimacy\"]},\"deriv\":\"from G0575 and a derivative of G0245\",\"pronun\":{\"ipa\":\"ɑ.pɑl.lo.triˈo.o\",\"ipa_mod\":\"ɑ.pɑl.low.triˈow.ow\",\"sbl\":\"apallotrioō\",\"dic\":\"ah-pahl-loh-tree-OH-oh\",\"dic_mod\":\"ah-pahl-loh-tree-OH-oh\"},\"see\":[\"G0245\",\"G0575\"]}', 'alienate, be alien'),
