(530, 530, 'ἅπαξ', '{\"def\":{\"short\":\"one (or a single) time (numerically or conclusively)\",\"long\":[\"once, one time\",\"once for all\"]},\"deriv\":\"probably from G0537\",\"pronun\":{\"ipa\":\"ˈhɑ.pɑk͡s\",\"ipa_mod\":\"ˈɑ.pɑk͡s\",\"sbl\":\"hapax\",\"dic\":\"HA-pahks\",\"dic_mod\":\"A-pahks\"},\"see\":[\"G0537\"]}', 'once'),
(531, 531, 'ἀπαράβατος', '{\"def\":{\"short\":\"not passing away, i.e., untransferable (perpetual)\",\"long\":[\"unviolated, not to be violated, inviolable\",\"unchangeable and therefore not liable to pass to a successor\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G3845\",\"pronun\":{\"ipa\":\"ɑ.pɑˈrɑ.βɑ.tos\",\"ipa_mod\":\"ɑ.pɑˈrɑ.vɑ.tows\",\"sbl\":\"aparabatos\",\"dic\":\"ah-pa-RA-va-tose\",\"dic_mod\":\"ah-pa-RA-va-tose\"},\"see\":[\"G0001\",\"G3845\"]}', 'unchangeable'),
