(560, 560, 'ἀπελπίζω', '{\"def\":{\"short\":\"to hope out, i.e., fully expect\",\"long\":[\"nothing despairing\",\"despairing of no one\",\"causing no one to despair\"]},\"deriv\":\"from G0575 and G1679\",\"pronun\":{\"ipa\":\"ɑ.pɛlˈpi.zo\",\"ipa_mod\":\"ɑ.pe̞lˈpi.zow\",\"sbl\":\"apelpizō\",\"dic\":\"ah-pel-PEE-zoh\",\"dic_mod\":\"ah-pale-PEE-zoh\"},\"see\":[\"G0575\",\"G1679\"]}', 'hope for again'),
(561, 561, 'ἀπέναντι', '{\"def\":{\"short\":\"from in front, i.e., opposite, before or against\",\"long\":[\"over against, opposite\",\"in sight of, before\",\"in opposition to, against\"]},\"deriv\":\"from G0575 and G1725\",\"pronun\":{\"ipa\":\"ɑˈpɛ.nɑn.ti\",\"ipa_mod\":\"ɑˈpe̞.nɑn.ti\",\"sbl\":\"apenanti\",\"dic\":\"ah-PEH-nahn-tee\",\"dic_mod\":\"ah-PAY-nahn-tee\"},\"see\":[\"G0575\",\"G1725\"]}', 'before, contrary, over against, in the presence of'),
(562, 562, 'ἀπέραντος', '{\"def\":{\"short\":\"unfinished, i.e., (by implication) interminable\",\"long\":[\"that can not be passed through, boundless, endless\"]},\"deriv\":\"from G0001 (as a negative particle) and a secondary derivative of G4008\",\"pronun\":{\"ipa\":\"ɑˈpɛ.rɑn.tos\",\"ipa_mod\":\"ɑˈpe̞.rɑn.tows\",\"sbl\":\"aperantos\",\"dic\":\"ah-PEH-rahn-tose\",\"dic_mod\":\"ah-PAY-rahn-tose\"},\"see\":[\"G0001\",\"G4008\"]}', 'endless'),
(563, 563, 'ἀπερισπάστως', '{\"def\":{\"short\":\"undistractedly, i.e., free from (domestic) solicitude\",\"long\":[\"without distraction, without solicitude or anxiety or care\"]},\"deriv\":\"adverb from a compound of G0001 (as a negative participle) and a presumed derivative of G4049\",\"pronun\":{\"ipa\":\"ɑ.pɛ.riˈspɑ.stos\",\"ipa_mod\":\"ɑ.pe̞.riˈspɑ.stows\",\"sbl\":\"aperispastōs\",\"dic\":\"ah-peh-ree-SPA-stose\",\"dic_mod\":\"ah-pay-ree-SPA-stose\"},\"see\":[\"G0001\",\"G4049\"]}', 'without distraction'),
(564, 564, 'ἀπερίτμητος', '{\"def\":{\"short\":\"uncircumcised (figuratively)\",\"long\":[\"uncircumcised\",[\"metaphorically those whose heart and ears are covered, i.e., whose soul and senses are closed to divine admonition\"]]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G4059\",\"pronun\":{\"ipa\":\"ɑ.pɛˈri.tme.tos\",\"ipa_mod\":\"ɑ.pe̞ˈri.tme̞.tows\",\"sbl\":\"aperitmētos\",\"dic\":\"ah-peh-REE-tmay-tose\",\"dic_mod\":\"ah-pay-REE-tmay-tose\"},\"see\":[\"G0001\",\"G4059\"]}', 'uncircumcised'),
(565, 565, 'ἀπέρχομαι', '{\"def\":{\"short\":\"to go off (i.e., depart), aside (i.e., apart) or behind (i.e., follow), literally or figuratively\",\"long\":[\"to go away, depart\",[\"to go away in order to follow any one, go after him, to follow his party, follow him as a leader\"],\"to go away\",[\"of departing evils and sufferings\",\"of good things taken away from one\",\"of an evanescent state of things\"]]},\"deriv\":\"from G0575 and G2064\",\"pronun\":{\"ipa\":\"ɑˈpɛr.xo.mɛ\",\"ipa_mod\":\"ɑˈpe̞r.xow.me\",\"sbl\":\"aperchomai\",\"dic\":\"ah-PER-hoh-meh\",\"dic_mod\":\"ah-PARE-hoh-may\"},\"see\":[\"G0575\",\"G2064\"]}', 'come, depart, go (aside, away, back, out, ...ways), pass away, be past'),
(566, 566, 'ἀπέχει', '{\"def\":{\"short\":\"it is sufficient\",\"long\":[\"it is sufficient, it is enough\"]},\"deriv\":\"third person singular present indicative active of G0568 used impersonally\",\"pronun\":{\"ipa\":\"ɑˈpɛ.xi\",\"ipa_mod\":\"ɑˈpe̞.çi\",\"sbl\":\"apechei\",\"dic\":\"ah-PEH-hee\",\"dic_mod\":\"ah-PAY-hee\"},\"see\":[\"G0568\"]}', 'it is enough'),
(567, 567, 'ἀπέχομαι', '{\"def\":{\"short\":\"to hold oneself off, i.e., refrain\",\"long\":[\"to hold one\'s self off, refrain, abstain\"]},\"deriv\":\"middle voice (reflexively) of G0568\",\"pronun\":{\"ipa\":\"ɑˈpɛ.xo.mɛ\",\"ipa_mod\":\"ɑˈpe̞.xow.me\",\"sbl\":\"apechomai\",\"dic\":\"ah-PEH-hoh-meh\",\"dic_mod\":\"ah-PAY-hoh-may\"},\"see\":[\"G0568\"]}', 'abstain'),
(568, 568, 'ἀπέχω', '{\"def\":{\"short\":\"(actively) to have out, i.e., receive in full; (intransitively) to keep (oneself) away, i.e., be distant (literally or figuratively)\",\"long\":[\"have\",[\"to hold back, keep off, prevent\",\"to have wholly or in full, to have received\",\"it is enough, sufficient\"],\"to be away, absent, distant\",\"to hold one\'s self off, abstain\"]},\"deriv\":\"from G0575 and G2192\",\"pronun\":{\"ipa\":\"ɑˈpɛ.xo\",\"ipa_mod\":\"ɑˈpe̞.xow\",\"sbl\":\"apechō\",\"dic\":\"ah-PEH-hoh\",\"dic_mod\":\"ah-PAY-hoh\"},\"see\":[\"G0575\",\"G2192\"]}', 'be, have, receive'),
(569, 569, 'ἀπιστέω', '{\"def\":{\"short\":\"to be unbelieving, i.e., (transitively) disbelieve, or (by implication) disobey\",\"long\":[\"to betray a trust, be unfaithful\",\"to have no belief, disbelieve\"]},\"deriv\":\"from G0571\",\"pronun\":{\"ipa\":\"ɑ.piˈstɛ.o\",\"ipa_mod\":\"ɑ.piˈste̞.ow\",\"sbl\":\"apisteō\",\"dic\":\"ah-pee-STEH-oh\",\"dic_mod\":\"ah-pee-STAY-oh\"},\"see\":[\"G0571\"]}', 'believe not'),
(570, 570, 'ἀπιστία', '{\"def\":{\"short\":\"faithlessness, i.e., (negatively) disbelief (lack of Christian faith), or (positively) unfaithfulness (disobedience)\",\"long\":[\"unfaithfulness, faithless\",\"want of faith, unbelief\",\"weakness of faith\"]},\"deriv\":\"from G0571\",\"pronun\":{\"ipa\":\"ɑ.piˈsti.ɑ\",\"ipa_mod\":\"ɑ.piˈsti.ɑ\",\"sbl\":\"apistia\",\"dic\":\"ah-pee-STEE-ah\",\"dic_mod\":\"ah-pee-STEE-ah\"},\"see\":[\"G0571\"]}', 'unbelief'),
(571, 571, 'ἄπιστος', '{\"def\":{\"short\":\"(actively) disbelieving, i.e., without Christian faith (specially, a heathen); (passively) untrustworthy (person), or incredible (thing)\",\"long\":[\"unfaithful, faithless, (not to be trusted, perfidious)\",\"incredible\",[\"of things\"],\"unbelieving, incredulous\",[\"without trust (in God)\"]]},\"deriv\":\"from G0001 (as a negative particle) and G4103\",\"pronun\":{\"ipa\":\"ˈɑ.pi.stos\",\"ipa_mod\":\"ˈɑ.pi.stows\",\"sbl\":\"apistos\",\"dic\":\"AH-pee-stose\",\"dic_mod\":\"AH-pee-stose\"},\"see\":[\"G0001\",\"G4103\"]}', 'that believeth not, faithless, incredible thing, infidel, unbeliever(-ing)'),
(572, 572, 'ἁπλότης', '{\"def\":{\"short\":\"singleness, i.e., (subjectively) sincerity (without dissimulation or self-seeking), or (objectively) generosity (copious bestowal)\",\"long\":[\"singleness, simplicity, sincerity, mental honesty\",[\"the virtue of one who is free from pretence and hypocrisy\"],\"not self seeking, openness of heart manifesting itself by generousity\"]},\"deriv\":\"from G0573\",\"pronun\":{\"ipa\":\"hɑˈplo.tes\",\"ipa_mod\":\"ɑˈplow.te̞s\",\"sbl\":\"haplotēs\",\"dic\":\"ha-PLOH-tase\",\"dic_mod\":\"a-PLOH-tase\"},\"see\":[\"G0573\"]}', 'bountifulness, liberal(-ity), simplicity, singleness'),
(573, 573, 'ἁπλοῦς', '{\"def\":{\"short\":\"properly, folded together, i.e., single (figuratively, clear)\",\"long\":[\"simple, single\",\"whole\",\"good fulfilling its office, sound\",[\"of the eye\"]]},\"deriv\":\"probably from G0001 (as a particle of union) and the base of G4120\",\"pronun\":{\"ipa\":\"hɑˈplus\",\"ipa_mod\":\"ɑˈplus\",\"sbl\":\"haplous\",\"dic\":\"ha-PLOOS\",\"dic_mod\":\"a-PLOOS\"},\"see\":[\"G0001\",\"G4120\"]}', 'single');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(574, 574, 'ἁπλῶς', '{\"def\":{\"short\":\"bountifully\",\"long\":[\"simply, openly, frankly, sincerely\"]},\"deriv\":\"adverb from G0573 (in the objective sense of G0572)\",\"pronun\":{\"ipa\":\"hɑˈplos\",\"ipa_mod\":\"ɑˈplows\",\"sbl\":\"haplōs\",\"dic\":\"ha-PLOSE\",\"dic_mod\":\"a-PLOSE\"},\"see\":[\"G0572\",\"G0573\"]}', 'liberally'),
(575, 575, 'ἀπό', '{\"def\":{\"short\":\"\\\"off,\\\" i.e., away (from something near), in various senses (of place, time, or relation; literal or figurative)\",\"long\":[\"of separation\",[\"of local separation, after verbs of motion from a place i.e., of departing, of fleeing\",\"of separation of a part from the whole\",[\"where of a whole some part is taken\"],\"of any kind of separation of one thing from another by which the union or fellowship of the two is destroyed\",\"of a state of separation, that is of distance\",[\"physical, of distance of place\",\"temporal, of distance of time\"]],\"of origin\",[\"of the place whence anything is, comes, befalls, is taken\",\"of origin of a cause\"]]},\"deriv\":\"a primary particle\",\"pronun\":{\"ipa\":\"ɑˈpo\",\"ipa_mod\":\"ɑˈpow\",\"sbl\":\"apo\",\"dic\":\"ah-POH\",\"dic_mod\":\"ah-POH\"}}', '(X here-)after, ago, at, because of, before, by (the space of), for(-th), from, in, (out) of, off, (up-)on(-ce), since, with'),
(576, 576, 'ἀποβαίνω', '{\"def\":{\"short\":\"literally, to disembark; figuratively, to eventuate\",\"long\":[\"to come down from, i.e., a ship\",\"to turn out, result, to be the outcome\"]},\"deriv\":\"from G0575 and the base of G0939\",\"pronun\":{\"ipa\":\"ɑ.poˈβɛ.no\",\"ipa_mod\":\"ɑ.powˈve.now\",\"sbl\":\"apobainō\",\"dic\":\"ah-poh-VEH-noh\",\"dic_mod\":\"ah-poh-VAY-noh\"},\"see\":[\"G0575\",\"G0939\"]}', 'become, go out, turn'),
(577, 577, 'ἀποβάλλω', '{\"def\":{\"short\":\"to throw off; figuratively, to lose\",\"long\":[\"to throw off, cast away\"]},\"deriv\":\"from G0575 and G0906\",\"pronun\":{\"ipa\":\"ɑ.poˈβɑl.lo\",\"ipa_mod\":\"ɑ.powˈvɑl.low\",\"sbl\":\"apoballō\",\"dic\":\"ah-poh-VAHL-loh\",\"dic_mod\":\"ah-poh-VAHL-loh\"},\"see\":[\"G0575\",\"G0906\"]}', 'cast away'),
(578, 578, 'ἀποβλέπω', '{\"def\":{\"short\":\"to look away from everything else, i.e., (figuratively) intently regard\",\"long\":[\"to turn the eyes away from other things and fix them on some one thing\",[\"to look at attentively\"],\"to look with steadfast mental gaze\"]},\"deriv\":\"from G0575 and G0991\",\"pronun\":{\"ipa\":\"ɑ.poˈβlɛ.po\",\"ipa_mod\":\"ɑ.powˈvle̞.pow\",\"sbl\":\"apoblepō\",\"dic\":\"ah-poh-VLEH-poh\",\"dic_mod\":\"ah-poh-VLAY-poh\"},\"see\":[\"G0575\",\"G0991\"]}', 'have respect'),
(579, 579, 'ἀπόβλητος', '{\"def\":{\"short\":\"cast off, i.e., (figuratively) such as to be rejected\",\"long\":[\"thrown away, to be thrown away, rejected, despised, abominated\",[\"as unclean\"]]},\"deriv\":\"from G0577\",\"pronun\":{\"ipa\":\"ɑˈpo.βle.tos\",\"ipa_mod\":\"ɑˈpow.vle̞.tows\",\"sbl\":\"apoblētos\",\"dic\":\"ah-POH-vlay-tose\",\"dic_mod\":\"ah-POH-vlay-tose\"},\"see\":[\"G0577\"]}', 'be refused'),
(580, 580, 'ἀποβολή', '{\"def\":{\"short\":\"rejection; figuratively, loss\",\"long\":[\"rejection, repudiation\",[\"to throw away from one\'s self, cast off, repudiate\"],\"a losing, a loss\"]},\"deriv\":\"from G0577\",\"pronun\":{\"ipa\":\"ɑ.po.βoˈle\",\"ipa_mod\":\"ɑ.pow.vowˈle̞\",\"sbl\":\"apobolē\",\"dic\":\"ah-poh-voh-LAY\",\"dic_mod\":\"ah-poh-voh-LAY\"},\"see\":[\"G0577\"]}', 'casting away, loss'),
(581, 581, 'ἀπογενόμενος', '{\"def\":{\"short\":\"absent, i.e., deceased (figuratively, renounced)\",\"long\":[\"to be removed from, depart\",\"to die, to die to anything\"]},\"deriv\":\"past participle of a compound of G0575 and G1096\",\"pronun\":{\"ipa\":\"ɑ.po.ɣɛˈno.mɛ.nos\",\"ipa_mod\":\"ɑ.pow.ʝe̞ˈnow.me̞.nows\",\"sbl\":\"apogenomenos\",\"dic\":\"ah-poh-geh-NOH-meh-nose\",\"dic_mod\":\"ah-poh-gay-NOH-may-nose\"},\"see\":[\"G0575\",\"G1096\"]}', 'being dead'),
(582, 582, 'ἀπογραφή', '{\"def\":{\"short\":\"an enrollment; by implication, an assessment\",\"long\":[\"a writing off, transcript (from some pattern)\",\"an enrolment or registration in the public records of persons together with their income and property, as the basis of a census or valuation, i.e., that it might appear how much tax should be levied upon each one\"]},\"deriv\":\"from G0583\",\"pronun\":{\"ipa\":\"ɑ.po.ɣrɑˈfe\",\"ipa_mod\":\"ɑ.pow.ɣrɑˈfe̞\",\"sbl\":\"apographē\",\"dic\":\"ah-poh-gra-FAY\",\"dic_mod\":\"ah-poh-gra-FAY\"},\"see\":[\"G0583\"]}', 'taxing'),
(583, 583, 'ἀπογράφω', '{\"def\":{\"short\":\"to write off (a copy or list), i.e., enrol\",\"long\":[\"to write off, copy (from some pattern)\",\"to enter in a register or records\",[\"specifically to enter in public records the names of men, their property and income\",\"to enroll\"]]},\"deriv\":\"from G0575 and G1125\",\"pronun\":{\"ipa\":\"ɑ.poˈɣrɑ.fo\",\"ipa_mod\":\"ɑ.powˈɣrɑ.fow\",\"sbl\":\"apographō\",\"dic\":\"ah-poh-GRA-foh\",\"dic_mod\":\"ah-poh-GRA-foh\"},\"see\":[\"G0575\",\"G1125\"]}', 'tax, write'),
(584, 584, 'ἀποδείκνυμι', '{\"def\":{\"short\":\"to show off, i.e., exhibit; figuratively, to demonstrate, i.e., accredit\",\"long\":[\"to point away from one\'s self, to point out, show forth, to expose to view, exhibit\",\"to declare, to show, to prove what kind of person anyone is, to prove by arguments, demonstrate\"]},\"deriv\":\"from G0575 and G1166\",\"pronun\":{\"ipa\":\"ɑ.poˈði.kny.mi\",\"ipa_mod\":\"ɑ.powˈði.knju.mi\",\"sbl\":\"apodeiknymi\",\"dic\":\"ah-poh-THEE-knoo-mee\",\"dic_mod\":\"ah-poh-THEE-knyoo-mee\"},\"see\":[\"G0575\",\"G1166\"]}', '(ap-)prove, set forth, shew'),
(585, 585, 'ἀπόδειξις', '{\"def\":{\"short\":\"manifestation\",\"long\":[\"a making manifest, showing forth\",\"a demonstration, proof\"]},\"deriv\":\"from G0584\",\"pronun\":{\"ipa\":\"ɑˈpo.ði.k͡sis\",\"ipa_mod\":\"ɑˈpow.ði.k͡sis\",\"sbl\":\"apodeixis\",\"dic\":\"ah-POH-thee-ksees\",\"dic_mod\":\"ah-POH-thee-ksees\"},\"see\":[\"G0584\"]}', 'demonstration'),
(586, 586, 'ἀποδεκατόω', '{\"def\":{\"short\":\"to tithe (as debtor or creditor)\",\"long\":[\"to give, pay a tithe of anything\",\"to exact receive a tenth from anyone\"]},\"deriv\":\"from G0575 and G1183\",\"pronun\":{\"ipa\":\"ɑ.po.ðɛ.kɑˈto.o\",\"ipa_mod\":\"ɑ.pow.ðe̞.kɑˈtow.ow\",\"sbl\":\"apodekatoō\",\"dic\":\"ah-poh-theh-ka-TOH-oh\",\"dic_mod\":\"ah-poh-thay-ka-TOH-oh\"},\"see\":[\"G0575\",\"G1183\"]}', '(give, pay, take) tithe'),
(587, 587, 'ἀπόδεκτος', '{\"def\":{\"short\":\"accepted, i.e., agreeable\",\"long\":[\"accepted, acceptable, agreeable\"]},\"deriv\":\"from G0588\",\"pronun\":{\"ipa\":\"ɑˈpo.ðɛk.tos\",\"ipa_mod\":\"ɑˈpow.ðe̞k.tows\",\"sbl\":\"apodektos\",\"dic\":\"ah-POH-thek-tose\",\"dic_mod\":\"ah-POH-thake-tose\"},\"see\":[\"G0588\"]}', 'acceptable'),
(588, 588, 'ἀποδέχομαι', '{\"def\":{\"short\":\"to take fully, i.e., welcome (persons), approve (things)\",\"long\":[\"to accept from, receive\",\"to accept what is offered from without\"]},\"deriv\":\"from G0575 and G1209\",\"pronun\":{\"ipa\":\"ɑ.poˈðɛ.xo.mɛ\",\"ipa_mod\":\"ɑ.powˈðe̞.xow.me\",\"sbl\":\"apodechomai\",\"dic\":\"ah-poh-THEH-hoh-meh\",\"dic_mod\":\"ah-poh-THAY-hoh-may\"},\"see\":[\"G0575\",\"G1209\"]}', 'accept, receive (gladly)'),
(589, 589, 'ἀποδημέω', '{\"def\":{\"short\":\"to go abroad, i.e., visit a foreign land\",\"long\":[\"to go away into foreign parts, go abroad\"]},\"deriv\":\"from G0590\",\"pronun\":{\"ipa\":\"ɑ.po.ðeˈmɛ.o\",\"ipa_mod\":\"ɑ.pow.ðe̞ˈme̞.ow\",\"sbl\":\"apodēmeō\",\"dic\":\"ah-poh-thay-MEH-oh\",\"dic_mod\":\"ah-poh-thay-MAY-oh\"},\"see\":[\"G0590\"]}', 'go (travel) into a far country, journey'),
(590, 590, 'ἀπόδημος', '{\"def\":{\"short\":\"absent from one\'s own people, i.e., a foreign traveller\",\"long\":[\"away from one\'s people, go abroad\"]},\"deriv\":\"from G0575 and G1218\",\"pronun\":{\"ipa\":\"ɑˈpo.ðe.mos\",\"ipa_mod\":\"ɑˈpow.ðe̞.mows\",\"sbl\":\"apodēmos\",\"dic\":\"ah-POH-thay-mose\",\"dic_mod\":\"ah-POH-thay-mose\"},\"see\":[\"G0575\",\"G1218\"]}', 'taking a far journey'),
(591, 591, 'ἀποδίδωμι', '{\"def\":{\"short\":\"to give away, i.e., up, over, back, etc. (in various applications)\",\"long\":[\"to deliver, to give away for one\'s own profit what is one\'s own, to sell\",\"to pay off, discharge what is due\",[\"a debt, wages, tribute, taxes, produce due\",\"things promised under oath\",\"conjugal duty\",\"to render account\"],\"to give back, restore\",\"to requite, recompense in a good or a bad sense\"]},\"deriv\":\"from G0575 and G1325\",\"pronun\":{\"ipa\":\"ɑ.poˈði.ðo.mi\",\"ipa_mod\":\"ɑ.powˈði.ðow.mi\",\"sbl\":\"apodidōmi\",\"dic\":\"ah-poh-THEE-thoh-mee\",\"dic_mod\":\"ah-poh-THEE-thoh-mee\"},\"see\":[\"G0575\",\"G1325\"]}', 'deliver (again), give (again), (re-)pay(-ment be made), perform, recompense, render, requite, restore, reward, sell, yield'),
(592, 592, 'ἀποδιορίζω', '{\"def\":{\"short\":\"to disjoin (by a boundary, figuratively, a party)\",\"long\":[\"to disjoin, part, separate from another\",\"making divisions or separations\"]},\"deriv\":\"from G0575 and a compound of G1223 and G3724\",\"pronun\":{\"ipa\":\"ɑ.po.ði.oˈri.zo\",\"ipa_mod\":\"ɑ.pow.ði.owˈri.zow\",\"sbl\":\"apodiorizō\",\"dic\":\"ah-poh-thee-oh-REE-zoh\",\"dic_mod\":\"ah-poh-thee-oh-REE-zoh\"},\"see\":[\"G0575\",\"G1223\",\"G3724\"]}', 'separate'),
(593, 593, 'ἀποδοκιμάζω', '{\"def\":{\"short\":\"to disapprove, i.e., (by implication) to repudiate\",\"long\":[\"to disapprove, reject, repudiate\"]},\"deriv\":\"from G0575 and G1381\",\"pronun\":{\"ipa\":\"ɑ.po.ðo.kiˈmɑ.zo\",\"ipa_mod\":\"ɑ.pow.ðow.ciˈmɑ.zow\",\"sbl\":\"apodokimazō\",\"dic\":\"ah-poh-thoh-kee-MA-zoh\",\"dic_mod\":\"ah-poh-thoh-kee-MA-zoh\"},\"see\":[\"G0575\",\"G1381\"]}', 'disallow, reject'),
(594, 594, 'ἀποδοχή', '{\"def\":{\"short\":\"acceptance\",\"long\":[\"reception, admission, acceptance, approbation\"]},\"deriv\":\"from G0588\",\"pronun\":{\"ipa\":\"ɑ.po.ðoˈxe\",\"ipa_mod\":\"ɑ.pow.ðowˈxe̞\",\"sbl\":\"apodochē\",\"dic\":\"ah-poh-thoh-HAY\",\"dic_mod\":\"ah-poh-thoh-HAY\"},\"see\":[\"G0588\"]}', 'acceptation'),
(595, 595, 'ἀπόθεσις', '{\"def\":{\"short\":\"a laying aside (literally or figuratively)\",\"long\":[\"a putting off or away\"]},\"deriv\":\"from G0659\",\"pronun\":{\"ipa\":\"ɑˈpo.θɛ.sis\",\"ipa_mod\":\"ɑˈpow.θe̞.sis\",\"sbl\":\"apothesis\",\"dic\":\"ah-POH-theh-sees\",\"dic_mod\":\"ah-POH-thay-sees\"},\"see\":[\"G0659\"]}', 'putting away (off)'),
(596, 596, 'ἀποθήκη', '{\"def\":{\"short\":\"a repository, i.e., granary\",\"long\":[\"a place in which anything is laid by or up\",\"a storehouse, granary\"]},\"deriv\":\"from G0659\",\"pronun\":{\"ipa\":\"ɑ.poˈθe.ke\",\"ipa_mod\":\"ɑ.powˈθe̞.ke̞\",\"sbl\":\"apothēkē\",\"dic\":\"ah-poh-THAY-kay\",\"dic_mod\":\"ah-poh-THAY-kay\"},\"see\":[\"G0659\"]}', 'barn, garner'),
(597, 597, 'ἀποθησαυρίζω', '{\"def\":{\"short\":\"to treasure away\",\"long\":[\"to put away, lay by in store, to treasure away\",\"to store up abundance for future use\"]},\"deriv\":\"from G0575 and G2343\",\"pronun\":{\"ipa\":\"ɑ.po.θe.sɛβˈri.zo\",\"ipa_mod\":\"ɑ.pow.θe̞.saˈri.zow\",\"sbl\":\"apothēsaurizō\",\"dic\":\"ah-poh-thay-sev-REE-zoh\",\"dic_mod\":\"ah-poh-thay-sa-REE-zoh\"},\"see\":[\"G0575\",\"G2343\"]}', 'lay up in store'),
(598, 598, 'ἀποθλίβω', '{\"def\":{\"short\":\"to crowd (from every side)\",\"long\":[\"to press on all sides, squeeze, press hard\",[\"of pressing out grapes and olives\"]]},\"deriv\":\"from G0575 and G2346\",\"pronun\":{\"ipa\":\"ɑ.poˈθli.βo\",\"ipa_mod\":\"ɑ.powˈθli.vow\",\"sbl\":\"apothlibō\",\"dic\":\"ah-poh-THLEE-voh\",\"dic_mod\":\"ah-poh-THLEE-voh\"},\"see\":[\"G0575\",\"G2346\"]}', 'press'),
(599, 599, 'ἀποθνήσκω', '{\"def\":{\"short\":\"to die off (literally or figuratively)\",\"long\":[\"to die\",[\"of the natural death of man\",\"of the violent death of man or animals\",\"to perish by means of something\",\"of trees which dry up, of seeds which rot when planted\",\"of eternal death, to be subject to eternal misery in hell\"]]},\"deriv\":\"from G0575 and G2348\",\"pronun\":{\"ipa\":\"ɑ.poˈθne.sko\",\"ipa_mod\":\"ɑ.powˈθne̞.skow\",\"sbl\":\"apothnēskō\",\"dic\":\"ah-poh-THNAY-skoh\",\"dic_mod\":\"ah-poh-THNAY-skoh\"},\"see\":[\"G0575\",\"G2348\"]}', 'be dead, death, die, lie a-dying, be slain (X with)'),
