(675, 675, 'Ἄππιος', '{\"def\":{\"lit\":\"the market place of Appius\",\"short\":\"(in the genitive, i.e., possessive case) of Appius, the name of a Roman\",\"long\":[\"a town in Italy, 39 miles (60 km) from Rome on the Appian Way\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈɑp.pi.os\",\"ipa_mod\":\"ˈɑp.pi.ows\",\"sbl\":\"appios\",\"dic\":\"AP-pee-ose\",\"dic_mod\":\"AP-pee-ose\"}}', 'Appii'),
(676, 676, 'ἀπρόσιτος', '{\"def\":{\"short\":\"inaccessible\",\"long\":[\"unapproachable, inaccessible\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of a comparative of G4314 and εἶμι (to go)\",\"pronun\":{\"ipa\":\"ɑˈpro.si.tos\",\"ipa_mod\":\"ɑˈprow.si.tows\",\"sbl\":\"aprositos\",\"dic\":\"ah-PROH-see-tose\",\"dic_mod\":\"ah-PROH-see-tose\"},\"see\":[\"G0001\",\"G4314\"]}', 'which no man can approach'),
(677, 677, 'ἀπρόσκοπος', '{\"def\":{\"short\":\"actively, inoffensive, i.e., not leading into sin; passively, faultless, i.e., not led into sin\",\"long\":[\"having nothing to strike against, not causing to stumble\",[\"of a smooth road\",\"metaphorically of not leading others to sin by one\'s mode of life\"],\"not striking against or stumbling\",[\"metaphorically not led into sin, blameless\"],\"without offense, not troubled by a consciousness of sin\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G4350\",\"pronun\":{\"ipa\":\"ɑˈpro.sko.pos\",\"ipa_mod\":\"ɑˈprow.skow.pows\",\"sbl\":\"aproskopos\",\"dic\":\"ah-PROH-skoh-pose\",\"dic_mod\":\"ah-PROH-skoh-pose\"},\"see\":[\"G0001\",\"G4350\"]}', 'none (void of, without) offence'),
(678, 678, 'ἀπροσωπολήπτως', '{\"def\":{\"short\":\"in a way not accepting the person, i.e., impartially\",\"long\":[\"without respect of persons, impartial\"]},\"deriv\":\"adverb from a compound of G0001 (as a negative particle) and a presumed derivative of a presumed compound of G4383 and G2983 (compare G4381)\",\"pronun\":{\"ipa\":\"ɑ.pro.so.poˈle.ptos\",\"ipa_mod\":\"ɑ.prow.sow.powˈle̞.ptows\",\"sbl\":\"aprosōpolēptōs\",\"dic\":\"ah-proh-soh-poh-LAY-ptose\",\"dic_mod\":\"ah-proh-soh-poh-LAY-ptose\"},\"see\":[\"G0001\",\"G2983\",\"G4381\",\"G4383\"]}', 'without respect of persons'),
(679, 679, 'ἄπταιστος', '{\"def\":{\"short\":\"not stumbling, i.e., (figuratively) without sin\",\"long\":[\"not stumbling, standing firm, exempt from falling\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G4417\",\"pronun\":{\"ipa\":\"ˈɑ.ptɛ.stos\",\"ipa_mod\":\"ˈɑ.pte.stows\",\"sbl\":\"aptaistos\",\"dic\":\"AH-pteh-stose\",\"dic_mod\":\"AH-ptay-stose\"},\"see\":[\"G0001\",\"G4417\"]}', 'from falling'),
(680, 680, 'ἅπτομαι', '{\"def\":{\"short\":\"properly, to attach oneself to, i.e., to touch (in many implied relations)\",\"long\":[\"to fasten one\'s self to, adhere to, cling to\",[\"to touch\",\"of carnal intercourse with a women or cohabitation\",\"of levitical practice of having no fellowship with heathen practices\",\"to touch, assail anyone\"]]},\"deriv\":\"reflexive of G0681\",\"pronun\":{\"ipa\":\"ˈhɑ.pto.mɛ\",\"ipa_mod\":\"ˈɑ.ptow.me\",\"sbl\":\"haptomai\",\"dic\":\"HA-ptoh-meh\",\"dic_mod\":\"A-ptoh-may\"},\"see\":[\"G0681\"],\"comment\":\"Things not to be touched appear to be both women and certain kinds of food, so abstinence of certain kinds of food and drink are recommended.\"}', 'touch'),
(681, 681, 'ἅπτω', '{\"def\":{\"short\":\"properly, to fasten to, i.e., (specially) to set on fire\",\"long\":[\"to fasten to, adhere to\",[\"to fasten fire to a thing, kindle, set of fire\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈhɑ.pto\",\"ipa_mod\":\"ˈɑ.ptow\",\"sbl\":\"haptō\",\"dic\":\"HA-ptoh\",\"dic_mod\":\"A-ptoh\"}}', 'kindle, light'),
(682, 682, 'Ἀπφία', '{\"def\":{\"lit\":\"fruitful\",\"short\":\"Apphia, a woman of Collosae\",\"long\":[\"a Christian member of Philemon\'s household\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ɑpˈfi.ɑ\",\"ipa_mod\":\"ɑp̪ˈfi.ɑ\",\"sbl\":\"apphia\",\"dic\":\"ap-FEE-ah\",\"dic_mod\":\"ap-FEE-ah\"}}', 'Apphia');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(683, 683, 'ἀπωθέομαι', '{\"def\":{\"short\":\"to push off, figuratively, to reject\",\"long\":[\"to thrust away, push away, repel\",\"to thrust away from one\'s self, to drive away from one\'s self\",[\"repudiate, reject, refuse\"]]},\"deriv\":\"from G0575 and the middle voice of ὠθέω or ὤθω (to shove)\",\"pronun\":{\"ipa\":\"ɑ.poˈθɛ.o.mɛ\",\"ipa_mod\":\"ɑ.powˈθe̞.ow.me\",\"sbl\":\"apōtheomai\",\"dic\":\"ah-poh-THEH-oh-meh\",\"dic_mod\":\"ah-poh-THAY-oh-may\"},\"see\":[\"G0575\"]}', 'cast away, put away (from), thrust away (from)'),
(684, 684, 'ἀπώλεια', '{\"def\":{\"short\":\"ruin or loss (physical, spiritual or eternal)\",\"long\":[\"destroying, utter destruction\",[\"of vessels\"],\"a perishing, ruin, destruction\",[\"of money\",\"the destruction which consists of eternal misery in hell\"]]},\"deriv\":\"from a presumed derivative of G0622\",\"pronun\":{\"ipa\":\"ɑˈpo.li.ɑ\",\"ipa_mod\":\"ɑˈpow.li.ɑ\",\"sbl\":\"apōleia\",\"dic\":\"ah-POH-lee-ah\",\"dic_mod\":\"ah-POH-lee-ah\"},\"see\":[\"G0622\"]}', 'damnable(-nation), destruction, die, perdition, X perish, pernicious ways, waste'),
(685, 685, 'ἀρά', '{\"def\":{\"short\":\"properly, prayer (as lifted to Heaven), i.e., (by implication) imprecation\",\"long\":[\"a prayer, a supplication\",\"an imprecation, curse, malediction\"]},\"deriv\":\"probably from G0142\",\"pronun\":{\"ipa\":\"ɑˈrɑ\",\"ipa_mod\":\"ɑˈrɑ\",\"sbl\":\"ara\",\"dic\":\"ah-RA\",\"dic_mod\":\"ah-RA\"},\"see\":[\"G0142\"]}', 'curse'),
(686, 686, 'ἄρα', '{\"def\":{\"short\":\"a particle denoting an inference more or less decisive (as follows)\",\"long\":[\"therefore, so then, wherefore\"]},\"deriv\":\"probably from G0142 (through the idea of drawing a conclusion)\",\"pronun\":{\"ipa\":\"ˈɑ.rɑ\",\"ipa_mod\":\"ˈɑ.rɑ\",\"sbl\":\"ara\",\"dic\":\"AH-ra\",\"dic_mod\":\"AH-ra\"},\"see\":[\"G0142\",\"G0687\",\"G1065\",\"G1487\",\"G3767\"],\"comment\":\"Often used in connection with other particles, especially G1065 or G3767 (after) or G1487 (before). Compare also G0687.\"}', 'haply, (what) manner (of man), no doubt, perhaps, so be, then, therefore, truly, wherefore'),
(687, 687, 'ἆρα', '{\"def\":{\"short\":\"therefore\",\"long\":[\"therefore\"]},\"deriv\":\"a form of G0686, denoting an interrogation to which a negative answer is presumed\",\"pronun\":{\"ipa\":\"ˈɑ.rɑ\",\"ipa_mod\":\"ˈɑ.rɑ\",\"sbl\":\"ara\",\"dic\":\"AH-ra\",\"dic_mod\":\"AH-ra\"},\"see\":[\"G0686\"],\"comment\":\"This word is a Greek interrogative particle that implies anxiety or impatience on the part of the questioner.\"}', 'therefore'),
(688, 688, 'Ἀραβία', '{\"def\":{\"lit\":\"desert, barren\",\"short\":\"Arabia, a region of Asia\",\"long\":[\"a well known peninsula of Asia lying towards Africa, and bounded by Egypt, Palestine, Syria, Mesopotamia, Babylonia, the Gulf of Arabia, the Persian Gulf, the Red Sea and the Indian Ocean\"]},\"deriv\":\"of Hebrew origin (H6152)\",\"pronun\":{\"ipa\":\"ɑ.rɑˈβi.ɑ\",\"ipa_mod\":\"ɑ.rɑˈvi.ɑ\",\"sbl\":\"arabia\",\"dic\":\"ah-ra-VEE-ah\",\"dic_mod\":\"ah-ra-VEE-ah\"},\"see\":[\"H6152\"]}', 'Arabia'),
(689, 689, 'Ἀράμ', '{\"def\":{\"lit\":\"high\",\"short\":\"Aram (i.e., Ram), an Israelite\",\"long\":[\"an ancestor of Christ\"]},\"deriv\":\"of Hebrew origin (H7410)\",\"pronun\":{\"ipa\":\"ɑˈrɑm\",\"ipa_mod\":\"ɑˈrɑm\",\"sbl\":\"aram\",\"dic\":\"ah-RAHM\",\"dic_mod\":\"ah-RAHM\"},\"see\":[\"H7410\"]}', 'Aram'),
(690, 690, 'Ἄραψ', '{\"def\":{\"short\":\"an Arab or native of Arabia\",\"long\":[\"an Arabian\"]},\"deriv\":\"from G0688\",\"pronun\":{\"ipa\":\"ˈɑ.rɑp͡s\",\"ipa_mod\":\"ˈɑ.rɑp͡s\",\"sbl\":\"araps\",\"dic\":\"AH-rahps\",\"dic_mod\":\"AH-rahps\"},\"see\":[\"G0688\"]}', 'Arabian'),
(691, 691, 'ἀργέω', '{\"def\":{\"short\":\"to be idle, i.e., (figuratively) to delay\",\"long\":[\"to be idle, inactive, to linger, delay\"]},\"deriv\":\"from G0692\",\"pronun\":{\"ipa\":\"ɑrˈɣɛ.o\",\"ipa_mod\":\"ɑrˈɣe̞.ow\",\"sbl\":\"argeō\",\"dic\":\"ar-GEH-oh\",\"dic_mod\":\"ar-GAY-oh\"},\"see\":[\"G0692\"]}', 'linger'),
(692, 692, 'ἀργός', '{\"def\":{\"short\":\"inactive, i.e., unemployed; (by implication) lazy, useless\",\"long\":[\"free from labor, at leisure\",\"lazy, shunning the labor which one ought to perform\"]},\"deriv\":\"from G0001 (as a negative particle) and G2041\",\"pronun\":{\"ipa\":\"ɑrˈɣos\",\"ipa_mod\":\"ɑrˈɣows\",\"sbl\":\"argos\",\"dic\":\"ar-GOSE\",\"dic_mod\":\"ar-GOSE\"},\"see\":[\"G0001\",\"G2041\"]}', 'barren, idle, slow'),
(693, 693, 'ἀργύρεος', '{\"def\":{\"short\":\"made of silver\",\"long\":[\"of silver, silvery\"]},\"deriv\":\"from G0696\",\"pronun\":{\"ipa\":\"ɑrˈɣy.rɛ.os\",\"ipa_mod\":\"ɑrˈɣju.re̞.ows\",\"sbl\":\"argyreos\",\"dic\":\"ar-GOO-reh-ose\",\"dic_mod\":\"ar-GYOO-ray-ose\"},\"see\":[\"G0696\"]}', '(of) silver'),
(694, 694, 'ἀργύριον', '{\"def\":{\"short\":\"silvery, i.e., (by implication) cash; specially, a silverling (i.e., drachma or shekel)\",\"long\":[\"silver\",\"money\",\"a silver coin, silver piece, a shekel\"]},\"deriv\":\"neuter of a presumed derivative of G0696\",\"pronun\":{\"ipa\":\"ɑrˈɣy.ri.on\",\"ipa_mod\":\"ɑrˈɣju.ri.own\",\"sbl\":\"argyrion\",\"dic\":\"ar-GOO-ree-one\",\"dic_mod\":\"ar-GYOO-ree-one\"},\"see\":[\"G0696\"]}', 'money, (piece of) silver (piece)'),
(695, 695, 'ἀργυροκόπος', '{\"def\":{\"short\":\"a beater (i.e., worker) of silver\",\"long\":[\"a silversmith\"]},\"deriv\":\"from G0696 and G2875\",\"pronun\":{\"ipa\":\"ɑr.ɣy.roˈko.pos\",\"ipa_mod\":\"ɑr.ʝju.rowˈkow.pows\",\"sbl\":\"argyrokopos\",\"dic\":\"ar-goo-roh-KOH-pose\",\"dic_mod\":\"ar-gyoo-roh-KOH-pose\"},\"see\":[\"G0696\",\"G2875\"]}', 'silversmith'),
(696, 696, 'ἄργυρος', '{\"def\":{\"short\":\"silver (the metal, in the articles or coin)\",\"long\":[\"silver\",[\"1 Corinthians 3:12 refers to the silver with which the columns of noble buildings were covered and the rafters adorned\",\"things made of silver\",[\"vessels\",\"images of gods\"]]]},\"deriv\":\"from ἀργός (shining)\",\"pronun\":{\"ipa\":\"ˈɑr.ɣy.ros\",\"ipa_mod\":\"ˈɑr.ʝju.rows\",\"sbl\":\"argyros\",\"dic\":\"AR-goo-rose\",\"dic_mod\":\"AR-gyoo-rose\"}}', 'silver'),
(697, 697, 'Ἄρειος Πάγος', '{\"def\":{\"lit\":\"martial peak\",\"short\":\"rock of Ares, a place in Athens\",\"long\":[\"a rocky height in the city of Athens, opposite the western end of the Acropolis toward the west\"]},\"deriv\":\"from Ἄρης (the name of the Greek deity of war) and a derivative of G4078\",\"pronun\":{\"ipa\":\"ˈɑ.ri.os ˈpɑ.ɣos\",\"ipa_mod\":\"ˈɑ.ri.ows ˈpɑ.ɣows\",\"sbl\":\"areios pagos\",\"dic\":\"AH-ree-ose PA-gose\",\"dic_mod\":\"AH-ree-ose PA-gose\"},\"see\":[\"G4078\"],\"comment\":\"This hill belonged to Mars (Ares in Greek) and was called Mars\' Hill; so called, because, as the story went, Mars, having slain Halirrhothius, son of Neptune, for the attempted violation of his daughter Alicippe, was tried for the murder here before twelve gods as judges. This place was the location where the judges convened who, by appointment of Solon, had jurisdiction of capital offenses, (as willful murder, arson, poisoning, malicious wounding, and breach of established religious usages). The court itself was called Areopagus from the place where it sat. It is also called \\\"Areum judicium\\\" (the area of judges) or \\\"curia\\\" (senate or meeting house). To that hill the apostle Paul was led not to defend himself before judges but that he might set forth his opinions on divine subjects to a greater multitude of people, flocking together there and eager to hear something new.\"}', 'Areopagus, Mars\' Hill'),
(698, 698, 'Ἀρεοπαγίτης', '{\"def\":{\"short\":\"an Areopagite or member of the court held on Mars\' Hill\",\"long\":[\"a member of the court of Areopagus, an Areopagite\"]},\"deriv\":\"from G0697\",\"pronun\":{\"ipa\":\"ɑ.rɛ.o.pɑˈɣi.tes\",\"ipa_mod\":\"ɑ.re̞.ow.pɑˈɣi.te̞s\",\"sbl\":\"areopagitēs\",\"dic\":\"ah-reh-oh-pa-GEE-tase\",\"dic_mod\":\"ah-ray-oh-pa-GEE-tase\"},\"see\":[\"G0697\"]}', 'Areopagite'),
(699, 699, 'ἀρεσκεία', '{\"def\":{\"short\":\"complaisance\",\"long\":[\"desire to please\"]},\"deriv\":\"from a derivative of G0700\",\"pronun\":{\"ipa\":\"ɑ.rɛˈski.ɑ\",\"ipa_mod\":\"ɑ.re̞ˈsci.ɑ\",\"sbl\":\"areskeia\",\"dic\":\"ah-reh-SKEE-ah\",\"dic_mod\":\"ah-ray-SKEE-ah\"},\"see\":[\"G0700\"]}', 'pleasing'),
(700, 700, 'ἀρέσκω', '{\"def\":{\"short\":\"to be agreeable (or by implication, to seek to be so)\",\"long\":[\"to please\",\"to strive to please\",[\"to accommodate one\'s self to the opinions desires and interests of others\"]]},\"deriv\":\"probably from G0142 (through the idea of exciting emotion)\",\"pronun\":{\"ipa\":\"ɑˈrɛ.sko\",\"ipa_mod\":\"ɑˈre̞.skow\",\"sbl\":\"areskō\",\"dic\":\"ah-REH-skoh\",\"dic_mod\":\"ah-RAY-skoh\"},\"see\":[\"G0142\"]}', 'please'),
(701, 701, 'ἀρεστός', '{\"def\":{\"short\":\"agreeable; by implication, fit\",\"long\":[\"pleasing, agreeable\"]},\"deriv\":\"from G0700\",\"pronun\":{\"ipa\":\"ɑ.rɛˈstos\",\"ipa_mod\":\"ɑ.re̞ˈstows\",\"sbl\":\"arestos\",\"dic\":\"ah-reh-STOSE\",\"dic_mod\":\"ah-ray-STOSE\"},\"see\":[\"G0700\"]}', '(things that) please(-ing), reason'),
(702, 702, 'Ἀρέτας', '{\"def\":{\"lit\":\"graver\",\"short\":\"Aretas, an Arabian\",\"long\":[\"An Arabian king\"]},\"deriv\":\"of foreign origin\",\"pronun\":{\"ipa\":\"ɑˈrɛ.tɑs\",\"ipa_mod\":\"ɑˈre̞.tɑs\",\"sbl\":\"aretas\",\"dic\":\"ah-REH-tahs\",\"dic_mod\":\"ah-RAY-tahs\"},\"comment\":\"He made war in AD 36 on his son-in-law Herod Antipas for having divorced his daughter; and with such success as completely to destroy his army. In consequence of this, Vitellius, governor of Syria, being order by Tiberius to march an army against Aretes, prepared for war. But Tiberius meantime, having died (see Mark 16,37), he recalled his troops from march, dismissed them to winter quarters, and departed for Rome. After his departure Aretas held sway over the region of Damascus (how acquired, we do not know) and placed an ethnarch over the city, who tried to capture Paul.\"}', 'Aretas'),
(703, 703, 'ἀρέτη', '{\"def\":{\"short\":\"properly, manliness (valor), i.e., excellence (intrinsic or attributed)\",\"long\":[\"a virtuous course of thought, feeling and action\",[\"virtue, moral goodness\"],\"any particular moral excellence, as modesty, purity\"]},\"deriv\":\"from the same as G0730\",\"pronun\":{\"ipa\":\"ɑˈrɛ.te\",\"ipa_mod\":\"ɑˈre̞.te̞\",\"sbl\":\"aretē\",\"dic\":\"ah-REH-tay\",\"dic_mod\":\"ah-RAY-tay\"},\"see\":[\"G0730\"]}', 'praise, virtue'),
(704, 704, 'ἀρήν', '{\"def\":{\"short\":\"a lamb (as a male)\",\"long\":[\"a sheep, a lamb\"]},\"deriv\":\"perhaps the same as G0730\",\"pronun\":{\"ipa\":\"ɑˈren\",\"ipa_mod\":\"ɑˈre̞n\",\"sbl\":\"arēn\",\"dic\":\"ah-RANE\",\"dic_mod\":\"ah-RANE\"},\"see\":[\"G0730\"]}', 'lamb'),
(705, 705, 'ἀριθμέω', '{\"def\":{\"short\":\"to enumerate or count\",\"long\":[\"to number\"]},\"deriv\":\"from G0706\",\"pronun\":{\"ipa\":\"ɑ.riθˈmɛ.o\",\"ipa_mod\":\"ɑ.riθˈme̞.ow\",\"sbl\":\"arithmeō\",\"dic\":\"ah-reeth-MEH-oh\",\"dic_mod\":\"ah-reeth-MAY-oh\"},\"see\":[\"G0706\"]}', 'number'),
(706, 706, 'ἀριθμός', '{\"def\":{\"short\":\"a number (as reckoned up)\",\"long\":[\"a fixed and definite number\",\"an indefinite number, a multitude\"]},\"deriv\":\"from G0142\",\"pronun\":{\"ipa\":\"ɑ.riθˈmos\",\"ipa_mod\":\"ɑ.riθˈmows\",\"sbl\":\"arithmos\",\"dic\":\"ah-reeth-MOSE\",\"dic_mod\":\"ah-reeth-MOSE\"},\"see\":[\"G0142\"]}', 'number'),
(707, 707, 'Ἀριμαθαία', '{\"def\":{\"lit\":\"heights\",\"short\":\"Arimathaea (or Ramah), a place in Palestine\",\"long\":[\"the name of several cities in Palestine\"]},\"deriv\":\"of Hebrew origin (H7414)\",\"pronun\":{\"ipa\":\"ɑ.ri.mɑˈθɛ.ɑ\",\"ipa_mod\":\"ɑ.ri.mɑˈθe.ɑ\",\"sbl\":\"arimathaia\",\"dic\":\"ah-ree-ma-THEH-ah\",\"dic_mod\":\"ah-ree-ma-THAY-ah\"},\"see\":[\"H7414\"],\"comment\":\"The city mentioned in Matthew 27:57, Mark 15:43, Luke 23:51, John 19:38 appears to have been the same as the birthplace of Samuel in Mount Ephraim.\"}', 'Arimathaea'),
(708, 708, 'Ἀρίσταρχος', '{\"def\":{\"lit\":\"the best ruler\",\"short\":\"best ruling; Aristarchus, a Macedonian\",\"long\":[\"a certain Christian of Thessalonica, and accompanied Paul on his third missionary journey\"]},\"deriv\":\"from the same as G0712 and G0757\",\"pronun\":{\"ipa\":\"ɑˈri.stɑr.xos\",\"ipa_mod\":\"ɑˈri.stɑr.xows\",\"sbl\":\"aristarchos\",\"dic\":\"ah-REE-stahr-hose\",\"dic_mod\":\"ah-REE-stahr-hose\"},\"see\":[\"G0712\",\"G0757\"]}', 'Aristarchus'),
(709, 709, 'ἀριστάω', '{\"def\":{\"short\":\"to take the principle meal\",\"long\":[\"to breakfast\",\"by later usage, to dine\"]},\"deriv\":\"from G0712\",\"pronun\":{\"ipa\":\"ɑ.riˈstɑ.o\",\"ipa_mod\":\"ɑ.riˈstɑ.ow\",\"sbl\":\"aristaō\",\"dic\":\"ah-ree-STA-oh\",\"dic_mod\":\"ah-ree-STA-oh\"},\"see\":[\"G0712\"]}', 'dine'),
(710, 710, 'ἀριστερός', '{\"def\":{\"short\":\"the left hand (as second-best)\",\"long\":[\"left\"]},\"deriv\":\"apparently a comparative of the same as G0712\",\"pronun\":{\"ipa\":\"ɑ.ri.stɛˈros\",\"ipa_mod\":\"ɑ.ri.ste̞ˈrows\",\"sbl\":\"aristeros\",\"dic\":\"ah-ree-steh-ROSE\",\"dic_mod\":\"ah-ree-stay-ROSE\"},\"see\":[\"G0712\"]}', 'left (hand)'),
(711, 711, 'Ἀριστόβουλος', '{\"def\":{\"lit\":\"the best councillor\",\"short\":\"best counselling; Aristoboulus, a Christian\",\"long\":[\"a certain Christian\"]},\"deriv\":\"from the same as G0712 and G1012\",\"pronun\":{\"ipa\":\"ɑ.riˈsto.βu.los\",\"ipa_mod\":\"ɑ.riˈstow.vu.lows\",\"sbl\":\"aristoboulos\",\"dic\":\"ah-ree-STOH-voo-lose\",\"dic_mod\":\"ah-ree-STOH-voo-lose\"},\"see\":[\"G0712\",\"G1012\"]}', 'Aristobulus'),
(712, 712, 'ἄριστον', '{\"def\":{\"short\":\"the best meal (or breakfast; perhaps from ἦρι (\\\"early\\\")), i.e., luncheon\",\"long\":[\"the first food taken early in the morning before work, breakfast\",\"later usage: dinner\"]},\"deriv\":\"apparently neuter of a superlative from the same as G0730\",\"pronun\":{\"ipa\":\"ˈɑ.ri.ston\",\"ipa_mod\":\"ˈɑ.ri.stown\",\"sbl\":\"ariston\",\"dic\":\"AH-ree-stone\",\"dic_mod\":\"AH-ree-stone\"},\"see\":[\"G0730\"]}', 'dinner'),
(713, 713, 'ἀρκετός', '{\"def\":{\"short\":\"satisfactory\",\"long\":[\"sufficient, enough\"]},\"deriv\":\"from G0714\",\"pronun\":{\"ipa\":\"ɑr.kɛˈtos\",\"ipa_mod\":\"ɑr.ce̞ˈtows\",\"sbl\":\"arketos\",\"dic\":\"ar-keh-TOSE\",\"dic_mod\":\"ar-kay-TOSE\"},\"see\":[\"G0714\"]}', 'enough, suffice(-ient)'),
(714, 714, 'ἀρκέω', '{\"def\":{\"short\":\"properly, to ward off, i.e., (by implication) to avail (figuratively, be satisfactory)\",\"long\":[\"to be possessed of unfailing strength\",[\"to be strong, to suffice, to be enough\",[\"to defend, ward off\"],\"to be satisfied, to be contented\"]]},\"deriv\":\"apparently a primary verb (but probably akin to G0142 through the idea of raising a barrier)\",\"pronun\":{\"ipa\":\"ɑrˈkɛ.o\",\"ipa_mod\":\"ɑrˈke̞.ow\",\"sbl\":\"arkeō\",\"dic\":\"ar-KEH-oh\",\"dic_mod\":\"ar-KAY-oh\"},\"see\":[\"G0142\"]}', 'be content, be enough, suffice, be sufficient'),
(715, 715, 'ἄρκτος', '{\"def\":{\"short\":\"a bear (as obstructing by ferocity)\",\"long\":[\"a bear\"]},\"deriv\":\"probably from G0714\",\"pronun\":{\"ipa\":\"ˈɑr.k.tos\",\"ipa_mod\":\"ˈɑr.k.tows\",\"sbl\":\"arktos\",\"dic\":\"AR-k-tose\",\"dic_mod\":\"AR-k-tose\"},\"see\":[\"G0714\"]}', 'bear'),
(716, 716, 'ἅρμα', '{\"def\":{\"short\":\"a chariot (as raised or fitted together (compare G0719))\",\"long\":[\"a chariot\",\"of war chariots (i.e., armed with scythes)\",\"chariots (drawn with many horses)\"]},\"deriv\":\"probably from G0142 (perhaps with G0001 (as a particle of union) prefixed)\",\"pronun\":{\"ipa\":\"ˈhɑr.mɑ\",\"ipa_mod\":\"ˈɑr.mɑ\",\"sbl\":\"harma\",\"dic\":\"HAHR-ma\",\"dic_mod\":\"AHR-ma\"},\"see\":[\"G0001\",\"G0142\",\"G0719\"]}', 'chariot'),
(717, 717, 'Ἀρμαγεδδών', '{\"def\":{\"lit\":\"the hill, city of Megiddo\",\"short\":\"Armageddon (or Har-Meggiddon), a symbolic name\",\"long\":[\"A city in northern Israel where many battles were fought\",\"a battle field\"]},\"deriv\":\"of Hebrew origin (H2022 and H4023)\",\"pronun\":{\"ipa\":\"ɑr.mɑ.ɣɛðˈðon\",\"ipa_mod\":\"ɑr.mɑ.ʝe̞ðˈðown\",\"sbl\":\"armageddōn\",\"dic\":\"ar-ma-geth-THONE\",\"dic_mod\":\"ar-ma-gayth-THONE\"},\"see\":[\"H2022\",\"H4023\"],\"comment\":\"In Revelation 16:16, the scene of the struggle of good and evil is suggested by that battle plain of Esdraelon, which was famous for two great victories, of Barak over the Canaanites, and of Gideon over the Midianites; and for two great disasters, the deaths of Saul and Josiah. Hence in Revelation a place of great slaughter, the scene of a terrible retribution upon the wicked.\"}', 'Armageddon'),
(718, 718, 'ἁρμόζω', '{\"def\":{\"short\":\"to joint, i.e., (figuratively) to woo (reflexively, to betroth)\",\"long\":[\"to join, to fit together\",[\"of carpenter\'s, fastening together beams and planks to build houses, ships, etc.\"],\"to betroth a daughter to any one\",[\"to join to one\'s self, i.e., to marry the daughter of any one\",\"to betroth, to give one in marriage to any one\"]]},\"deriv\":\"from G0719\",\"pronun\":{\"ipa\":\"hɑrˈmo.zo\",\"ipa_mod\":\"ɑrˈmow.zow\",\"sbl\":\"harmozō\",\"dic\":\"hahr-MOH-zoh\",\"dic_mod\":\"ahr-MOH-zoh\"},\"see\":[\"G0719\"]}', 'espouse'),
(719, 719, 'ἁρμός', '{\"def\":{\"short\":\"an articulation (of the body)\",\"long\":[\"a joining, a joint\"]},\"deriv\":\"from the same as G0716\",\"pronun\":{\"ipa\":\"hɑrˈmos\",\"ipa_mod\":\"ɑrˈmows\",\"sbl\":\"harmos\",\"dic\":\"hahr-MOSE\",\"dic_mod\":\"ahr-MOSE\"},\"see\":[\"G0716\"]}', 'joint'),
(720, 720, 'ἀρνέομαι', '{\"def\":{\"short\":\"to contradict, i.e., disavow, reject, abnegate\",\"long\":[\"to deny\",\"to deny someone\",[\"to deny one\'s self\",[\"to disregard his own interests or to prove false to himself\",\"act entirely unlike himself\"]],\"to deny, abnegate, abjure\",\"not to accept, to reject, to refuse something offered\"]},\"deriv\":\"perhaps from G0001 (as a negative particle) and the middle voice of G4483\",\"pronun\":{\"ipa\":\"ɑrˈnɛ.o.mɛ\",\"ipa_mod\":\"ɑrˈne̞.ow.me\",\"sbl\":\"arneomai\",\"dic\":\"ar-NEH-oh-meh\",\"dic_mod\":\"ar-NAY-oh-may\"},\"see\":[\"G0001\",\"G4483\"]}', 'deny, refuse'),
(721, 721, 'ἀρνίον', '{\"def\":{\"short\":\"a lambkin\",\"long\":[\"a little lamb, a lamb\"]},\"deriv\":\"diminutive from G0704\",\"pronun\":{\"ipa\":\"ɑrˈni.on\",\"ipa_mod\":\"ɑrˈni.own\",\"sbl\":\"arnion\",\"dic\":\"ar-NEE-one\",\"dic_mod\":\"ar-NEE-one\"},\"see\":[\"G0704\"]}', 'lamb'),
(722, 722, 'ἀροτριόω', '{\"def\":{\"short\":\"to plow\",\"long\":[\"to plough\"]},\"deriv\":\"from G0723\",\"pronun\":{\"ipa\":\"ɑ.ro.triˈo.o\",\"ipa_mod\":\"ɑ.row.triˈow.ow\",\"sbl\":\"arotrioō\",\"dic\":\"ah-roh-tree-OH-oh\",\"dic_mod\":\"ah-roh-tree-OH-oh\"},\"see\":[\"G0723\"]}', 'plough'),
(723, 723, 'ἄροτρον', '{\"def\":{\"short\":\"a plow\",\"long\":[\"a plough\"]},\"deriv\":\"from ἀρόω (to till)\",\"pronun\":{\"ipa\":\"ˈɑ.ro.tron\",\"ipa_mod\":\"ˈɑ.row.trown\",\"sbl\":\"arotron\",\"dic\":\"AH-roh-trone\",\"dic_mod\":\"AH-roh-trone\"}}', 'plough'),
(724, 724, 'ἁρπαγή', '{\"def\":{\"short\":\"pillage (properly abstract)\",\"long\":[\"the act of plundering, robbery\",\"plunder spoil\"]},\"deriv\":\"from G0726\",\"pronun\":{\"ipa\":\"hɑr.pɑˈɣe\",\"ipa_mod\":\"ɑr.pɑˈɣe̞\",\"sbl\":\"harpagē\",\"dic\":\"hahr-pa-GAY\",\"dic_mod\":\"ahr-pa-GAY\"},\"see\":[\"G0726\"]}', 'extortion, ravening, spoiling'),
(725, 725, 'ἁρπαγμός', '{\"def\":{\"short\":\"plunder (properly concrete)\",\"long\":[\"the act of seizing, robbery\",\"a thing seized or to be seized\",[\"booty to deem anything a prize\",\"a thing to be seized upon or to be held fast, retained\"]]},\"deriv\":\"from G0726\",\"pronun\":{\"ipa\":\"hɑr.pɑɣˈmos\",\"ipa_mod\":\"ɑr.pɑɣˈmows\",\"sbl\":\"harpagmos\",\"dic\":\"hahr-pahg-MOSE\",\"dic_mod\":\"ahr-pahg-MOSE\"},\"see\":[\"G0726\"]}', 'robbery'),
(726, 726, 'ἁρπάζω', '{\"def\":{\"short\":\"to seize (in various applications)\",\"long\":[\"to seize, carry off by force\",\"to seize on, claim for one\'s self eagerly\",\"to snatch out or away\"]},\"deriv\":\"from a derivative of G0138\",\"pronun\":{\"ipa\":\"hɑrˈpɑ.zo\",\"ipa_mod\":\"ɑrˈpɑ.zow\",\"sbl\":\"harpazō\",\"dic\":\"hahr-PA-zoh\",\"dic_mod\":\"ahr-PA-zoh\"},\"see\":[\"G0138\"]}', 'catch (away, up), pluck, pull, take (by force)'),
(727, 727, 'ἅρπαξ', '{\"def\":{\"short\":\"rapacious\",\"long\":[\"rapacious, ravenous\",\"a extortioner, a robber\"]},\"deriv\":\"from G0726\",\"pronun\":{\"ipa\":\"ˈhɑr.pɑk͡s\",\"ipa_mod\":\"ˈɑr.pɑk͡s\",\"sbl\":\"harpax\",\"dic\":\"HAHR-pahks\",\"dic_mod\":\"AHR-pahks\"},\"see\":[\"G0726\"]}', 'extortion, ravening'),
(728, 728, 'ἀῤῥαβών', '{\"def\":{\"short\":\"a pledge, i.e., part of the purchase-money or property given in advance as security for the rest\",\"long\":[\"an earnest\",[\"money which in purchases is given as a pledge or downpayment that the full amount will subsequently be paid\"]]},\"deriv\":\"of Hebrew origin (H6162)\",\"pronun\":{\"ipa\":\"ɑr.rɑˈβon\",\"ipa_mod\":\"ɑr.rɑˈvown\",\"sbl\":\"arrhabōn\",\"dic\":\"ar-ra-VONE\",\"dic_mod\":\"ar-ra-VONE\"},\"see\":[\"H6162\"]}', 'earnest'),
(729, 729, 'ἄῤῥαφος', '{\"def\":{\"short\":\"unsewed, i.e., of a single piece\",\"long\":[\"not sewed together, without a seam\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of the same as G4476\",\"pronun\":{\"ipa\":\"ˈɑr.rɑ.fos\",\"ipa_mod\":\"ˈɑr.rɑ.fows\",\"sbl\":\"arrhaphos\",\"dic\":\"AR-ra-fose\",\"dic_mod\":\"AR-ra-fose\"},\"see\":[\"G0001\",\"G4476\"]}', 'without seam'),
(730, 730, 'ἄῤῥην', '{\"def\":{\"short\":\"male (as stronger for lifting)\",\"long\":[\"a male\"]},\"deriv\":\"probably from G0142\",\"pronun\":{\"ipa\":\"ˈɑr.ren\",\"ipa_mod\":\"ˈɑr.re̞n\",\"sbl\":\"arrhēn\",\"dic\":\"AR-rane\",\"dic_mod\":\"AR-rane\"},\"see\":[\"G0142\"]}', 'male, man'),
(731, 731, 'ἄῤῥητος', '{\"def\":{\"short\":\"unsaid, i.e., (by implication) inexpressible\",\"long\":[\"unsaid, unspoken\",\"unspeakable (on account of its sacredness)\"]},\"deriv\":\"from G0001 (as a negative particle) and the same as G4490\",\"pronun\":{\"ipa\":\"ˈɑr.re.tos\",\"ipa_mod\":\"ˈɑr.re̞.tows\",\"sbl\":\"arrhētos\",\"dic\":\"AR-ray-tose\",\"dic_mod\":\"AR-ray-tose\"},\"see\":[\"G0001\",\"G4490\"]}', 'unspeakable'),
(732, 732, 'ἄῤῥωστος', '{\"def\":{\"short\":\"infirm\",\"long\":[\"without strength, weak, sick\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G4517\",\"pronun\":{\"ipa\":\"ˈɑr.ro.stos\",\"ipa_mod\":\"ˈɑr.row.stows\",\"sbl\":\"arrhōstos\",\"dic\":\"AR-roh-stose\",\"dic_mod\":\"AR-roh-stose\"},\"see\":[\"G0001\",\"G4517\"]}', 'sick (folk, -ly)'),
(733, 733, 'ἀρσενοκοίτης', '{\"def\":{\"short\":\"a sodomite\",\"long\":[\"one who lies with a male as with a female, sodomite, homosexual\"]},\"deriv\":\"from G0730 and G2845\",\"pronun\":{\"ipa\":\"ɑr.sɛ.noˈky.tes\",\"ipa_mod\":\"ɑr.se̞.nowˈky.te̞s\",\"sbl\":\"arsenokoitēs\",\"dic\":\"ar-seh-noh-KOO-tase\",\"dic_mod\":\"ar-say-noh-KOO-tase\"},\"see\":[\"G0730\",\"G2845\"]}', 'abuser of (that defile) self with mankind'),
(734, 734, 'Ἀρτεμάς', '{\"def\":{\"lit\":\"gift of Artemis\",\"short\":\"gift of Artemis; Artemas (or Artemidorus), a Christian\",\"long\":[\"a friend of Paul the apostle\"]},\"deriv\":\"contracted from a compound of G0735 and G1435\",\"pronun\":{\"ipa\":\"ɑr.tɛˈmɑs\",\"ipa_mod\":\"ɑr.te̞ˈmɑs\",\"sbl\":\"artemas\",\"dic\":\"ar-teh-MAHS\",\"dic_mod\":\"ar-tay-MAHS\"},\"see\":[\"G0735\",\"G1435\"]}', 'Artemas'),
(735, 735, 'Ἄρτεμις', '{\"def\":{\"lit\":\"complete light, flow restrained\",\"short\":\"prompt; Artemis, the name of a Grecian goddess borrowed by the Asiatics for one of their deities\",\"long\":[\"Diana, Artemis (in Greek)\"]},\"deriv\":\"probably from the same as G0736\",\"pronun\":{\"ipa\":\"ˈɑr.tɛ.mis\",\"ipa_mod\":\"ˈɑr.te̞.mis\",\"sbl\":\"artemis\",\"dic\":\"AR-teh-mees\",\"dic_mod\":\"AR-tay-mees\"},\"see\":[\"G0736\"],\"comment\":\"The Tauric or Persian or Ephesian goddess (also known as Artemis) was the goddess of many Asiatic people. It is to be distinguished from the Artemis of the Greeks, the sister of Apollo. A very splendid temple was built to her at Ephesus, which was set on fire by Herostratus and reduced to ashes; but afterwards in the times of Alexander the Great. It was rebuilt in a style of greater magnificence.\"}', 'Diana'),
(736, 736, 'ἀρτέμων', '{\"def\":{\"short\":\"properly, something ready (or else more remotely from G0142 (compare G0740); something hung up), i.e., (specially) the topsail (rather foresail or jib) of a vessel\",\"long\":[\"a top-sail or foresail of a ship\"]},\"deriv\":\"from a derivative of G0737\",\"pronun\":{\"ipa\":\"ɑrˈtɛ.mon\",\"ipa_mod\":\"ɑrˈte̞.mown\",\"sbl\":\"artemōn\",\"dic\":\"ar-TEH-mone\",\"dic_mod\":\"ar-TAY-mone\"},\"see\":[\"G0142\",\"G0737\",\"G0740\"]}', 'mainsail'),
(737, 737, 'ἄρτι', '{\"def\":{\"short\":\"just now\",\"long\":[\"just now, this moment\",\"now at this time, at this very time, this moment\"]},\"deriv\":\"adverb from a derivative of G0142 (compare G0740) through the idea of suspension\",\"pronun\":{\"ipa\":\"ˈɑr.ti\",\"ipa_mod\":\"ˈɑr.ti\",\"sbl\":\"arti\",\"dic\":\"AR-tee\",\"dic_mod\":\"AR-tee\"},\"see\":[\"G0142\",\"G0740\"]}', 'this day (hour), hence(-forth), here(-after), hither(-to), (even) now, (this) present'),
(738, 738, 'ἀρτιγέννητος', '{\"def\":{\"short\":\"just born, i.e., (figuratively) a young convert\",\"long\":[\"just born, newborn\"]},\"deriv\":\"from G0737 and G1084\",\"pronun\":{\"ipa\":\"ɑr.tiˈɣɛn.ne.tos\",\"ipa_mod\":\"ɑr.tiˈɣe̞n.ne̞.tows\",\"sbl\":\"artigennētos\",\"dic\":\"ar-tee-GEN-nay-tose\",\"dic_mod\":\"ar-tee-GANE-nay-tose\"},\"see\":[\"G0737\",\"G1084\"]}', 'new born'),
(739, 739, 'ἄρτιος', '{\"def\":{\"short\":\"fresh, i.e., (by implication) complete\",\"long\":[\"fitted\",\"complete, perfect\",[\"having reference apparently to \\\"special aptitude for given uses\\\"\"]]},\"deriv\":\"from G0737\",\"pronun\":{\"ipa\":\"ˈɑr.ti.os\",\"ipa_mod\":\"ˈɑr.ti.ows\",\"sbl\":\"artios\",\"dic\":\"AR-tee-ose\",\"dic_mod\":\"AR-tee-ose\"},\"see\":[\"G0737\"]}', 'perfect'),
(740, 740, 'ἄρτος', '{\"def\":{\"short\":\"bread (as raised) or a loaf\",\"long\":[\"food composed of flour mixed with water and baked\",[\"the Israelites made it in the form of an oblong or round cake, as thick as one\'s thumb, and as large as a plate or platter hence it was not to be cut but broken\",\"loaves were consecrated to the Lord\",\"of the bread used at the love-feasts and at the Lord\'s Table\"],\"food of any kind\"]},\"deriv\":\"from G0142\",\"pronun\":{\"ipa\":\"ˈɑr.tos\",\"ipa_mod\":\"ˈɑr.tows\",\"sbl\":\"artos\",\"dic\":\"AR-tose\",\"dic_mod\":\"AR-tose\"},\"see\":[\"G0142\"]}', '(shew-)bread, loaf'),
(741, 741, 'ἀρτύω', '{\"def\":{\"short\":\"to prepare, i.e., spice (with stimulating condiments)\",\"long\":[\"to prepare, arrange, with respect to food\",\"to season, make savory\"]},\"deriv\":\"from a presumed derivative of G0142\",\"pronun\":{\"ipa\":\"ɑrˈty.o\",\"ipa_mod\":\"ɑrˈtju.ow\",\"sbl\":\"artyō\",\"dic\":\"ar-TOO-oh\",\"dic_mod\":\"ar-TYOO-oh\"},\"see\":[\"G0142\"]}', 'season'),
(742, 742, 'Ἀρφαξάδ', '{\"def\":{\"lit\":\"stronghold of Chaldees\",\"short\":\"Arphaxad, a post-diluvian patriarch\",\"long\":[\"the son of Shem and ancestor of Eber\"]},\"deriv\":\"of Hebrew origin (H0775)\",\"pronun\":{\"ipa\":\"ɑr.fɑˈk͡sɑð\",\"ipa_mod\":\"ɑr.fɑˈk͡sɑð\",\"sbl\":\"arphaxad\",\"dic\":\"ar-fa-KSAHTH\",\"dic_mod\":\"ar-fa-KSAHTH\"},\"see\":[\"H0775\"]}', 'Arphaxad'),
(743, 743, 'ἀρχάγγελος', '{\"def\":{\"short\":\"a chief angel\",\"long\":[\"archangel, or chief of the angels The Jews, after the exile, distinguished several orders of angels; some reckoned four angels (according to the four sides of God\'s throne) of the highest rank; but the majority reckoned seven (after the pattern of the seven Amshaspands, the highest spirits of the religion of Zoroaster)\"]},\"deriv\":\"from G0757 and G0032\",\"pronun\":{\"ipa\":\"ɑrˈxɑŋ.ɣɛ.los\",\"ipa_mod\":\"ɑrˈxɑŋ.ʝe̞.lows\",\"sbl\":\"archangelos\",\"dic\":\"ar-HAHNG-geh-lose\",\"dic_mod\":\"ar-HAHNG-gay-lose\"},\"see\":[\"G0032\",\"G0757\"]}', 'archangel'),
(744, 744, 'ἀρχαῖος', '{\"def\":{\"short\":\"original or primeval\",\"long\":[\"that has been from the beginning, original, primal, old ancient\",[\"of men, things, times, conditions\"]]},\"deriv\":\"from G0746\",\"pronun\":{\"ipa\":\"ɑrˈxɛ.os\",\"ipa_mod\":\"ɑrˈxe.ows\",\"sbl\":\"archaios\",\"dic\":\"ar-HEH-ose\",\"dic_mod\":\"ar-HAY-ose\"},\"see\":[\"G0746\"]}', '(them of) old (time)'),
(745, 745, 'Ἀρχέλαος', '{\"def\":{\"lit\":\"prince of the people\",\"short\":\"people-ruling; Archelaus, a Jewish king\",\"long\":[\"A son of Herod the Great by Malthace, a Samaritan woman\"]},\"deriv\":\"from G0757 and G2994\",\"pronun\":{\"ipa\":\"ɑrˈxɛ.lɑ.os\",\"ipa_mod\":\"ɑrˈxe̞.lɑ.ows\",\"sbl\":\"archelaos\",\"dic\":\"ar-HEH-la-ose\",\"dic_mod\":\"ar-HAY-la-ose\"},\"see\":[\"G0757\",\"G2994\"],\"comment\":\"He and his brother Antipas were brought up with a certain private man in Rome. After the death of his father, he ruled as ethnarch over Judaea, Samaria and Idumaea, (with the exception of the cities of Gaza, Gadara and Hipo). The Jews and the Samaritans having accused him at Rome of tyranny, he was banished by the emperor Augustus to Vienna of the Allobroges and died there.\"}', 'Archelaus'),
(746, 746, 'ἀρχή', '{\"def\":{\"short\":\"(properly abstract) a commencement, or (concretely) chief (in various applications of order, time, place, or rank)\",\"long\":[\"beginning, origin\",\"the person or thing that commences, the first person or thing in a series, the leader\",\"that by which anything begins to be, the origin, the active cause\",\"the extremity of a thing\",[\"of the corners of a sail\"],\"the first place, principality, rule, magistracy\",[\"of angels and demons\"]]},\"deriv\":\"from G0756\",\"pronun\":{\"ipa\":\"ɑrˈxe\",\"ipa_mod\":\"ɑrˈxe̞\",\"sbl\":\"archē\",\"dic\":\"ar-HAY\",\"dic_mod\":\"ar-HAY\"},\"see\":[\"G0756\"]}', 'beginning, corner, (at the, the) first (estate), magistrate, power, principality, principle, rule'),
(747, 747, 'ἀρχηγός', '{\"def\":{\"short\":\"a chief leader\",\"long\":[\"the chief leader, prince\",[\"of Christ\"],\"one that takes the lead in any thing and thus affords an example, a predecessor in a matter, pioneer\",\"the author\"]},\"deriv\":\"from G0746 and G0071\",\"pronun\":{\"ipa\":\"ɑr.xeˈɣos\",\"ipa_mod\":\"ɑr.xe̞ˈɣows\",\"sbl\":\"archēgos\",\"dic\":\"ar-hay-GOSE\",\"dic_mod\":\"ar-hay-GOSE\"},\"see\":[\"G0071\",\"G0746\"]}', 'author, captain, prince'),
(748, 748, 'ἀρχιερατικός', '{\"def\":{\"short\":\"high-priestly\",\"long\":[\"high priestly, pontifical\"]},\"deriv\":\"from G0746 and a derivative of G2413\",\"pronun\":{\"ipa\":\"ɑr.xi.ɛ.rɑ.tiˈkos\",\"ipa_mod\":\"ɑr.çi.e̞.rɑ.tiˈkows\",\"sbl\":\"archieratikos\",\"dic\":\"ar-hee-eh-ra-tee-KOSE\",\"dic_mod\":\"ar-hee-ay-ra-tee-KOSE\"},\"see\":[\"G0746\",\"G2413\"]}', 'of the high-priest'),
(749, 749, 'ἀρχιερεύς', '{\"def\":{\"short\":\"the high-priest (literally, of the Jews; typically, Christ); by extension a chief priest\",\"long\":[\"chief priest, high priest\",\"the high priests, these comprise in addition to one holding the high priestly office, both those who had previously discharged it and although disposed, continued to have great power in the State, as well as the members of the families from which high priest were created, provided that they had much influence in public affairs.\",\"Used of Christ because by undergoing a bloody death he offered himself as an expiatory sacrifice to God, and has entered into the heavenly sanctuary where he continually intercedes on our behalf.\"]},\"deriv\":\"from G0746 and G2409\",\"pronun\":{\"ipa\":\"ɑr.xi.ɛˈrɛβs\",\"ipa_mod\":\"ɑr.çi.e̞ˈrefs\",\"sbl\":\"archiereus\",\"dic\":\"ar-hee-eh-REVS\",\"dic_mod\":\"ar-hee-ay-RAYFS\"},\"see\":[\"G0746\",\"G2409\"],\"comment\":\"The high priest, above all others, was honored with the title of priest, the chief of priests. It was lawful for him to perform the common duties of the priesthood; but his chief duty was, once a year on the day of atonement, to enter into the Holy of Holies (from which the other priests were excluded) and offer sacrifices for his own sins and the sins of the people, and to preside over the Sanhedrin, or Supreme Council, when convened for judicial deliberations. According to Mosaic law, no one could aspire to the high priesthood unless he were of the tribe of Aaron and descended from a high priestly family, and he on whom the office was conferred held it till death. But from the time of Antiochus Epiphanes, when the kings of Seleucideae and afterwards the Herodian princes and the Romans arrogated to themselves the power of appointing the high priests, the office neither remained vested in the pontifical family nor was conferred on any for life; but it became venal, and could be transferred from one to another according to the will of civic or military rulers. Hence it came to pass, that during the one hundred and seven years intervening between Herod the Great and the destruction of the holy city, twenty eight persons held this office.\"}', 'chief (high) priest, chief of the priests'),
(750, 750, 'ἀρχιποίμην', '{\"def\":{\"short\":\"a head shepherd\",\"long\":[\"chief shepherd\",[\"of Christ the head of the church\"]]},\"deriv\":\"from G0746 and G4166\",\"pronun\":{\"ipa\":\"ɑr.xiˈpy.men\",\"ipa_mod\":\"ɑr.çiˈpy.me̞n\",\"sbl\":\"archipoimēn\",\"dic\":\"ar-hee-POO-mane\",\"dic_mod\":\"ar-hee-POO-mane\"},\"see\":[\"G0746\",\"G4166\"]}', 'chief shepherd'),
(751, 751, 'Ἄρχιππος', '{\"def\":{\"lit\":\"master of the horse\",\"short\":\"horse-ruler; Archippus, a Christian\",\"long\":[\"a certain Christian teacher in Colosse\"]},\"deriv\":\"from G0746 and G2462\",\"pronun\":{\"ipa\":\"ˈɑr.xip.pos\",\"ipa_mod\":\"ˈɑr.çip.pows\",\"sbl\":\"archippos\",\"dic\":\"AR-heep-pose\",\"dic_mod\":\"AR-heep-pose\"},\"see\":[\"G0746\",\"G2462\"]}', 'Archippus'),
(752, 752, 'ἀρχισυνάγωγος', '{\"def\":{\"short\":\"director of the synagogue services\",\"long\":[\"ruler of the synagogue. It was his duty to select the readers or teachers in the synagogue, to examine the discourses of the public speakers, and to see that all things were done with decency and in accordance with ancestral usage.\"]},\"deriv\":\"from G0746 and G4864\",\"pronun\":{\"ipa\":\"ɑr.xi.syˈnɑ.ɣo.ɣos\",\"ipa_mod\":\"ɑr.çi.sjuˈnɑ.ɣow.ɣows\",\"sbl\":\"archisynagōgos\",\"dic\":\"ar-hee-soo-NA-goh-gose\",\"dic_mod\":\"ar-hee-syoo-NA-goh-gose\"},\"see\":[\"G0746\",\"G4864\"]}', '(chief) ruler of the synagogue'),
(753, 753, 'ἀρχιτέκτων', '{\"def\":{\"short\":\"a chief constructor, i.e., \\\"architect\\\"\",\"long\":[\"a master builder, an architect, the superintendent in the erection of buildings\"]},\"deriv\":\"from G0746 and G5045\",\"pronun\":{\"ipa\":\"ɑr.xiˈtɛk.ton\",\"ipa_mod\":\"ɑr.çiˈte̞k.town\",\"sbl\":\"architektōn\",\"dic\":\"ar-hee-TEK-tone\",\"dic_mod\":\"ar-hee-TAKE-tone\"},\"see\":[\"G0746\",\"G5045\"]}', 'masterbuilder'),
(754, 754, 'ἀρχιτελώνης', '{\"def\":{\"short\":\"a principle tax-gatherer\",\"long\":[\"a chief of tax collectors, chief publican\"]},\"deriv\":\"from G0746 and G5057\",\"pronun\":{\"ipa\":\"ɑr.xi.tɛˈlo.nes\",\"ipa_mod\":\"ɑr.çi.te̞ˈlow.ne̞s\",\"sbl\":\"architelōnēs\",\"dic\":\"ar-hee-teh-LOH-nase\",\"dic_mod\":\"ar-hee-tay-LOH-nase\"},\"see\":[\"G0746\",\"G5057\"]}', 'chief among the publicans'),
(755, 755, 'ἀρχιτρίκλινος', '{\"def\":{\"short\":\"director of the entertainment\",\"long\":[\"the superintendent of the dining room, a table master\"]},\"deriv\":\"from G0746 and a compound of G5140 and G2827 (a dinner-bed, because composed of three couches)\",\"pronun\":{\"ipa\":\"ɑr.xiˈtri.kli.nos\",\"ipa_mod\":\"ɑr.çiˈtri.kli.nows\",\"sbl\":\"architriklinos\",\"dic\":\"ar-hee-TREE-klee-nose\",\"dic_mod\":\"ar-hee-TREE-klee-nose\"},\"see\":[\"G0746\",\"G2827\",\"G5140\"],\"comment\":\"This is different from the toast-master, who was one of the guests selected by lot to prescribe to the rest the mode of drinking. The table master was to place in order the tables and the couches, arrange the courses, taste the food and wine beforehand, and so forth.\"}', 'governor (ruler) of the feast'),
(756, 756, 'ἄρχομαι', '{\"def\":{\"short\":\"to commence (in order of time)\",\"long\":[\"to be the first to do (anything), to begin\",\"to be chief, leader, ruler\",\"to begin, make a beginning\"]},\"deriv\":\"middle voice of G0757 (through the implication, of precedence)\",\"pronun\":{\"ipa\":\"ˈɑr.xo.mɛ\",\"ipa_mod\":\"ˈɑr.xow.me\",\"sbl\":\"archomai\",\"dic\":\"AR-hoh-meh\",\"dic_mod\":\"AR-hoh-may\"},\"see\":[\"G0757\"]}', '(rehearse from the) begin(-ning)'),
(757, 757, 'ἄρχω', '{\"def\":{\"short\":\"to be first (in political rank or power)\",\"long\":[\"to be chief, to lead, to rule\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈɑr.xo\",\"ipa_mod\":\"ˈɑr.xow\",\"sbl\":\"archō\",\"dic\":\"AR-hoh\",\"dic_mod\":\"AR-hoh\"}}', 'reign (rule) over'),
(758, 758, 'ἄρχων', '{\"def\":{\"short\":\"a first (in rank or power)\",\"long\":[\"a ruler, commander, chief, leader\"]},\"deriv\":\"present participle of G0757\",\"pronun\":{\"ipa\":\"ˈɑr.xon\",\"ipa_mod\":\"ˈɑr.xown\",\"sbl\":\"archōn\",\"dic\":\"AR-hone\",\"dic_mod\":\"AR-hone\"},\"see\":[\"G0757\"]}', 'chief (ruler), magistrate, prince, ruler'),
(759, 759, 'ἄρωμα', '{\"def\":{\"short\":\"an aromatic\",\"long\":[\"spice, perfume\"]},\"deriv\":\"from G0142 (in the sense of sending off scent)\",\"pronun\":{\"ipa\":\"ˈɑ.ro.mɑ\",\"ipa_mod\":\"ˈɑ.row.mɑ\",\"sbl\":\"arōma\",\"dic\":\"AH-roh-ma\",\"dic_mod\":\"AH-roh-ma\"},\"see\":[\"G0142\"]}', '(sweet) spice'),
(760, 760, 'Ἀσά', '{\"def\":{\"lit\":\"physician, cure\",\"short\":\"Asa, an Israelite\",\"long\":[\"son of Abijah and king of Judah\"]},\"deriv\":\"of Hebrew origin (H0609)\",\"pronun\":{\"ipa\":\"ɑˈsɑ\",\"ipa_mod\":\"ɑˈsɑ\",\"sbl\":\"asa\",\"dic\":\"ah-SA\",\"dic_mod\":\"ah-SA\"},\"see\":[\"H0609\"]}', 'Asa'),
(761, 761, 'ἀσάλευτος', '{\"def\":{\"short\":\"unshaken, i.e., (by implication) immovable (figuratively)\",\"long\":[\"unshaken, unmoved\",\"metaphorically not liable to overthrow and disorder, firm stable\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G4531\",\"pronun\":{\"ipa\":\"ɑˈsɑ.lɛβ.tos\",\"ipa_mod\":\"ɑˈsɑ.lef.tows\",\"sbl\":\"asaleutos\",\"dic\":\"ah-SA-lev-tose\",\"dic_mod\":\"ah-SA-layf-tose\"},\"see\":[\"G0001\",\"G4531\"]}', 'which cannot be moved, unmovable'),
(762, 762, 'ἄσβεστος', '{\"def\":{\"short\":\"not extinguished, i.e., (by implication) perpetual\",\"long\":[\"unquenched, unquenchable\",[\"of eternal hell fire to punish the damned\"]]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G4570\",\"pronun\":{\"ipa\":\"ˈɑs.βɛ.stos\",\"ipa_mod\":\"ˈɑs.ve̞.stows\",\"sbl\":\"asbestos\",\"dic\":\"AS-veh-stose\",\"dic_mod\":\"AS-vay-stose\"},\"see\":[\"G0001\",\"G4570\"]}', 'not to be quenched, unquenchable'),
(763, 763, 'ἀσέβεια', '{\"def\":{\"short\":\"impiety, i.e., (by implication) wickedness\",\"long\":[\"want of reverence towards God, impiety, ungodliness\"]},\"deriv\":\"from G0765\",\"pronun\":{\"ipa\":\"ɑˈsɛ.βi.ɑ\",\"ipa_mod\":\"ɑˈse̞.vi.ɑ\",\"sbl\":\"asebeia\",\"dic\":\"ah-SEH-vee-ah\",\"dic_mod\":\"ah-SAY-vee-ah\"},\"see\":[\"G0765\"]}', 'ungodly(-liness)'),
(764, 764, 'ἀσεβέω', '{\"def\":{\"short\":\"to be (by implied act) impious or wicked\",\"long\":[\"to be ungodly, act impiously\"]},\"deriv\":\"from G0765\",\"pronun\":{\"ipa\":\"ɑ.sɛˈβɛ.o\",\"ipa_mod\":\"ɑ.se̞ˈve̞.ow\",\"sbl\":\"asebeō\",\"dic\":\"ah-seh-VEH-oh\",\"dic_mod\":\"ah-say-VAY-oh\"},\"see\":[\"G0765\"]}', 'commit (live, that after should live) ungodly'),
(765, 765, 'ἀσεβής', '{\"def\":{\"short\":\"irreverent, i.e., (by extension) impious or wicked\",\"long\":[\"destitute of reverential awe towards God, condemning God, impious\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G4576\",\"pronun\":{\"ipa\":\"ɑ.sɛˈβes\",\"ipa_mod\":\"ɑ.se̞ˈve̞s\",\"sbl\":\"asebēs\",\"dic\":\"ah-seh-VASE\",\"dic_mod\":\"ah-say-VASE\"},\"see\":[\"G0001\",\"G4576\"]}', 'ungodly (man)'),
(766, 766, 'ἀσέλγεια', '{\"def\":{\"short\":\"licentiousness (sometimes including other vices)\",\"long\":[\"unbridled lust, excess, licentiousness, lasciviousness, wantonness, outrageousness, shamelessness, insolence\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and a presumed σελγής (of uncertain derivation, but apparently meaning continent)\",\"pronun\":{\"ipa\":\"ɑˈsɛl.ɣi.ɑ\",\"ipa_mod\":\"ɑˈse̞l.ʝi.ɑ\",\"sbl\":\"aselgeia\",\"dic\":\"ah-SEL-gee-ah\",\"dic_mod\":\"ah-SALE-gee-ah\"},\"see\":[\"G0001\"]}', 'filthy, lasciviousness, wantonness'),
(767, 767, 'ἄσημος', '{\"def\":{\"short\":\"unmarked, i.e., (figuratively) ignoble\",\"long\":[\"unmarked or unstamped\",[\"of money\"],\"unknown, of no mark, insignificant, ignoble\"]},\"deriv\":\"from G0001 (as a negative particle) and the base of G4591\",\"pronun\":{\"ipa\":\"ˈɑ.se.mos\",\"ipa_mod\":\"ˈɑ.se̞.mows\",\"sbl\":\"asēmos\",\"dic\":\"AH-say-mose\",\"dic_mod\":\"AH-say-mose\"},\"see\":[\"G0001\",\"G4591\"]}', 'mean'),
(768, 768, 'Ἀσήρ', '{\"def\":{\"lit\":\"blessed\",\"short\":\"Aser (i.e., Asher), an Israelite tribe\",\"long\":[\"the eighth son of Jacob\"]},\"deriv\":\"of Hebrew origin (H0836)\",\"pronun\":{\"ipa\":\"ɑˈser\",\"ipa_mod\":\"ɑˈse̞r\",\"sbl\":\"asēr\",\"dic\":\"ah-SARE\",\"dic_mod\":\"ah-SARE\"},\"see\":[\"H0836\"]}', 'Aser'),
(769, 769, 'ἀσθένεια', '{\"def\":{\"short\":\"feebleness (of mind or body); by implication, malady; morally, frailty\",\"long\":[\"want of strength, weakness, infirmity\",[\"of the body\",[\"its native weakness and frailty\",\"feebleness of health or sickness\"],\"of the soul\",[\"want of strength and capacity requisite\",[\"to understand a thing\",\"to do things great and glorious\",\"to restrain corrupt desires\",\"to bear trials and troubles\"]]]]},\"deriv\":\"from G0772\",\"pronun\":{\"ipa\":\"ɑˈsθɛ.ni.ɑ\",\"ipa_mod\":\"ɑˈsθe̞.ni.ɑ\",\"sbl\":\"astheneia\",\"dic\":\"ah-STHEH-nee-ah\",\"dic_mod\":\"ah-STHAY-nee-ah\"},\"see\":[\"G0772\"]}', 'disease, infirmity, sickness, weakness'),
(770, 770, 'ἀσθενέω', '{\"def\":{\"short\":\"to be feeble (in any sense)\",\"long\":[\"to be weak, feeble, to be without strength, powerless\",\"to be weak in means, needy, poor\",\"to be feeble, sick\"]},\"deriv\":\"from G0772\",\"pronun\":{\"ipa\":\"ɑ.sθɛˈnɛ.o\",\"ipa_mod\":\"ɑ.sθe̞ˈne̞.ow\",\"sbl\":\"astheneō\",\"dic\":\"ah-stheh-NEH-oh\",\"dic_mod\":\"ah-sthay-NAY-oh\"},\"see\":[\"G0772\"]}', 'be diseased, impotent folk (man), (be) sick, (be, be made) weak'),
(771, 771, 'ἀσθένημα', '{\"def\":{\"short\":\"a scruple of conscience\",\"long\":[\"infirmity\",[\"of error arising from weakness of mind\"]]},\"deriv\":\"from G0770\",\"pronun\":{\"ipa\":\"ɑˈsθɛ.ne.mɑ\",\"ipa_mod\":\"ɑˈsθe̞.ne̞.mɑ\",\"sbl\":\"asthenēma\",\"dic\":\"ah-STHEH-nay-ma\",\"dic_mod\":\"ah-STHAY-nay-ma\"},\"see\":[\"G0770\"]}', 'infirmity'),
(772, 772, 'ἀσθενής', '{\"def\":{\"short\":\"strengthless (in various applications, literal, figurative and moral)\",\"long\":[\"weak, infirm, feeble\"]},\"deriv\":\"from G0001 (as a negative particle) and the base of G4599\",\"pronun\":{\"ipa\":\"ɑ.sθɛˈnes\",\"ipa_mod\":\"ɑ.sθe̞ˈne̞s\",\"sbl\":\"asthenēs\",\"dic\":\"ah-stheh-NASE\",\"dic_mod\":\"ah-sthay-NASE\"},\"see\":[\"G0001\",\"G4599\"]}', 'more feeble, impotent, sick, without strength, weak(-er, -ness) (thing)'),
(773, 773, 'Ἀσία', '{\"def\":{\"lit\":\"orient\",\"short\":\"Asia, i.e., Asia Minor, or (usually) only its western shore\",\"long\":[\"Asia proper or proconsular Asia embracing Mysia, Lydia, Phrygia, and Caria, corresponding closely to Turkey today\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ɑˈsi.ɑ\",\"ipa_mod\":\"ɑˈsi.ɑ\",\"sbl\":\"asia\",\"dic\":\"ah-SEE-ah\",\"dic_mod\":\"ah-SEE-ah\"}}', 'Asia'),
(774, 774, 'Ἀσιανός', '{\"def\":{\"short\":\"an Asian (i.e., Asiatic) or an inhabitant of Asia\",\"long\":[\"a native of Asia, Asian, Asiatic\"]},\"deriv\":\"from G0773\",\"pronun\":{\"ipa\":\"ɑ.si.ɑˈnos\",\"ipa_mod\":\"ɑ.si.ɑˈnows\",\"sbl\":\"asianos\",\"dic\":\"ah-see-ah-NOSE\",\"dic_mod\":\"ah-see-ah-NOSE\"},\"see\":[\"G0773\"]}', 'of Asia'),
(775, 775, 'Ἀσιάρχης', '{\"def\":{\"short\":\"an Asiarch or president of the public festivities in a city of Asia Minor\",\"long\":[\"an Asiarch, President of Asia\"]},\"deriv\":\"from G0773 and G0746\",\"pronun\":{\"ipa\":\"ɑ.siˈɑr.xes\",\"ipa_mod\":\"ɑ.siˈɑr.xe̞s\",\"sbl\":\"asiarchēs\",\"dic\":\"ah-see-AR-hase\",\"dic_mod\":\"ah-see-AR-hase\"},\"see\":[\"G0746\",\"G0773\"],\"comment\":\"Each of the cities of proconsular Asia, at the autumnal equinox, assembled its most honorable and opulent citizens in order to select one to preside over the games to be exhibited that year, at his expense, in the honor of the gods and the Roman Emperor. Thereupon each city reported the name of the person selected to a general assembly held by some leading city, as Ephesus, Smyrna, Sardis. The general council selected ten out of the number of candidates, and sent them to the proconsul, and the proconsul chose one of these ten to preside over the rest.\"}', 'chief of Asia'),
(776, 776, 'ἀσιτία', '{\"def\":{\"short\":\"fasting (the state)\",\"long\":[\"abstinence from food (whether voluntary or enforced)\"]},\"deriv\":\"from G0777\",\"pronun\":{\"ipa\":\"ɑ.siˈti.ɑ\",\"ipa_mod\":\"ɑ.siˈti.ɑ\",\"sbl\":\"asitia\",\"dic\":\"ah-see-TEE-ah\",\"dic_mod\":\"ah-see-TEE-ah\"},\"see\":[\"G0777\"]}', 'abstinence'),
(777, 777, 'ἄσιτος', '{\"def\":{\"short\":\"without (taking) food\",\"long\":[\"fasting, without having eaten\"]},\"deriv\":\"from G0001 (as a negative particle) and G4621\",\"pronun\":{\"ipa\":\"ˈɑ.si.tos\",\"ipa_mod\":\"ˈɑ.si.tows\",\"sbl\":\"asitos\",\"dic\":\"AH-see-tose\",\"dic_mod\":\"AH-see-tose\"},\"see\":[\"G0001\",\"G4621\"]}', 'fasting'),
(778, 778, 'ἀσκέω', '{\"def\":{\"short\":\"to elaborate, i.e., (figuratively) train (by implication, strive)\",\"long\":[\"to form by art, to adorn\",\"to exercise (one\'s) self, take pains, labor, strive\"]},\"deriv\":\"probably from the same as G4632\",\"pronun\":{\"ipa\":\"ɑˈskɛ.o\",\"ipa_mod\":\"ɑˈske̞.ow\",\"sbl\":\"askeō\",\"dic\":\"ah-SKEH-oh\",\"dic_mod\":\"ah-SKAY-oh\"},\"see\":[\"G4632\"]}', 'exercise'),
(779, 779, 'ἀσκός', '{\"def\":{\"short\":\"a leathern (or skin) bag used as a bottle\",\"long\":[\"a leathern bag or bottle, in which water or wine was kept\"]},\"deriv\":\"from the same as G0778\",\"pronun\":{\"ipa\":\"ɑˈskos\",\"ipa_mod\":\"ɑˈskows\",\"sbl\":\"askos\",\"dic\":\"ah-SKOSE\",\"dic_mod\":\"ah-SKOSE\"},\"see\":[\"G0778\"]}', 'bottle'),
(780, 780, 'ἀσμένως', '{\"def\":{\"short\":\"with pleasure\",\"long\":[\"with joy, gladness\"]},\"deriv\":\"adverb from a derivative of the base of G2237\",\"pronun\":{\"ipa\":\"ɑˈsmɛ.nos\",\"ipa_mod\":\"ɑˈsme̞.nows\",\"sbl\":\"asmenōs\",\"dic\":\"ah-SMEH-nose\",\"dic_mod\":\"ah-SMAY-nose\"},\"see\":[\"G2237\"]}', 'gladly'),
(781, 781, 'ἄσοφος', '{\"def\":{\"short\":\"unwise\",\"long\":[\"unwise, foolish\"]},\"deriv\":\"from G0001 (as a negative particle) and G4680\",\"pronun\":{\"ipa\":\"ˈɑ.so.fos\",\"ipa_mod\":\"ˈɑ.sow.fows\",\"sbl\":\"asophos\",\"dic\":\"AH-soh-fose\",\"dic_mod\":\"AH-soh-fose\"},\"see\":[\"G0001\",\"G4680\"]}', 'fool'),
(782, 782, 'ἀσπάζομαι', '{\"def\":{\"short\":\"to enfold in the arms, i.e., (by implication) to salute, (figuratively) to welcome\",\"long\":[\"to draw to one\'s self\",[\"to salute one, greet, bid welcome, wish well to\",\"to receive joyfully, welcome\"]]},\"deriv\":\"from G0001 (as a particle of union) and a presumed form of G4685\",\"pronun\":{\"ipa\":\"ɑˈspɑ.zo.mɛ\",\"ipa_mod\":\"ɑˈspɑ.zow.me\",\"sbl\":\"aspazomai\",\"dic\":\"ah-SPA-zoh-meh\",\"dic_mod\":\"ah-SPA-zoh-may\"},\"see\":[\"G0001\",\"G4685\"],\"comment\":\"Used of those accosting anyone; of those who visit one to see him a little while, departing almost immediately afterwards; to pay respects to a distinguished person by visiting him; of those who greet one whom they meet in the way (even not in the East, Christians and Mohammedans do not greet one another); a salutation was made not merely by a slight gesture and a few words, but generally by embracing and kissing, a journey was retarded frequently by saluting.\"}', 'embrace, greet, salute, take leave'),
(783, 783, 'ἀσπασμός', '{\"def\":{\"short\":\"a greeting (in person or by letter)\",\"long\":[\"a salutation, either oral or written\"]},\"deriv\":\"from G0782\",\"pronun\":{\"ipa\":\"ɑ.spɑˈsmos\",\"ipa_mod\":\"ɑ.spɑˈsmows\",\"sbl\":\"aspasmos\",\"dic\":\"ah-spa-SMOSE\",\"dic_mod\":\"ah-spa-SMOSE\"},\"see\":[\"G0782\"]}', 'greeting, salutation'),
(784, 784, 'ἄσπιλος', '{\"def\":{\"short\":\"unblemished (physically or morally)\",\"long\":[\"spotless\",\"metaphorically\",[\"free from censure, irreproachable\",\"free from vice, unsullied\"]]},\"deriv\":\"from G0001 (as a negative particle) and G4695\",\"pronun\":{\"ipa\":\"ˈɑ.spi.los\",\"ipa_mod\":\"ˈɑ.spi.lows\",\"sbl\":\"aspilos\",\"dic\":\"AH-spee-lose\",\"dic_mod\":\"AH-spee-lose\"},\"see\":[\"G0001\",\"G4695\"]}', 'without spot, unspotted'),
(785, 785, 'ἀσπίς', '{\"def\":{\"short\":\"a buckler (or round shield); used of a serpent (as coiling itself), probably the \\\"asp\\\"\",\"long\":[\"an asp, a small and most venomous serpent, the bite of which is fatal unless the bitten part be immediately cut away\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ɑˈspis\",\"ipa_mod\":\"ɑˈspis\",\"sbl\":\"aspis\",\"dic\":\"ah-SPEES\",\"dic_mod\":\"ah-SPEES\"}}', 'asp'),
(786, 786, 'ἄσπονδος', '{\"def\":{\"short\":\"literally, without libation (which usually accompanied a treaty), i.e., (by implication) truceless\",\"long\":[\"without a treaty or covenant\",[\"of things not mutually agreed upon e.g., abstinences from hostilities\"],\"that cannot be persuaded to enter into a covenant, implacable\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G4689\",\"pronun\":{\"ipa\":\"ˈɑ.spon.ðos\",\"ipa_mod\":\"ˈɑ.spown̪.ðows\",\"sbl\":\"aspondos\",\"dic\":\"AH-spone-those\",\"dic_mod\":\"AH-spone-those\"},\"see\":[\"G0001\",\"G4689\"]}', 'implacable, truce-breaker');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(787, 787, 'ἀσσάριον', '{\"def\":{\"short\":\"an assarius or as, a Roman coin\",\"long\":[\"an assarium or assarius, the name of a coin equal to the tenth part of a drachma\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ɑsˈsɑ.ri.on\",\"ipa_mod\":\"ɑsˈsɑ.ri.own\",\"sbl\":\"assarion\",\"dic\":\"as-SA-ree-one\",\"dic_mod\":\"as-SA-ree-one\"}}', 'farthing'),
(788, 788, 'ἆσσον', '{\"def\":{\"short\":\"more nearly, i.e., very near\",\"long\":[\"nearer\"]},\"deriv\":\"neuter comparative of the base of G1451\",\"pronun\":{\"ipa\":\"ˈɑs.son\",\"ipa_mod\":\"ˈɑs.sown\",\"sbl\":\"asson\",\"dic\":\"AS-sone\",\"dic_mod\":\"AS-sone\"},\"see\":[\"G1451\"]}', 'close'),
(789, 789, 'Ἄσσος', '{\"def\":{\"lit\":\"approaching\",\"short\":\"Assus, a city of Asia MInor\",\"long\":[\"a seaport of the Roman province of Asia in the district anciently called Mysia, on the north shore of the Gulf of Adramyttium, and about 7 miles (11 km) from Lesbos\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ˈɑs.sos\",\"ipa_mod\":\"ˈɑs.sows\",\"sbl\":\"assos\",\"dic\":\"AS-sose\",\"dic_mod\":\"AS-sose\"}}', 'Assos'),
(790, 790, 'ἀστατέω', '{\"def\":{\"short\":\"to be non-stationary, i.e., (figuratively) homeless\",\"long\":[\"to wander about, to rove without a settled abode\"]},\"deriv\":\"from G0001 (as a negative particle) and a derivative of G2476\",\"pronun\":{\"ipa\":\"ɑ.stɑˈtɛ.o\",\"ipa_mod\":\"ɑ.stɑˈte̞.ow\",\"sbl\":\"astateō\",\"dic\":\"ah-sta-TEH-oh\",\"dic_mod\":\"ah-sta-TAY-oh\"},\"see\":[\"G0001\",\"G2476\"]}', 'have no certain dwelling-place'),
(791, 791, 'ἀστεῖος', '{\"def\":{\"short\":\"urbane, i.e., (by implication) handsome\",\"long\":[\"of the city\",\"of polished manners\",\"elegant (of body), comely, fair\"]},\"deriv\":\"from ἄστυ (a city)\",\"pronun\":{\"ipa\":\"ɑˈsti.os\",\"ipa_mod\":\"ɑˈsti.ows\",\"sbl\":\"asteios\",\"dic\":\"ah-STEE-ose\",\"dic_mod\":\"ah-STEE-ose\"}}', 'fair'),
(792, 792, 'ἀστήρ', '{\"def\":{\"short\":\"a star (as strown over the sky), literally or figuratively\",\"long\":[\"a star\"]},\"deriv\":\"probably from the base of G4766\",\"pronun\":{\"ipa\":\"ɑˈster\",\"ipa_mod\":\"ɑˈste̞r\",\"sbl\":\"astēr\",\"dic\":\"ah-STARE\",\"dic_mod\":\"ah-STARE\"},\"see\":[\"G4766\"]}', 'star'),
(793, 793, 'ἀστήρικτος', '{\"def\":{\"short\":\"unfixed, i.e., (figuratively) vacillating\",\"long\":[\"unstable, unsteadfast\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of G4741\",\"pronun\":{\"ipa\":\"ɑˈste.rik.tos\",\"ipa_mod\":\"ɑˈste̞.rik.tows\",\"sbl\":\"astēriktos\",\"dic\":\"ah-STAY-reek-tose\",\"dic_mod\":\"ah-STAY-reek-tose\"},\"see\":[\"G0001\",\"G4741\"]}', 'unstable'),
(794, 794, 'ἄστοργος', '{\"def\":{\"short\":\"hard-hearted towards kindred\",\"long\":[\"without natural affection, unsociable, inhuman, unloving\"]},\"deriv\":\"from G0001 (as a negative particle) and a presumed derivative of στέργω (to cherish affectionately)\",\"pronun\":{\"ipa\":\"ˈɑ.stor.ɣos\",\"ipa_mod\":\"ˈɑ.stowr.ɣows\",\"sbl\":\"astorgos\",\"dic\":\"AH-store-gose\",\"dic_mod\":\"AH-store-gose\"},\"see\":[\"G0001\"]}', 'without natural affection'),
