(839, 839, 'αὔριον', '{\"def\":{\"short\":\"properly, fresh, i.e., (adverb with ellipsis of G2250) to-morrow\",\"long\":[\"tomorrow\"]},\"deriv\":\"from a derivative of the same as G0109 (meaning a breeze, i.e. the morning air)\",\"pronun\":{\"ipa\":\"ˈɛβ.ri.on\",\"ipa_mod\":\"ˈa.ri.own\",\"sbl\":\"aurion\",\"dic\":\"EV-ree-one\",\"dic_mod\":\"A-ree-one\"},\"see\":[\"G0109\",\"G2250\"]}', '(to-)morrow, next day'),
(840, 840, 'αὐστηρός', '{\"def\":{\"short\":\"rough (properly as a gale), i.e., (figuratively) severe\",\"long\":[\"of mind and manners: harsh, rough, rigid\"]},\"deriv\":\"from a (presumed) derivative of the same as G0109 (meaning blown)\",\"pronun\":{\"ipa\":\"ɑβ.steˈros\",\"ipa_mod\":\"ɑf.ste̞ˈrows\",\"sbl\":\"austēros\",\"dic\":\"av-stay-ROSE\",\"dic_mod\":\"af-stay-ROSE\"},\"see\":[\"G0109\"]}', 'austere'),
(841, 841, 'αὐτάρκεια', '{\"def\":{\"short\":\"self-satisfaction, i.e., (abstractly) contentedness, or (concretely) a competence\",\"long\":[\"a perfect condition of life in which no aid or support is needed\",\"sufficiency of the necessities of life\",\"a mind contented with its lot, contentment\"]},\"deriv\":\"from G0842\",\"pronun\":{\"ipa\":\"ɑβˈtɑr.ki.ɑ\",\"ipa_mod\":\"ɑfˈtɑr.ci.ɑ\",\"sbl\":\"autarkeia\",\"dic\":\"av-TAHR-kee-ah\",\"dic_mod\":\"af-TAHR-kee-ah\"},\"see\":[\"G0842\"]}', 'contentment, sufficiency'),
