(857, 857, 'ἀφειδία', '{\"def\":{\"short\":\"unsparingness, i.e., austerity (asceticism)\",\"long\":[\"unsparingly severity\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and G5339\",\"pronun\":{\"ipa\":\"ɑ.fiˈði.ɑ\",\"ipa_mod\":\"ɑ.fiˈði.ɑ\",\"sbl\":\"apheidia\",\"dic\":\"ah-fee-THEE-ah\",\"dic_mod\":\"ah-fee-THEE-ah\"},\"see\":[\"G0001\",\"G5339\"]}', 'neglecting'),
(858, 858, 'ἀφελότης', '{\"def\":{\"short\":\"smoothness, i.e., (figuratively) simplicity\",\"long\":[\"simplicity, singleness\"]},\"deriv\":\"from a compound of G0001 (as a negative particle) and φέλλος (in the sense of a stone as stubbing the foot)\",\"pronun\":{\"ipa\":\"ɑ.fɛˈlo.tes\",\"ipa_mod\":\"ɑ.fe̞ˈlow.te̞s\",\"sbl\":\"aphelotēs\",\"dic\":\"ah-feh-LOH-tase\",\"dic_mod\":\"ah-fay-LOH-tase\"},\"see\":[\"G0001\"]}', 'singleness'),
