(863, 863, 'ἀφίημι', '{\"def\":{\"short\":\"to send forth, in various applications (as follow)\",\"long\":[\"to send away\",[\"to bid going away or depart\",[\"of a husband divorcing his wife\"],\"to send forth, yield up, to expire\",\"to let go, let alone, let be\",[\"to disregard\",\"to leave, not to discuss now, (a topic)\",[\"of teachers, writers and speakers\"],\"to omit, neglect\"],\"to let go, give up a debt, forgive, to remit\",\"to give up, keep no longer\"],\"to permit, allow, not to hinder, to give up a thing to a person\",\"to leave, go way from one\",[\"in order to go to another place\",\"to depart from any one\",\"to depart from one and leave him to himself so that all mutual claims are abandoned\",\"to desert wrongfully\",\"to go away leaving something behind\",\"to leave one by not taking him as a companion\",\"to leave on dying, leave behind one\",\"to leave so that what is left may remain, leave remaining\",\"abandon, leave destitute\"]]},\"deriv\":\"from G0575 and ἵημι (to send; an intensive form of εἶμι, to go)\",\"pronun\":{\"ipa\":\"ɑˈfi.e.mi\",\"ipa_mod\":\"ɑˈfi.e̞.mi\",\"sbl\":\"aphiēmi\",\"dic\":\"ah-FEE-ay-mee\",\"dic_mod\":\"ah-FEE-ay-mee\"},\"see\":[\"G0575\"]}', 'cry, forgive, forsake, lay aside, leave, let (alone, be, go, have), omit, put (send) away, remit, suffer, yield up'),
(864, 864, 'ἀφικνέομαι', '{\"def\":{\"short\":\"to go (i.e., spread) forth (by rumor)\",\"long\":[\"to come from a place\",\"to come to, arrive\"]},\"deriv\":\"from G0575 and the base of G2425\",\"pronun\":{\"ipa\":\"ɑ.fiˈknɛ.o.mɛ\",\"ipa_mod\":\"ɑ.fiˈkne̞.ow.me\",\"sbl\":\"aphikneomai\",\"dic\":\"ah-fee-KNEH-oh-meh\",\"dic_mod\":\"ah-fee-KNAY-oh-may\"},\"see\":[\"G0575\",\"G2425\"]}', 'come abroad'),
(865, 865, 'ἀφιλάγαθος', '{\"def\":{\"short\":\"hostile to virtue\",\"long\":[\"opposed to goodness and good men\"]},\"deriv\":\"from G0001 (as a negative particle) and G5358\",\"pronun\":{\"ipa\":\"ɑ.fiˈlɑ.ɣɑ.θos\",\"ipa_mod\":\"ɑ.fiˈlɑ.ɣɑ.θows\",\"sbl\":\"aphilagathos\",\"dic\":\"ah-fee-LA-ga-those\",\"dic_mod\":\"ah-fee-LA-ga-those\"},\"see\":[\"G0001\",\"G5358\"]}', 'despiser of those that are good'),
