(871, 871, 'ἀφομοιόω', '{\"def\":{\"short\":\"to assimilate closely\",\"long\":[\"to cause a model to pass off into an image or shape like it\",\"to express itself in it, to copy\",\"to produce a facsimile\",\"to be made like, render similar\"]},\"deriv\":\"from G0575 and G3666\",\"pronun\":{\"ipa\":\"ɑ.fo.myˈo.o\",\"ipa_mod\":\"ɑ.fow.myˈow.ow\",\"sbl\":\"aphomoioō\",\"dic\":\"ah-foh-moo-OH-oh\",\"dic_mod\":\"ah-foh-moo-OH-oh\"},\"see\":[\"G0575\",\"G3666\"]}', 'make like'),
