(1113, 1113, 'γογγυστής', '{\"def\":{\"short\":\"a grumbler\",\"long\":[\"a murmurer, one who discontentedly complains (against God)\"]},\"deriv\":\"from G1111\",\"pronun\":{\"ipa\":\"ɣoŋ.ɣyˈstes\",\"ipa_mod\":\"ɣowŋ.ʝjuˈste̞s\",\"sbl\":\"gongystēs\",\"dic\":\"gohng-goo-STASE\",\"dic_mod\":\"gohng-gyoo-STASE\"},\"see\":[\"G1111\"]}', 'murmurer'),
(1114, 1114, 'γόης', '{\"def\":{\"short\":\"properly, a wizard (as muttering spells), i.e., (by implication) an imposter\",\"long\":[\"a wailer, a howler\",\"a juggler, enchanter (because incantations used to be uttered in a kind of howl)\",\"a deceiver, imposter\"]},\"deriv\":\"from γοάω (to wail)\",\"pronun\":{\"ipa\":\"ˈɣo.es\",\"ipa_mod\":\"ˈɣow.e̞s\",\"sbl\":\"goēs\",\"dic\":\"GOH-ase\",\"dic_mod\":\"GOH-ase\"}}', 'seducer'),
(1115, 1115, 'Γολγοθᾶ', '{\"def\":{\"lit\":\"skull\",\"short\":\"the skull; Golgotha, a knoll near Jerusalem\",\"long\":[\"the name of a place outside Jerusalem where Jesus was crucified; so called, apparently because its form resembled a skull\"]},\"deriv\":\"of Chaldee origin (compare H1538)\",\"pronun\":{\"ipa\":\"ɣol.ɣoˈθɑ\",\"ipa_mod\":\"ɣowl.ɣowˈθɑ\",\"sbl\":\"golgotha\",\"dic\":\"gole-goh-THA\",\"dic_mod\":\"gole-goh-THA\"},\"see\":[\"H1538\"]}', 'Golgotha'),
(1116, 1116, 'Γόμοῤῥα', '{\"def\":{\"lit\":\"submersion\",\"short\":\"Gomorrha (i.e., Amorah), a place near the Dead Sea\",\"long\":[\"a city in eastern part of Judah that was destroyed when the Lord rained fire and brimstone on it; now covered by the Dead Sea\"]},\"deriv\":\"of Hebrew origin (H6017)\",\"pronun\":{\"ipa\":\"ˈɣo.mor.rɑ\",\"ipa_mod\":\"ˈɣow.mowr.rɑ\",\"sbl\":\"gomorrha\",\"dic\":\"GOH-more-ra\",\"dic_mod\":\"GOH-more-ra\"},\"see\":[\"H6017\"]}', 'Gomorrha'),
(1117, 1117, 'γόμος', '{\"def\":{\"short\":\"a load (as filling), i.e., (specially) a cargo, or (by extension) wares\",\"long\":[\"a lading or freight of a ship, cargo, merchandise conveyed in a ship\",\"any merchandise\"]},\"deriv\":\"from G1073\",\"pronun\":{\"ipa\":\"ˈɣo.mos\",\"ipa_mod\":\"ˈɣow.mows\",\"sbl\":\"gomos\",\"dic\":\"GOH-mose\",\"dic_mod\":\"GOH-mose\"},\"see\":[\"G1073\"]}', 'burden, merchandise'),
(1118, 1118, 'γονεύς', '{\"def\":{\"short\":\"a parent\",\"long\":[\"fathers, parent, the parents\"]},\"deriv\":\"from the base of G1096\",\"pronun\":{\"ipa\":\"ɣoˈnɛβs\",\"ipa_mod\":\"ɣowˈnefs\",\"sbl\":\"goneus\",\"dic\":\"goh-NEVS\",\"dic_mod\":\"goh-NAYFS\"},\"see\":[\"G1096\"]}', 'parent'),
(1119, 1119, 'γόνυ', '{\"def\":{\"short\":\"the \\\"knee\\\"\",\"long\":[\"the knee, to kneel down\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈɣo.ny\",\"ipa_mod\":\"ˈɣow.nju\",\"sbl\":\"gony\",\"dic\":\"GOH-noo\",\"dic_mod\":\"GOH-nyoo\"}}', 'knee(X -l)'),
(1120, 1120, 'γονυπετέω', '{\"def\":{\"short\":\"to fall on the knee\",\"long\":[\"to fall on the knees, the act of imploring aid, and of expressing reverence and honor\"]},\"deriv\":\"from a compound of G1119 and the alternate of G4098\",\"pronun\":{\"ipa\":\"ɣo.ny.pɛˈtɛ.o\",\"ipa_mod\":\"ɣow.nju.pe̞ˈte̞.ow\",\"sbl\":\"gonypeteō\",\"dic\":\"goh-noo-peh-TEH-oh\",\"dic_mod\":\"goh-nyoo-pay-TAY-oh\"},\"see\":[\"G1119\",\"G4098\"]}', 'bow the knee, kneel down');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1121, 1121, 'γράμμα', '{\"def\":{\"short\":\"a writing, i.e., a letter, note, epistle, book, etc.; plural learning\",\"long\":[\"a letter\",\"any writing, a document or record\",[\"a note of hand, bill, bond, account, written acknowledgement of a debt\",\"a letter, an epistle\",\"the sacred writings (of the Old Testament)\"],\"letters, i.e., learning\",[\"of sacred learning\"]]},\"deriv\":\"from G1125\",\"pronun\":{\"ipa\":\"ˈɣrɑm.mɑ\",\"ipa_mod\":\"ˈɣrɑm.mɑ\",\"sbl\":\"gramma\",\"dic\":\"GRAHM-ma\",\"dic_mod\":\"GRAHM-ma\"},\"see\":[\"G1125\"]}', 'bill, learning, letter, scripture, writing, written'),
(1122, 1122, 'γραμματεύς', '{\"def\":{\"short\":\"a professional writer\",\"long\":[\"a clerk, scribe, especially a public servant, secretary, recorder, whose office and influence differed in different states\",\"a man learned in the Mosaic law and in the sacred writings, an interpreter, teacher\",\"a religious teacher: so instructed that from his learning and ability to teach advantage may redound to the kingdom of heaven\"]},\"deriv\":\"from G1121. a writer, i.e. (professionally) scribe or secretary\",\"pronun\":{\"ipa\":\"ɣrɑm.mɑˈtɛβs\",\"ipa_mod\":\"ɣrɑm.mɑˈtefs\",\"sbl\":\"grammateus\",\"dic\":\"grahm-ma-TEVS\",\"dic_mod\":\"grahm-ma-TAYFS\"},\"see\":[\"G1121\"],\"comment\":\"Scribes examined the more difficult and subtle questions of the law; added to the Mosaic law decisions of various kinds thought to elucidate its meaning and scope, and did this to the detriment of religion. Since the advice of men skilled in the law was needed in the examination in the causes and the solution of the difficult questions, they were enrolled in the Sanhedrin, and are mentioned in connection with the priests and elders of the people.\"}', 'scribe, town-clerk'),
(1123, 1123, 'γραπτός', '{\"def\":{\"short\":\"inscribed (figuratively)\",\"long\":[\"written\"]},\"deriv\":\"from G1125\",\"pronun\":{\"ipa\":\"ɣrɑˈptos\",\"ipa_mod\":\"ɣrɑˈptows\",\"sbl\":\"graptos\",\"dic\":\"gra-PTOSE\",\"dic_mod\":\"gra-PTOSE\"},\"see\":[\"G1125\"]}', 'written'),
(1124, 1124, 'γραφή', '{\"def\":{\"short\":\"a document, i.e., holy Writ (or its contents or a statement in it)\",\"long\":[\"a writing, thing written\",\"the Scripture, used to denote either the book itself, or its contents\",\"a certain portion or section of the Holy Scripture\"]},\"deriv\":\"from G1125\",\"pronun\":{\"ipa\":\"ɣrɑˈfe\",\"ipa_mod\":\"ɣrɑˈfe̞\",\"sbl\":\"graphē\",\"dic\":\"gra-FAY\",\"dic_mod\":\"gra-FAY\"},\"see\":[\"G1125\"]}', 'scripture'),
(1125, 1125, 'γράφω', '{\"def\":{\"short\":\"to \\\"grave\\\", especially to write; figuratively, to describe\",\"long\":[\"to write, with reference to the form of the letters\",[\"to delineate (or form) letters on a tablet, parchment, paper, or other material\"],\"to write, with reference to the contents of the writing\",[\"to express in written characters\",\"to commit to writing (things not to be forgotten), write down, record\",\"used of those things which stand written in the sacred books (of the Old Testament)\",\"to write to one, i.e., by writing (in a written epistle) to give information, directions\"],\"to fill with writing\",\"to draw up in writing, compose\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈɣrɑ.fo\",\"ipa_mod\":\"ˈɣrɑ.fow\",\"sbl\":\"graphō\",\"dic\":\"GRA-foh\",\"dic_mod\":\"GRA-foh\"}}', 'describe, write(-ing, -ten)'),
(1126, 1126, 'γραώδης', '{\"def\":{\"short\":\"crone-like, i.e., silly\",\"long\":[\"old womanish, old wives, an old woman\"]},\"deriv\":\"from γραύς (an old woman) and G1491\",\"pronun\":{\"ipa\":\"ɣrɑˈo.ðes\",\"ipa_mod\":\"ɣrɑˈow.ðe̞s\",\"sbl\":\"graōdēs\",\"dic\":\"gra-OH-thase\",\"dic_mod\":\"gra-OH-thase\"},\"see\":[\"G1491\"]}', 'old wives\''),
(1127, 1127, 'γρηγορεύω', '{\"def\":{\"short\":\"to keep awake, i.e., watch (literally or figuratively)\",\"long\":[\"to watch\",\"metaphorically give strict attention to, be cautious, active\",[\"to take heed lest through remission and indolence some destructive calamity suddenly overtake one\"]]},\"deriv\":\"from G1453\",\"pronun\":{\"ipa\":\"ɣre.ɣoˈrɛβ.o\",\"ipa_mod\":\"ɣre̞.ɣowˈrev.ow\",\"sbl\":\"grēgoreuō\",\"dic\":\"gray-goh-REV-oh\",\"dic_mod\":\"gray-goh-RAVE-oh\"},\"see\":[\"G1453\"]}', 'be vigilant, wake, (be) watch(-ful)'),
(1128, 1128, 'γυμνάζω', '{\"def\":{\"short\":\"to practise naked (in the games), i.e., train (figuratively)\",\"long\":[\"to exercise naked (in a palaestra or school of athletics)\",\"to exercise vigorously, in any way, either the body or the mind\"]},\"deriv\":\"from G1131\",\"pronun\":{\"ipa\":\"ɣymˈnɑ.zo\",\"ipa_mod\":\"ʝjumˈnɑ.zow\",\"sbl\":\"gymnazō\",\"dic\":\"goom-NA-zoh\",\"dic_mod\":\"gyoom-NA-zoh\"},\"see\":[\"G1131\"]}', 'exercise'),
(1129, 1129, 'γυμνασία', '{\"def\":{\"short\":\"training, i.e., (figuratively) asceticism\",\"long\":[\"the exercise of the body in a palaestra or school of athletics\",\"any exercise whatever\",[\"the exercise of conscientiousness relative to the body such as is characteristic of ascetics and consists in abstinence from matrimony and certain kinds of food\"]]},\"deriv\":\"from G1128\",\"pronun\":{\"ipa\":\"ɣym.nɑˈsi.ɑ\",\"ipa_mod\":\"ʝjum.nɑˈsi.ɑ\",\"sbl\":\"gymnasia\",\"dic\":\"goom-na-SEE-ah\",\"dic_mod\":\"gyoom-na-SEE-ah\"},\"see\":[\"G1128\"]}', 'exercise'),
(1130, 1130, 'γυμνητεύω', '{\"def\":{\"short\":\"to strip, i.e., (reflexively) go poorly clad\",\"long\":[\"to be lightly or poorly clad\",\"to be a light armed soldier\"]},\"deriv\":\"from a derivative of G1131\",\"pronun\":{\"ipa\":\"ɣym.neˈtɛβ.o\",\"ipa_mod\":\"ʝjum.ne̞ˈtev.ow\",\"sbl\":\"gymnēteuō\",\"dic\":\"goom-nay-TEV-oh\",\"dic_mod\":\"gyoom-nay-TAVE-oh\"},\"see\":[\"G1131\"]}', 'be naked'),
(1131, 1131, 'γυμνός', '{\"def\":{\"short\":\"nude (absolute or relative, literal or figurative)\",\"long\":[\"properly\",[\"unclad, without clothing, the naked body\",\"ill clad\",\"clad in undergarments only (the outer garments or cloak being laid aside)\",\"of the soul, whose garment is the body, stripped of the body, without a body\"],\"metaphorically\",[\"naked, i.e., open, lay bare\",\"only, mere, bare, i.e., mere grain not the plant itself\"]]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ɣymˈnos\",\"ipa_mod\":\"ʝjumˈnows\",\"sbl\":\"gymnos\",\"dic\":\"goom-NOSE\",\"dic_mod\":\"gyoom-NOSE\"}}', 'naked'),
(1132, 1132, 'γυμνότης', '{\"def\":{\"short\":\"nudity (absolute or comparative)\",\"long\":[\"nakedness of the body\"]},\"deriv\":\"from G1131\",\"pronun\":{\"ipa\":\"ɣymˈno.tes\",\"ipa_mod\":\"ʝjumˈnow.te̞s\",\"sbl\":\"gymnotēs\",\"dic\":\"goom-NOH-tase\",\"dic_mod\":\"gyoom-NOH-tase\"},\"see\":[\"G1131\"]}', 'nakedness'),
(1133, 1133, 'γυναικάριον', '{\"def\":{\"short\":\"a little (i.e., foolish) woman\",\"long\":[\"a little woman: used contemptuously\"]},\"deriv\":\"a diminutive from G1135\",\"pronun\":{\"ipa\":\"ɣy.nɛˈkɑ.ri.on\",\"ipa_mod\":\"ʝju.neˈkɑ.ri.own\",\"sbl\":\"gynaikarion\",\"dic\":\"goo-neh-KA-ree-one\",\"dic_mod\":\"gyoo-nay-KA-ree-one\"},\"see\":[\"G1135\"]}', 'silly woman'),
(1134, 1134, 'γυναικεῖος', '{\"def\":{\"short\":\"feminine\",\"long\":[\"of or belonging to a woman, feminine, female\"]},\"deriv\":\"from G1135\",\"pronun\":{\"ipa\":\"ɣy.nɛˈki.os\",\"ipa_mod\":\"ʝju.neˈci.ows\",\"sbl\":\"gynaikeios\",\"dic\":\"goo-neh-KEE-ose\",\"dic_mod\":\"gyoo-nay-KEE-ose\"},\"see\":[\"G1135\"]}', 'wife'),
(1135, 1135, 'γυνή', '{\"def\":{\"short\":\"a woman; specially, a wife\",\"long\":[\"a woman of any age, whether a virgin, or married, or a widow\",\"a wife\",[\"of a betrothed woman\"]]},\"deriv\":\"probably from the base of G1096\",\"pronun\":{\"ipa\":\"ɣyˈne\",\"ipa_mod\":\"ʝjuˈne̞\",\"sbl\":\"gynē\",\"dic\":\"goo-NAY\",\"dic_mod\":\"gyoo-NAY\"},\"see\":[\"G1096\"]}', 'wife, woman'),
(1136, 1136, 'Γώγ', '{\"def\":{\"lit\":\"mountain\",\"short\":\"Gog, a symbolic name for some future Antichrist\",\"long\":[\"the king of the land of Magog who will come from the north and attack the land of Israel\"]},\"deriv\":\"of Hebrew origin (H1463)\",\"pronun\":{\"ipa\":\"ɣoɣ\",\"ipa_mod\":\"ɣowɣ\",\"sbl\":\"gōg\",\"dic\":\"goge\",\"dic_mod\":\"goge\"},\"see\":[\"H1463\"]}', 'Gog'),
(1137, 1137, 'γωνία', '{\"def\":{\"short\":\"an angle\",\"long\":[\"corner\",[\"an external angle, a corner\",\"internal corner, i.e., a secret place\"]]},\"deriv\":\"probably akin to G1119\",\"pronun\":{\"ipa\":\"ɣoˈni.ɑ\",\"ipa_mod\":\"ɣowˈni.ɑ\",\"sbl\":\"gōnia\",\"dic\":\"goh-NEE-ah\",\"dic_mod\":\"goh-NEE-ah\"},\"see\":[\"G1119\"]}', 'corner, quarter'),
(1138, 1138, 'Δαβίδ', '{\"def\":{\"lit\":\"beloved\",\"short\":\"David, the Israelite king\",\"long\":[\"second king of Israel, and ancestor of Jesus Christ\"]},\"deriv\":\"of Hebrew origin (H1732)\",\"pronun\":{\"ipa\":\"ðɑˈβið\",\"ipa_mod\":\"ðɑˈvið\",\"sbl\":\"dabid\",\"dic\":\"tha-VEETH\",\"dic_mod\":\"tha-VEETH\"},\"see\":[\"H1732\"],\"comment\":\"Sometimes transliterated \\\"Dabid.\\\"\"}', 'David'),
(1139, 1139, 'δαιμονίζομαι', '{\"def\":{\"short\":\"to be exercised by a daemon\",\"long\":[\"to be under the power of a demon\"]},\"deriv\":\"middle voice from G1142\",\"pronun\":{\"ipa\":\"ðɛ.moˈni.zo.mɛ\",\"ipa_mod\":\"ðe.mowˈni.zow.me\",\"sbl\":\"daimonizomai\",\"dic\":\"theh-moh-NEE-zoh-meh\",\"dic_mod\":\"thay-moh-NEE-zoh-may\"},\"see\":[\"G1142\"],\"comment\":\"These are persons who are often afflicted with especially severe diseases, either bodily or mentally (such as paralysis, blindness, deafness, loss of speech, epilepsy, melancholy, insanity) in whom demons had entered, and so held possession of them as not only to afflict them with ills, but also to dethrone the reason and take control of the physical body; accordingly the possessed were wont to express the mind and consciousness of the demons dwelling in them; and their cure requires the expulsion of the demon. This is different from a mere disease (regardless of the severity).\"}', 'have a (be vexed with, be possessed with) devil(-s)'),
(1140, 1140, 'δαιμόνιον', '{\"def\":{\"short\":\"a daemonic being; by extension a deity\",\"long\":[\"the divine power, deity, divinity\",\"a spirit, a being inferior to God, superior to men\",\"evil spirits or the messengers and ministers of the devil\"]},\"deriv\":\"neuter of a derivative of G1142\",\"pronun\":{\"ipa\":\"ðɛˈmo.ni.on\",\"ipa_mod\":\"ðeˈmow.ni.own\",\"sbl\":\"daimonion\",\"dic\":\"theh-MOH-nee-one\",\"dic_mod\":\"thay-MOH-nee-one\"},\"see\":[\"G1142\"]}', 'devil, god'),
(1141, 1141, 'δαιμονιώδης', '{\"def\":{\"short\":\"daemon-like\",\"long\":[\"resembling or proceeding from an evil spirit, demon-like\"]},\"deriv\":\"from G1140 and G1142\",\"pronun\":{\"ipa\":\"ðɛ.mo.niˈo.ðes\",\"ipa_mod\":\"ðe.mow.niˈow.ðe̞s\",\"sbl\":\"daimoniōdēs\",\"dic\":\"theh-moh-nee-OH-thase\",\"dic_mod\":\"thay-moh-nee-OH-thase\"},\"see\":[\"G1140\",\"G1142\"]}', 'devilish'),
(1142, 1142, 'δαίμων', '{\"def\":{\"short\":\"a daemon or supernatural spirit (of a bad nature)\",\"long\":[\"a god, a goddess\",[\"an inferior deity, whether good or bad\"],\"in the New Testament, an evil spirit\"]},\"deriv\":\"from δαίω (to distribute fortunes)\",\"pronun\":{\"ipa\":\"ˈðɛ.mon\",\"ipa_mod\":\"ˈðe.mown\",\"sbl\":\"daimōn\",\"dic\":\"THEH-mone\",\"dic_mod\":\"THAY-mone\"}}', 'devil'),
(1143, 1143, 'δάκνω', '{\"def\":{\"short\":\"to bite, i.e., (figuratively) thwart\",\"long\":[\"to bite with the teeth\",\"metaphorically, to wound the soul, cut lacerate, rend with reproaches\"]},\"deriv\":\"a prolonged form of a primary root\",\"pronun\":{\"ipa\":\"ˈðɑ.kno\",\"ipa_mod\":\"ˈðɑ.know\",\"sbl\":\"daknō\",\"dic\":\"THA-knoh\",\"dic_mod\":\"THA-knoh\"}}', 'bite'),
(1144, 1144, 'δάκρυ', '{\"def\":{\"short\":\"a tear\",\"long\":[\"a tear\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈðɑ.kry\",\"ipa_mod\":\"ˈðɑ.krju\",\"sbl\":\"dakry\",\"dic\":\"THA-kroo\",\"dic_mod\":\"THA-kryoo\"}}', 'tear'),
(1145, 1145, 'δακρύω', '{\"def\":{\"short\":\"to shed tears\",\"long\":[\"to weep, shed tears\"]},\"deriv\":\"from G1144\",\"pronun\":{\"ipa\":\"ðɑˈkry.o\",\"ipa_mod\":\"ðɑˈkrju.ow\",\"sbl\":\"dakryō\",\"dic\":\"tha-KROO-oh\",\"dic_mod\":\"tha-KRYOO-oh\"},\"see\":[\"G1144\",\"G2799\"],\"comment\":\"Compare G2799.\"}', 'weep'),
(1146, 1146, 'δακτύλιος', '{\"def\":{\"short\":\"a finger-ring\",\"long\":[\"a ring (on the finger)\"]},\"deriv\":\"from G1147\",\"pronun\":{\"ipa\":\"ðɑkˈty.li.os\",\"ipa_mod\":\"ðɑkˈtju.li.ows\",\"sbl\":\"daktylios\",\"dic\":\"thahk-TOO-lee-ose\",\"dic_mod\":\"thahk-TYOO-lee-ose\"},\"see\":[\"G1147\"]}', 'ring'),
(1147, 1147, 'δάκτυλος', '{\"def\":{\"short\":\"a finger\",\"long\":[\"a finger\"]},\"deriv\":\"probably from G1176\",\"pronun\":{\"ipa\":\"ˈðɑk.ty.los\",\"ipa_mod\":\"ˈðɑk.tju.lows\",\"sbl\":\"daktylos\",\"dic\":\"THAHK-too-lose\",\"dic_mod\":\"THAHK-tyoo-lose\"},\"see\":[\"G1176\"]}', 'finger'),
(1148, 1148, 'Δαλμανουθά', '{\"def\":{\"lit\":\"slow firebrand\",\"short\":\"Dalmanutha, a place in Palestine\",\"long\":[\"is a town on the west side of the Sea of Galilee, near Magdala\"]},\"deriv\":\"probably of Chaldee origin\",\"pronun\":{\"ipa\":\"ðɑl.mɑ.nuˈθɑ\",\"ipa_mod\":\"ðɑl.mɑ.nuˈθɑ\",\"sbl\":\"dalmanoutha\",\"dic\":\"thahl-ma-noo-THA\",\"dic_mod\":\"thahl-ma-noo-THA\"}}', 'Dalmanutha'),
(1149, 1149, 'Δαλματία', '{\"def\":{\"lit\":\"a priestly robe\",\"short\":\"Dalmatia, a region of Europe\",\"long\":[\"a part of Illyricum on the Adriatic Sea; on the east adjoining Pannonia and upper Moesia, on the north separated from Liburia by the river Titus, and extending southwards as far as the river Drinus and the city Lissus\"]},\"deriv\":\"probably of foreign derivation\",\"pronun\":{\"ipa\":\"ðɑl.mɑˈti.ɑ\",\"ipa_mod\":\"ðɑl.mɑˈti.ɑ\",\"sbl\":\"dalmatia\",\"dic\":\"thahl-ma-TEE-ah\",\"dic_mod\":\"thahl-ma-TEE-ah\"}}', 'Dalmatia'),
(1150, 1150, 'δαμάζω', '{\"def\":{\"short\":\"to tame\",\"long\":[\"to tame\",\"curb, restrain\"]},\"deriv\":\"a variation of an obsolete primary of the same meaning\",\"pronun\":{\"ipa\":\"ðɑˈmɑ.zo\",\"ipa_mod\":\"ðɑˈmɑ.zow\",\"sbl\":\"damazō\",\"dic\":\"tha-MA-zoh\",\"dic_mod\":\"tha-MA-zoh\"}}', 'tame'),
(1151, 1151, 'δάμαλις', '{\"def\":{\"short\":\"a heifer (as tame)\",\"long\":[\"a young cow, heifer\",[\"of the red heifer with whose ashes, by the Mosaic law, those were to be sprinkled who had become defiled\"]]},\"deriv\":\"probably from the base of G1150\",\"pronun\":{\"ipa\":\"ˈðɑ.mɑ.lis\",\"ipa_mod\":\"ˈðɑ.mɑ.lis\",\"sbl\":\"damalis\",\"dic\":\"THA-ma-lees\",\"dic_mod\":\"THA-ma-lees\"},\"see\":[\"G1150\"]}', 'heifer'),
(1152, 1152, 'Δάμαρις', '{\"def\":{\"lit\":\"a heifer\",\"short\":\"perhaps gentle; Damaris, an Athenian woman\",\"long\":[\"an Athenian woman converted to Christianity by Paul\'s preaching\"]},\"deriv\":\"probably from the base of G1150\",\"pronun\":{\"ipa\":\"ˈðɑ.mɑ.ris\",\"ipa_mod\":\"ˈðɑ.mɑ.ris\",\"sbl\":\"damaris\",\"dic\":\"THA-ma-rees\",\"dic_mod\":\"THA-ma-rees\"},\"see\":[\"G1150\"]}', 'Damaris'),
(1153, 1153, 'Δαμασκηνός', '{\"def\":{\"short\":\"a Damascene or inhabitant of Damascus\",\"long\":[\"of Damascus\"]},\"deriv\":\"from G1154\",\"pronun\":{\"ipa\":\"ðɑ.mɑ.skeˈnos\",\"ipa_mod\":\"ðɑ.mɑ.ske̞ˈnows\",\"sbl\":\"damaskēnos\",\"dic\":\"tha-ma-skay-NOSE\",\"dic_mod\":\"tha-ma-skay-NOSE\"},\"see\":[\"G1154\"]}', 'Damascene'),
(1154, 1154, 'Δαμασκός', '{\"def\":{\"lit\":\"silent is the sackcloth weaver\",\"short\":\"Damascus, a city of Syria\",\"long\":[\"one of the most ancient and most important cities of Syria lying in almost lovely and fertile plain at the eastern base of the Antilibanus\"]},\"deriv\":\"of Hebrew origin (H1834)\",\"pronun\":{\"ipa\":\"ðɑ.mɑˈskos\",\"ipa_mod\":\"ðɑ.mɑˈskows\",\"sbl\":\"damaskos\",\"dic\":\"tha-ma-SKOSE\",\"dic_mod\":\"tha-ma-SKOSE\"},\"see\":[\"H1834\"]}', 'Damascus'),
(1155, 1155, 'δανείζω', '{\"def\":{\"short\":\"to loan on interest; reflexively, to borrow\",\"long\":[\"to lend money\",\"to have money lent to one\'s self\",\"to take a loan, borrow\"]},\"deriv\":\"from G1156\",\"pronun\":{\"ipa\":\"ðɑˈni.zo\",\"ipa_mod\":\"ðɑˈni.zow\",\"sbl\":\"daneizō\",\"dic\":\"tha-NEE-zoh\",\"dic_mod\":\"tha-NEE-zoh\"},\"see\":[\"G1156\"]}', 'borrow, lend'),
