(1198, 1198, 'δέσμιος', '{\"def\":{\"short\":\"a captive (as bound)\",\"long\":[\"bound, in bonds, a captive, a prisoner\"]},\"deriv\":\"from G1199\",\"pronun\":{\"ipa\":\"ˈðɛ.smi.os\",\"ipa_mod\":\"ˈðe̞.smi.ows\",\"sbl\":\"desmios\",\"dic\":\"THEH-smee-ose\",\"dic_mod\":\"THAY-smee-ose\"},\"see\":[\"G1199\"]}', 'in bonds, prisoner'),
(1199, 1199, 'δεσμόν', '{\"def\":{\"short\":\"a band, i.e., ligament (of the body) or shackle (of a prisoner); figuratively, an impediment or disability\",\"long\":[\"a band or bond\"]},\"deriv\":\"neuter and masculine respectively from G1210\",\"pronun\":{\"ipa\":\"ðɛˈsmon\",\"ipa_mod\":\"ðe̞ˈsmown\",\"sbl\":\"desmon\",\"dic\":\"theh-SMONE\",\"dic_mod\":\"thay-SMONE\"},\"see\":[\"G1210\"]}', 'band, bond, chain, string'),
(1200, 1200, 'δεσμοφύλαξ', '{\"def\":{\"short\":\"a jailer (as guarding the prisoners)\",\"long\":[\"a keeper of a prison, a jailor\"]},\"deriv\":\"from G1199 and G5441\",\"pronun\":{\"ipa\":\"ðɛ.smoˈfy.lɑk͡s\",\"ipa_mod\":\"ðe̞.smowˈfju.lɑk͡s\",\"sbl\":\"desmophylax\",\"dic\":\"theh-smoh-FOO-lahks\",\"dic_mod\":\"thay-smoh-FYOO-lahks\"},\"see\":[\"G1199\",\"G5441\"]}', 'jailor, keeper of the prison'),
(1201, 1201, 'δεσμωτήριον', '{\"def\":{\"short\":\"a place of bondage, i.e., a dungeon\",\"long\":[\"a prison, a jail\"]},\"deriv\":\"from a derivative of G1199 (equivalent to G1196)\",\"pronun\":{\"ipa\":\"ðɛ.smoˈte.ri.on\",\"ipa_mod\":\"ðe̞.smowˈte̞.ri.own\",\"sbl\":\"desmōtērion\",\"dic\":\"theh-smoh-TAY-ree-one\",\"dic_mod\":\"thay-smoh-TAY-ree-one\"},\"see\":[\"G1196\",\"G1199\"]}', 'prison'),
(1202, 1202, 'δεσμώτης', '{\"def\":{\"short\":\"(passively) a captive\",\"long\":[\"one bound, a prisoner\"]},\"deriv\":\"from the same as G1201\",\"pronun\":{\"ipa\":\"ðɛˈsmo.tes\",\"ipa_mod\":\"ðe̞ˈsmow.te̞s\",\"sbl\":\"desmōtēs\",\"dic\":\"theh-SMOH-tase\",\"dic_mod\":\"thay-SMOH-tase\"},\"see\":[\"G1201\"]}', 'prisoner'),
(1203, 1203, 'δεσπότης', '{\"def\":{\"short\":\"an absolute ruler (\\\"despot\\\")\",\"long\":[\"a master, Lord\"]},\"deriv\":\"perhaps from G1210 and πόσις (a husband)\",\"pronun\":{\"ipa\":\"ðɛˈspo.tes\",\"ipa_mod\":\"ðe̞ˈspow.te̞s\",\"sbl\":\"despotēs\",\"dic\":\"theh-SPOH-tase\",\"dic_mod\":\"thay-SPOH-tase\"},\"see\":[\"G1210\"]}', 'Lord, master'),
(1204, 1204, 'δεῦρο', '{\"def\":{\"short\":\"here; used also imperative hither!; and of time, hitherto\",\"long\":[\"of place,\",[\"hither to this place\",\"in urging and calling, here! come!\"],\"of time, hither to, now\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈðɛβ.ro\",\"ipa_mod\":\"ˈðev.row\",\"sbl\":\"deuro\",\"dic\":\"THEV-roh\",\"dic_mod\":\"THAVE-roh\"}}', 'come (hither), hither(-to)'),
(1205, 1205, 'δεῦτε', '{\"def\":{\"short\":\"come hither!\",\"long\":[\"come hither, come here, come\",\"interjection, come!, come now!\"]},\"deriv\":\"from G1204 and an imperative form of εἶμι (to go)\",\"pronun\":{\"ipa\":\"ˈðɛβ.tɛ\",\"ipa_mod\":\"ˈðef.te̞\",\"sbl\":\"deute\",\"dic\":\"THEV-teh\",\"dic_mod\":\"THAYF-tay\"},\"see\":[\"G1204\"]}', 'come, X follow'),
(1206, 1206, 'δευτεραῖος', '{\"def\":{\"short\":\"secondary, i.e., (specially) on the second day\",\"long\":[\"of or belonging to the second\",\"of one who comes or does a thing, on the second day\"]},\"deriv\":\"from G1208\",\"pronun\":{\"ipa\":\"ðɛβ.tɛˈrɛ.os\",\"ipa_mod\":\"ðef.te̞ˈre.ows\",\"sbl\":\"deuteraios\",\"dic\":\"thev-teh-REH-ose\",\"dic_mod\":\"thayf-tay-RAY-ose\"},\"see\":[\"G1208\"]}', 'next day'),
(1207, 1207, 'δευτερόπρωτος', '{\"def\":{\"short\":\"second-first, i.e., (specially) a designation of the Sabbath immediately after the Paschal week (being the second after Passover day, and the first of the seven Sabbaths intervening before Pentecost)\",\"long\":[\"second-first\",\"the second of the first Sabbaths after the feast of the Passover\"]},\"deriv\":\"from G1208 and G4413\",\"pronun\":{\"ipa\":\"ðɛβ.tɛˈro.pro.tos\",\"ipa_mod\":\"ðef.te̞ˈrow.prow.tows\",\"sbl\":\"deuteroprōtos\",\"dic\":\"thev-teh-ROH-proh-tose\",\"dic_mod\":\"thayf-tay-ROH-proh-tose\"},\"see\":[\"G1208\",\"G4413\"]}', 'second...after the first'),
(1208, 1208, 'δεύτερος', '{\"def\":{\"short\":\"(ordinal) second (in time, place, or rank; also adverb)\",\"long\":[\"the second, the other of two\"]},\"deriv\":\"as the comparative of G1417\",\"pronun\":{\"ipa\":\"ˈðɛβ.tɛ.ros\",\"ipa_mod\":\"ˈðef.te̞.rows\",\"sbl\":\"deuteros\",\"dic\":\"THEV-teh-rose\",\"dic_mod\":\"THAYF-tay-rose\"},\"see\":[\"G1417\"]}', 'afterward, again, second(-arily) (time)'),
(1209, 1209, 'δέχομαι', '{\"def\":{\"short\":\"to receive (in various applications, literally or figuratively)\",\"long\":[\"to take with the hand\",[\"to take hold of, take up\"],\"to take up, receive\",[\"used of a place receiving one\",\"to receive or grant access to, a visitor, not to refuse intercourse or friendship\",[\"to receive hospitality\",\"to receive into one\'s family to bring up or educate\"],\"of the thing offered in speaking, teaching, instructing\",[\"to receive favorably, give ear to, embrace, make one\'s own, approve, not to reject\"],\"to receive, i.e., to take upon one\'s self, sustain, bear, endure\"],\"to receive, get\",[\"to learn\"]]},\"deriv\":\"middle voice of a primary verb\",\"pronun\":{\"ipa\":\"ˈðɛ.xo.mɛ\",\"ipa_mod\":\"ˈðe̞.xow.me\",\"sbl\":\"dechomai\",\"dic\":\"THEH-hoh-meh\",\"dic_mod\":\"THAY-hoh-may\"},\"see\":[\"G2983\"],\"comment\":\"Compare G2983.\"}', 'accept, receive, take'),
(1210, 1210, 'δέω', '{\"def\":{\"short\":\"to bind (in various applications, literally or figuratively)\",\"long\":[\"to bind tie, fasten\",[\"to bind, fasten with chains, to throw into chains\",\"metaphorically\",[\"Satan is said to bind a woman bent together by means of a demon, as his messenger, taking possession of the woman and preventing her from standing upright\",\"to bind, put under obligation, of the law, duty etc.\",[\"to be bound to one, a wife, a husband\"],\"to forbid, prohibit, declare to be illicit\"]]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈðɛ.o\",\"ipa_mod\":\"ˈðe̞.ow\",\"sbl\":\"deō\",\"dic\":\"THEH-oh\",\"dic_mod\":\"THAY-oh\"},\"see\":[\"G1163\",\"G1189\"]}', 'bind, be in bonds, knit, tie, wind'),
(1211, 1211, 'δή', '{\"def\":{\"short\":\"a particle of emphasis or explicitness; now, then, etc\",\"long\":[\"now, then, verily, in truth, really, surely, certainly\",\"forthwith, at once\"]},\"deriv\":\"probably akin to G1161\",\"pronun\":{\"ipa\":\"ðe\",\"ipa_mod\":\"ðe̞\",\"sbl\":\"dē\",\"dic\":\"thay\",\"dic_mod\":\"thay\"},\"see\":[\"G1161\"]}', 'also, and, doubtless, now, therefore'),
(1212, 1212, 'δῆλος', '{\"def\":{\"short\":\"clear\",\"long\":[\"clear, evident, manifest\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈðe.los\",\"ipa_mod\":\"ˈðe̞.lows\",\"sbl\":\"dēlos\",\"dic\":\"THAY-lose\",\"dic_mod\":\"THAY-lose\"}}', '+ bewray, certain, evident, manifest'),
(1213, 1213, 'δηλόω', '{\"def\":{\"short\":\"to make plain (by words)\",\"long\":[\"to make manifest\",\"to make known by relating, to declare\",\"to give one to understand, to indicate, signify\"]},\"deriv\":\"from G1212\",\"pronun\":{\"ipa\":\"ðeˈlo.o\",\"ipa_mod\":\"ðe̞ˈlow.ow\",\"sbl\":\"dēloō\",\"dic\":\"thay-LOH-oh\",\"dic_mod\":\"thay-LOH-oh\"},\"see\":[\"G1212\"]}', 'declare, shew, signify'),
(1214, 1214, 'Δημᾶς', '{\"def\":{\"lit\":\"governor of the people\",\"short\":\"Demas, a Christian\",\"long\":[\"a companion of Paul, who deserted the apostle when he was a prisoner at Rome and returned to Thessalonica\"]},\"deriv\":\"probably for G1216\",\"pronun\":{\"ipa\":\"ðeˈmɑs\",\"ipa_mod\":\"ðe̞ˈmɑs\",\"sbl\":\"dēmas\",\"dic\":\"thay-MAHS\",\"dic_mod\":\"thay-MAHS\"},\"see\":[\"G1216\"]}', 'Demas'),
(1215, 1215, 'δημηγορέω', '{\"def\":{\"short\":\"to be a people-gatherer, i.e., to address a public assembly\",\"long\":[\"to address a public assembly, make a speech to the people\"]},\"deriv\":\"from a compound of G1218 and G0058\",\"pronun\":{\"ipa\":\"ðe.me.ɣoˈrɛ.o\",\"ipa_mod\":\"ðe̞.me̞.ɣowˈre̞.ow\",\"sbl\":\"dēmēgoreō\",\"dic\":\"thay-may-goh-REH-oh\",\"dic_mod\":\"thay-may-goh-RAY-oh\"},\"see\":[\"G0058\",\"G1218\"]}', 'make an oration'),
(1216, 1216, 'Δημήτριος', '{\"def\":{\"lit\":\"belonging to Ceres\",\"short\":\"Demetrius, the name of an Ephesian and of a Christian\",\"long\":[\"a heathen silversmith at Ephesus\",\"a certain Christian, 3Jo 12\"]},\"deriv\":\"from Δημήτηρ (Ceres)\",\"pronun\":{\"ipa\":\"ðeˈme.tri.os\",\"ipa_mod\":\"ðe̞ˈme̞.tri.ows\",\"sbl\":\"dēmētrios\",\"dic\":\"thay-MAY-tree-ose\",\"dic_mod\":\"thay-MAY-tree-ose\"}}', 'Demetrius'),
(1217, 1217, 'δημιουργός', '{\"def\":{\"short\":\"a worker for the people, i.e., mechanic (spoken of the Creator)\",\"long\":[\"a workman for the public\",\"the author of any work, an artisan, framer, builder\"]},\"deriv\":\"from G1218 and G2041\",\"pronun\":{\"ipa\":\"ðe.mi.urˈɣos\",\"ipa_mod\":\"ðe̞.mi.urˈɣows\",\"sbl\":\"dēmiourgos\",\"dic\":\"thay-mee-oor-GOSE\",\"dic_mod\":\"thay-mee-oor-GOSE\"},\"see\":[\"G1218\",\"G2041\"]}', 'maker'),
(1218, 1218, 'δῆμος', '{\"def\":{\"short\":\"the public (as bound together socially)\",\"long\":[\"the people, the mass of people assembled in a public place\"]},\"deriv\":\"from G1210\",\"pronun\":{\"ipa\":\"ˈðe.mos\",\"ipa_mod\":\"ˈðe̞.mows\",\"sbl\":\"dēmos\",\"dic\":\"THAY-mose\",\"dic_mod\":\"THAY-mose\"},\"see\":[\"G1210\"]}', 'people'),
(1219, 1219, 'δημόσιος', '{\"def\":{\"short\":\"public; (feminine singular dative case as adverb) in public\",\"long\":[\"belonging to the people or state, public\",\"publicly, in public places, in full view of all\"]},\"deriv\":\"from G1218\",\"pronun\":{\"ipa\":\"ðeˈmo.si.os\",\"ipa_mod\":\"ðe̞ˈmow.si.ows\",\"sbl\":\"dēmosios\",\"dic\":\"thay-MOH-see-ose\",\"dic_mod\":\"thay-MOH-see-ose\"},\"see\":[\"G1218\"]}', 'common, openly, publickly'),
(1220, 1220, 'δηνάριον', '{\"def\":{\"lit\":\"containing ten\",\"short\":\"a denarius (or ten asses)\",\"long\":[\"A Roman silver coin in New Testament time\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ðeˈnɑ.ri.on\",\"ipa_mod\":\"ðe̞ˈnɑ.ri.own\",\"sbl\":\"dēnarion\",\"dic\":\"thay-NA-ree-one\",\"dic_mod\":\"thay-NA-ree-one\"},\"comment\":\"It took its name from it being equal to ten \\\"asses,\\\" a number after 217 BC increased to sixteen (about 3.898 grams or .1375 oz.). It was the principal silver coin of the Roman empire. From the parable of the laborers in the vineyard, it would seem that a denarius was then the ordinary pay for a day\'s wages (see Matthew 20:2–13).\"}', 'pence, penny(-worth)'),
(1221, 1221, 'δήποτε', '{\"def\":{\"short\":\"a particle of generalization; indeed, at any time\",\"long\":[\"now at length\",\"at any time\",\"at last\",\"just exactly\"]},\"deriv\":\"from G1211 and G4218\",\"pronun\":{\"ipa\":\"ˈðe.po.tɛ\",\"ipa_mod\":\"ˈðe̞.pow.te̞\",\"sbl\":\"dēpote\",\"dic\":\"THAY-poh-teh\",\"dic_mod\":\"THAY-poh-tay\"},\"see\":[\"G1211\",\"G4218\"]}', '(what-)soever'),
(1222, 1222, 'δήπου', '{\"def\":{\"short\":\"a particle of asseveration; indeed doubtless\",\"long\":[\"it is used when something is affirmed in a slightly ironical manner, as if with an pretence of uncertainty\",[\"perhaps\",\"doubtless\",\"verily\",\"truly\"]]},\"deriv\":\"from G1211 and G4225\",\"pronun\":{\"ipa\":\"ˈðe.pu\",\"ipa_mod\":\"ˈðe̞.pu\",\"sbl\":\"dēpou\",\"dic\":\"THAY-poo\",\"dic_mod\":\"THAY-poo\"},\"see\":[\"G1211\",\"G4225\"]}', 'verily'),
(1223, 1223, 'διά', '{\"def\":{\"short\":\"through (in very wide applications, local, causal, or occasional)\",\"long\":[\"through\",[\"of place\",[\"with\",\"in\"],\"of time\",[\"throughout\",\"during\"],\"of means\",[\"by\",\"by the means of\"]],\"through\",[\"the ground or reason by which something is or is not done\",[\"by reason of\",\"on account of\",\"because of for this reason\",\"therefore\",\"on this account\"]]]},\"deriv\":\"a primary preposition denoting the channel of an act\",\"pronun\":{\"ipa\":\"ðiˈɑ\",\"ipa_mod\":\"ðiˈɑ\",\"sbl\":\"dia\",\"dic\":\"thee-AH\",\"dic_mod\":\"thee-AH\"}}', 'after, always, among, at, to avoid, because of (that), briefly, by, for (cause)...fore, from, in, by occasion of, of, by reason of, for sake, that, thereby, therefore, X though, through(-out), to, wherefore, with(-in)'),
(1224, 1224, 'διαβαίνω', '{\"def\":{\"short\":\"to cross\",\"long\":[\"to pass through, cross over\"]},\"deriv\":\"from G1223 and the base of G0939\",\"pronun\":{\"ipa\":\"ði.ɑˈβɛ.no\",\"ipa_mod\":\"ði.ɑˈve.now\",\"sbl\":\"diabainō\",\"dic\":\"thee-ah-VEH-noh\",\"dic_mod\":\"thee-ah-VAY-noh\"},\"see\":[\"G0939\",\"G1223\"]}', 'come over, pass (through)'),
(1225, 1225, 'διαβάλλω', '{\"def\":{\"short\":\"(figuratively) to traduce\",\"long\":[\"to throw over or across, to send over\",\"to traduce, calumniate, slander, accuse, defame\"]},\"deriv\":\"from G1223 and G0906\",\"pronun\":{\"ipa\":\"ði.ɑˈβɑl.lo\",\"ipa_mod\":\"ði.ɑˈvɑl.low\",\"sbl\":\"diaballō\",\"dic\":\"thee-ah-VAHL-loh\",\"dic_mod\":\"thee-ah-VAHL-loh\"},\"see\":[\"G0906\",\"G1223\"]}', 'accuse'),
(1226, 1226, 'διαβεβαιόομαι', '{\"def\":{\"short\":\"to confirm thoroughly (by words), i.e., asseverate\",\"long\":[\"to affirm strongly, assert confidently\"]},\"deriv\":\"middle voice of a compound of G1223 and G0950\",\"pronun\":{\"ipa\":\"ði.ɑ.βɛ.βɛˈo.o.mɛ\",\"ipa_mod\":\"ði.ɑ.ve̞.veˈow.ow.me\",\"sbl\":\"diabebaioomai\",\"dic\":\"thee-ah-veh-veh-OH-oh-meh\",\"dic_mod\":\"thee-ah-vay-vay-OH-oh-may\"},\"see\":[\"G0950\",\"G1223\"]}', 'affirm constantly'),
(1227, 1227, 'διαβλέπω', '{\"def\":{\"short\":\"to look through, i.e., recover full vision\",\"long\":[\"to look through, penetrate by vision\",[\"to look fixedly, stare straight before one\",\"to see clearly\"]]},\"deriv\":\"from G1223 and G0991\",\"pronun\":{\"ipa\":\"ði.ɑˈβlɛ.po\",\"ipa_mod\":\"ði.ɑˈvle̞.pow\",\"sbl\":\"diablepō\",\"dic\":\"thee-ah-VLEH-poh\",\"dic_mod\":\"thee-ah-VLAY-poh\"},\"see\":[\"G0991\",\"G1223\"]}', 'see clearly'),
(1228, 1228, 'διάβολος', '{\"def\":{\"short\":\"a traducer; specially, Satan (compare H7854)\",\"long\":[\"prone to slander, slanderous, accusing falsely\",[\"a calumniator, false accuser, slanderer,\"],\"metaphorically applied to a man who, by opposing the cause of God, may be said to act the part of the devil or to side with him Satan the prince of the demons, the author of evil, persecuting good men, estranging mankind from God and enticing them to sin, afflicting them with diseases by means of demons who take possession of their bodies at his bidding\"]},\"deriv\":\"from G1225\",\"pronun\":{\"ipa\":\"ðiˈɑ.βo.los\",\"ipa_mod\":\"ðiˈɑ.vow.lows\",\"sbl\":\"diabolos\",\"dic\":\"thee-AH-voh-lose\",\"dic_mod\":\"thee-AH-voh-lose\"},\"see\":[\"H7854\",\"G1225\"]}', 'false accuser, devil, slanderer'),
(1229, 1229, 'διαγγέλλω', '{\"def\":{\"short\":\"to herald thoroughly\",\"long\":[\"to carry a message through, announce everywhere, through places, through assemblies of men etc.\",\"to publish abroad, declare\"]},\"deriv\":\"from G1223 and the base of G0032\",\"pronun\":{\"ipa\":\"ði.ɑŋˈɣɛl.lo\",\"ipa_mod\":\"ði.ɑŋˈɣe̞l.low\",\"sbl\":\"diangellō\",\"dic\":\"thee-ang-GEL-loh\",\"dic_mod\":\"thee-ang-GALE-loh\"},\"see\":[\"G0032\",\"G1223\"]}', 'declare, preach, signify'),
(1230, 1230, 'διαγίνομαι', '{\"def\":{\"short\":\"to elapse meanwhile\",\"long\":[\"to be through, continue\",\"to be between, intervene, used of time, to have intervened, elapsed, passed meanwhile\"]},\"deriv\":\"from G1223 and G1096\",\"pronun\":{\"ipa\":\"ði.ɑˈɣi.no.mɛ\",\"ipa_mod\":\"ði.ɑˈɣi.now.me\",\"sbl\":\"diaginomai\",\"dic\":\"thee-ah-GEE-noh-meh\",\"dic_mod\":\"thee-ah-GEE-noh-may\"},\"see\":[\"G1096\",\"G1223\"]}', 'X after, be past, be spent'),
(1231, 1231, 'διαγινώσκω', '{\"def\":{\"short\":\"to know thoroughly, i.e., ascertain exactly\",\"long\":[\"to distinguish, i.e., to know accurately, ascertain exactly\",\"in a legal sense, to examine, determine, decide\"]},\"deriv\":\"from G1223 and G1097\",\"pronun\":{\"ipa\":\"ði.ɑ.ɣiˈno.sko\",\"ipa_mod\":\"ði.ɑ.ʝiˈnow.skow\",\"sbl\":\"diaginōskō\",\"dic\":\"thee-ah-gee-NOH-skoh\",\"dic_mod\":\"thee-ah-gee-NOH-skoh\"},\"see\":[\"G1097\",\"G1223\"]}', '(would) enquire, know the uttermost'),
(1232, 1232, 'διαγνωρίζω', '{\"def\":{\"short\":\"to tell abroad\",\"long\":[\"to publish abroad, make known thoroughly\",\"to discriminate\"]},\"deriv\":\"from G1123 and G1107\",\"pronun\":{\"ipa\":\"ði.ɑ.ɣnoˈri.zo\",\"ipa_mod\":\"ði.ɑ.ɣnowˈri.zow\",\"sbl\":\"diagnōrizō\",\"dic\":\"thee-ah-gnoh-REE-zoh\",\"dic_mod\":\"thee-ah-gnoh-REE-zoh\"},\"see\":[\"G1107\",\"G1123\"]}', 'make known'),
(1233, 1233, 'διάγνωσις', '{\"def\":{\"short\":\"(magisterial) examination (\\\"diagnosis\\\")\",\"long\":[\"to distinguish\",\"in a legal sense, examination, opinion, decision\"]},\"deriv\":\"from G1231\",\"pronun\":{\"ipa\":\"ðiˈɑ.ɣno.sis\",\"ipa_mod\":\"ðiˈɑ.ɣnow.sis\",\"sbl\":\"diagnōsis\",\"dic\":\"thee-AH-gnoh-sees\",\"dic_mod\":\"thee-AH-gnoh-sees\"},\"see\":[\"G1231\"]}', 'hearing'),
(1234, 1234, 'διαγογγύζω', '{\"def\":{\"short\":\"to complain throughout a crowd\",\"long\":[\"to murmur\",[\"either of a whole crowd, or among one another\",\"always used of many indignantly complaining\"]]},\"deriv\":\"from G1223 and G1111\",\"pronun\":{\"ipa\":\"ði.ɑ.ɣoŋˈɣy.zo\",\"ipa_mod\":\"ði.ɑ.ɣowŋˈɣju.zow\",\"sbl\":\"diagongyzō\",\"dic\":\"thee-ah-gohng-GOO-zoh\",\"dic_mod\":\"thee-ah-gohng-GYOO-zoh\"},\"see\":[\"G1111\",\"G1223\"]}', 'murmur');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1235, 1235, 'διαγρηγορέω', '{\"def\":{\"short\":\"to waken thoroughly\",\"long\":[\"to watch through\",\"to remain awake\",\"to be fully awake\"]},\"deriv\":\"from G1223 and G1127\",\"pronun\":{\"ipa\":\"ði.ɑ.ɣre.ɣoˈrɛ.o\",\"ipa_mod\":\"ði.ɑ.ɣre̞.ɣowˈre̞.ow\",\"sbl\":\"diagrēgoreō\",\"dic\":\"thee-ah-gray-goh-REH-oh\",\"dic_mod\":\"thee-ah-gray-goh-RAY-oh\"},\"see\":[\"G1127\",\"G1223\"]}', 'be awake'),
(1236, 1236, 'διάγω', '{\"def\":{\"short\":\"to pass time or life\",\"long\":[\"to lead through, lead across, send across\",\"to pass, to live\"]},\"deriv\":\"from G1223 and G0071\",\"pronun\":{\"ipa\":\"ðiˈɑ.ɣo\",\"ipa_mod\":\"ðiˈɑ.ɣow\",\"sbl\":\"diagō\",\"dic\":\"thee-AH-goh\",\"dic_mod\":\"thee-AH-goh\"},\"see\":[\"G0071\",\"G1223\"]}', 'lead life, living'),
(1237, 1237, 'διαδέχομαι', '{\"def\":{\"short\":\"to receive in turn, i.e., (figuratively) succeed to\",\"long\":[\"to receive through another, anything left or bequeathed by him, to receive in succession, receive in turn, succeed to\"]},\"deriv\":\"from G1223 and G1209\",\"pronun\":{\"ipa\":\"ði.ɑˈðɛ.xo.mɛ\",\"ipa_mod\":\"ði.ɑˈðe̞.xow.me\",\"sbl\":\"diadechomai\",\"dic\":\"thee-ah-THEH-hoh-meh\",\"dic_mod\":\"thee-ah-THAY-hoh-may\"},\"see\":[\"G1209\",\"G1223\"]}', 'come after'),
