(1241, 1241, 'διαζώννυμι', '{\"def\":{\"short\":\"to gird tightly\",\"long\":[\"to bind or gird all around\",\"to gird one\'s self with a thing, gird a thing around one\'s self\"]},\"deriv\":\"from G1223 and G2224\",\"pronun\":{\"ipa\":\"ði.ɑˈzon.ny.mi\",\"ipa_mod\":\"ði.ɑˈzown.nju.mi\",\"sbl\":\"diazōnnymi\",\"dic\":\"thee-ah-ZONE-noo-mee\",\"dic_mod\":\"thee-ah-ZONE-nyoo-mee\"},\"see\":[\"G1223\",\"G2224\"]}', 'gird'),
(1242, 1242, 'διαθήκη', '{\"def\":{\"short\":\"properly, a disposition, i.e., (specially) a contract (especially a devisory will)\",\"long\":[\"a disposition, arrangement, of any sort, which one wishes to be valid, the last disposition which one makes of his earthly possessions after his death, a testament or will\",\"a compact, a covenant, a testament\",[\"God\'s covenant with Noah, etc.\"]]},\"deriv\":\"from G1303\",\"pronun\":{\"ipa\":\"ði.ɑˈθe.ke\",\"ipa_mod\":\"ði.ɑˈθe̞.ke̞\",\"sbl\":\"diathēkē\",\"dic\":\"thee-ah-THAY-kay\",\"dic_mod\":\"thee-ah-THAY-kay\"},\"see\":[\"G1303\"]}', 'covenant, testament'),
(1243, 1243, 'διαίρεσις', '{\"def\":{\"short\":\"a distinction or (concretely) variety\",\"long\":[\"division, distribution\",\"distinction, difference\",[\"in particular, a distinction arising from a different distribution to different persons\"]]},\"deriv\":\"from G1244\",\"pronun\":{\"ipa\":\"ðiˈɛ.rɛ.sis\",\"ipa_mod\":\"ðiˈe.re̞.sis\",\"sbl\":\"diairesis\",\"dic\":\"thee-EH-reh-sees\",\"dic_mod\":\"thee-A-ray-sees\"},\"see\":[\"G1244\"]}', 'difference, diversity'),
