(1254, 1254, 'διακωλύω', '{\"def\":{\"short\":\"to hinder altogether, i.e., utterly prohibit\",\"long\":[\"to hinder, prevent\"]},\"deriv\":\"from G1223 and G2967\",\"pronun\":{\"ipa\":\"ði.ɑ.koˈly.o\",\"ipa_mod\":\"ði.ɑ.kowˈlju.ow\",\"sbl\":\"diakōlyō\",\"dic\":\"thee-ah-koh-LOO-oh\",\"dic_mod\":\"thee-ah-koh-LYOO-oh\"},\"see\":[\"G1223\",\"G2967\"]}', 'forbid'),
(1255, 1255, 'διαλαλέω', '{\"def\":{\"short\":\"to talk throughout a company, i.e., converse or (genitive case) publish\",\"long\":[\"to converse together, to talk with\"]},\"deriv\":\"from G1223 and G2980\",\"pronun\":{\"ipa\":\"ði.ɑ.lɑˈlɛ.o\",\"ipa_mod\":\"ði.ɑ.lɑˈle̞.ow\",\"sbl\":\"dialaleō\",\"dic\":\"thee-ah-la-LEH-oh\",\"dic_mod\":\"thee-ah-la-LAY-oh\"},\"see\":[\"G1223\",\"G2980\"]}', 'commune, noise abroad'),
(1256, 1256, 'διαλέγομαι', '{\"def\":{\"short\":\"to say thoroughly, i.e., discuss (in argument or exhortation)\",\"long\":[\"to think different things with one\'s self, mingle thought with thought\",[\"to ponder, revolve in mind\"],\"to converse, discourse with one, argue, discuss\"]},\"deriv\":\"middle voice from G1223 and G3004\",\"pronun\":{\"ipa\":\"ði.ɑˈlɛ.ɣo.mɛ\",\"ipa_mod\":\"ði.ɑˈle̞.ɣow.me\",\"sbl\":\"dialegomai\",\"dic\":\"thee-ah-LEH-goh-meh\",\"dic_mod\":\"thee-ah-LAY-goh-may\"},\"see\":[\"G1223\",\"G3004\"]}', 'dispute, preach (unto), reason (with), speak'),
(1257, 1257, 'διαλείπω', '{\"def\":{\"short\":\"to leave off in the middle, i.e., intermit\",\"long\":[\"to interpose a delay, to intermit, leave off for a time something already begun\"]},\"deriv\":\"from G1223 and G3007\",\"pronun\":{\"ipa\":\"ði.ɑˈli.po\",\"ipa_mod\":\"ði.ɑˈli.pow\",\"sbl\":\"dialeipō\",\"dic\":\"thee-ah-LEE-poh\",\"dic_mod\":\"thee-ah-LEE-poh\"},\"see\":[\"G1223\",\"G3007\"]}', 'cease'),
