(1259, 1259, 'διαλλάσσω', '{\"def\":{\"short\":\"to change thoroughly, i.e., (mentally) to conciliate\",\"long\":[\"to change\",\"to change the mind of anyone, to reconcile\",\"to be reconciled, to renew friendship with one\"]},\"deriv\":\"from G1223 and G0236\",\"pronun\":{\"ipa\":\"ði.ɑlˈlɑs.so\",\"ipa_mod\":\"ði.ɑlˈlɑs.sow\",\"sbl\":\"diallassō\",\"dic\":\"thee-al-LAHS-soh\",\"dic_mod\":\"thee-al-LAHS-soh\"},\"see\":[\"G0236\",\"G1223\"]}', 'reconcile'),
(1260, 1260, 'διαλογίζομαι', '{\"def\":{\"short\":\"to reckon thoroughly, i.e., (genitive case) to deliberate (by reflection or discussion)\",\"long\":[\"to bring together different reasons, to reckon up the reasons, to reason, revolve in one\'s mind, deliberate\"]},\"deriv\":\"from G1223 and G3049\",\"pronun\":{\"ipa\":\"ði.ɑ.loˈɣi.zo.mɛ\",\"ipa_mod\":\"ði.ɑ.lowˈɣi.zow.me\",\"sbl\":\"dialogizomai\",\"dic\":\"thee-ah-loh-GEE-zoh-meh\",\"dic_mod\":\"thee-ah-loh-GEE-zoh-may\"},\"see\":[\"G1223\",\"G3049\"]}', 'cast in mind, consider, dispute, muse, reason, think'),
(1261, 1261, 'διαλογισμός', '{\"def\":{\"short\":\"discussion, i.e., (internal) consideration (by implication, purpose), or (external) debate\",\"long\":[\"the thinking of a man deliberating with himself\",[\"a thought, inward reasoning\",\"purpose, design\"],\"a deliberating, questioning about what is true\",[\"hesitation, doubting\",\"disputing, arguing\"]]},\"deriv\":\"from G1260\",\"pronun\":{\"ipa\":\"ði.ɑ.lo.ɣiˈsmos\",\"ipa_mod\":\"ði.ɑ.low.ʝiˈsmows\",\"sbl\":\"dialogismos\",\"dic\":\"thee-ah-loh-gee-SMOSE\",\"dic_mod\":\"thee-ah-loh-gee-SMOSE\"},\"see\":[\"G1260\"]}', 'dispute, doubtful(-ing), imagination, reasoning, thought'),
(1262, 1262, 'διαλύω', '{\"def\":{\"short\":\"to dissolve utterly\",\"long\":[\"to dissolve\"]},\"deriv\":\"from G1223 and G3089\",\"pronun\":{\"ipa\":\"ði.ɑˈly.o\",\"ipa_mod\":\"ði.ɑˈlju.ow\",\"sbl\":\"dialyō\",\"dic\":\"thee-ah-LOO-oh\",\"dic_mod\":\"thee-ah-LYOO-oh\"},\"see\":[\"G1223\",\"G3089\"]}', 'scatter'),
