(1265, 1265, 'διαμένω', '{\"def\":{\"short\":\"to stay constantly (in being or relation)\",\"long\":[\"to stay permanently, remain permanently, continue\"]},\"deriv\":\"from G1223 and G3306\",\"pronun\":{\"ipa\":\"ði.ɑˈmɛ.no\",\"ipa_mod\":\"ði.ɑˈme̞.now\",\"sbl\":\"diamenō\",\"dic\":\"thee-ah-MEH-noh\",\"dic_mod\":\"thee-ah-MAY-noh\"},\"see\":[\"G1223\",\"G3306\"]}', 'continue, remain'),
(1266, 1266, 'διαμερίζω', '{\"def\":{\"short\":\"to partition thoroughly (literally in distribution, figuratively in dissension)\",\"long\":[\"to cleave asunder, cut in pieces\",\"to be divided into opposing parts, to be at variance, in dissension\",\"to distribute\"]},\"deriv\":\"from G1223 and G3307\",\"pronun\":{\"ipa\":\"ði.ɑ.mɛˈri.zo\",\"ipa_mod\":\"ði.ɑ.me̞ˈri.zow\",\"sbl\":\"diamerizō\",\"dic\":\"thee-ah-meh-REE-zoh\",\"dic_mod\":\"thee-ah-may-REE-zoh\"},\"see\":[\"G1223\",\"G3307\"]}', 'cloven, divide, part'),
(1267, 1267, 'διαμερισμός', '{\"def\":{\"short\":\"disunion (of opinion and conduct)\",\"long\":[\"a parting, distribution\",\"disunion, dissension\"]},\"deriv\":\"from G1266\",\"pronun\":{\"ipa\":\"ði.ɑ.mɛ.riˈsmos\",\"ipa_mod\":\"ði.ɑ.me̞.riˈsmows\",\"sbl\":\"diamerismos\",\"dic\":\"thee-ah-meh-ree-SMOSE\",\"dic_mod\":\"thee-ah-may-ree-SMOSE\"},\"see\":[\"G1266\"]}', 'division'),
