(1355, 1355, 'διόπερ', '{\"def\":{\"short\":\"on which very account\",\"long\":[\"on which very account\"]},\"deriv\":\"from G1352 and G4007\",\"pronun\":{\"ipa\":\"ðiˈo.pɛr\",\"ipa_mod\":\"ðiˈow.pe̞r\",\"sbl\":\"dioper\",\"dic\":\"thee-OH-per\",\"dic_mod\":\"thee-OH-pare\"},\"see\":[\"G1352\",\"G4007\"]}', 'wherefore'),
(1356, 1356, 'διοπετής', '{\"def\":{\"short\":\"sky-fallen (i.e., an aerolite)\",\"long\":[\"fallen from Zeus, i.e., from heaven\",\"an image of the Ephesian Artemis which was supposed to have fallen from heaven\"]},\"deriv\":\"from the alternate of G2203 and the alternate of G4098\",\"pronun\":{\"ipa\":\"ði.o.pɛˈtes\",\"ipa_mod\":\"ði.ow.pe̞ˈte̞s\",\"sbl\":\"diopetēs\",\"dic\":\"thee-oh-peh-TASE\",\"dic_mod\":\"thee-oh-pay-TASE\"},\"see\":[\"G2203\",\"G4098\"]}', 'which fell down from Jupiter'),
(1357, 1357, 'διόρθωσις', '{\"def\":{\"short\":\"rectification, i.e., (specially) the Messianic restauration\",\"long\":[\"in a physical sense, a making straight, restoring to its natural and normal condition something which in some way protrudes or has got out of line, as broken or misshapen limbs\",\"of acts and institutions, reformation\"]},\"deriv\":\"from a compound of G1223 and a derivative of G3717, meaning to straighten thoroughly\",\"pronun\":{\"ipa\":\"ðiˈor.θo.sis\",\"ipa_mod\":\"ðiˈowr.θow.sis\",\"sbl\":\"diorthōsis\",\"dic\":\"thee-ORE-thoh-sees\",\"dic_mod\":\"thee-ORE-thoh-sees\"},\"see\":[\"G1223\",\"G3717\"]}', 'reformation'),
