(1543, 1543, 'ἑκατοντάρχης', '{\"def\":{\"short\":\"the captain of one hundred men\",\"long\":[\"an officer in the Roman army\"]},\"deriv\":\"from G1540 and G0757\",\"pronun\":{\"ipa\":\"hɛk.ɑ.tonˈtɑr.xes\",\"ipa_mod\":\"e̞k.ɑ.townˈtɑr.xe̞s\",\"sbl\":\"hekatontarchēs\",\"dic\":\"hek-ah-tone-TAHR-hase\",\"dic_mod\":\"ake-ah-tone-TAHR-hase\"},\"see\":[\"G0757\",\"G1540\"]}', 'centurion'),
(1544, 1544, 'ἐκβάλλω', '{\"def\":{\"short\":\"to eject (literally or figuratively)\",\"long\":[\"to cast out, drive out, to send out\",[\"with notion of violence\",[\"to drive out (cast out)\",\"to cast out\",[\"of the world, i.e., be deprived of the power and influence he exercises in the world\",\"a thing: excrement from the belly into the sink\"],\"to expel a person from a society: to banish from a family\",\"to compel one to depart; to bid one depart, in stern though not violent language\",\"so employed that the rapid motion of the one going is transferred to the one sending forth\",[\"to command or cause one to depart in haste\"],\"to draw out with force, tear out\",\"with implication of force overcoming opposite force\",[\"to cause a thing to move straight on its intended goal\"],\"to reject with contempt, to cast off or away\"],\"without the notion of violence\",[\"to draw out, extract, one thing inserted in another\",\"to bring out of, to draw or bring forth\",\"to except, to leave out, i.e., not receive\",\"to lead one forth or away somewhere with a force which he cannot resist\"]]]},\"deriv\":\"from G1537 and G0906\",\"pronun\":{\"ipa\":\"ɛkˈβɑl.lo\",\"ipa_mod\":\"e̞kˈvɑl.low\",\"sbl\":\"ekballō\",\"dic\":\"ek-VAHL-loh\",\"dic_mod\":\"ake-VAHL-loh\"},\"see\":[\"G0906\",\"G1537\"]}', 'bring forth, cast (forth, out), drive (out), expel, leave, pluck (pull, take, thrust) out, put forth (out), send away (forth, out)'),
(1545, 1545, 'ἔκβασις', '{\"def\":{\"short\":\"an exit (literally or figuratively)\",\"long\":[\"an egress, way out, exit\",[\"applied figuratively to the way of escape from temptation\"],\"the issue referring to the end of one\'s life\",[\"Hebrews 13:7 refers not only to end of physical life, but the manner in which they closed a well spent life as exhibited by their spirit in dying\"]]},\"deriv\":\"from a compound of G1537 and the base of G0939 (meaning to go out)\",\"pronun\":{\"ipa\":\"ˈɛk.βɑ.sis\",\"ipa_mod\":\"ˈe̞k.vɑ.sis\",\"sbl\":\"ekbasis\",\"dic\":\"EK-va-sees\",\"dic_mod\":\"AKE-va-sees\"},\"see\":[\"G0939\",\"G1537\"]}', 'end, way to escape'),
(1546, 1546, 'ἐκβολή', '{\"def\":{\"short\":\"ejection, i.e., (specially) a throwing overboard of the cargo\",\"long\":[\"a casting out\",\"the throwing overboard of goods and lading whereby sailors lighten a ship in a storm to keep her from sinking\"]},\"deriv\":\"from G1544\",\"pronun\":{\"ipa\":\"ɛk.βoˈle\",\"ipa_mod\":\"e̞k.vowˈle̞\",\"sbl\":\"ekbolē\",\"dic\":\"ek-voh-LAY\",\"dic_mod\":\"ake-voh-LAY\"},\"see\":[\"G1544\"]}', '+ lighten the ship'),
(1547, 1547, 'ἐκγαμίζω', '{\"def\":{\"short\":\"to marry off a daughter\",\"long\":[\"to give away in marriage: a daughter\",\"to marry, to be given in marriage\"]},\"deriv\":\"from G1537 and a form of G1061 (compare G1548)\",\"pronun\":{\"ipa\":\"ɛk.ɣɑˈmi.zo\",\"ipa_mod\":\"e̞k.ɣɑˈmi.zow\",\"sbl\":\"ekgamizō\",\"dic\":\"ek-ga-MEE-zoh\",\"dic_mod\":\"ake-ga-MEE-zoh\"},\"see\":[\"G1061\",\"G1537\",\"G1548\"]}', 'give in marriage'),
(1548, 1548, 'ἐκγαμίσκω', '{\"def\":{\"short\":\"the same as 1547\",\"long\":[\"to give away in marriage: a daughter\",\"to marry, to be given in marriage\"]},\"deriv\":\"from G1537 and G1061\",\"pronun\":{\"ipa\":\"ɛk.ɣɑˈmi.sko\",\"ipa_mod\":\"e̞k.ɣɑˈmi.skow\",\"sbl\":\"ekgamiskō\",\"dic\":\"ek-ga-MEE-skoh\",\"dic_mod\":\"ake-ga-MEE-skoh\"},\"see\":[\"G1061\",\"G1537\"]}', 'give in marriage'),
(1549, 1549, 'ἔκγονον', '{\"def\":{\"short\":\"a descendant, i.e., (specially) grandchild\",\"long\":[\"sprung from one, born, begotten\",\"a son, daughter, offspring, children, descendants\",\"grandchildren\"]},\"deriv\":\"neuter of a derivative of a compound of G1537 and G1096\",\"pronun\":{\"ipa\":\"ˈɛk.ɣo.non\",\"ipa_mod\":\"ˈe̞k.ɣow.nown\",\"sbl\":\"ekgonon\",\"dic\":\"EK-goh-none\",\"dic_mod\":\"AKE-goh-none\"},\"see\":[\"G1096\",\"G1537\"]}', 'nephew'),
(1550, 1550, 'ἐκδαπανάω', '{\"def\":{\"short\":\"to expend (wholly), i.e., (figuratively) exhaust\",\"long\":[\"to exhaust by expending, to spend wholly, use up,\",\"to spend one\'s self wholly\"]},\"deriv\":\"from G1537 and G1159\",\"pronun\":{\"ipa\":\"ɛk.ðɑ.pɑˈnɑ.o\",\"ipa_mod\":\"e̞k.ðɑ.pɑˈnɑ.ow\",\"sbl\":\"ekdapanaō\",\"dic\":\"ek-tha-pa-NA-oh\",\"dic_mod\":\"ake-tha-pa-NA-oh\"},\"see\":[\"G1159\",\"G1537\"]}', 'spend'),
(1551, 1551, 'ἐκδέχομαι', '{\"def\":{\"short\":\"to accept from some source, i.e., (by implication) to await\",\"long\":[\"to receive, accept\",\"to look for, expect, wait for, await\"]},\"deriv\":\"from G1537 and G1209\",\"pronun\":{\"ipa\":\"ɛkˈðɛ.xo.mɛ\",\"ipa_mod\":\"e̞kˈðe̞.xow.me\",\"sbl\":\"ekdechomai\",\"dic\":\"ek-THEH-hoh-meh\",\"dic_mod\":\"ake-THAY-hoh-may\"},\"see\":[\"G1209\",\"G1537\"]}', 'expect, look (tarry) for, wait (for)'),
(1552, 1552, 'ἔκδηλος', '{\"def\":{\"short\":\"wholly evident\",\"long\":[\"evident, clear, conspicuous\"]},\"deriv\":\"from G1537 and G1212\",\"pronun\":{\"ipa\":\"ˈɛk.ðe.los\",\"ipa_mod\":\"ˈe̞k.ðe̞.lows\",\"sbl\":\"ekdēlos\",\"dic\":\"EK-thay-lose\",\"dic_mod\":\"AKE-thay-lose\"},\"see\":[\"G1212\",\"G1537\"]}', 'manifest'),
(1553, 1553, 'ἐκδημέω', '{\"def\":{\"short\":\"to emigrate, i.e., (figuratively) vacate or quit\",\"long\":[\"to go abroad\",\"emigrate, depart\",\"to be or live abroad\"]},\"deriv\":\"from a compound of G1537 and G1218\",\"pronun\":{\"ipa\":\"ɛk.ðeˈmɛ.o\",\"ipa_mod\":\"e̞k.ðe̞ˈme̞.ow\",\"sbl\":\"ekdēmeō\",\"dic\":\"ek-thay-MEH-oh\",\"dic_mod\":\"ake-thay-MAY-oh\"},\"see\":[\"G1218\",\"G1537\"]}', 'be absent');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1554, 1554, 'ἐκδίδωμι', '{\"def\":{\"short\":\"to give forth, i.e., (specially) to lease\",\"long\":[\"to give out of one\'s house, power, hand, stores\",\"to give out, give up, give over\",\"to let out for hire\",\"to farm out\",\"to let out for one\'s advantage\"]},\"deriv\":\"from G1537 and G1325\",\"pronun\":{\"ipa\":\"ɛkˈði.ðo.mi\",\"ipa_mod\":\"e̞kˈði.ðow.mi\",\"sbl\":\"ekdidōmi\",\"dic\":\"ek-THEE-thoh-mee\",\"dic_mod\":\"ake-THEE-thoh-mee\"},\"see\":[\"G1325\",\"G1537\"]}', 'let forth (out)'),
(1555, 1555, 'ἐκδιηγέομαι', '{\"def\":{\"short\":\"to narrate through wholly\",\"long\":[\"to narrate in full or wholly\",\"to relate, tell, declare\"]},\"deriv\":\"from G1537 and a compound of G1223 and G2233\",\"pronun\":{\"ipa\":\"ɛk.ði.eˈɣɛ.o.mɛ\",\"ipa_mod\":\"e̞k.ði.e̞ˈɣe̞.ow.me\",\"sbl\":\"ekdiēgeomai\",\"dic\":\"ek-thee-ay-GEH-oh-meh\",\"dic_mod\":\"ake-thee-ay-GAY-oh-may\"},\"see\":[\"G1223\",\"G1537\",\"G2233\"]}', 'declare'),
(1556, 1556, 'ἐκδικέω', '{\"def\":{\"short\":\"to vindicate, retaliate, punish\",\"long\":[\"to vindicate one\'s right, do one justice\",[\"to protect, defend, one person from another\"],\"to avenge a thing\",[\"to punish a person for a thing\"]]},\"deriv\":\"from G1558\",\"pronun\":{\"ipa\":\"ɛk.ðiˈkɛ.o\",\"ipa_mod\":\"e̞k.ðiˈke̞.ow\",\"sbl\":\"ekdikeō\",\"dic\":\"ek-thee-KEH-oh\",\"dic_mod\":\"ake-thee-KAY-oh\"},\"see\":[\"G1558\"]}', '(a-, re-)venge'),
(1557, 1557, 'ἐκδίκησις', '{\"def\":{\"short\":\"vindication, retribution\",\"long\":[\"a revenging, vengeance, punishment\"]},\"deriv\":\"from G1556\",\"pronun\":{\"ipa\":\"ɛkˈði.ke.sis\",\"ipa_mod\":\"e̞kˈði.ke̞.sis\",\"sbl\":\"ekdikēsis\",\"dic\":\"ek-THEE-kay-sees\",\"dic_mod\":\"ake-THEE-kay-sees\"},\"see\":[\"G1556\"]}', '(a-, re-)venge(-ance), punishment'),
(1558, 1558, 'ἔκδικος', '{\"def\":{\"short\":\"carrying justice out, i.e., a punisher\",\"long\":[\"without law and justice\",\"exacting penalty from one\",[\"an avenger, punisher\"]]},\"deriv\":\"from G1537 and G1349\",\"pronun\":{\"ipa\":\"ˈɛk.ði.kos\",\"ipa_mod\":\"ˈe̞k.ði.kows\",\"sbl\":\"ekdikos\",\"dic\":\"EK-thee-kose\",\"dic_mod\":\"AKE-thee-kose\"},\"see\":[\"G1349\",\"G1537\"]}', 'a (re-)venger'),
(1559, 1559, 'ἐκδιώκω', '{\"def\":{\"short\":\"to pursue out, i.e., expel or persecute implacably\",\"long\":[\"to drive out, banish\",\"to pursue\",[\"to persecute, oppress with calamities\"]]},\"deriv\":\"from G1537 and G1377\",\"pronun\":{\"ipa\":\"ɛk.ðiˈo.ko\",\"ipa_mod\":\"e̞k.ðiˈow.kow\",\"sbl\":\"ekdiōkō\",\"dic\":\"ek-thee-OH-koh\",\"dic_mod\":\"ake-thee-OH-koh\"},\"see\":[\"G1377\",\"G1537\"]}', 'persecute'),
(1560, 1560, 'ἔκδοτος', '{\"def\":{\"short\":\"given out or over, i.e., surrendered\",\"long\":[\"given over, delivered up\",[\"to enemies, or to the power, the will, of some one\"]]},\"deriv\":\"from G1537 and a derivative of G1325\",\"pronun\":{\"ipa\":\"ˈɛk.ðo.tos\",\"ipa_mod\":\"ˈe̞k.ðow.tows\",\"sbl\":\"ekdotos\",\"dic\":\"EK-thoh-tose\",\"dic_mod\":\"AKE-thoh-tose\"},\"see\":[\"G1325\",\"G1537\"]}', 'delivered'),
(1561, 1561, 'ἐκδοχή', '{\"def\":{\"short\":\"expectation\",\"long\":[\"the act or manner of receiving from\",[\"reception\",\"succession\",\"interpretation\",\"expectation, waiting\"]]},\"deriv\":\"from G1551\",\"pronun\":{\"ipa\":\"ɛk.ðoˈxe\",\"ipa_mod\":\"e̞k.ðowˈxe̞\",\"sbl\":\"ekdochē\",\"dic\":\"ek-thoh-HAY\",\"dic_mod\":\"ake-thoh-HAY\"},\"see\":[\"G1551\"]}', 'looking for'),
(1562, 1562, 'ἐκδύω', '{\"def\":{\"short\":\"to cause to sink out of, i.e., (specially as of clothing) to divest\",\"long\":[\"to take off\",[\"to strip one of his garments\"],\"to take off from one\'s self, to put off the one\'s raiments\",\"figuratively, to put off the body, the clothing of the soul\"]},\"deriv\":\"from G1537 and the base of G1416\",\"pronun\":{\"ipa\":\"ɛkˈðy.o\",\"ipa_mod\":\"e̞kˈðju.ow\",\"sbl\":\"ekdyō\",\"dic\":\"ek-THOO-oh\",\"dic_mod\":\"ake-THYOO-oh\"},\"see\":[\"G1416\",\"G1537\"]}', 'strip, take off from, unclothe'),
(1563, 1563, 'ἐκεῖ', '{\"def\":{\"short\":\"there; by extension, thither\",\"long\":[\"there, in or to that place\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ɛkˈi\",\"ipa_mod\":\"e̞cˈi\",\"sbl\":\"ekei\",\"dic\":\"ek-EE\",\"dic_mod\":\"ake-EE\"}}', 'there, thither(-ward), (to) yonder (place)'),
(1564, 1564, 'ἐκεῖθεν', '{\"def\":{\"short\":\"thence\",\"long\":[\"thence, from that place\"]},\"deriv\":\"from G1563\",\"pronun\":{\"ipa\":\"ɛkˈi.θɛn\",\"ipa_mod\":\"e̞cˈi.θe̞n\",\"sbl\":\"ekeithen\",\"dic\":\"ek-EE-then\",\"dic_mod\":\"ake-EE-thane\"},\"see\":[\"G1563\"]}', 'from that place, (from) thence, there'),
(1565, 1565, 'ἐκεῖνος', '{\"def\":{\"short\":\"that one (or (neuter) thing); often intensified by the article prefixed\",\"long\":[\"he, she it, etc.\"]},\"deriv\":\"from G1563\",\"pronun\":{\"ipa\":\"ɛkˈi.nos\",\"ipa_mod\":\"e̞cˈi.nows\",\"sbl\":\"ekeinos\",\"dic\":\"ek-EE-nose\",\"dic_mod\":\"ake-EE-nose\"},\"see\":[\"G1563\",\"G3778\"]}', 'he, it, the other (same), selfsame, that (same, very), X their, X them, they, this, those'),
(1566, 1566, 'ἐκεῖσε', '{\"def\":{\"short\":\"thither\",\"long\":[\"there, at that place\"]},\"deriv\":\"from G1563\",\"pronun\":{\"ipa\":\"ɛkˈi.sɛ\",\"ipa_mod\":\"e̞cˈi.se̞\",\"sbl\":\"ekeise\",\"dic\":\"ek-EE-seh\",\"dic_mod\":\"ake-EE-say\"},\"see\":[\"G1563\"]}', 'there'),
(1567, 1567, 'ἐκζητέω', '{\"def\":{\"short\":\"to search out, i.e., (figuratively)investigate, crave, demand, (by Hebraism) worship\",\"long\":[\"to seek out, search for\",\"to seek out, i.e., investigate, scrutinise\",\"to seek out for one\'s self, beg, crave\",\"to demand back, require\"]},\"deriv\":\"from G1537 and G2212\",\"pronun\":{\"ipa\":\"ɛk.zeˈtɛ.o\",\"ipa_mod\":\"e̞k.ze̞ˈte̞.ow\",\"sbl\":\"ekzēteō\",\"dic\":\"ek-zay-TEH-oh\",\"dic_mod\":\"ake-zay-TAY-oh\"},\"see\":[\"G1537\",\"G2212\"]}', '(en-, re-)quire, seek after (carefully, diligently)'),
(1568, 1568, 'ἐκθαμβέω', '{\"def\":{\"short\":\"to astonish utterly\",\"long\":[\"to throw into terror or amazement\",[\"to alarm thoroughly, to terrify\"],\"to be struck with amazement\",[\"to be thoroughly amazed, astounded\",\"to be struck with terror\"]]},\"deriv\":\"from G1569\",\"pronun\":{\"ipa\":\"ɛk.θɑmˈβɛ.o\",\"ipa_mod\":\"e̞k.θɑɱˈve̞.ow\",\"sbl\":\"ekthambeō\",\"dic\":\"ek-thahm-VEH-oh\",\"dic_mod\":\"ake-thahm-VAY-oh\"},\"see\":[\"G1569\"]}', 'affright, greatly (sore) amaze'),
(1569, 1569, 'ἔκθαμβος', '{\"def\":{\"short\":\"utterly astounded\",\"long\":[\"quite astonished, amazed\",\"terrifying, dreadful\",\"wonder or marvel greatly\"]},\"deriv\":\"from G1537 and G2285\",\"pronun\":{\"ipa\":\"ˈɛk.θɑm.βos\",\"ipa_mod\":\"ˈe̞k.θɑɱ.vows\",\"sbl\":\"ekthambos\",\"dic\":\"EK-thahm-vose\",\"dic_mod\":\"AKE-thahm-vose\"},\"see\":[\"G1537\",\"G2285\"]}', 'greatly wondering'),
(1570, 1570, 'ἔκθετος', '{\"def\":{\"short\":\"put out, i.e., exposed to perish\",\"long\":[\"cast out, exposed\"]},\"deriv\":\"from G1537 and a derivative of G5087\",\"pronun\":{\"ipa\":\"ˈɛk.θɛ.tos\",\"ipa_mod\":\"ˈe̞k.θe̞.tows\",\"sbl\":\"ekthetos\",\"dic\":\"EK-theh-tose\",\"dic_mod\":\"AKE-thay-tose\"},\"see\":[\"G1537\",\"G5087\"]}', 'cast out'),
(1571, 1571, 'ἐκκαθαίρω', '{\"def\":{\"short\":\"to cleanse thoroughly\",\"long\":[\"to cleanse out, clean thoroughly, to cleanse\"]},\"deriv\":\"from G1537 and G2508\",\"pronun\":{\"ipa\":\"ɛk.kɑˈθɛ.ro\",\"ipa_mod\":\"e̞k.kɑˈθe.row\",\"sbl\":\"ekkathairō\",\"dic\":\"ek-ka-THEH-roh\",\"dic_mod\":\"ake-ka-THAY-roh\"},\"see\":[\"G1537\",\"G2508\"]}', 'purge (out)'),
(1572, 1572, 'ἐκκαίω', '{\"def\":{\"short\":\"to inflame deeply\",\"long\":[\"to burn out\",\"to set on fire\",\"to be kindled, to burn\"]},\"deriv\":\"from G1537 and G2545\",\"pronun\":{\"ipa\":\"ɛkˈkɛ.o\",\"ipa_mod\":\"e̞kˈke.ow\",\"sbl\":\"ekkaiō\",\"dic\":\"ek-KEH-oh\",\"dic_mod\":\"ake-KAY-oh\"},\"see\":[\"G1537\",\"G2545\"]}', 'burn'),
(1573, 1573, 'ἐκκακέω', '{\"def\":{\"short\":\"to be (bad or) weak, i.e., (by implication) to fail (in heart)\",\"long\":[\"to be utterly spiritless, to be wearied out, exhausted\"]},\"deriv\":\"from G1537 and G2556\",\"pronun\":{\"ipa\":\"ɛk.kɑˈkɛ.o\",\"ipa_mod\":\"e̞k.kɑˈke̞.ow\",\"sbl\":\"ekkakeō\",\"dic\":\"ek-ka-KEH-oh\",\"dic_mod\":\"ake-ka-KAY-oh\"},\"see\":[\"G1537\",\"G2556\"]}', 'faint, be weary'),
(1574, 1574, 'ἐκκεντέω', '{\"def\":{\"short\":\"to transfix\",\"long\":[\"to put out, dig out\",\"to dig through, transfix, pierce\"]},\"deriv\":\"from G1537 and the base of G2759\",\"pronun\":{\"ipa\":\"ɛk.kɛnˈtɛ.o\",\"ipa_mod\":\"e̞k.ce̞nˈte̞.ow\",\"sbl\":\"ekkenteō\",\"dic\":\"ek-ken-TEH-oh\",\"dic_mod\":\"ake-kane-TAY-oh\"},\"see\":[\"G1537\",\"G2759\"]}', 'pierce'),
(1575, 1575, 'ἐκκλάω', '{\"def\":{\"short\":\"to exscind\",\"long\":[\"to break off, to cut off\"]},\"deriv\":\"from G1537 and G2806\",\"pronun\":{\"ipa\":\"ɛkˈklɑ.o\",\"ipa_mod\":\"e̞kˈklɑ.ow\",\"sbl\":\"ekklaō\",\"dic\":\"ek-KLA-oh\",\"dic_mod\":\"ake-KLA-oh\"},\"see\":[\"G1537\",\"G2806\"]}', 'break off'),
(1576, 1576, 'ἐκκλείω', '{\"def\":{\"short\":\"to shut out (literally or figuratively)\",\"long\":[\"to shut out, to turn out of doors\",\"to prevent the approach of one\"]},\"deriv\":\"from G1537 and G2808\",\"pronun\":{\"ipa\":\"ɛkˈkli.o\",\"ipa_mod\":\"e̞kˈkli.ow\",\"sbl\":\"ekkleiō\",\"dic\":\"ek-KLEE-oh\",\"dic_mod\":\"ake-KLEE-oh\"},\"see\":[\"G1537\",\"G2808\"]}', 'exclude'),
(1577, 1577, 'ἐκκλησία', '{\"def\":{\"short\":\"a calling out, i.e., (concretely) a popular meeting, especially a religious congregation (Jewish synagogue, or Christian community of members on earth or saints in heaven or both)\",\"long\":[\"a gathering of citizens called out from their homes into some public place, an assembly\",[\"an assembly of the people convened at the public place of the council for the purpose of deliberating\",\"the assembly of the Israelites\",\"any gathering or throng of men assembled by chance, tumultuously\",\"in a Christian sense\",[\"an assembly of Christians gathered for worship in a religious meeting\",\"a company of Christian, or of those who, hoping for eternal salvation through Jesus Christ, observe their own religious rites, hold their own religious meetings, and manage their own affairs, according to regulations prescribed for the body for order\'s sake\",\"those who anywhere, in a city, village, constitute such a company and are united into one body\",\"the whole body of Christians scattered throughout the earth\",\"the assembly of faithful Christians already dead and received into heaven\"]]]},\"deriv\":\"from a compound of G1537 and a derivative of G2564\",\"pronun\":{\"ipa\":\"ɛk.kleˈsi.ɑ\",\"ipa_mod\":\"e̞k.kle̞ˈsi.ɑ\",\"sbl\":\"ekklēsia\",\"dic\":\"ek-klay-SEE-ah\",\"dic_mod\":\"ake-klay-SEE-ah\"},\"see\":[\"G1537\",\"G2564\"]}', 'assembly, church'),
(1578, 1578, 'ἐκκλίνω', '{\"def\":{\"short\":\"to deviate, i.e., (absolutely) to shun (literally or figuratively), or (relatively) to decline (from piety)\",\"long\":[\"to turn aside, deviate (from the right way and course)\",\"to turn (one\'s self) away, to turn away from, keep aloof from one\'s society\",\"to shun one\"]},\"deriv\":\"from G1537 and G2827\",\"pronun\":{\"ipa\":\"ɛkˈkli.no\",\"ipa_mod\":\"e̞kˈkli.now\",\"sbl\":\"ekklinō\",\"dic\":\"ek-KLEE-noh\",\"dic_mod\":\"ake-KLEE-noh\"},\"see\":[\"G1537\",\"G2827\"]}', 'avoid, eschew, go out of the way'),
(1579, 1579, 'ἐκκολυμβάω', '{\"def\":{\"short\":\"to escape by swimming\",\"long\":[\"to swim out of\"]},\"deriv\":\"from G1537 and G2860\",\"pronun\":{\"ipa\":\"ɛk.ko.lymˈβɑ.o\",\"ipa_mod\":\"e̞k.kow.ljuɱˈvɑ.ow\",\"sbl\":\"ekkolymbaō\",\"dic\":\"ek-koh-loom-VA-oh\",\"dic_mod\":\"ake-koh-lyoom-VA-oh\"},\"see\":[\"G1537\",\"G2860\"]}', 'swim out'),
(1580, 1580, 'ἐκκομίζω', '{\"def\":{\"short\":\"to bear forth (to burial)\",\"long\":[\"to carry out\",[\"a dead man for burial\"]]},\"deriv\":\"from G1537 and G2865\",\"pronun\":{\"ipa\":\"ɛk.koˈmi.zo\",\"ipa_mod\":\"e̞k.kowˈmi.zow\",\"sbl\":\"ekkomizō\",\"dic\":\"ek-koh-MEE-zoh\",\"dic_mod\":\"ake-koh-MEE-zoh\"},\"see\":[\"G1537\",\"G2865\"]}', 'carry out'),
(1581, 1581, 'ἐκκόπτω', '{\"def\":{\"short\":\"to exscind; figuratively, to frustrate\",\"long\":[\"to cut out, cut off\",[\"of a tree\"],\"metaphorically to cut off occasion\"]},\"deriv\":\"from G1537 and G2875\",\"pronun\":{\"ipa\":\"ɛkˈko.pto\",\"ipa_mod\":\"e̞kˈkow.ptow\",\"sbl\":\"ekkoptō\",\"dic\":\"ek-KOH-ptoh\",\"dic_mod\":\"ake-KOH-ptoh\"},\"see\":[\"G1537\",\"G2875\"]}', 'cut down (off, out), hew down, hinder'),
(1582, 1582, 'ἐκκρέμαμαι', '{\"def\":{\"short\":\"to hang upon the lips of a speaker, i.e., listen closely\",\"long\":[\"to hang from\"]},\"deriv\":\"middle voice from G1537 and G2910\",\"pronun\":{\"ipa\":\"ɛkˈkrɛ.mɑ.mɛ\",\"ipa_mod\":\"e̞kˈkre̞.mɑ.me\",\"sbl\":\"ekkremamai\",\"dic\":\"ek-KREH-ma-meh\",\"dic_mod\":\"ake-KRAY-ma-may\"},\"see\":[\"G1537\",\"G2910\"]}', 'be very attentive'),
