(1599, 1599, 'ἐκπέμπω', '{\"def\":{\"short\":\"to despatch\",\"long\":[\"to send away, send forth\"]},\"deriv\":\"from G1537 and G3992\",\"pronun\":{\"ipa\":\"ɛkˈpɛm.po\",\"ipa_mod\":\"e̞kˈpe̞m.pow\",\"sbl\":\"ekpempō\",\"dic\":\"ek-PEM-poh\",\"dic_mod\":\"ake-PAME-poh\"},\"see\":[\"G1537\",\"G3992\"]}', 'send away (forth)'),
(1600, 1600, 'ἐκπετάννυμι', '{\"def\":{\"short\":\"to fly out, i.e., (by analogy) to extend\",\"long\":[\"to spread out, stretch forth\"]},\"deriv\":\"from G1537 and a form of G4072\",\"pronun\":{\"ipa\":\"ɛk.pɛˈtɑn.ny.mi\",\"ipa_mod\":\"e̞k.pe̞ˈtɑn.nju.mi\",\"sbl\":\"ekpetannymi\",\"dic\":\"ek-peh-TAHN-noo-mee\",\"dic_mod\":\"ake-pay-TAHN-nyoo-mee\"},\"see\":[\"G1537\",\"G4072\"]}', 'stretch forth'),
(1601, 1601, 'ἐκπίπτω', '{\"def\":{\"short\":\"to drop away; specially, be driven out of one\'s course; figuratively, to lose, become inefficient\",\"long\":[\"to fall out of, to fall down from, to fall off\",\"metaphorically\",[\"to fall from a thing, to lose it\",\"to perish, to fall\",[\"to fall from a place from which one cannot keep\",\"fall from a position\",\"to fall powerless, to fall to the ground, be without effect\",[\"of the divine promise of salvation\"]]]]},\"deriv\":\"from G1537 and G4098\",\"pronun\":{\"ipa\":\"ɛkˈpi.pto\",\"ipa_mod\":\"e̞kˈpi.ptow\",\"sbl\":\"ekpiptō\",\"dic\":\"ek-PEE-ptoh\",\"dic_mod\":\"ake-PEE-ptoh\"},\"see\":[\"G1537\",\"G4098\"]}', 'be cast, fail, fall (away, off), take none effect'),
