(1605, 1605, 'ἐκπλήσσω', '{\"def\":{\"short\":\"to strike with astonishment\",\"long\":[\"to strike out, expel by a blow, drive out or away\",\"to cast off by a blow, to drive out\",[\"commonly, to strike one out of self-possession, to strike with panic, shock, astonish\"],\"to be struck with amazement, astonished, amazed\"]},\"deriv\":\"from G1537 and G4141\",\"pronun\":{\"ipa\":\"ɛkˈples.so\",\"ipa_mod\":\"e̞kˈple̞s.sow\",\"sbl\":\"ekplēssō\",\"dic\":\"ek-PLASE-soh\",\"dic_mod\":\"ake-PLASE-soh\"},\"see\":[\"G1537\",\"G4141\"]}', 'amaze, astonish'),
(1606, 1606, 'ἐκπνέω', '{\"def\":{\"short\":\"to expire\",\"long\":[\"to breathe out, breathe out one\'s life, breathe one\'s last, expire\"]},\"deriv\":\"from G1537 and G4154\",\"pronun\":{\"ipa\":\"ɛkˈpnɛ.o\",\"ipa_mod\":\"e̞kˈpne̞.ow\",\"sbl\":\"ekpneō\",\"dic\":\"ek-PNEH-oh\",\"dic_mod\":\"ake-PNAY-oh\"},\"see\":[\"G1537\",\"G4154\"]}', 'give up the ghost'),
(1607, 1607, 'ἐκπορεύομαι', '{\"def\":{\"short\":\"to depart, be discharged, proceed, project\",\"long\":[\"to go forth, go out, depart\",\"metaphorically\",[\"to come forth, to issue, to proceed\",[\"of feelings, affections, deeds, sayings\"],\"to flow forth\",[\"of a river\"],\"to project, from the mouth of one\",\"to spread abroad, of a rumour\"]]},\"deriv\":\"from G1537 and G4198\",\"pronun\":{\"ipa\":\"ɛk.poˈrɛβ.o.mɛ\",\"ipa_mod\":\"e̞k.powˈrev.ow.me\",\"sbl\":\"ekporeuomai\",\"dic\":\"ek-poh-REV-oh-meh\",\"dic_mod\":\"ake-poh-RAVE-oh-may\"},\"see\":[\"G1537\",\"G4198\"]}', 'come (forth, out of), depart, go (forth, out), issue, proceed (out of)'),
(1608, 1608, 'ἐκπορνεύω', '{\"def\":{\"short\":\"to be utterly unchaste\",\"long\":[\"to go a whoring, \\\"give one\'s self over to fornication\\\"\"]},\"deriv\":\"from G1537 and G4203\",\"pronun\":{\"ipa\":\"ɛk.porˈnɛβ.o\",\"ipa_mod\":\"e̞k.powrˈnev.ow\",\"sbl\":\"ekporneuō\",\"dic\":\"ek-pore-NEV-oh\",\"dic_mod\":\"ake-pore-NAVE-oh\"},\"see\":[\"G1537\",\"G4203\"]}', 'give self over to fornication'),
