(1658, 1658, 'ἐλεύθερος', '{\"def\":{\"short\":\"unrestrained (to go at pleasure), i.e., (as a citizen) not a slave (whether freeborn or manumitted), or (genitive case) exempt (from obligation or liability)\",\"long\":[\"freeborn\",[\"in a civil sense, one who is not a slave\",\"of one who ceases to be a slave, freed, manumitted\"],\"free, exempt, unrestrained, not bound by an obligation\",\"in an ethical sense: free from the yoke of the Mosaic Law\"]},\"deriv\":\"probably from the alternate of G2064\",\"pronun\":{\"ipa\":\"ɛˈlɛβ.θɛ.ros\",\"ipa_mod\":\"e̞ˈlef.θe̞.rows\",\"sbl\":\"eleutheros\",\"dic\":\"eh-LEV-theh-rose\",\"dic_mod\":\"ay-LAYF-thay-rose\"},\"see\":[\"G2064\"]}', 'free (man, woman), at liberty'),
(1659, 1659, 'ἐλευθερόω', '{\"def\":{\"short\":\"to liberate, i.e., (figuratively) to exempt (from moral, ceremonial or mortal liability)\",\"long\":[\"to make free\",\"set at liberty: from the dominion of sin\"]},\"deriv\":\"from G1658\",\"pronun\":{\"ipa\":\"ɛ.lɛβ.θɛˈro.o\",\"ipa_mod\":\"e̞.lef.θe̞ˈrow.ow\",\"sbl\":\"eleutheroō\",\"dic\":\"eh-lev-theh-ROH-oh\",\"dic_mod\":\"ay-layf-thay-ROH-oh\"},\"see\":[\"G1658\"]}', 'deliver, make free'),
(1660, 1660, 'ἔλευσις', '{\"def\":{\"short\":\"an advent\",\"long\":[\"a coming, advent\"]},\"deriv\":\"from the alternate of G2064\",\"pronun\":{\"ipa\":\"ˈɛ.lɛβ.sis\",\"ipa_mod\":\"ˈe̞.lef.sis\",\"sbl\":\"eleusis\",\"dic\":\"EH-lev-sees\",\"dic_mod\":\"A-layf-sees\"},\"see\":[\"G2064\"]}', 'coming'),
(1661, 1661, 'ἐλεφάντινος', '{\"def\":{\"short\":\"elephantine, i.e., (by implication) composed of ivory\",\"long\":[\"of ivory\"]},\"deriv\":\"from ἔλεφας (an \\\"elephant\\\")\",\"pronun\":{\"ipa\":\"ɛ.lɛˈfɑn.ti.nos\",\"ipa_mod\":\"e̞.le̞ˈfɑn.ti.nows\",\"sbl\":\"elephantinos\",\"dic\":\"eh-leh-FAHN-tee-nose\",\"dic_mod\":\"ay-lay-FAHN-tee-nose\"}}', 'of ivory'),
(1662, 1662, 'Ἐλιακείμ', '{\"def\":{\"lit\":\"raising up by God\",\"short\":\"Eliakim, an Israelite\",\"long\":[\"the eldest son of Abiud or Judah, bother of Joseph, and father of Azor Matthew 1:13\",\"son of Melea, and father of Jonan Luke 3:30,31\"]},\"deriv\":\"of Hebrew origin (H0471)\",\"pronun\":{\"ipa\":\"ɛ.li.ɑˈkim\",\"ipa_mod\":\"e̞.li.ɑˈcim\",\"sbl\":\"eliakeim\",\"dic\":\"eh-lee-ah-KEEM\",\"dic_mod\":\"ay-lee-ah-KEEM\"},\"see\":[\"H0471\"]}', 'Eliakim'),
(1663, 1663, 'Ἐλιέζερ', '{\"def\":{\"lit\":\"God is his help\",\"short\":\"Eliezer, an Israelite\",\"long\":[\"the son of Jorim, in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H0461)\",\"pronun\":{\"ipa\":\"ɛ.liˈɛ.zɛr\",\"ipa_mod\":\"e̞.liˈe̞.ze̞r\",\"sbl\":\"eliezer\",\"dic\":\"eh-lee-EH-zer\",\"dic_mod\":\"ay-lee-A-zare\"},\"see\":[\"H0461\"]}', 'Eliezer'),
(1664, 1664, 'Ἐλιούδ', '{\"def\":{\"lit\":\"God his Praise\",\"short\":\"God of majesty; Eliud, an Israelite\",\"long\":[\"the son of Achim in the genealogy of Christ\"]},\"deriv\":\"of Hebrew origin (H0410 and H1935)\",\"pronun\":{\"ipa\":\"ɛ.liˈuð\",\"ipa_mod\":\"e̞.liˈuð\",\"sbl\":\"elioud\",\"dic\":\"eh-lee-OOTH\",\"dic_mod\":\"ay-lee-OOTH\"},\"see\":[\"H0410\",\"H1935\"]}', 'Eliud'),
(1665, 1665, 'Ἐλισάβετ', '{\"def\":{\"lit\":\"oath of God\",\"short\":\"Elisabet, an Israelitess\",\"long\":[\"the wife of Zacharias and mother of John the Baptist, of the priestly family, and a relative of Mary, Luke 1:36\"]},\"deriv\":\"of Hebrew origin (H0472)\",\"pronun\":{\"ipa\":\"ɛ.liˈsɑ.βɛt\",\"ipa_mod\":\"e̞.liˈsɑ.ve̞t\",\"sbl\":\"elisabet\",\"dic\":\"eh-lee-SA-vet\",\"dic_mod\":\"ay-lee-SA-vate\"},\"see\":[\"H0472\"]}', 'Elisabeth'),
(1666, 1666, 'Ἐλισσαῖος', '{\"def\":{\"lit\":\"God his salvation\",\"short\":\"Elissaeus, an Israelite\",\"long\":[\"a distinguished Old Testament prophet, the disciple, companion, and successor of Elijah\"]},\"deriv\":\"of Hebrew origin (H0477)\",\"pronun\":{\"ipa\":\"ɛ.lisˈsɛ.os\",\"ipa_mod\":\"e̞.lisˈse.ows\",\"sbl\":\"elissaios\",\"dic\":\"eh-lees-SEH-ose\",\"dic_mod\":\"ay-lees-SAY-ose\"},\"see\":[\"H0477\"]}', 'Elissaeus');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1667, 1667, 'ἑλίσσω', '{\"def\":{\"short\":\"to coil or wrap\",\"long\":[\"to roll up, fold together\"]},\"deriv\":\"a form of G1507\",\"pronun\":{\"ipa\":\"hɛˈlis.so\",\"ipa_mod\":\"e̞ˈlis.sow\",\"sbl\":\"helissō\",\"dic\":\"heh-LEES-soh\",\"dic_mod\":\"ay-LEES-soh\"},\"see\":[\"G1507\"]}', 'fold up'),
(1668, 1668, 'ἕλκος', '{\"def\":{\"short\":\"an ulcer (as if drawn together)\",\"long\":[\"a wound, especially a wound producing a discharge pus\",\"a sore, an ulcer\"]},\"deriv\":\"probably from G1670\",\"pronun\":{\"ipa\":\"ˈhɛl.kos\",\"ipa_mod\":\"ˈe̞l.kows\",\"sbl\":\"helkos\",\"dic\":\"HEL-kose\",\"dic_mod\":\"ALE-kose\"},\"see\":[\"G1670\"]}', 'sore'),
(1669, 1669, 'ἑλκόω', '{\"def\":{\"short\":\"to cause to ulcerate, i.e., (passively) be ulcerous\",\"long\":[\"to make sore, cause to ulcerate\",\"to be ulcerated\",\"full of sores\"]},\"deriv\":\"from G1668\",\"pronun\":{\"ipa\":\"hɛlˈko.o\",\"ipa_mod\":\"e̞lˈkow.ow\",\"sbl\":\"helkoō\",\"dic\":\"hel-KOH-oh\",\"dic_mod\":\"ale-KOH-oh\"},\"see\":[\"G1668\"]}', 'full of sores'),
(1670, 1670, 'ἑλκύω', '{\"def\":{\"short\":\"to drag (literally or figuratively)\",\"long\":[\"to draw, drag off\",\"metaphorically, to draw by inward power, lead, impel\"]},\"deriv\":\"probably akin to G0138\",\"pronun\":{\"ipa\":\"hɛlˈky.o\",\"ipa_mod\":\"e̞lˈkju.ow\",\"sbl\":\"helkyō\",\"dic\":\"hel-KOO-oh\",\"dic_mod\":\"ale-KYOO-oh\"},\"see\":[\"G0138\",\"G1667\"],\"comment\":\"Compare G1667.\"}', 'draw'),
(1671, 1671, 'Ἑλλάς', '{\"def\":{\"lit\":\"unstable, the miry one\",\"short\":\"Hellas (or Greece), a country of Europe\",\"long\":[\"a country in southern Europe\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"hɛlˈlɑs\",\"ipa_mod\":\"e̞lˈlɑs\",\"sbl\":\"hellas\",\"dic\":\"hel-LAHS\",\"dic_mod\":\"ale-LAHS\"}}', 'Greece'),
(1672, 1672, 'Ἕλλην', '{\"def\":{\"short\":\"a Hellen (Grecian) or inhabitant of Hellas; by extension a Greek-speaking person, especially a non-Jew\",\"long\":[\"a Greek either by nationality, whether a native of the main land or of the Greek islands or colonies\",\"in a wider sense the name embraces all nations not Jews that made the language, customs, and learning of the Greeks their own; the primary reference is to a difference of religion and worship\"]},\"deriv\":\"from G1671\",\"pronun\":{\"ipa\":\"ˈhɛl.len\",\"ipa_mod\":\"ˈe̞l.le̞n\",\"sbl\":\"hellēn\",\"dic\":\"HEL-lane\",\"dic_mod\":\"ALE-lane\"},\"see\":[\"G1671\"]}', 'Gentile, Greek'),
(1673, 1673, 'Ἑλληνικός', '{\"def\":{\"short\":\"Hellenic, i.e., Grecian (in language)\",\"long\":[\"a Grecian\"]},\"deriv\":\"from G1672\",\"pronun\":{\"ipa\":\"hɛl.len.iˈkos\",\"ipa_mod\":\"e̞l.le̞n.iˈkows\",\"sbl\":\"hellēnikos\",\"dic\":\"hel-lane-ee-KOSE\",\"dic_mod\":\"ale-lane-ee-KOSE\"},\"see\":[\"G1672\"]}', 'Greek'),
(1674, 1674, 'Ἑλληνίς', '{\"def\":{\"short\":\"a Grecian (i.e., non-Jewish) woman\",\"long\":[\"a Greek women\",\"a Gentile women, not a Jewess\"]},\"deriv\":\"feminine of G1672\",\"pronun\":{\"ipa\":\"hɛl.lenˈis\",\"ipa_mod\":\"e̞l.le̞nˈis\",\"sbl\":\"hellēnis\",\"dic\":\"hel-lane-EES\",\"dic_mod\":\"ale-lane-EES\"},\"see\":[\"G1672\"]}', 'Greek'),
(1675, 1675, 'Ἑλληνιστής', '{\"def\":{\"short\":\"a Hellenist or Greek-speaking Jew\",\"long\":[\"a Hellenist\",[\"one who imitates the manners and customs or the worship of the Greeks, and use the Greek tongue\",\"used in the New Testament of Jews born in foreign lands and speaking Greek\"]]},\"deriv\":\"from a derivative of G1672\",\"pronun\":{\"ipa\":\"hɛl.len.iˈstes\",\"ipa_mod\":\"e̞l.le̞n.iˈste̞s\",\"sbl\":\"hellēnistēs\",\"dic\":\"hel-lane-ee-STASE\",\"dic_mod\":\"ale-lane-ee-STASE\"},\"see\":[\"G1672\"]}', 'Grecian'),
(1676, 1676, 'Ἑλληνιστί', '{\"def\":{\"short\":\"Hellenistically, i.e., in the Grecian language\",\"long\":[\"in Greek, the Greek language\"]},\"deriv\":\"adverb from the same as G1675\",\"pronun\":{\"ipa\":\"hɛl.len.iˈsti\",\"ipa_mod\":\"e̞l.le̞n.iˈsti\",\"sbl\":\"hellēnisti\",\"dic\":\"hel-lane-ee-STEE\",\"dic_mod\":\"ale-lane-ee-STEE\"},\"see\":[\"G1675\"]}', 'Greek'),
(1677, 1677, 'ἐλλογέω', '{\"def\":{\"short\":\"to reckon in, i.e., attribute\",\"long\":[\"to reckon in, set to one\'s account, lay to one\'s charge, impute\"]},\"deriv\":\"from G1722 and G3056 (in the sense of account)\",\"pronun\":{\"ipa\":\"ɛl.loˈɣɛ.o\",\"ipa_mod\":\"e̞l.lowˈɣe̞.ow\",\"sbl\":\"ellogeō\",\"dic\":\"el-loh-GEH-oh\",\"dic_mod\":\"ale-loh-GAY-oh\"},\"see\":[\"G1722\",\"G3056\"]}', 'impute, put on account'),
(1678, 1678, 'Ἐλμωδάμ', '{\"def\":{\"lit\":\"measure\",\"short\":\"Elmodam, an Israelite\",\"long\":[\"son of Er, in the genealogy of Joseph, Luke 3:28\"]},\"deriv\":\"of Hebrew origin (perhaps for H0486)\",\"pronun\":{\"ipa\":\"ɛl.moˈðɑm\",\"ipa_mod\":\"e̞l.mowˈðɑm\",\"sbl\":\"elmōdam\",\"dic\":\"el-moh-THAHM\",\"dic_mod\":\"ale-moh-THAHM\"},\"see\":[\"H0486\"]}', 'Elmodam'),
(1679, 1679, 'ἐλπίζω', '{\"def\":{\"short\":\"to expect or confide\",\"long\":[\"to hope\",[\"in a religious sense, to wait for salvation with joy and full confidence\"],\"hopefully to trust in\"]},\"deriv\":\"from G1680\",\"pronun\":{\"ipa\":\"ɛlˈpi.zo\",\"ipa_mod\":\"e̞lˈpi.zow\",\"sbl\":\"elpizō\",\"dic\":\"el-PEE-zoh\",\"dic_mod\":\"ale-PEE-zoh\"},\"see\":[\"G1680\"]}', '(have, thing) hope(-d) (for), trust'),
(1680, 1680, 'ἐλπίς', '{\"def\":{\"short\":\"expectation (abstractly or concretely) or confidence\",\"long\":[\"expectation of evil, fear\",\"expectation of good, hope\",[\"in the Christian sense\",[\"joyful and confident expectation of eternal salvation\"]],\"on hope, in hope, having hope\",[\"the author of hope, or he who is its foundation\",\"the thing hoped for\"]]},\"deriv\":\"from a primary ἔλπω (to anticipate, usually with pleasure)\",\"pronun\":{\"ipa\":\"ɛlˈpis\",\"ipa_mod\":\"e̞lˈpis\",\"sbl\":\"elpis\",\"dic\":\"el-PEES\",\"dic_mod\":\"ale-PEES\"}}', 'faith, hope'),
(1681, 1681, 'Ἐλύμας', '{\"def\":{\"lit\":\"a wise man\",\"short\":\"Elymas, a wizard\",\"long\":[\"the Arabic name of the Jewish magnus or sorcerer Barjesus, Acts 13:6\"]},\"deriv\":\"of foreign origin\",\"pronun\":{\"ipa\":\"ɛˈly.mɑs\",\"ipa_mod\":\"e̞ˈlju.mɑs\",\"sbl\":\"elymas\",\"dic\":\"eh-LOO-mahs\",\"dic_mod\":\"ay-LYOO-mahs\"}}', 'Elymas'),
(1682, 1682, 'ἐλοΐ', '{\"def\":{\"lit\":\"my God\",\"short\":\"my God\",\"long\":[\"Aramaic for the phrase \\\"my God\\\"\"]},\"deriv\":\"of Chaldean origin (H0426 with pronominal suffix) my God\",\"pronun\":{\"ipa\":\"ɛ.loˈi\",\"ipa_mod\":\"e̞.lowˈi\",\"sbl\":\"eloi\",\"dic\":\"eh-loh-EE\",\"dic_mod\":\"ay-loh-EE\"},\"see\":[\"H0426\"]}', 'Eloi'),
(1683, 1683, 'ἐμαυτοῦ', '{\"def\":{\"short\":\"of myself so likewise the dative case ἐμαυτῷ <pronunciation strongs=\\\"em-ow-to\'\\\"/>, and accusative case ἐμαυτόν <pronunciation strongs=\\\"em-ow-ton\'\\\"/>\",\"long\":[\"I, me, myself etc.\"]},\"deriv\":\"genitive case compound of G1700 and G0846\",\"pronun\":{\"ipa\":\"ɛ.mɑβˈtu\",\"ipa_mod\":\"e̞.mɑfˈtu\",\"sbl\":\"emautou\",\"dic\":\"eh-mav-TOO\",\"dic_mod\":\"ay-maf-TOO\"},\"see\":[\"G0846\",\"G1700\"]}', 'me, mine own (self), myself'),
(1684, 1684, 'ἐμβαίνω', '{\"def\":{\"short\":\"to walk on, i.e., embark (aboard a vessel), reach (a pool)\",\"long\":[\"to go into, step into\"]},\"deriv\":\"from G1722 and the base of G0939\",\"pronun\":{\"ipa\":\"ɛmˈβɛ.no\",\"ipa_mod\":\"e̞ɱˈve.now\",\"sbl\":\"embainō\",\"dic\":\"em-VEH-noh\",\"dic_mod\":\"ame-VAY-noh\"},\"see\":[\"G0939\",\"G1722\"]}', 'come (get) into, enter (into), go (up) into, step in, take ship'),
(1685, 1685, 'ἐμβάλλω', '{\"def\":{\"short\":\"to throw on, i.e., (figuratively) subject to (eternal punishment)\",\"long\":[\"to throw in, cast into\"]},\"deriv\":\"from G1722 and G0906\",\"pronun\":{\"ipa\":\"ɛmˈβɑl.lo\",\"ipa_mod\":\"e̞ɱˈvɑl.low\",\"sbl\":\"emballō\",\"dic\":\"em-VAHL-loh\",\"dic_mod\":\"ame-VAHL-loh\"},\"see\":[\"G0906\",\"G1722\"]}', 'cast into'),
(1686, 1686, 'ἐμβάπτω', '{\"def\":{\"short\":\"to whelm on, i.e., wet (a part of the person, etc.) by contact with a fluid\",\"long\":[\"to dip in\"]},\"deriv\":\"from G1722 and G0911\",\"pronun\":{\"ipa\":\"ɛmˈβɑ.pto\",\"ipa_mod\":\"e̞ɱˈvɑ.ptow\",\"sbl\":\"embaptō\",\"dic\":\"em-VA-ptoh\",\"dic_mod\":\"ame-VA-ptoh\"},\"see\":[\"G0911\",\"G1722\"]}', 'dip'),
(1687, 1687, 'ἐμβατεύω', '{\"def\":{\"short\":\"equivalent to G1684; to intrude on (figuratively)\",\"long\":[\"to enter, to frequent, haunt\",[\"often of the gods frequenting favorite sports\",\"often to come into possession of a thing\",\"to invade, make hostile incursion into\"],\"to enter\",[\"to go into details in narrating\",\"to investigate, search into, scrutinise minutely\"]]},\"deriv\":\"from G1722 and a presumed derivative of the base of G0939\",\"pronun\":{\"ipa\":\"ɛm.βɑˈtɛβ.o\",\"ipa_mod\":\"e̞ɱ.vɑˈtev.ow\",\"sbl\":\"embateuō\",\"dic\":\"em-va-TEV-oh\",\"dic_mod\":\"ame-va-TAVE-oh\"},\"see\":[\"G0939\",\"G1684\",\"G1722\"]}', 'intrude into'),
(1688, 1688, 'ἐμβιβάζω', '{\"def\":{\"short\":\"to place on, i.e., transfer (aboard a vessel)\",\"long\":[\"to put in or on, lead in, cause to enter\"]},\"deriv\":\"from G1722 and βιβάζω (to mount; causative of G1684)\",\"pronun\":{\"ipa\":\"ɛm.βiˈβɑ.zo\",\"ipa_mod\":\"e̞ɱ.viˈvɑ.zow\",\"sbl\":\"embibazō\",\"dic\":\"em-vee-VA-zoh\",\"dic_mod\":\"ame-vee-VA-zoh\"},\"see\":[\"G1684\",\"G1722\"]}', 'put in'),
(1689, 1689, 'ἐμβλέπω', '{\"def\":{\"short\":\"to look on, i.e., (relatively) to observe fixedly, or (absolutely) to discern clearly\",\"long\":[\"to turn one\'s eyes on\",\"look at\",\"metaphorically to look at with the mind, to consider\"]},\"deriv\":\"from G1722 and G0991\",\"pronun\":{\"ipa\":\"ɛmˈβlɛ.po\",\"ipa_mod\":\"e̞ɱˈvle̞.pow\",\"sbl\":\"emblepō\",\"dic\":\"em-VLEH-poh\",\"dic_mod\":\"ame-VLAY-poh\"},\"see\":[\"G0991\",\"G1722\"]}', 'behold, gaze up, look upon, (could) see'),
(1690, 1690, 'ἐμβριμάομαι', '{\"def\":{\"short\":\"to have indignation on, i.e., (transitively) to blame, (intransitively) to sigh with chagrin, (specially) to sternly enjoin\",\"long\":[\"to charge with earnest admonition, sternly to charge, threatened to enjoin\"]},\"deriv\":\"from G1722 and βριμάομαι (to snort with anger)\",\"pronun\":{\"ipa\":\"ɛm.βriˈmɑ.o.mɛ\",\"ipa_mod\":\"e̞ɱ.vriˈmɑ.ow.me\",\"sbl\":\"embrimaomai\",\"dic\":\"em-vree-MA-oh-meh\",\"dic_mod\":\"ame-vree-MA-oh-may\"},\"see\":[\"G1722\"]}', 'straitly charge, groan, murmur against'),
(1691, 1691, 'ἐμέ', '{\"def\":{\"short\":\"me\",\"long\":[\"I, me, myself, etc.\"]},\"deriv\":\"a prolonged form of G3165\",\"pronun\":{\"ipa\":\"ɛˈmɛ\",\"ipa_mod\":\"e̞ˈme̞\",\"sbl\":\"eme\",\"dic\":\"eh-MEH\",\"dic_mod\":\"ay-MAY\"},\"see\":[\"G3165\"]}', 'I, me, my(-self)'),
(1692, 1692, 'ἐμέω', '{\"def\":{\"short\":\"to vomit\",\"long\":[\"to vomit, vomit forth, throw up\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ɛˈmɛ.o\",\"ipa_mod\":\"e̞ˈme̞.ow\",\"sbl\":\"emeō\",\"dic\":\"eh-MEH-oh\",\"dic_mod\":\"ay-MAY-oh\"}}', '(will) spue'),
(1693, 1693, 'ἐμμαίνομαι', '{\"def\":{\"short\":\"to rave on, i.e., rage at\",\"long\":[\"to rage against one\"]},\"deriv\":\"from G1722 and G3105\",\"pronun\":{\"ipa\":\"ɛmˈmɛ.no.mɛ\",\"ipa_mod\":\"e̞mˈme.now.me\",\"sbl\":\"emmainomai\",\"dic\":\"em-MEH-noh-meh\",\"dic_mod\":\"ame-MAY-noh-may\"},\"see\":[\"G1722\",\"G3105\"]}', 'be mad against'),
(1694, 1694, 'Ἐμμανουήλ', '{\"def\":{\"lit\":\"God with us\",\"short\":\"God with us; Emmanuel, a name of Christ\",\"long\":[\"the title applied to the Messiah, born of the virgin, Matthew 1:23, Isaiah 7:14, because Jesus was God united with man, and showed that God was dwelling with man\"]},\"deriv\":\"of Hebrew origin (H6005)\",\"pronun\":{\"ipa\":\"ɛm.mɑ.nuˈel\",\"ipa_mod\":\"e̞m.mɑ.nuˈe̞l\",\"sbl\":\"emmanouēl\",\"dic\":\"em-ma-noo-ALE\",\"dic_mod\":\"ame-ma-noo-ALE\"},\"see\":[\"H6005\"]}', 'Emmanuel'),
(1695, 1695, 'Ἐμμαούς', '{\"def\":{\"lit\":\"warm baths\",\"short\":\"Emmaus, a place in Palestine\",\"long\":[\"the village to which the two disciples were going when our Lord appeared to them on the way, on the day of resurrection\"]},\"deriv\":\"probably of Hebrew origin (compare H3222)\",\"pronun\":{\"ipa\":\"ɛm.mɑˈus\",\"ipa_mod\":\"e̞m.mɑˈus\",\"sbl\":\"emmaous\",\"dic\":\"em-ma-OOS\",\"dic_mod\":\"ame-ma-OOS\"},\"see\":[\"H3222\"],\"comment\":\"Luke indicates its distance from Jerusalem as sixty stadia or 7.5 miles (12 km). Josephus mentions \\\"a village called Emmaus\\\" at the same distance. The site of Emmaus remains yet to be identified.\"}', 'Emmaus'),
(1696, 1696, 'ἐμμένω', '{\"def\":{\"short\":\"to stay in the same place, i.e., (figuratively) persevere\",\"long\":[\"to remain in, continue\",\"to persevere in anything, a state of mind etc.\",\"to hold fast, to be true to, abide by, keep\"]},\"deriv\":\"from G1722 and G3306\",\"pronun\":{\"ipa\":\"ɛmˈmɛ.no\",\"ipa_mod\":\"e̞mˈme̞.now\",\"sbl\":\"emmenō\",\"dic\":\"em-MEH-noh\",\"dic_mod\":\"ame-MAY-noh\"},\"see\":[\"G1722\",\"G3306\"]}', 'continue'),
(1697, 1697, 'Ἐμμόρ', '{\"def\":{\"lit\":\"an ass\",\"short\":\"Emmor (i.e., Chamor), a Canaanite\",\"long\":[\"the father of Sychem, Acts 7:16\"]},\"deriv\":\"of Hebrew origin (H2544)\",\"pronun\":{\"ipa\":\"ɛmˈmor\",\"ipa_mod\":\"e̞mˈmowr\",\"sbl\":\"emmor\",\"dic\":\"em-MORE\",\"dic_mod\":\"ame-MORE\"},\"see\":[\"H2544\"]}', 'Emmor'),
(1698, 1698, 'ἐμοί', '{\"def\":{\"short\":\"to me\",\"long\":[\"I, me, myself, etc.\"]},\"deriv\":\"a prolonged form of G3427\",\"pronun\":{\"ipa\":\"ɛˈmy\",\"ipa_mod\":\"e̞ˈmy\",\"sbl\":\"emoi\",\"dic\":\"eh-MOO\",\"dic_mod\":\"ay-MOO\"},\"see\":[\"G3427\"]}', 'I, me, mine, my'),
(1699, 1699, 'ἐμός', '{\"def\":{\"short\":\"my\",\"long\":[\"my, mine, etc.\"]},\"deriv\":\"from the oblique cases of G1473 (G1698, G1700, G1691)\",\"pronun\":{\"ipa\":\"ɛˈmos\",\"ipa_mod\":\"e̞ˈmows\",\"sbl\":\"emos\",\"dic\":\"eh-MOSE\",\"dic_mod\":\"ay-MOSE\"},\"see\":[\"G1473\",\"G1691\",\"G1698\",\"G1700\"]}', 'of me, mine (own), my'),
(1700, 1700, 'ἐμοῦ', '{\"def\":{\"short\":\"of me\",\"long\":[\"me, my, mine, etc.\"]},\"deriv\":\"a prolonged form of G3449\",\"pronun\":{\"ipa\":\"ɛˈmu\",\"ipa_mod\":\"e̞ˈmu\",\"sbl\":\"emou\",\"dic\":\"eh-MOO\",\"dic_mod\":\"ay-MOO\"},\"see\":[\"G3449\"]}', 'me, mine, my'),
(1701, 1701, 'ἐμπαιγμός', '{\"def\":{\"short\":\"derision\",\"long\":[\"a mocking, scoffing\"]},\"deriv\":\"from G1702\",\"pronun\":{\"ipa\":\"ɛm.pɛɣˈmos\",\"ipa_mod\":\"e̞m.peɣˈmows\",\"sbl\":\"empaigmos\",\"dic\":\"em-peg-MOSE\",\"dic_mod\":\"ame-page-MOSE\"},\"see\":[\"G1702\"]}', 'mocking'),
(1702, 1702, 'ἐμπαίζω', '{\"def\":{\"short\":\"to jeer at, i.e., deride\",\"long\":[\"to play with, trifle with\",[\"to mock\",\"to delude, deceive\"]]},\"deriv\":\"from G1722 and G3815\",\"pronun\":{\"ipa\":\"ɛmˈpɛ.zo\",\"ipa_mod\":\"e̞mˈpe.zow\",\"sbl\":\"empaizō\",\"dic\":\"em-PEH-zoh\",\"dic_mod\":\"ame-PAY-zoh\"},\"see\":[\"G1722\",\"G3815\"]}', 'mock'),
(1703, 1703, 'ἐμπαίκτης', '{\"def\":{\"short\":\"a derider, i.e., (by implication) a false teacher\",\"long\":[\"a mocker, a scoffer\"]},\"deriv\":\"from G1702\",\"pronun\":{\"ipa\":\"ɛmˈpɛk.tes\",\"ipa_mod\":\"e̞mˈpek.te̞s\",\"sbl\":\"empaiktēs\",\"dic\":\"em-PEK-tase\",\"dic_mod\":\"ame-PAKE-tase\"},\"see\":[\"G1702\"]}', 'mocker, scoffer'),
