(1711, 1711, 'ἐμπορία', '{\"def\":{\"short\":\"traffic\",\"long\":[\"trade, merchandise\"]},\"deriv\":\"feminine from G1713\",\"pronun\":{\"ipa\":\"ɛm.poˈri.ɑ\",\"ipa_mod\":\"e̞m.powˈri.ɑ\",\"sbl\":\"emporia\",\"dic\":\"em-poh-REE-ah\",\"dic_mod\":\"ame-poh-REE-ah\"},\"see\":[\"G1713\"]}', 'merchandise'),
