(1714, 1714, 'ἐμπρήθω', '{\"def\":{\"short\":\"to enkindle, i.e., set on fire\",\"long\":[\"to burn\",\"destroy by fire\"]},\"deriv\":\"from G1722 and πρήθω (to blow a flame)\",\"pronun\":{\"ipa\":\"ɛmˈpre.θo\",\"ipa_mod\":\"e̞mˈpre̞.θow\",\"sbl\":\"emprēthō\",\"dic\":\"em-PRAY-thoh\",\"dic_mod\":\"ame-PRAY-thoh\"},\"see\":[\"G1722\"]}', 'burn up'),
(1715, 1715, 'ἔμπροσθεν', '{\"def\":{\"short\":\"in front of (in place (literally or figuratively) or time)\",\"long\":[\"in front, before\",[\"before, i.e., in that local region which is in front of a person or thing\",\"before, in the presence of, i.e., opposite to, over against one\",\"before, in the sight of\",\"before, denoting rank\"]]},\"deriv\":\"from G1722 and G4314\",\"pronun\":{\"ipa\":\"ˈɛm.pro.sθɛn\",\"ipa_mod\":\"ˈe̞m.prow.sθe̞n\",\"sbl\":\"emprosthen\",\"dic\":\"EM-proh-sthen\",\"dic_mod\":\"AME-proh-sthane\"},\"see\":[\"G1722\",\"G4314\"]}', 'against, at, before, (in presence, sight) of'),
