(1782, 1782, 'ἐντεῦθεν', '{\"def\":{\"short\":\"hence (literally or figuratively); (repeated) on both sides\",\"long\":[\"from this place, hence\",\"on the one side and on the other, on each side\"]},\"deriv\":\"from the same as G1759\",\"pronun\":{\"ipa\":\"ɛnˈtɛβ.θɛn\",\"ipa_mod\":\"e̞nˈtef.θe̞n\",\"sbl\":\"enteuthen\",\"dic\":\"en-TEV-then\",\"dic_mod\":\"ane-TAYF-thane\"},\"see\":[\"G1759\"]}', '(from) hence, on either side'),
(1783, 1783, 'ἔντευξις', '{\"def\":{\"short\":\"an interview, i.e., (specially) supplication\",\"long\":[\"a falling in with, meeting with\",[\"an interview\",[\"a coming together\",\"to visit\",\"converse or for any other cause\"],\"that for which an interview is held\",[\"a conference or conversation\",\"a petition, supplication\"]]]},\"deriv\":\"from G1793\",\"pronun\":{\"ipa\":\"ˈɛn.tɛβ.k͡sis\",\"ipa_mod\":\"ˈe̞n.tef.k͡sis\",\"sbl\":\"enteuxis\",\"dic\":\"EN-tev-ksees\",\"dic_mod\":\"ANE-tayf-ksees\"},\"see\":[\"G1793\"]}', 'intercession, prayer'),
(1784, 1784, 'ἔντιμος', '{\"def\":{\"short\":\"valued (figuratively)\",\"long\":[\"held in honor, prized, precious\"]},\"deriv\":\"from G1722 and G5092\",\"pronun\":{\"ipa\":\"ˈɛn.ti.mos\",\"ipa_mod\":\"ˈe̞n.ti.mows\",\"sbl\":\"entimos\",\"dic\":\"EN-tee-mose\",\"dic_mod\":\"ANE-tee-mose\"},\"see\":[\"G1722\",\"G5092\"]}', 'dear, more honourable, precious, in reputation'),
(1785, 1785, 'ἐντολή', '{\"def\":{\"short\":\"injunction, i.e., an authoritative prescription\",\"long\":[\"an order, command, charge, precept, injunction\",[\"that which is prescribed to one by reason of his office\"],\"a commandment\",[\"a prescribed rule in accordance with which a thing is done\",[\"a precept relating to lineage, of the Mosaic precept concerning the priesthood\",\"ethically used of the commandments in the Mosaic law or Jewish tradition\"]]]},\"deriv\":\"from G1781\",\"pronun\":{\"ipa\":\"ɛn.toˈle\",\"ipa_mod\":\"e̞n.towˈle̞\",\"sbl\":\"entolē\",\"dic\":\"en-toh-LAY\",\"dic_mod\":\"ane-toh-LAY\"},\"see\":[\"G1781\"]}', 'commandment, precept');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1786, 1786, 'ἐντόπιος', '{\"def\":{\"short\":\"a resident\",\"long\":[\"a dweller in a place\",\"a resident or native of a place\"]},\"deriv\":\"from G1722 and G5117\",\"pronun\":{\"ipa\":\"ɛnˈto.pi.os\",\"ipa_mod\":\"e̞nˈtow.pi.ows\",\"sbl\":\"entopios\",\"dic\":\"en-TOH-pee-ose\",\"dic_mod\":\"ane-TOH-pee-ose\"},\"see\":[\"G1722\",\"G5117\"]}', 'of that place'),
(1787, 1787, 'ἐντός', '{\"def\":{\"short\":\"inside (adverb or noun)\",\"long\":[\"within, inside\",[\"within you i.e., in the midst of you\",\"within you i.e., your soul\"]]},\"deriv\":\"from G1722\",\"pronun\":{\"ipa\":\"ɛnˈtos\",\"ipa_mod\":\"e̞nˈtows\",\"sbl\":\"entos\",\"dic\":\"en-TOSE\",\"dic_mod\":\"ane-TOSE\"},\"see\":[\"G1722\"]}', 'within'),
(1788, 1788, 'ἐντρέπω', '{\"def\":{\"short\":\"to invert, i.e., (figuratively and reflexively) in a good sense, to respect; or in a bad one, to confound\",\"long\":[\"to shame one\",\"to be ashamed\",\"to reverence a person\",\"to turn about\"]},\"deriv\":\"from G1722 and the base of G5157\",\"pronun\":{\"ipa\":\"ɛnˈtrɛ.po\",\"ipa_mod\":\"e̞nˈtre̞.pow\",\"sbl\":\"entrepō\",\"dic\":\"en-TREH-poh\",\"dic_mod\":\"ane-TRAY-poh\"},\"see\":[\"G1722\",\"G5157\"]}', 'regard, (give) reference, shame'),
(1789, 1789, 'ἐντρέφω', '{\"def\":{\"short\":\"(figuratively) to educate\",\"long\":[\"to nourish in: a person or a thing\",\"metaphorically to educate, form the mind\"]},\"deriv\":\"from G1722 and G5142\",\"pronun\":{\"ipa\":\"ɛnˈtrɛ.fo\",\"ipa_mod\":\"e̞nˈtre̞.fow\",\"sbl\":\"entrephō\",\"dic\":\"en-TREH-foh\",\"dic_mod\":\"ane-TRAY-foh\"},\"see\":[\"G1722\",\"G5142\"]}', 'nourish up in'),
(1790, 1790, 'ἔντρομος', '{\"def\":{\"short\":\"terrified\",\"long\":[\"trembling, terrified\"]},\"deriv\":\"from G1722 and G5156\",\"pronun\":{\"ipa\":\"ˈɛn.tro.mos\",\"ipa_mod\":\"ˈe̞n.trow.mows\",\"sbl\":\"entromos\",\"dic\":\"EN-troh-mose\",\"dic_mod\":\"ANE-troh-mose\"},\"see\":[\"G1722\",\"G5156\"]}', 'X quake, X trembled'),
(1791, 1791, 'ἐντροπή', '{\"def\":{\"short\":\"confusion\",\"long\":[\"shame\",\"respect, reverence\"]},\"deriv\":\"from G1788\",\"pronun\":{\"ipa\":\"ɛn.troˈpe\",\"ipa_mod\":\"e̞n.trowˈpe̞\",\"sbl\":\"entropē\",\"dic\":\"en-troh-PAY\",\"dic_mod\":\"ane-troh-PAY\"},\"see\":[\"G1788\"]}', 'shame'),
(1792, 1792, 'ἐντρυφάω', '{\"def\":{\"short\":\"to revel in\",\"long\":[\"to live in luxury, live delicately or luxuriously, to revel in\",\"to take delight in\"]},\"deriv\":\"from G1722 and G5171\",\"pronun\":{\"ipa\":\"ɛn.tryˈfɑ.o\",\"ipa_mod\":\"e̞n.trjuˈfɑ.ow\",\"sbl\":\"entryphaō\",\"dic\":\"en-troo-FA-oh\",\"dic_mod\":\"ane-tryoo-FA-oh\"},\"see\":[\"G1722\",\"G5171\"]}', 'sporting selves'),
(1793, 1793, 'ἐντυγχάνω', '{\"def\":{\"short\":\"to chance upon, i.e., (by implication) confer with; by extension to entreat (in favor or against)\",\"long\":[\"to light upon a person or a thing, fall in with, hit upon, a person or a thing\",\"to go to or meet a person, especially for the purpose of conversation, consultation, or supplication\",\"to pray, entreat\",\"make intercession for any one\"]},\"deriv\":\"from G1722 and G5177\",\"pronun\":{\"ipa\":\"ɛn.tyŋˈxɑ.no\",\"ipa_mod\":\"e̞n.tjuŋˈxɑ.now\",\"sbl\":\"entynchanō\",\"dic\":\"en-toong-HA-noh\",\"dic_mod\":\"ane-tyoong-HA-noh\"},\"see\":[\"G1722\",\"G5177\"]}', 'deal with, make intercession'),
(1794, 1794, 'ἐντυλίσσω', '{\"def\":{\"short\":\"to entwine, i.e., wind up in\",\"long\":[\"to roll up, wrap together\"]},\"deriv\":\"from G1722 and τυλίσσω (to twist; probably akin to G1507)\",\"pronun\":{\"ipa\":\"ɛn.tyˈlis.so\",\"ipa_mod\":\"e̞n.tjuˈlis.sow\",\"sbl\":\"entylissō\",\"dic\":\"en-too-LEES-soh\",\"dic_mod\":\"ane-tyoo-LEES-soh\"},\"see\":[\"G1507\",\"G1722\"]}', 'wrap in (together)'),
(1795, 1795, 'ἐντυπόω', '{\"def\":{\"short\":\"to enstamp, i.e., engrave\",\"long\":[\"to engrave, imprint (a figure)\"]},\"deriv\":\"from G1722 and a derivative of G5179\",\"pronun\":{\"ipa\":\"ɛn.tyˈpo.o\",\"ipa_mod\":\"e̞n.tjuˈpow.ow\",\"sbl\":\"entypoō\",\"dic\":\"en-too-POH-oh\",\"dic_mod\":\"ane-tyoo-POH-oh\"},\"see\":[\"G1722\",\"G5179\"]}', 'engrave'),
(1796, 1796, 'ἐνυβρίζω', '{\"def\":{\"short\":\"to insult\",\"long\":[\"to insult\"]},\"deriv\":\"from G1722 and G5195\",\"pronun\":{\"ipa\":\"ɛn.yˈβri.zo\",\"ipa_mod\":\"e̞n.juˈvri.zow\",\"sbl\":\"enybrizō\",\"dic\":\"en-oo-VREE-zoh\",\"dic_mod\":\"ane-yoo-VREE-zoh\"},\"see\":[\"G1722\",\"G5195\"]}', 'do despite unto'),
(1797, 1797, 'ἐνυπνιάζομαι', '{\"def\":{\"short\":\"to dream\",\"long\":[\"to dream (divinely suggested) dreams\",\"metaphorically, to be beguiled with sensual images and carried away to an impious course of conduct\"]},\"deriv\":\"middle voice from G1798\",\"pronun\":{\"ipa\":\"ɛn.y.pniˈɑ.zo.mɛ\",\"ipa_mod\":\"e̞n.ju.pniˈɑ.zow.me\",\"sbl\":\"enypniazomai\",\"dic\":\"en-oo-pnee-AH-zoh-meh\",\"dic_mod\":\"ane-yoo-pnee-AH-zoh-may\"},\"see\":[\"G1798\"]}', 'dream(-er)'),
(1798, 1798, 'ἐνύπνιον', '{\"def\":{\"short\":\"something seen in sleep, i.e., a dream (vision in a dream)\",\"long\":[\"a dream\"]},\"deriv\":\"from G1722 and G5258\",\"pronun\":{\"ipa\":\"ɛnˈy.pni.on\",\"ipa_mod\":\"e̞nˈju.pni.own\",\"sbl\":\"enypnion\",\"dic\":\"en-OO-pnee-one\",\"dic_mod\":\"ane-YOO-pnee-one\"},\"see\":[\"G1722\",\"G5258\"]}', 'dream'),
(1799, 1799, 'ἐνώπιον', '{\"def\":{\"short\":\"in the face of (literally or figuratively)\",\"long\":[\"in the presence of, before\",[\"of occupied place: in that place which is before, or over against, opposite, any one and towards which another turns his eyes\"]]},\"deriv\":\"neuter of a compound of G1722 and a derivative of G3700\",\"pronun\":{\"ipa\":\"ɛnˈo.pi.on\",\"ipa_mod\":\"e̞nˈow.pi.own\",\"sbl\":\"enōpion\",\"dic\":\"en-OH-pee-one\",\"dic_mod\":\"ane-OH-pee-one\"},\"see\":[\"G1722\",\"G3700\"]}', 'before, in the presence (sight) of, to'),
(1800, 1800, 'Ἐνώς', '{\"def\":{\"lit\":\"mortal man\",\"short\":\"Enos (i.e., Enosh), a patriarch\",\"long\":[\"the son of Seth\"]},\"deriv\":\"of Hebrew origin (H0583)\",\"pronun\":{\"ipa\":\"ɛnˈos\",\"ipa_mod\":\"e̞nˈows\",\"sbl\":\"enōs\",\"dic\":\"en-OSE\",\"dic_mod\":\"ane-OSE\"},\"see\":[\"H0583\"]}', 'Enos'),
(1801, 1801, 'ἐνωτίζομαι', '{\"def\":{\"short\":\"to take in one\'s ear, i.e., to listen\",\"long\":[\"to receive into the ear, to give ear to, listen\"]},\"deriv\":\"middle voice from a compound of G1722 and G3775\",\"pronun\":{\"ipa\":\"ɛn.oˈti.zo.mɛ\",\"ipa_mod\":\"e̞n.owˈti.zow.me\",\"sbl\":\"enōtizomai\",\"dic\":\"en-oh-TEE-zoh-meh\",\"dic_mod\":\"ane-oh-TEE-zoh-may\"},\"see\":[\"G1722\",\"G3775\"]}', 'hearken'),
(1802, 1802, 'Ἐνώχ', '{\"def\":{\"lit\":\"dedicated\",\"short\":\"Enoch (i.e., Chanok), an antediluvian\",\"long\":[\"the son of Jared and father of Methuselah\"]},\"deriv\":\"of Hebrew origin (H2585)\",\"pronun\":{\"ipa\":\"ɛnˈox\",\"ipa_mod\":\"e̞nˈowx\",\"sbl\":\"enōch\",\"dic\":\"en-OKE\",\"dic_mod\":\"ane-OKE\"},\"see\":[\"H2585\"]}', 'Enoch'),
(1803, 1803, 'ἕξ', '{\"def\":{\"short\":\"six\",\"long\":[\"six\"]},\"deriv\":\"a primary numeral\",\"pronun\":{\"ipa\":\"hɛk͡s\",\"ipa_mod\":\"e̞k͡s\",\"sbl\":\"hex\",\"dic\":\"heks\",\"dic_mod\":\"ayks\"}}', 'six'),
(1804, 1804, 'ἐξαγγέλλω', '{\"def\":{\"short\":\"to publish, i.e., celebrate\",\"long\":[\"to tell out or forth\",\"to declare abroad, divulge, publish\",\"to make known by praising or proclaiming, to celebrate\"]},\"deriv\":\"from G1537 and the base of G0032\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑŋˈɣɛl.lo\",\"ipa_mod\":\"e̞k͡s.ɑŋˈɣe̞l.low\",\"sbl\":\"exangellō\",\"dic\":\"eks-ang-GEL-loh\",\"dic_mod\":\"ayks-ang-GALE-loh\"},\"see\":[\"G0032\",\"G1537\"]}', 'shew forth'),
(1805, 1805, 'ἐξαγοράζω', '{\"def\":{\"short\":\"to buy up, i.e., ransom; figuratively, to rescue from loss (improve opportunity)\",\"long\":[\"to redeem\",[\"by payment of a price to recover from the power of another, to ransom, buy off\",\"metaphorically of Christ freeing the elect from the dominion of the Mosaic Law at the price of his vicarious death\"],\"to buy up, to buy up for one\'s self, for one\'s use\",[\"to make wise and sacred use of every opportunity for doing good, so that zeal and well doing are as it were the purchase money by which we make the time our own\"]]},\"deriv\":\"from G1537 and G0059\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.ɣoˈrɑ.zo\",\"ipa_mod\":\"e̞k͡s.ɑ.ɣowˈrɑ.zow\",\"sbl\":\"exagorazō\",\"dic\":\"eks-ah-goh-RA-zoh\",\"dic_mod\":\"ayks-ah-goh-RA-zoh\"},\"see\":[\"G0059\",\"G1537\"]}', 'redeem'),
(1806, 1806, 'ἐξάγω', '{\"def\":{\"short\":\"to lead forth\",\"long\":[\"to lead out\"]},\"deriv\":\"from G1537 and G0071\",\"pronun\":{\"ipa\":\"ɛk͡sˈɑ.ɣo\",\"ipa_mod\":\"e̞k͡sˈɑ.ɣow\",\"sbl\":\"exagō\",\"dic\":\"eks-AH-goh\",\"dic_mod\":\"ayks-AH-goh\"},\"see\":[\"G0071\",\"G1537\"]}', 'bring forth (out), fetch (lead) out'),
(1807, 1807, 'ἐξαιρέω', '{\"def\":{\"short\":\"actively, to tear out; middle voice, to select; figuratively, to release\",\"long\":[\"to pluck out, draw out, i.e., root out\",\"to choose out (for one\'s self), select, one person from many\",\"to rescue, deliver\"]},\"deriv\":\"from G1537 and G0138\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛˈrɛ.o\",\"ipa_mod\":\"e̞k͡s.eˈre̞.ow\",\"sbl\":\"exaireō\",\"dic\":\"eks-eh-REH-oh\",\"dic_mod\":\"ayks-ay-RAY-oh\"},\"see\":[\"G0138\",\"G1537\"]}', 'deliver, pluck out, rescue'),
(1808, 1808, 'ἐξαίρω', '{\"def\":{\"short\":\"to remove\",\"long\":[\"to lift up or take away out of a place\",\"to remove\"]},\"deriv\":\"from G1537 and G0142\",\"pronun\":{\"ipa\":\"ɛk͡sˈɛ.ro\",\"ipa_mod\":\"e̞k͡sˈe.row\",\"sbl\":\"exairō\",\"dic\":\"eks-EH-roh\",\"dic_mod\":\"ayks-A-roh\"},\"see\":[\"G0142\",\"G1537\"]}', 'put (take) away'),
(1809, 1809, 'ἐξαιτέομαι', '{\"def\":{\"short\":\"to demand (for trial)\",\"long\":[\"to ask from, demand of\",[\"to ask or beg for one\'s self, to ask that one be given up to one from the power of another\",\"in a good sense,\",[\"to beg one from another\",\"ask for the pardon\",\"the safety of some one\"],\"in a bad sense\",[\"for torture\",\"for punishment\"]]]},\"deriv\":\"middle voice from G1537 and G0154\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛˈtɛ.o.mɛ\",\"ipa_mod\":\"e̞k͡s.eˈte̞.ow.me\",\"sbl\":\"exaiteomai\",\"dic\":\"eks-eh-TEH-oh-meh\",\"dic_mod\":\"ayks-ay-TAY-oh-may\"},\"see\":[\"G0154\",\"G1537\"]}', 'desire'),
(1810, 1810, 'ἐξαίφνης', '{\"def\":{\"short\":\"of a sudden (unexpectedly)\",\"long\":[\"of a sudden, suddenly, unexpectedly\"]},\"deriv\":\"from G1537 and the base of G0160\",\"pronun\":{\"ipa\":\"ɛk͡sˈɛ.fnes\",\"ipa_mod\":\"e̞k͡sˈe.fne̞s\",\"sbl\":\"exaiphnēs\",\"dic\":\"eks-EH-fnase\",\"dic_mod\":\"ayks-A-fnase\"},\"see\":[\"G0160\",\"G1537\",\"G1819\"],\"comment\":\"Compare G1819.\"}', 'suddenly'),
(1811, 1811, 'ἐξακολουθέω', '{\"def\":{\"short\":\"to follow out, i.e., (figuratively) to imitate, obey, yield to\",\"long\":[\"to follow out or up, tread in one\'s steps\",[\"metaphorically, to imitate one\'s ways of acting\",\"to follow one\'s authority\",\"to comply with, yield to\"]]},\"deriv\":\"from G1537 and G0190\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.ko.luˈθɛ.o\",\"ipa_mod\":\"e̞k͡s.ɑ.kow.luˈθe̞.ow\",\"sbl\":\"exakoloutheō\",\"dic\":\"eks-ah-koh-loo-THEH-oh\",\"dic_mod\":\"ayks-ah-koh-loo-THAY-oh\"},\"see\":[\"G0190\",\"G1537\"]}', 'follow'),
(1812, 1812, 'ἑξακόσιοι', '{\"def\":{\"short\":\"six hundred\",\"long\":[\"six hundred\"]},\"deriv\":\"plural ordinal from G1803 and G1540\",\"pronun\":{\"ipa\":\"hɛk͡s.ɑˈko.si.y\",\"ipa_mod\":\"e̞k͡s.ɑˈkow.si.y\",\"sbl\":\"hexakosioi\",\"dic\":\"heks-ah-KOH-see-oo\",\"dic_mod\":\"ayks-ah-KOH-see-oo\"},\"see\":[\"G1540\",\"G1803\"]}', 'six hundred'),
(1813, 1813, 'ἐξαλείφω', '{\"def\":{\"short\":\"to smear out, i.e., obliterate (erase tears, figuratively, pardon sin)\",\"long\":[\"to anoint or wash in every part\",[\"to besmear: i.e., cover with lime (to whitewash or plaster)\"],\"to wipe off, wipe away\",[\"to obliterate, erase, wipe out, blot out\"]]},\"deriv\":\"from G1537 and G0218\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑˈli.fo\",\"ipa_mod\":\"e̞k͡s.ɑˈli.fow\",\"sbl\":\"exaleiphō\",\"dic\":\"eks-ah-LEE-foh\",\"dic_mod\":\"ayks-ah-LEE-foh\"},\"see\":[\"G0218\",\"G1537\"]}', 'blot out, wipe away'),
(1814, 1814, 'ἐξάλλομαι', '{\"def\":{\"short\":\"to spring forth\",\"long\":[\"to leap up\"]},\"deriv\":\"from G1537 and G0242\",\"pronun\":{\"ipa\":\"ɛk͡sˈɑl.lo.mɛ\",\"ipa_mod\":\"e̞k͡sˈɑl.low.me\",\"sbl\":\"exallomai\",\"dic\":\"eks-AL-loh-meh\",\"dic_mod\":\"ayks-AL-loh-may\"},\"see\":[\"G0242\",\"G1537\"]}', 'leap up'),
(1815, 1815, 'ἐξανάστασις', '{\"def\":{\"short\":\"a rising from death\",\"long\":[\"a rising up, a rising again\",\"resurrection\"]},\"deriv\":\"from G1817\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑˈnɑ.stɑ.sis\",\"ipa_mod\":\"e̞k͡s.ɑˈnɑ.stɑ.sis\",\"sbl\":\"exanastasis\",\"dic\":\"eks-ah-NA-sta-sees\",\"dic_mod\":\"ayks-ah-NA-sta-sees\"},\"see\":[\"G1817\"]}', 'resurrection'),
(1816, 1816, 'ἐξανατέλλω', '{\"def\":{\"short\":\"to start up out of the ground, i.e., germinate\",\"long\":[\"to make spring up, cause to shoot forth\",\"to spring up\"]},\"deriv\":\"from G1537 and G0393\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.nɑˈtɛl.lo\",\"ipa_mod\":\"e̞k͡s.ɑ.nɑˈte̞l.low\",\"sbl\":\"exanatellō\",\"dic\":\"eks-ah-na-TEL-loh\",\"dic_mod\":\"ayks-ah-na-TALE-loh\"},\"see\":[\"G0393\",\"G1537\"]}', 'spring up'),
(1817, 1817, 'ἐξανίστημι', '{\"def\":{\"short\":\"objectively, to produce, i.e., (figuratively) beget; subjectively, to arise, i.e., (figuratively) object\",\"long\":[\"to make to rise up, to rise up, to produce\",\"to rise (in an assembly to speak)\"]},\"deriv\":\"from G1537 and G0450\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑˈni.ste.mi\",\"ipa_mod\":\"e̞k͡s.ɑˈni.ste̞.mi\",\"sbl\":\"exanistēmi\",\"dic\":\"eks-ah-NEE-stay-mee\",\"dic_mod\":\"ayks-ah-NEE-stay-mee\"},\"see\":[\"G0450\",\"G1537\"]}', 'raise (rise) up'),
(1818, 1818, 'ἐξαπατάω', '{\"def\":{\"short\":\"to seduce wholly\",\"long\":[\"to deceive\"]},\"deriv\":\"from G1537 and G0538\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.pɑˈtɑ.o\",\"ipa_mod\":\"e̞k͡s.ɑ.pɑˈtɑ.ow\",\"sbl\":\"exapataō\",\"dic\":\"eks-ah-pa-TA-oh\",\"dic_mod\":\"ayks-ah-pa-TA-oh\"},\"see\":[\"G0538\",\"G1537\"]}', 'beguile, deceive'),
(1819, 1819, 'ἐξάπινα', '{\"def\":{\"short\":\"of a sudden, i.e., unexpectedly\",\"long\":[\"suddenly\"]},\"deriv\":\"from G1537 and a derivative of the same as G0160\",\"pronun\":{\"ipa\":\"ɛk͡sˈɑ.pi.nɑ\",\"ipa_mod\":\"e̞k͡sˈɑ.pi.nɑ\",\"sbl\":\"exapina\",\"dic\":\"eks-AH-pee-na\",\"dic_mod\":\"ayks-AH-pee-na\"},\"see\":[\"G0160\",\"G1537\",\"G1810\"],\"comment\":\"Compare G1810.\"}', 'suddenly'),
(1820, 1820, 'ἐξαπορέομαι', '{\"def\":{\"short\":\"to be utterly at a loss, i.e., despond\",\"long\":[\"to be utterly at loss, be utterly destitute of measures or resources, to renounce all hope, be in despair\"]},\"deriv\":\"middle voice from G1537 and G0639\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.poˈrɛ.o.mɛ\",\"ipa_mod\":\"e̞k͡s.ɑ.powˈre̞.ow.me\",\"sbl\":\"exaporeomai\",\"dic\":\"eks-ah-poh-REH-oh-meh\",\"dic_mod\":\"ayks-ah-poh-RAY-oh-may\"},\"see\":[\"G0639\",\"G1537\"]}', '(in) despair'),
(1821, 1821, 'ἐξαποστέλλω', '{\"def\":{\"short\":\"to send away forth, i.e., (on a mission) to despatch, or (peremptorily) to dismiss\",\"long\":[\"to send forth\",\"to send away\"]},\"deriv\":\"from G1537 and G0649\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑ.poˈstɛl.lo\",\"ipa_mod\":\"e̞k͡s.ɑ.powˈste̞l.low\",\"sbl\":\"exapostellō\",\"dic\":\"eks-ah-poh-STEL-loh\",\"dic_mod\":\"ayks-ah-poh-STALE-loh\"},\"see\":[\"G0649\",\"G1537\"]}', 'send (away, forth, out)'),
(1822, 1822, 'ἐξαρτίζω', '{\"def\":{\"short\":\"to finish out (time); figuratively, to equip fully (a teacher)\",\"long\":[\"to complete, finish\",[\"to furnish perfectly\",\"to finish, accomplish, (as it were, to render the days complete)\"]]},\"deriv\":\"from G1537 and a derivative of G0739\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑrˈti.zo\",\"ipa_mod\":\"e̞k͡s.ɑrˈti.zow\",\"sbl\":\"exartizō\",\"dic\":\"eks-ar-TEE-zoh\",\"dic_mod\":\"ayks-ar-TEE-zoh\"},\"see\":[\"G0739\",\"G1537\"]}', 'accomplish, thoroughly furnish'),
(1823, 1823, 'ἐξαστράπτω', '{\"def\":{\"short\":\"to lighten forth, i.e., (figuratively) to be radiant (of very white garments)\",\"long\":[\"to send forth lightning, to lighten\",\"to flash out like lightning, to shine, be radiant\",[\"of garments\"]]},\"deriv\":\"from G1537 and G0797\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑˈstrɑ.pto\",\"ipa_mod\":\"e̞k͡s.ɑˈstrɑ.ptow\",\"sbl\":\"exastraptō\",\"dic\":\"eks-ah-STRA-ptoh\",\"dic_mod\":\"ayks-ah-STRA-ptoh\"},\"see\":[\"G0797\",\"G1537\"]}', 'glistening'),
(1824, 1824, 'ἐξαυτῆς', '{\"def\":{\"short\":\"from that hour, i.e., instantly\",\"long\":[\"on the instant, forthwith\"]},\"deriv\":\"from G1537 and the genitive case singular feminine of G0846 (G5610 being understood)\",\"pronun\":{\"ipa\":\"ɛk͡s.ɑβˈtes\",\"ipa_mod\":\"e̞k͡s.ɑfˈte̞s\",\"sbl\":\"exautēs\",\"dic\":\"eks-av-TASE\",\"dic_mod\":\"ayks-af-TASE\"},\"see\":[\"G0846\",\"G1537\",\"G5610\"]}', 'by and by, immediately, presently, straightway'),
(1825, 1825, 'ἐξεγείρω', '{\"def\":{\"short\":\"to rouse fully, i.e., (figuratively) to resuscitate (from death), release (from infliction)\",\"long\":[\"to arouse, raise up (from sleep)\",\"to rouse up, stir up, incite\"]},\"deriv\":\"from G1537 and G1453\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛˈɣi.ro\",\"ipa_mod\":\"e̞k͡s.e̞ˈʝi.row\",\"sbl\":\"exegeirō\",\"dic\":\"eks-eh-GEE-roh\",\"dic_mod\":\"ayks-ay-GEE-roh\"},\"see\":[\"G1453\",\"G1537\"]}', 'raise up'),
(1826, 1826, 'ἔξειμι', '{\"def\":{\"short\":\"to issue, i.e., leave (a place), escape (to the shore)\",\"long\":[\"to go forth, go out\"]},\"deriv\":\"from G1537 and εἶμι (to go)\",\"pronun\":{\"ipa\":\"ˈɛk͡s.i.mi\",\"ipa_mod\":\"ˈe̞k͡s.i.mi\",\"sbl\":\"exeimi\",\"dic\":\"EKS-ee-mee\",\"dic_mod\":\"AYKS-ee-mee\"},\"see\":[\"G1537\"]}', 'depart, get (to land), go out'),
(1827, 1827, 'ἐξελέγχω', '{\"def\":{\"short\":\"to convict fully, i.e., (by implication) to punish\",\"long\":[\"to prove to be in the wrong, convict\"]},\"deriv\":\"from G1537 and G1651\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛˈlɛŋ.xo\",\"ipa_mod\":\"e̞k͡s.e̞ˈle̞ŋ.xow\",\"sbl\":\"exelenchō\",\"dic\":\"eks-eh-LENG-hoh\",\"dic_mod\":\"ayks-ay-LAYNG-hoh\"},\"see\":[\"G1537\",\"G1651\"]}', 'convince'),
(1828, 1828, 'ἐξέλκω', '{\"def\":{\"short\":\"to drag forth, i.e., (figuratively) to entice (to sin)\",\"long\":[\"to draw out\",\"lure forth (metaphorically)\"]},\"deriv\":\"from G1537 and G1670\",\"pronun\":{\"ipa\":\"ɛk͡sˈɛl.ko\",\"ipa_mod\":\"e̞k͡sˈe̞l.kow\",\"sbl\":\"exelkō\",\"dic\":\"eks-EL-koh\",\"dic_mod\":\"ayks-ALE-koh\"},\"see\":[\"G1537\",\"G1670\"],\"comment\":\"In hunting and fishing, game is lured from its hiding place. In like manner, man is is allured from the safety of self-restraint to sin. In James 1:14, the language of the hunting is transferred to the seduction of a harlot.\"}', 'draw away'),
(1829, 1829, 'ἐξέραμα', '{\"def\":{\"short\":\"vomit, i.e., food disgorged\",\"long\":[\"vomit\",\"what is cast out by vomiting\"]},\"deriv\":\"from a comparative of G1537 and a presumed ἐράω (to spue)\",\"pronun\":{\"ipa\":\"ɛk͡sˈɛ.rɑ.mɑ\",\"ipa_mod\":\"e̞k͡sˈe̞.rɑ.mɑ\",\"sbl\":\"exerama\",\"dic\":\"eks-EH-ra-ma\",\"dic_mod\":\"ayks-A-ra-ma\"},\"see\":[\"G1537\"]}', 'vomit'),
(1830, 1830, 'ἐξερευνάω', '{\"def\":{\"short\":\"to explore (figuratively)\",\"long\":[\"to search out, search anxiously and diligently\"]},\"deriv\":\"from G1537 and G2045\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛ.rɛβˈnɑ.o\",\"ipa_mod\":\"e̞k͡s.e̞.revˈnɑ.ow\",\"sbl\":\"exereunaō\",\"dic\":\"eks-eh-rev-NA-oh\",\"dic_mod\":\"ayks-ay-rave-NA-oh\"},\"see\":[\"G1537\",\"G2045\"]}', 'search diligently'),
(1831, 1831, 'ἐξέρχομαι', '{\"def\":{\"short\":\"to issue (literally or figuratively)\",\"long\":[\"to go or come forth of\",[\"with mention of the place out of which one goes, or the point from which he departs\",[\"of those who leave a place of their own accord\",\"of those who are expelled or cast out\"]],\"metaphorically\",[\"to go out of an assembly, i.e., forsake it\",\"to come forth from physically, arise from, to be born of\",\"to go forth from one\'s power, escape from it in safety\",\"to come forth (from privacy) into the world, before the public, (of those who by novelty of opinion attract attention)\",\"of things\",[\"of reports, rumours, messages, precepts\",\"to be made known, declared\",\"to be spread, to be proclaimed\",\"to come forth\",[\"emitted as from the heart or the mouth\",\"to flow forth from the body\",\"to emanate, issue\",[\"used of a sudden flash of lightning\",\"used of a thing vanishing\",\"used of a hope which has disappeared\"]]]]]},\"deriv\":\"from G1537 and G2064\",\"pronun\":{\"ipa\":\"ɛk͡sˈɛr.xo.mɛ\",\"ipa_mod\":\"e̞k͡sˈe̞r.xow.me\",\"sbl\":\"exerchomai\",\"dic\":\"eks-ER-hoh-meh\",\"dic_mod\":\"ayks-ARE-hoh-may\"},\"see\":[\"G1537\",\"G2064\"]}', 'come (forth, out), depart (out of), escape, get out, go (abroad, away, forth, out, thence), proceed (forth), spread abroad'),
(1832, 1832, 'ἔξεστι', '{\"def\":{\"short\":\"so also ἐξόν <pronunciation strongs=\\\"ex-on\'\\\"/> neuter present participle of the same (with or without some form of G1510 expressed); impersonally, it is right (through the figurative idea of being out in public)\",\"long\":[\"it is lawful\"]},\"deriv\":\"third person singular present indicative of a compound of G1537 and G1510\",\"pronun\":{\"ipa\":\"ˈɛk͡s.ɛ.sti\",\"ipa_mod\":\"ˈe̞k͡s.e̞.sti\",\"sbl\":\"exesti\",\"dic\":\"EKS-eh-stee\",\"dic_mod\":\"AYKS-ay-stee\"},\"see\":[\"G1510\",\"G1510\",\"G1537\"]}', 'be lawful, let, X may(-est)'),
(1833, 1833, 'ἐξετάζω', '{\"def\":{\"short\":\"to test thoroughly (by questions), i.e., ascertain or interrogate\",\"long\":[\"to search out\",[\"to examine strictly, enquire\",\"enquire of someone\"]]},\"deriv\":\"from G1537 and ἐτάζω (to examine)\",\"pronun\":{\"ipa\":\"ɛk͡s.ɛˈtɑ.zo\",\"ipa_mod\":\"e̞k͡s.e̞ˈtɑ.zow\",\"sbl\":\"exetazō\",\"dic\":\"eks-eh-TA-zoh\",\"dic_mod\":\"ayks-ay-TA-zoh\"},\"see\":[\"G1537\"]}', 'ask, enquire, search'),
(1834, 1834, 'ἐξηγέομαι', '{\"def\":{\"short\":\"to consider out (aloud), i.e., rehearse, unfold\",\"long\":[\"to lead out, be leader, go before\",\"metaphorically, to draw out in narrative, unfold a teaching\",[\"to recount, rehearse\",\"to unfold, declare\",[\"the things relating to God\",\"used in Greek writing of the interpretation of things sacred and divine, oracles, dreams, etc.\"]]]},\"deriv\":\"from G1537 and G2233\",\"pronun\":{\"ipa\":\"ɛk͡s.eˈɣɛ.o.mɛ\",\"ipa_mod\":\"e̞k͡s.e̞ˈɣe̞.ow.me\",\"sbl\":\"exēgeomai\",\"dic\":\"eks-ay-GEH-oh-meh\",\"dic_mod\":\"ayks-ay-GAY-oh-may\"},\"see\":[\"G1537\",\"G2233\"]}', 'declare, tell'),
(1835, 1835, 'ἑξήκοντα', '{\"def\":{\"short\":\"sixty\",\"long\":[\"sixty\"]},\"deriv\":\"the tenth multiple of G1803\",\"pronun\":{\"ipa\":\"hɛk͡sˈe.kon.tɑ\",\"ipa_mod\":\"e̞k͡sˈe̞.kown.tɑ\",\"sbl\":\"hexēkonta\",\"dic\":\"heks-A-kone-ta\",\"dic_mod\":\"ayks-A-kone-ta\"},\"see\":[\"G1803\"]}', 'sixty(-fold), threescore'),
(1836, 1836, 'ἑξῆς', '{\"def\":{\"short\":\"successive\",\"long\":[\"successively in order\",\"the next following, the next in succession\"]},\"deriv\":\"from G2192 (in the sense of taking hold of, i.e. adjoining)\",\"pronun\":{\"ipa\":\"hɛk͡sˈes\",\"ipa_mod\":\"e̞k͡sˈe̞s\",\"sbl\":\"hexēs\",\"dic\":\"heks-ASE\",\"dic_mod\":\"ayks-ASE\"},\"see\":[\"G2192\"]}', 'after, following, X morrow, next'),
(1837, 1837, 'ἐξηχέομαι', '{\"def\":{\"short\":\"to \\\"echo\\\" forth, i.e., resound (be generally reported)\",\"long\":[\"to sound forth, emit, sound, resound\"]},\"deriv\":\"middle voice from G1537 and G2278\",\"pronun\":{\"ipa\":\"ɛk͡s.eˈxɛ.o.mɛ\",\"ipa_mod\":\"e̞k͡s.e̞ˈxe̞.ow.me\",\"sbl\":\"exēcheomai\",\"dic\":\"eks-ay-HEH-oh-meh\",\"dic_mod\":\"ayks-ay-HAY-oh-may\"},\"see\":[\"G1537\",\"G2278\"]}', 'sound forth'),
(1838, 1838, 'ἕξις', '{\"def\":{\"short\":\"habit, i.e., (by implication) practice\",\"long\":[\"a habit whether of body or mind\",\"a power acquired by custom, practice, use\"]},\"deriv\":\"from G2192\",\"pronun\":{\"ipa\":\"ˈhɛk͡s.is\",\"ipa_mod\":\"ˈe̞k͡s.is\",\"sbl\":\"hexis\",\"dic\":\"HEKS-ees\",\"dic_mod\":\"AYKS-ees\"},\"see\":[\"G2192\"]}', 'use'),
(1839, 1839, 'ἐξίστημι', '{\"def\":{\"short\":\"to put (stand) out of wits, i.e., astound, or (reflexively) become astounded, insane\",\"long\":[\"to throw out of position, displace\",[\"to amaze, to astonish, throw into wonderment\",\"to be amazed, astounded\",\"to be out of one\'s mind, besides one\'s self, insane\"]]},\"deriv\":\"from G1537 and G2476\",\"pronun\":{\"ipa\":\"ɛˈk͡sis.te.mi\",\"ipa_mod\":\"e̞ˈk͡sis.te̞.mi\",\"sbl\":\"existēmi\",\"dic\":\"eh-KSEES-tay-mee\",\"dic_mod\":\"ay-KSEES-tay-mee\"},\"see\":[\"G1537\",\"G2476\"]}', 'amaze, be (make) astonished, be beside self (selves), bewitch, wonder'),
(1840, 1840, 'ἐξισχύω', '{\"def\":{\"short\":\"to have full strength, i.e., be entirely competent\",\"long\":[\"to be eminently able, able, to have full strength\"]},\"deriv\":\"from G1537 and G2480\",\"pronun\":{\"ipa\":\"ɛ.k͡sisˈxy.o\",\"ipa_mod\":\"e̞.k͡sisˈxju.ow\",\"sbl\":\"exischyō\",\"dic\":\"eh-ksees-HOO-oh\",\"dic_mod\":\"ay-ksees-HYOO-oh\"},\"see\":[\"G1537\",\"G2480\"]}', 'be able'),
(1841, 1841, 'ἔξοδος', '{\"def\":{\"short\":\"an exit, i.e., (figuratively) death\",\"long\":[\"exit i.e., departure\",\"the close of one\'s career, one\'s final fate\",\"departure from life, decease\"]},\"deriv\":\"from G1537 and G3598\",\"pronun\":{\"ipa\":\"ˈɛk͡s.o.ðos\",\"ipa_mod\":\"ˈe̞k͡s.ow.ðows\",\"sbl\":\"exodos\",\"dic\":\"EKS-oh-those\",\"dic_mod\":\"AYKS-oh-those\"},\"see\":[\"G1537\",\"G3598\"]}', 'decease, departing'),
(1842, 1842, 'ἐξολοθρεύω', '{\"def\":{\"short\":\"to extirpate\",\"long\":[\"to destroy out of its place, destroy utterly, to extirpate\"]},\"deriv\":\"from G1537 and G3645\",\"pronun\":{\"ipa\":\"ɛk͡s.o.loˈθrɛβ.o\",\"ipa_mod\":\"e̞k͡s.ow.lowˈθrev.ow\",\"sbl\":\"exolothreuō\",\"dic\":\"eks-oh-loh-THREV-oh\",\"dic_mod\":\"ayks-oh-loh-THRAVE-oh\"},\"see\":[\"G1537\",\"G3645\"]}', 'destroy'),
(1843, 1843, 'ἐξομολογέω', '{\"def\":{\"short\":\"to acknowledge or (by implication, of assent) agree fully\",\"long\":[\"to confess\",\"to profess\",[\"acknowledge openly and joyfully\",\"to one\'s honor: to celebrate, give praise to\",\"to profess that one will do something, to promise, agree, engage\"]]},\"deriv\":\"from G1537 and G3670\",\"pronun\":{\"ipa\":\"ɛk͡s.o.mo.loˈɣɛ.o\",\"ipa_mod\":\"e̞k͡s.ow.mow.lowˈɣe̞.ow\",\"sbl\":\"exomologeō\",\"dic\":\"eks-oh-moh-loh-GEH-oh\",\"dic_mod\":\"ayks-oh-moh-loh-GAY-oh\"},\"see\":[\"G1537\",\"G3670\"]}', 'confess, profess, promise'),
(1844, 1844, 'ἐξορκίζω', '{\"def\":{\"short\":\"to exact an oath, i.e., conjure\",\"long\":[\"to extract an oath, to force to an oath\",\"to adjure\"]},\"deriv\":\"from G1537 and G3726\",\"pronun\":{\"ipa\":\"ɛk͡s.orˈki.zo\",\"ipa_mod\":\"e̞k͡s.owrˈki.zow\",\"sbl\":\"exorkizō\",\"dic\":\"eks-ore-KEE-zoh\",\"dic_mod\":\"ayks-ore-KEE-zoh\"},\"see\":[\"G1537\",\"G3726\"]}', 'adjure'),
(1845, 1845, 'ἐξορκιστής', '{\"def\":{\"short\":\"one that binds by an oath (or spell), i.e., (by implication) an \\\"exorcist\\\" (conjurer)\",\"long\":[\"he who extracts an oath of one\",\"an exorcist\",[\"one who employs a formula of conjuration for expelling demons\"]]},\"deriv\":\"from G1844\",\"pronun\":{\"ipa\":\"ɛk͡s.or.kiˈstes\",\"ipa_mod\":\"e̞k͡s.owr.ciˈste̞s\",\"sbl\":\"exorkistēs\",\"dic\":\"eks-ore-kee-STASE\",\"dic_mod\":\"ayks-ore-kee-STASE\"},\"see\":[\"G1844\"]}', 'exorcist'),
(1846, 1846, 'ἐξορύσσω', '{\"def\":{\"short\":\"to dig out, i.e., (by extension) to extract (an eye), remove (roofing)\",\"long\":[\"to dig out, to pluck out (the eyes)\",\"to dig through\"]},\"deriv\":\"from G1537 and G3736\",\"pronun\":{\"ipa\":\"ɛk͡s.oˈrys.so\",\"ipa_mod\":\"e̞k͡s.owˈrjus.sow\",\"sbl\":\"exoryssō\",\"dic\":\"eks-oh-ROOS-soh\",\"dic_mod\":\"ayks-oh-RYOOS-soh\"},\"see\":[\"G1537\",\"G3736\"]}', 'break up, pluck out'),
(1847, 1847, 'ἐξουδενόω', '{\"def\":{\"short\":\"to make utterly nothing of, i.e., despise\",\"long\":[\"to hold and treat as of no account, utterly to despise\",\"to set at nought, treat with contempt\"]},\"deriv\":\"from G1537 and a derivative of the neuter of G3762\",\"pronun\":{\"ipa\":\"ɛk͡s.u.ðɛˈno.o\",\"ipa_mod\":\"e̞k͡s.u.ðe̞ˈnow.ow\",\"sbl\":\"exoudenoō\",\"dic\":\"eks-oo-theh-NOH-oh\",\"dic_mod\":\"ayks-oo-thay-NOH-oh\"},\"see\":[\"G1537\",\"G1848\",\"G3762\"]}', 'set at nought'),
(1848, 1848, 'ἐξουθενέω', '{\"def\":{\"short\":\"to despise\",\"long\":[\"to make of no account, despise utterly\"]},\"deriv\":\"a variation of G1847 and meaning the same\",\"pronun\":{\"ipa\":\"ɛk͡s.u.θɛˈnɛ.o\",\"ipa_mod\":\"e̞k͡s.u.θe̞ˈne̞.ow\",\"sbl\":\"exoutheneō\",\"dic\":\"eks-oo-theh-NEH-oh\",\"dic_mod\":\"ayks-oo-thay-NAY-oh\"},\"see\":[\"G1847\"]}', 'contemptible, despise, least esteemed, set at nought'),
(1849, 1849, 'ἐξουσία', '{\"def\":{\"short\":\"privilege, i.e., (subjectively) force, capacity, competency, freedom, or (objectively) mastery (concretely, magistrate, superhuman, potentate, token of control), delegated influence\",\"long\":[\"power of choice, liberty of doing as one pleases\",[\"leave or permission\"],\"physical and mental power\",[\"the ability or strength with which one is endued, which he either possesses or exercises\"],\"the power of authority (influence) and of right (privilege)\",\"the power of rule or government (the power of him whose will and commands must be submitted to by others and obeyed)\",[\"universally\",[\"authority over mankind\"],\"specifically\",[\"the power of judicial decisions\",\"of authority to manage domestic affairs\"],\"metonymically\",[\"a thing subject to authority or rule\",[\"jurisdiction\"],\"one who possesses authority\",[\"a ruler, a human magistrate\",\"the leading and more powerful among created beings superior to man, spiritual potentates\"]],\"a sign of the husband\'s authority over his wife\",[\"the veil with which propriety required a women to cover herself\"],\"the sign of regal authority, a crown\"]]},\"deriv\":\"from G1832 (in the sense of ability)\",\"pronun\":{\"ipa\":\"ɛk͡s.uˈsi.ɑ\",\"ipa_mod\":\"e̞k͡s.uˈsi.ɑ\",\"sbl\":\"exousia\",\"dic\":\"eks-oo-SEE-ah\",\"dic_mod\":\"ayks-oo-SEE-ah\"},\"see\":[\"G1832\"]}', 'authority, jurisdiction, liberty, power, right, strength'),
(1850, 1850, 'ἐξουσιάζω', '{\"def\":{\"short\":\"to control\",\"long\":[\"to have power or authority, use power\",[\"to be master of any one, exercise authority over one\",\"to be master of the body\",[\"to have full and entire authority over the body\",\"to hold the body subject to one\'s will\"],\"to be brought under the power of anyone\"]]},\"deriv\":\"from G1849\",\"pronun\":{\"ipa\":\"ɛk͡s.u.siˈɑ.zo\",\"ipa_mod\":\"e̞k͡s.u.siˈɑ.zow\",\"sbl\":\"exousiazō\",\"dic\":\"eks-oo-see-AH-zoh\",\"dic_mod\":\"ayks-oo-see-AH-zoh\"},\"see\":[\"G1849\"]}', 'exercise authority upon, bring under the (have) power of'),
(1851, 1851, 'ἐξοχή', '{\"def\":{\"short\":\"prominence (figuratively)\",\"long\":[\"any prominence or projection, as a peak or summit of a mountain\",[\"in medical writing a proturbance, swelling, wart, etc.\"],\"metaphorically eminence, excellence, superiority\"]},\"deriv\":\"from a compound of G1537 and G2192 (meaning to stand out)\",\"pronun\":{\"ipa\":\"ɛk͡s.oˈxe\",\"ipa_mod\":\"e̞k͡s.owˈxe̞\",\"sbl\":\"exochē\",\"dic\":\"eks-oh-HAY\",\"dic_mod\":\"ayks-oh-HAY\"},\"see\":[\"G1537\",\"G2192\"]}', 'principal'),
(1852, 1852, 'ἐξυπνίζω', '{\"def\":{\"short\":\"to waken\",\"long\":[\"to wake up, awaken out of a sleep\"]},\"deriv\":\"from G1853\",\"pronun\":{\"ipa\":\"ɛk͡s.yˈpni.zo\",\"ipa_mod\":\"e̞k͡s.juˈpni.zow\",\"sbl\":\"exypnizō\",\"dic\":\"eks-oo-PNEE-zoh\",\"dic_mod\":\"ayks-yoo-PNEE-zoh\"},\"see\":[\"G1853\"]}', 'awake out of sleep'),
(1853, 1853, 'ἔξυπνος', '{\"def\":{\"short\":\"awake\",\"long\":[\"roused out of a sleep\"]},\"deriv\":\"from G1537 and G5258\",\"pronun\":{\"ipa\":\"ˈɛk͡s.y.pnos\",\"ipa_mod\":\"ˈe̞k͡s.ju.pnows\",\"sbl\":\"exypnos\",\"dic\":\"EKS-oo-pnose\",\"dic_mod\":\"AYKS-yoo-pnose\"},\"see\":[\"G1537\",\"G5258\"]}', 'X out of sleep'),
(1854, 1854, 'ἔξω', '{\"def\":{\"short\":\"out(-side) (of doors), literally or figuratively\",\"long\":[\"without, out of doors\"]},\"deriv\":\"adverb from G1537\",\"pronun\":{\"ipa\":\"ˈɛk͡s.o\",\"ipa_mod\":\"ˈe̞k͡s.ow\",\"sbl\":\"exō\",\"dic\":\"EKS-oh\",\"dic_mod\":\"AYKS-oh\"},\"see\":[\"G1537\"]}', 'away, forth, (with-)out(-ward) (of), strange'),
(1855, 1855, 'ἔξωθεν', '{\"def\":{\"short\":\"external(-ly)\",\"long\":[\"from without, outward\"]},\"deriv\":\"from G1854\",\"pronun\":{\"ipa\":\"ˈɛk͡s.o.θɛn\",\"ipa_mod\":\"ˈe̞k͡s.ow.θe̞n\",\"sbl\":\"exōthen\",\"dic\":\"EKS-oh-then\",\"dic_mod\":\"AYKS-oh-thane\"},\"see\":[\"G1854\"]}', 'out(-side, -ward, -wardly), (from) without'),
(1856, 1856, 'ἐξωθέω', '{\"def\":{\"short\":\"to expel; by implication, to propel\",\"long\":[\"to thrust out\",\"expel from one\'s abode\",\"to propel, drive\"]},\"deriv\":\"from G1537 and ὠθέω (to push)\",\"pronun\":{\"ipa\":\"ɛk͡s.oˈθɛ.o\",\"ipa_mod\":\"e̞k͡s.owˈθe̞.ow\",\"sbl\":\"exōtheō\",\"dic\":\"eks-oh-THEH-oh\",\"dic_mod\":\"ayks-oh-THAY-oh\"},\"see\":[\"G1537\"]}', 'drive out, thrust in'),
