(1895, 1895, 'ἐπειδήπερ', '{\"def\":{\"short\":\"since indeed (of cause)\",\"long\":[\"seeing that, forasmuch as\"]},\"deriv\":\"from G1894 and G4007\",\"pronun\":{\"ipa\":\"ɛp.iˈðe.pɛr\",\"ipa_mod\":\"e̞p.iˈðe̞.pe̞r\",\"sbl\":\"epeidēper\",\"dic\":\"ep-ee-THAY-per\",\"dic_mod\":\"ape-ee-THAY-pare\"},\"see\":[\"G1894\",\"G4007\"]}', 'forasmuch'),
(1896, 1896, 'ἐπεῖδον', '{\"def\":{\"short\":\"to regard (favorably or otherwise)\",\"long\":[\"to look upon, to regard\"]},\"deriv\":\"and other moods and persons of the same tense; from G1909 and G1492\",\"pronun\":{\"ipa\":\"ɛpˈi.ðon\",\"ipa_mod\":\"e̞pˈi.ðown\",\"sbl\":\"epeidon\",\"dic\":\"ep-EE-thone\",\"dic_mod\":\"ape-EE-thone\"},\"see\":[\"G1492\",\"G1909\"]}', 'behold, look upon'),
(1897, 1897, 'ἐπείπερ', '{\"def\":{\"short\":\"since indeed (of cause)\",\"long\":[\"since indeed, since at all events\"]},\"deriv\":\"from G1893 and G4007\",\"pronun\":{\"ipa\":\"ɛpˈi.pɛr\",\"ipa_mod\":\"e̞pˈi.pe̞r\",\"sbl\":\"epeiper\",\"dic\":\"ep-EE-per\",\"dic_mod\":\"ape-EE-pare\"},\"see\":[\"G1893\",\"G4007\"]}', 'seeing');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(1898, 1898, 'ἐπεισαγωγή', '{\"def\":{\"short\":\"a superintroduction\",\"long\":[\"a bringing in besides or in addition to what is or has been brought in\",[\"used of the introduction of a new wife in the place of one repudiated\",\"to come in besides or to those who are already within\",[\"to enter afterwards\"],\"to come in upon, come upon by entering\",[\"to enter against\"]]]},\"deriv\":\"from a compound of G1909 and G1521\",\"pronun\":{\"ipa\":\"ɛp.i.sɑ.ɣoˈɣe\",\"ipa_mod\":\"e̞p.i.sɑ.ɣowˈɣe̞\",\"sbl\":\"epeisagōgē\",\"dic\":\"ep-ee-sa-goh-GAY\",\"dic_mod\":\"ape-ee-sa-goh-GAY\"},\"see\":[\"G1521\",\"G1909\"]}', 'bringing in'),
(1899, 1899, 'ἔπειτα', '{\"def\":{\"short\":\"thereafter\",\"long\":[\"thereupon, thereafter, then, afterwards\"]},\"deriv\":\"from G1909 and G1534\",\"pronun\":{\"ipa\":\"ˈɛp.i.tɑ\",\"ipa_mod\":\"ˈe̞p.i.tɑ\",\"sbl\":\"epeita\",\"dic\":\"EP-ee-ta\",\"dic_mod\":\"APE-ee-ta\"},\"see\":[\"G1534\",\"G1909\"]}', 'after that(-ward), then'),
(1900, 1900, 'ἐπέκεινα', '{\"def\":{\"short\":\"upon those parts of, i.e., on the further side of\",\"long\":[\"beyond\"]},\"deriv\":\"from G1909 and (the accusative case plural neuter of) G1565\",\"pronun\":{\"ipa\":\"ɛpˈɛ.ki.nɑ\",\"ipa_mod\":\"e̞pˈe̞.ci.nɑ\",\"sbl\":\"epekeina\",\"dic\":\"ep-EH-kee-na\",\"dic_mod\":\"ape-A-kee-na\"},\"see\":[\"G1565\",\"G1909\"]}', 'beyond'),
(1901, 1901, 'ἐπεκτείνομαι', '{\"def\":{\"short\":\"to stretch (oneself) forward upon\",\"long\":[\"to stretch out to or towards\",\"to stretch (one\'s self) forward to\"]},\"deriv\":\"middle voice from G1909 and G1614\",\"pronun\":{\"ipa\":\"ɛp.ɛkˈti.no.mɛ\",\"ipa_mod\":\"e̞p.e̞kˈti.now.me\",\"sbl\":\"epekteinomai\",\"dic\":\"ep-ek-TEE-noh-meh\",\"dic_mod\":\"ape-ake-TEE-noh-may\"},\"see\":[\"G1614\",\"G1909\"]}', 'reach forth'),
(1902, 1902, 'ἐπενδύομαι', '{\"def\":{\"short\":\"to invest upon oneself\",\"long\":[\"to put on over\"]},\"deriv\":\"middle voice from G1909 and G1746\",\"pronun\":{\"ipa\":\"ɛp.ɛnˈðy.o.mɛ\",\"ipa_mod\":\"e̞p.e̞n̪ˈðju.ow.me\",\"sbl\":\"ependyomai\",\"dic\":\"ep-en-THOO-oh-meh\",\"dic_mod\":\"ape-ane-THYOO-oh-may\"},\"see\":[\"G1746\",\"G1909\"]}', 'be clothed upon'),
(1903, 1903, 'ἐπενδύτης', '{\"def\":{\"short\":\"a wrapper, i.e., outer garment\",\"long\":[\"an upper garment\",[\"John 21:7 seems to denote a kind of linen blouse or frock which fishermen used to wear at their work\"]]},\"deriv\":\"from G1902\",\"pronun\":{\"ipa\":\"ɛp.ɛnˈðy.tes\",\"ipa_mod\":\"e̞p.e̞n̪ˈðju.te̞s\",\"sbl\":\"ependytēs\",\"dic\":\"ep-en-THOO-tase\",\"dic_mod\":\"ape-ane-THYOO-tase\"},\"see\":[\"G1902\"]}', 'fisher\'s coat'),
(1904, 1904, 'ἐπέρχομαι', '{\"def\":{\"short\":\"to supervene, i.e., arrive, occur, impend, attack, (figuratively) influence\",\"long\":[\"to come to arrive\",[\"of time, come on, be at hand, be future\"],\"to come upon, overtake, one\",[\"of sleep\",\"of disease\",\"of calamities\",\"of the Holy Spirit, descending and operating in one\",\"of an enemy attacking one\"]]},\"deriv\":\"from G1909 and G2064\",\"pronun\":{\"ipa\":\"ɛpˈɛr.xo.mɛ\",\"ipa_mod\":\"e̞pˈe̞r.xow.me\",\"sbl\":\"eperchomai\",\"dic\":\"ep-ER-hoh-meh\",\"dic_mod\":\"ape-ARE-hoh-may\"},\"see\":[\"G1909\",\"G2064\"]}', 'come (in, upon)'),
(1905, 1905, 'ἐπερωτάω', '{\"def\":{\"short\":\"to ask for, i.e., inquire, seek\",\"long\":[\"to accost one with an enquiry, put a question to, enquiry of, ask, interrogate\",\"to address one with a request or demand\",[\"to ask of or demand of one\"]]},\"deriv\":\"from G1909 and G2065\",\"pronun\":{\"ipa\":\"ɛp.ɛ.roˈtɑ.o\",\"ipa_mod\":\"e̞p.e̞.rowˈtɑ.ow\",\"sbl\":\"eperōtaō\",\"dic\":\"ep-eh-roh-TA-oh\",\"dic_mod\":\"ape-ay-roh-TA-oh\"},\"see\":[\"G1909\",\"G2065\"]}', 'ask (after, questions), demand, desire, question'),
(1906, 1906, 'ἐπερώτημα', '{\"def\":{\"short\":\"an inquiry\",\"long\":[\"an enquiry, a question\",\"a demand\",\"earnestly seeking\",[\"craving, an intense desire\"]]},\"deriv\":\"from G1905\",\"pronun\":{\"ipa\":\"ɛp.ɛˈro.te.mɑ\",\"ipa_mod\":\"e̞p.e̞ˈrow.te̞.mɑ\",\"sbl\":\"eperōtēma\",\"dic\":\"ep-eh-ROH-tay-ma\",\"dic_mod\":\"ape-ay-ROH-tay-ma\"},\"see\":[\"G1905\"]}', 'answer'),
(1907, 1907, 'ἐπέχω', '{\"def\":{\"short\":\"to hold upon, i.e., (by implication) to retain; (by extension) to detain; (with implication, of G3563) to pay attention to\",\"long\":[\"to have or hold upon, apply, to observe, attend to\",[\"to give attention to\"],\"to hold towards, hold forth, present\",\"to check\",[\"delay, stop, stay\"]]},\"deriv\":\"from G1909 and G2192\",\"pronun\":{\"ipa\":\"ɛpˈɛ.xo\",\"ipa_mod\":\"e̞pˈe̞.xow\",\"sbl\":\"epechō\",\"dic\":\"ep-EH-hoh\",\"dic_mod\":\"ape-A-hoh\"},\"see\":[\"G1909\",\"G2192\",\"G3563\"]}', 'give (take) heed unto, hold forth, mark, stay'),
(1908, 1908, 'ἐπηρεάζω', '{\"def\":{\"short\":\"to insult, slander\",\"long\":[\"to insult\",\"to treat abusively, use despitefully\",\"to revile\",\"in a forensic sense, to accuse falsely\",\"to threaten\"]},\"deriv\":\"from a comparative of G1909 and (probably) ἀρειά (threats)\",\"pronun\":{\"ipa\":\"ɛp.e.rɛˈɑ.zo\",\"ipa_mod\":\"e̞p.e̞.re̞ˈɑ.zow\",\"sbl\":\"epēreazō\",\"dic\":\"ep-ay-reh-AH-zoh\",\"dic_mod\":\"ape-ay-ray-AH-zoh\"},\"see\":[\"G1909\"]}', 'use despitefully, falsely accuse'),
(1909, 1909, 'ἐπί', '{\"def\":{\"short\":\"properly, meaning superimposition (of time, place, order, etc.), as a relation of distribution (with the genitive case), i.e., over, upon, etc.; of rest (with the dative case) at, on, etc.; of direction (with the accusative case) towards, upon, etc\",\"long\":[\"upon, on, at, by, before\",\"of position, on, at, by, over, against\",\"to, over, on, at, across, against\"]},\"deriv\":\"a primary preposition\",\"pronun\":{\"ipa\":\"ɛˈpi\",\"ipa_mod\":\"e̞ˈpi\",\"sbl\":\"epi\",\"dic\":\"eh-PEE\",\"dic_mod\":\"ay-PEE\"}}', 'about (the times), above, after, against, among, as long as (touching), at, beside, X have charge of, (be-, where-)fore, in (a place, as much as, the time of, -to), (because) of, (up-)on (behalf of), over, (by, for) the space of, through(-out), (un-)to(-ward), with'),
(1910, 1910, 'ἐπιβαίνω', '{\"def\":{\"short\":\"to walk upon, i.e., mount, ascend, embark, arrive\",\"long\":[\"to get upon, mount\",[\"to embark in\",\"to go aboard (a ship)\"],\"to set foot in, enter\"]},\"deriv\":\"from G1909 and the base of G0939\",\"pronun\":{\"ipa\":\"ɛ.piˈβɛ.no\",\"ipa_mod\":\"e̞.piˈve.now\",\"sbl\":\"epibainō\",\"dic\":\"eh-pee-VEH-noh\",\"dic_mod\":\"ay-pee-VAY-noh\"},\"see\":[\"G0939\",\"G1909\"]}', 'come (into), enter into, go abroad, sit upon, take ship'),
(1911, 1911, 'ἐπιβάλλω', '{\"def\":{\"short\":\"to throw upon (literal or figurative, transitive or reflexive; usually with more or less force); specially (with G1438 implied) to reflect; impersonally, to belong to\",\"long\":[\"to cast upon, to lay upon\",[\"used of seizing one to lead him off as a prisoner\",\"to put (i.e., sew) on\"],\"to throw one\'s self upon, rush in\",[\"used of waves rushing into a ship\",\"to put one\'s mind upon a thing\",\"attend to\"],\"it belongs to me, falls to my share\"]},\"deriv\":\"from G1909 and G0906\",\"pronun\":{\"ipa\":\"ɛ.piˈβɑl.lo\",\"ipa_mod\":\"e̞.piˈvɑl.low\",\"sbl\":\"epiballō\",\"dic\":\"eh-pee-VAHL-loh\",\"dic_mod\":\"ay-pee-VAHL-loh\"},\"see\":[\"G0906\",\"G1438\",\"G1909\"]}', 'beat into, cast (up-)on, fall, lay (on), put (unto), stretch forth, think on'),
(1912, 1912, 'ἐπιβαρέω', '{\"def\":{\"short\":\"to be heavy upon, i.e., (pecuniarily) to be expensive to; figuratively, to be severe towards\",\"long\":[\"to put a burden upon, to load\",\"to be burdensome\"]},\"deriv\":\"from G1909 and G0916\",\"pronun\":{\"ipa\":\"ɛ.pi.βɑˈrɛ.o\",\"ipa_mod\":\"e̞.pi.vɑˈre̞.ow\",\"sbl\":\"epibareō\",\"dic\":\"eh-pee-va-REH-oh\",\"dic_mod\":\"ay-pee-va-RAY-oh\"},\"see\":[\"G0916\",\"G1909\"]}', 'be chargeable to, overcharge'),
(1913, 1913, 'ἐπιβιβάζω', '{\"def\":{\"short\":\"to cause to mount (an animal)\",\"long\":[\"to cause to mount\",\"to place upon\"]},\"deriv\":\"from G1909 and a reduplicated derivative of the base of G0939 (compare G0307)\",\"pronun\":{\"ipa\":\"ɛ.pi.βiˈβɑ.zo\",\"ipa_mod\":\"e̞.pi.viˈvɑ.zow\",\"sbl\":\"epibibazō\",\"dic\":\"eh-pee-vee-VA-zoh\",\"dic_mod\":\"ay-pee-vee-VA-zoh\"},\"see\":[\"G0307\",\"G0939\",\"G1909\"]}', 'set on'),
(1914, 1914, 'ἐπιβλέπω', '{\"def\":{\"short\":\"to gaze at (with favor, pity or partiality)\",\"long\":[\"to turn the eyes upon, to look upon, gaze upon\",\"to look up to, regard\",\"to have regard for, to regard\"]},\"deriv\":\"from G1909 and G0991\",\"pronun\":{\"ipa\":\"ɛ.piˈβlɛ.po\",\"ipa_mod\":\"e̞.piˈvle̞.pow\",\"sbl\":\"epiblepō\",\"dic\":\"eh-pee-VLEH-poh\",\"dic_mod\":\"ay-pee-VLAY-poh\"},\"see\":[\"G0991\",\"G1909\"]}', 'look upon, regard, have respect to'),
(1915, 1915, 'ἐπίβλημα', '{\"def\":{\"short\":\"a patch\",\"long\":[\"that which is thrown or put upon a thing, or that which is added to it\",[\"an addition\",\"that which is sewed on to cover a rent, a patch\"]]},\"deriv\":\"from G1911\",\"pronun\":{\"ipa\":\"ɛˈpi.βle.mɑ\",\"ipa_mod\":\"e̞ˈpi.vle̞.mɑ\",\"sbl\":\"epiblēma\",\"dic\":\"eh-PEE-vlay-ma\",\"dic_mod\":\"ay-PEE-vlay-ma\"},\"see\":[\"G1911\"]}', 'piece'),
(1916, 1916, 'ἐπιβοάω', '{\"def\":{\"short\":\"to exclaim against\",\"long\":[\"to cry out to, cry out\"]},\"deriv\":\"from G1909 and G0994\",\"pronun\":{\"ipa\":\"ɛ.pi.βoˈɑ.o\",\"ipa_mod\":\"e̞.pi.vowˈɑ.ow\",\"sbl\":\"epiboaō\",\"dic\":\"eh-pee-voh-AH-oh\",\"dic_mod\":\"ay-pee-voh-AH-oh\"},\"see\":[\"G0994\",\"G1909\"]}', 'cry'),
(1917, 1917, 'ἐπιβουλή', '{\"def\":{\"short\":\"a plan against someone, i.e., a plot\",\"long\":[\"a plan formed against one, a plot\"]},\"deriv\":\"from a presumed compound of G1909 and G1014\",\"pronun\":{\"ipa\":\"ɛ.pi.βuˈle\",\"ipa_mod\":\"e̞.pi.vuˈle̞\",\"sbl\":\"epiboulē\",\"dic\":\"eh-pee-voo-LAY\",\"dic_mod\":\"ay-pee-voo-LAY\"},\"see\":[\"G1014\",\"G1909\"]}', 'laying (lying) in wait'),
(1918, 1918, 'ἐπιγαμβρεύω', '{\"def\":{\"short\":\"to form affinity with, i.e., (specially) in a levirate way\",\"long\":[\"to be related to by marriage, enter into affinity with\",[\"become any one\'s father-in-law or son-in-law\",\"to marry the widow of a brother who has died childless\"]]},\"deriv\":\"from G1909 and a derivative of G1062\",\"pronun\":{\"ipa\":\"ɛ.pi.ɣɑmˈβrɛβ.o\",\"ipa_mod\":\"e̞.pi.ɣɑɱˈvrev.ow\",\"sbl\":\"epigambreuō\",\"dic\":\"eh-pee-gahm-VREV-oh\",\"dic_mod\":\"ay-pee-gahm-VRAVE-oh\"},\"see\":[\"G1062\",\"G1909\"]}', 'marry'),
(1919, 1919, 'ἐπίγειος', '{\"def\":{\"short\":\"worldly (physically or morally)\",\"long\":[\"existing upon the earth, earthly, terrestrial\"]},\"deriv\":\"from G1909 and G1093\",\"pronun\":{\"ipa\":\"ɛˈpi.ɣi.os\",\"ipa_mod\":\"e̞ˈpi.ʝi.ows\",\"sbl\":\"epigeios\",\"dic\":\"eh-PEE-gee-ose\",\"dic_mod\":\"ay-PEE-gee-ose\"},\"see\":[\"G1093\",\"G1909\"]}', 'earthly, in earth, terrestrial'),
(1920, 1920, 'ἐπιγίνομαι', '{\"def\":{\"short\":\"to arrive upon, i.e., spring up (as a wind)\",\"long\":[\"to become or happen afterwards\",[\"to be born after\"],\"to come to, arrive: of time\",\"to arise, spring up, come on\"]},\"deriv\":\"from G1909 and G1096\",\"pronun\":{\"ipa\":\"ɛ.piˈɣi.no.mɛ\",\"ipa_mod\":\"e̞.piˈɣi.now.me\",\"sbl\":\"epiginomai\",\"dic\":\"eh-pee-GEE-noh-meh\",\"dic_mod\":\"ay-pee-GEE-noh-may\"},\"see\":[\"G1096\",\"G1909\"]}', 'blow'),
(1921, 1921, 'ἐπιγινώσκω', '{\"def\":{\"short\":\"to know upon some mark, i.e., recognize; by implication, to become fully acquainted with, to acknowledge\",\"long\":[\"to become thoroughly acquainted with, to know thoroughly\",[\"to know accurately, know well\"],\"to know\",[\"to recognize\",[\"by sight, hearing, of certain signs, to perceive who a person is\"],\"to know i.e., to perceive\",\"to know i.e., to find out, ascertain\",\"to know i.e., to understand\"]]},\"deriv\":\"from G1909 and G1097\",\"pronun\":{\"ipa\":\"ɛ.pi.ɣiˈno.sko\",\"ipa_mod\":\"e̞.pi.ʝiˈnow.skow\",\"sbl\":\"epiginōskō\",\"dic\":\"eh-pee-gee-NOH-skoh\",\"dic_mod\":\"ay-pee-gee-NOH-skoh\"},\"see\":[\"G1097\",\"G1909\"]}', '(ac-, have, take)know(-ledge) (well), perceive'),
(1922, 1922, 'ἐπίγνωσις', '{\"def\":{\"short\":\"recognition, i.e., (by implication) full discernment, acknowledgement\",\"long\":[\"precise and correct knowledge\",[\"used in the New Testament of the knowledge of things ethical and divine\"]]},\"deriv\":\"from G1921\",\"pronun\":{\"ipa\":\"ɛˈpi.ɣno.sis\",\"ipa_mod\":\"e̞ˈpi.ɣnow.sis\",\"sbl\":\"epignōsis\",\"dic\":\"eh-PEE-gnoh-sees\",\"dic_mod\":\"ay-PEE-gnoh-sees\"},\"see\":[\"G1921\"]}', '(ac-)knowledge(-ing, -ment)'),
(1923, 1923, 'ἐπιγραφή', '{\"def\":{\"short\":\"an inscription\",\"long\":[\"an inscription, title\",[\"in the New Testament of an inscription in black letters upon a whitened tablet\",\"of the inscription on a coin\"]]},\"deriv\":\"from G1924\",\"pronun\":{\"ipa\":\"ɛ.pi.ɣrɑˈfe\",\"ipa_mod\":\"e̞.pi.ɣrɑˈfe̞\",\"sbl\":\"epigraphē\",\"dic\":\"eh-pee-gra-FAY\",\"dic_mod\":\"ay-pee-gra-FAY\"},\"see\":[\"G1924\"]}', 'superscription'),
(1924, 1924, 'ἐπιγράφω', '{\"def\":{\"short\":\"to inscribe (physically or mentally)\",\"long\":[\"to write upon, inscribe\",\"metaphorically to write upon the mind\"]},\"deriv\":\"from G1909 and G1125\",\"pronun\":{\"ipa\":\"ɛ.piˈɣrɑ.fo\",\"ipa_mod\":\"e̞.piˈɣrɑ.fow\",\"sbl\":\"epigraphō\",\"dic\":\"eh-pee-GRA-foh\",\"dic_mod\":\"ay-pee-GRA-foh\"},\"see\":[\"G1125\",\"G1909\"]}', 'inscription, write in (over, thereon)'),
(1925, 1925, 'ἐπιδείκνυμι', '{\"def\":{\"short\":\"to exhibit (physically or mentally)\",\"long\":[\"to exhibit, show\",[\"to bring forth to view, to show\",[\"furnish to be looked at, produce what may looked at\",\"to display something belonging to one\'s self\"],\"to prove, demonstrate, set forth to be known and acknowledged\"]]},\"deriv\":\"from G1909 and G1166\",\"pronun\":{\"ipa\":\"ɛ.piˈði.kny.mi\",\"ipa_mod\":\"e̞.piˈði.knju.mi\",\"sbl\":\"epideiknymi\",\"dic\":\"eh-pee-THEE-knoo-mee\",\"dic_mod\":\"ay-pee-THEE-knyoo-mee\"},\"see\":[\"G1166\",\"G1909\"]}', 'shew'),
(1926, 1926, 'ἐπιδέχομαι', '{\"def\":{\"short\":\"to admit (as a guest or (figuratively) teacher)\",\"long\":[\"to receive hospitality\",\"to admit, i.e., not to reject\"]},\"deriv\":\"from G1909 and G1209\",\"pronun\":{\"ipa\":\"ɛ.piˈðɛ.xo.mɛ\",\"ipa_mod\":\"e̞.piˈðe̞.xow.me\",\"sbl\":\"epidechomai\",\"dic\":\"eh-pee-THEH-hoh-meh\",\"dic_mod\":\"ay-pee-THAY-hoh-may\"},\"see\":[\"G1209\",\"G1909\"]}', 'receive'),
(1927, 1927, 'ἐπιδημέω', '{\"def\":{\"short\":\"to make oneself at home, i.e., (by extension) to reside (in a foreign country)\",\"long\":[\"to be present among one\'s people, in one\'s city or one\'s native land\",\"to be a sojourner\",[\"of a foreign resident, among any people, in any country\"]]},\"deriv\":\"from a compound of G1909 and G1218\",\"pronun\":{\"ipa\":\"ɛ.pi.ðeˈmɛ.o\",\"ipa_mod\":\"e̞.pi.ðe̞ˈme̞.ow\",\"sbl\":\"epidēmeō\",\"dic\":\"eh-pee-thay-MEH-oh\",\"dic_mod\":\"ay-pee-thay-MAY-oh\"},\"see\":[\"G1218\",\"G1909\"]}', '(be) dwelling (which were) there, stranger'),
(1928, 1928, 'ἐπιδιατάσσομαι', '{\"def\":{\"short\":\"to appoint besides, i.e., supplement (as a codicil)\",\"long\":[\"to ordain besides, to add something to what has been ordained\"]},\"deriv\":\"middle voice from G1909 and G1299\",\"pronun\":{\"ipa\":\"ɛ.pi.ði.ɑˈtɑs.so.mɛ\",\"ipa_mod\":\"e̞.pi.ði.ɑˈtɑs.sow.me\",\"sbl\":\"epidiatassomai\",\"dic\":\"eh-pee-thee-ah-TAHS-soh-meh\",\"dic_mod\":\"ay-pee-thee-ah-TAHS-soh-may\"},\"see\":[\"G1299\",\"G1909\"]}', 'add to'),
(1929, 1929, 'ἐπιδίδωμι', '{\"def\":{\"short\":\"to give over (by hand or surrender)\",\"long\":[\"to hand, give by hand\",\"to give over\",[\"give up to the power or will of one\"]]},\"deriv\":\"from G1909 and G1325\",\"pronun\":{\"ipa\":\"ɛ.piˈði.ðo.mi\",\"ipa_mod\":\"e̞.piˈði.ðow.mi\",\"sbl\":\"epididōmi\",\"dic\":\"eh-pee-THEE-thoh-mee\",\"dic_mod\":\"ay-pee-THEE-thoh-mee\"},\"see\":[\"G1325\",\"G1909\"]}', 'deliver unto, give, let (+ (her drive)), offer'),
(1930, 1930, 'ἐπιδιορθόω', '{\"def\":{\"short\":\"to straighten further, i.e., (figuratively) arrange additionally\",\"long\":[\"to set in order besides or further\"]},\"deriv\":\"from G1909 and a derivative of G3717\",\"pronun\":{\"ipa\":\"ɛ.pi.ði.orˈθo.o\",\"ipa_mod\":\"e̞.pi.ði.owrˈθow.ow\",\"sbl\":\"epidiorthoō\",\"dic\":\"eh-pee-thee-ore-THOH-oh\",\"dic_mod\":\"ay-pee-thee-ore-THOH-oh\"},\"see\":[\"G1909\",\"G3717\"]}', 'set in order'),
(1931, 1931, 'ἐπιδύω', '{\"def\":{\"short\":\"to set fully (as the sun)\",\"long\":[\"to go down, set (of the sun)\"]},\"deriv\":\"from G1909 and G1416\",\"pronun\":{\"ipa\":\"ɛ.piˈðy.o\",\"ipa_mod\":\"e̞.piˈðju.ow\",\"sbl\":\"epidyō\",\"dic\":\"eh-pee-THOO-oh\",\"dic_mod\":\"ay-pee-THYOO-oh\"},\"see\":[\"G1416\",\"G1909\"]}', 'go down'),
(1932, 1932, 'ἐπιείκεια', '{\"def\":{\"short\":\"suitableness, i.e., (by implication) equity, mildness\",\"long\":[\"mildness, gentleness, fairness\"]},\"deriv\":\"from G1933\",\"pronun\":{\"ipa\":\"ɛ.piˈi.ki.ɑ\",\"ipa_mod\":\"e̞.piˈi.ci.ɑ\",\"sbl\":\"epieikeia\",\"dic\":\"eh-pee-EE-kee-ah\",\"dic_mod\":\"ay-pee-EE-kee-ah\"},\"see\":[\"G1933\"]}', 'clemency, gentleness'),
