(1934, 1934, 'ἐπιζητέω', '{\"def\":{\"short\":\"to search (inquire) for; intensively, to demand, to crave\",\"long\":[\"to enquire for, seek for, search for, seek diligently\",\"to wish for, crave\",\"to demand, clamour for\"]},\"deriv\":\"from G1909 and G2212\",\"pronun\":{\"ipa\":\"ɛ.pi.zeˈtɛ.o\",\"ipa_mod\":\"e̞.pi.ze̞ˈte̞.ow\",\"sbl\":\"epizēteō\",\"dic\":\"eh-pee-zay-TEH-oh\",\"dic_mod\":\"ay-pee-zay-TAY-oh\"},\"see\":[\"G1909\",\"G2212\"]}', 'desire, enquire, seek (after, for)'),
(1935, 1935, 'ἐπιθανάτιος', '{\"def\":{\"short\":\"doomed to death\",\"long\":[\"doomed to death\"]},\"deriv\":\"from G1909 and G2288\",\"pronun\":{\"ipa\":\"ɛ.pi.θɑˈnɑ.ti.os\",\"ipa_mod\":\"e̞.pi.θɑˈnɑ.ti.ows\",\"sbl\":\"epithanatios\",\"dic\":\"eh-pee-tha-NA-tee-ose\",\"dic_mod\":\"ay-pee-tha-NA-tee-ose\"},\"see\":[\"G1909\",\"G2288\"]}', 'appointed to death'),
(1936, 1936, 'ἐπίθεσις', '{\"def\":{\"short\":\"an imposition (of hands officially)\",\"long\":[\"a laying on, imposition\"]},\"deriv\":\"from G2007\",\"pronun\":{\"ipa\":\"ɛˈpi.θɛ.sis\",\"ipa_mod\":\"e̞ˈpi.θe̞.sis\",\"sbl\":\"epithesis\",\"dic\":\"eh-PEE-theh-sees\",\"dic_mod\":\"ay-PEE-thay-sees\"},\"see\":[\"G2007\"],\"comment\":\"The laying on of hands was a sacred rite transmitted by the Jews to the Christians and employed in praying for another or in conferring upon him divine blessings, especially bodily health or the Holy Spirit (at the administration of baptism and the inauguration to their office of the teachers and ministers of the church).\"}', 'laying (putting) on'),
(1937, 1937, 'ἐπιθυμέω', '{\"def\":{\"short\":\"to set the heart upon, i.e., long for (rightfully or otherwise)\",\"long\":[\"to turn upon a thing\",\"to have a desire for, long for, to desire\",\"to lust after, covet\",[\"of those who seek things forbidden\"]]},\"deriv\":\"from G1909 and G2372\",\"pronun\":{\"ipa\":\"ɛ.pi.θyˈmɛ.o\",\"ipa_mod\":\"e̞.pi.θjuˈme̞.ow\",\"sbl\":\"epithymeō\",\"dic\":\"eh-pee-thoo-MEH-oh\",\"dic_mod\":\"ay-pee-thyoo-MAY-oh\"},\"see\":[\"G1909\",\"G2372\"]}', 'covet, desire, would fain, lust (after)'),
(1938, 1938, 'ἐπιθυμητής', '{\"def\":{\"short\":\"a craver\",\"long\":[\"one who longs for, a craver, lover, one eager for\"]},\"deriv\":\"from G1937\",\"pronun\":{\"ipa\":\"ɛ.pi.θy.meˈtes\",\"ipa_mod\":\"e̞.pi.θju.me̞ˈte̞s\",\"sbl\":\"epithymētēs\",\"dic\":\"eh-pee-thoo-may-TASE\",\"dic_mod\":\"ay-pee-thyoo-may-TASE\"},\"see\":[\"G1937\"]}', '+ lust after'),
(1939, 1939, 'ἐπιθυμία', '{\"def\":{\"short\":\"a longing (especially for what is forbidden)\",\"long\":[\"desire, craving, longing, desire for what is forbidden, lust\"]},\"deriv\":\"from G1937\",\"pronun\":{\"ipa\":\"ɛ.pi.θyˈmi.ɑ\",\"ipa_mod\":\"e̞.pi.θjuˈmi.ɑ\",\"sbl\":\"epithymia\",\"dic\":\"eh-pee-thoo-MEE-ah\",\"dic_mod\":\"ay-pee-thyoo-MEE-ah\"},\"see\":[\"G1937\"]}', 'concupiscence, desire, lust (after)'),
(1940, 1940, 'ἐπικαθίζω', '{\"def\":{\"short\":\"to seat upon\",\"long\":[\"to cause to sit upon, to set upon\",\"to sit upon\"]},\"deriv\":\"from G1909 and G2523\",\"pronun\":{\"ipa\":\"ɛ.pi.kɑˈθi.zo\",\"ipa_mod\":\"e̞.pi.kɑˈθi.zow\",\"sbl\":\"epikathizō\",\"dic\":\"eh-pee-ka-THEE-zoh\",\"dic_mod\":\"ay-pee-ka-THEE-zoh\"},\"see\":[\"G1909\",\"G2523\"]}', 'set on'),
(1941, 1941, 'ἐπικαλέομαι', '{\"def\":{\"short\":\"to entitle; by implication, to invoke (for aid, worship, testimony, decision, etc.)\",\"long\":[\"to put a name upon, to surname\",[\"to permit one\'s self to be surnamed\"],\"to be named after someone\",\"to call something to one\",[\"to cry out upon or against one\",\"to charge something to one as a crime or reproach\",\"to summon one on any charge, prosecute one for a crime\",\"to blame one for, accuse one of\"],\"to invoke\",[\"to call upon for one\'s self, in one\'s behalf\",[\"any one as a helper\",\"as my witness\",\"as my judge\",\"to appeal unto\"]],\"to call upon by pronouncing the name of Jehovah\",[\"an expression finding its explanation in the fact that prayers addressed to God ordinarily began with an invocation of the divine name\"]]},\"deriv\":\"middle voice from G1909 and G2564\",\"pronun\":{\"ipa\":\"ɛ.pi.kɑˈlɛ.o.mɛ\",\"ipa_mod\":\"e̞.pi.kɑˈle̞.ow.me\",\"sbl\":\"epikaleomai\",\"dic\":\"eh-pee-ka-LEH-oh-meh\",\"dic_mod\":\"ay-pee-ka-LAY-oh-may\"},\"see\":[\"G1909\",\"G2564\"]}', 'appeal (unto), call (on, upon), surname'),
