(1943, 1943, 'ἐπικαλύπτω', '{\"def\":{\"short\":\"to conceal, i.e., (figuratively) forgive\",\"long\":[\"to cover over\"]},\"deriv\":\"from G1909 and G2572\",\"pronun\":{\"ipa\":\"ɛ.pi.kɑˈly.pto\",\"ipa_mod\":\"e̞.pi.kɑˈlju.ptow\",\"sbl\":\"epikalyptō\",\"dic\":\"eh-pee-ka-LOO-ptoh\",\"dic_mod\":\"ay-pee-ka-LYOO-ptoh\"},\"see\":[\"G1909\",\"G2572\"]}', 'cover'),
(1944, 1944, 'ἐπικατάρατος', '{\"def\":{\"short\":\"imprecated, i.e., execrable\",\"long\":[\"accursed, execrable, exposed to divine vengeance, lying under God\'s curse\"]},\"deriv\":\"from G1909 and a derivative of G2672\",\"pronun\":{\"ipa\":\"ɛ.pi.kɑˈtɑ.rɑ.tos\",\"ipa_mod\":\"e̞.pi.kɑˈtɑ.rɑ.tows\",\"sbl\":\"epikataratos\",\"dic\":\"eh-pee-ka-TA-ra-tose\",\"dic_mod\":\"ay-pee-ka-TA-ra-tose\"},\"see\":[\"G1909\",\"G2672\"]}', 'accursed'),
(1945, 1945, 'ἐπίκειμαι', '{\"def\":{\"short\":\"to rest upon (literally or figuratively)\",\"long\":[\"to lie upon or over, rest upon, be laid or placed upon\",[\"on the burning coals\"],\"metaphorically\",[\"of things, of the pressure of a violent tempest\",\"of men, to press upon, to be urgent\"]]},\"deriv\":\"from G1909 and G2749\",\"pronun\":{\"ipa\":\"ɛˈpi.ki.mɛ\",\"ipa_mod\":\"e̞ˈpi.ci.me\",\"sbl\":\"epikeimai\",\"dic\":\"eh-PEE-kee-meh\",\"dic_mod\":\"ay-PEE-kee-may\"},\"see\":[\"G1909\",\"G2749\"]}', 'impose, be instant, (be) laid (there-, up-)on, (when) lay (on), lie (on), press upon'),
(1946, 1946, 'Ἐπικούρειος', '{\"def\":{\"lit\":\"a helper, defender\",\"short\":\"an Epicurean or follower of Epicurus\",\"long\":[\"belonging to the sect of Epicurius, the philosopher\"]},\"deriv\":\"from Ἐπίκουρος (compare G1947) (a noted philosopher)\",\"pronun\":{\"ipa\":\"ɛ.piˈku.ri.os\",\"ipa_mod\":\"e̞.piˈku.ri.ows\",\"sbl\":\"epikoureios\",\"dic\":\"eh-pee-KOO-ree-ose\",\"dic_mod\":\"ay-pee-KOO-ree-ose\"},\"see\":[\"G1947\"]}', 'Epicurean'),
