(1955, 1955, 'ἐπίλυσις', '{\"def\":{\"short\":\"explanation, i.e., application\",\"long\":[\"a loosening, unloosing\",\"metaphorically interpretation\"]},\"deriv\":\"from G1956\",\"pronun\":{\"ipa\":\"ɛˈpi.ly.sis\",\"ipa_mod\":\"e̞ˈpi.lju.sis\",\"sbl\":\"epilysis\",\"dic\":\"eh-PEE-loo-sees\",\"dic_mod\":\"ay-PEE-lyoo-sees\"},\"see\":[\"G1956\"]}', 'interpretation'),
(1956, 1956, 'ἐπιλύω', '{\"def\":{\"short\":\"to solve further, i.e., (figuratively) to explain, decide\",\"long\":[\"to unloose, untie\",\"to clear (a controversy), decide, settle\",\"to explain (what is obscure and hard to understand)\"]},\"deriv\":\"from G1909 and G3089\",\"pronun\":{\"ipa\":\"ɛ.piˈly.o\",\"ipa_mod\":\"e̞.piˈlju.ow\",\"sbl\":\"epilyō\",\"dic\":\"eh-pee-LOO-oh\",\"dic_mod\":\"ay-pee-LYOO-oh\"},\"see\":[\"G1909\",\"G3089\"]}', 'determine, expound'),
