(1960, 1960, 'ἐπιμελῶς', '{\"def\":{\"short\":\"carefully\",\"long\":[\"diligently, carefully\"]},\"deriv\":\"adverb from a derivative of G1959\",\"pronun\":{\"ipa\":\"ɛ.pi.mɛˈlos\",\"ipa_mod\":\"e̞.pi.me̞ˈlows\",\"sbl\":\"epimelōs\",\"dic\":\"eh-pee-meh-LOSE\",\"dic_mod\":\"ay-pee-may-LOSE\"},\"see\":[\"G1959\"]}', 'diligently'),
(1961, 1961, 'ἐπιμένω', '{\"def\":{\"short\":\"to stay over, i.e., remain (figuratively, persevere)\",\"long\":[\"to stay at or with, to tarry still, still to abide, to continue, remain\",[\"of tarrying in a place\",\"to persevere, continue\",[\"of the thing continued in\",\"in the work of teaching\",\"of the blessing for which one keeps himself fit\",\"denoting the action persisted in\"]]]},\"deriv\":\"from G1909 and G3306\",\"pronun\":{\"ipa\":\"ɛ.piˈmɛ.no\",\"ipa_mod\":\"e̞.piˈme̞.now\",\"sbl\":\"epimenō\",\"dic\":\"eh-pee-MEH-noh\",\"dic_mod\":\"ay-pee-MAY-noh\"},\"see\":[\"G1909\",\"G3306\"]}', 'abide (in), continue (in), tarry'),
(1962, 1962, 'ἐπινεύω', '{\"def\":{\"short\":\"to nod at, i.e., (by implication) to assent\",\"long\":[\"to nod to\",\"to express approval, to assent\"]},\"deriv\":\"from G1909 and G3506\",\"pronun\":{\"ipa\":\"ɛ.piˈnɛβ.o\",\"ipa_mod\":\"e̞.piˈnev.ow\",\"sbl\":\"epineuō\",\"dic\":\"eh-pee-NEV-oh\",\"dic_mod\":\"ay-pee-NAVE-oh\"},\"see\":[\"G1909\",\"G3506\"]}', 'consent'),
(1963, 1963, 'ἐπίνοια', '{\"def\":{\"short\":\"attention of the mind, i.e., (by implication) purpose\",\"long\":[\"thought, purpose\"]},\"deriv\":\"from G1909 and G3563\",\"pronun\":{\"ipa\":\"ɛˈpi.ny.ɑ\",\"ipa_mod\":\"e̞ˈpi.ny.ɑ\",\"sbl\":\"epinoia\",\"dic\":\"eh-PEE-noo-ah\",\"dic_mod\":\"ay-PEE-noo-ah\"},\"see\":[\"G1909\",\"G3563\"]}', 'thought'),
