(2047, 2047, 'ἐρημία', '{\"def\":{\"short\":\"solitude (concretely)\",\"long\":[\"a solitude, an uninhabited region, a waste\"]},\"deriv\":\"from G2048\",\"pronun\":{\"ipa\":\"ɛ.reˈmi.ɑ\",\"ipa_mod\":\"e̞.re̞ˈmi.ɑ\",\"sbl\":\"erēmia\",\"dic\":\"eh-ray-MEE-ah\",\"dic_mod\":\"ay-ray-MEE-ah\"},\"see\":[\"G2048\"]}', 'desert, wilderness'),
(2048, 2048, 'ἔρημος', '{\"def\":{\"short\":\"lonesome, i.e., (by implication) waste (usually as a noun, G5561 being implied)\",\"long\":[\"solitary, lonely, desolate, uninhabited\",[\"used of places\",[\"a desert, wilderness\",\"deserted places, lonely regions\",\"an uncultivated region fit for pasturage\"],\"used of persons\",[\"deserted by others\",\"deprived of the aid and protection of others, especially of friends, acquaintances, kindred\",\"bereft\",[\"of a flock deserted by the shepherd\",\"of a women neglected by her husband, from whom the husband withholds himself\"]]]]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈɛ.re.mos\",\"ipa_mod\":\"ˈe̞.re̞.mows\",\"sbl\":\"erēmos\",\"dic\":\"EH-ray-mose\",\"dic_mod\":\"A-ray-mose\"},\"see\":[\"G5561\"]}', 'desert, desolate, solitary, wilderness'),
