(2050, 2050, 'ἐρήμωσις', '{\"def\":{\"short\":\"despoliation\",\"long\":[\"a making desolate, desolation\"]},\"deriv\":\"from G2049\",\"pronun\":{\"ipa\":\"ɛˈre.mo.sis\",\"ipa_mod\":\"e̞ˈre̞.mow.sis\",\"sbl\":\"erēmōsis\",\"dic\":\"eh-RAY-moh-sees\",\"dic_mod\":\"ay-RAY-moh-sees\"},\"see\":[\"G2049\"]}', 'desolation'),
(2051, 2051, 'ἐρίζω', '{\"def\":{\"short\":\"to wrangle\",\"long\":[\"to wrangle, engage in strife\",[\"used to describe the calm temper of Jesus in contrast with the vehemence of the Jewish doctors wrangling together about tenets and practices\"]]},\"deriv\":\"from G2054\",\"pronun\":{\"ipa\":\"ɛˈri.zo\",\"ipa_mod\":\"e̞ˈri.zow\",\"sbl\":\"erizō\",\"dic\":\"eh-REE-zoh\",\"dic_mod\":\"ay-REE-zoh\"},\"see\":[\"G2054\"]}', 'strive'),
(2052, 2052, 'ἐριθεία', '{\"def\":{\"short\":\"properly, intrigue, i.e., (by implication) faction\",\"long\":[\"electioneering or intriguing for office\",[\"apparently, in the New Testament a courting distinction, a desire to put one\'s self forward, a partisan and fractious spirit which does not disdain low arts\",\"partisanship, fractiousness\"]]},\"deriv\":\"perhaps as the same as G2042\",\"pronun\":{\"ipa\":\"ɛ.riˈθi.ɑ\",\"ipa_mod\":\"e̞.riˈθi.ɑ\",\"sbl\":\"eritheia\",\"dic\":\"eh-ree-THEE-ah\",\"dic_mod\":\"ay-ree-THEE-ah\"},\"see\":[\"G2042\"]}', 'contention(-ious), strife'),
