(2057, 2057, 'Ἑρμᾶς', '{\"def\":{\"lit\":\"Mercury\",\"short\":\"Hermas, a Christian\",\"long\":[\"a Christian known by Paul\"]},\"deriv\":\"probably from G2060\",\"pronun\":{\"ipa\":\"hɛrˈmɑs\",\"ipa_mod\":\"e̞rˈmɑs\",\"sbl\":\"hermas\",\"dic\":\"her-MAHS\",\"dic_mod\":\"are-MAHS\"},\"see\":[\"G2060\"],\"comment\":\"According to tradition, he was one of the seventy disciples, and afterwards bishop of Dalmatia in AD 55.\"}', 'Hermas'),
(2058, 2058, 'ἑρμηνεία', '{\"def\":{\"short\":\"translation\",\"long\":[\"interpretation\",[\"of what has been spoken more or less obscurely by others\"]]},\"deriv\":\"from the same as G2059\",\"pronun\":{\"ipa\":\"hɛr.meˈni.ɑ\",\"ipa_mod\":\"e̞r.me̞ˈni.ɑ\",\"sbl\":\"hermēneia\",\"dic\":\"her-may-NEE-ah\",\"dic_mod\":\"are-may-NEE-ah\"},\"see\":[\"G2059\"]}', 'interpretation'),
