(2060, 2060, 'Ἑρμῆς', '{\"def\":{\"lit\":\"herald of the gods\",\"short\":\"Hermes, the name of the messenger of the Greek deities; also of a Christian\",\"long\":[\"a Greek deity called by the Romans Mercurius (Mercury)\",\"a certain Christian\"]},\"deriv\":\"perhaps from G2046\",\"pronun\":{\"ipa\":\"hɛrˈmes\",\"ipa_mod\":\"e̞rˈme̞s\",\"sbl\":\"hermēs\",\"dic\":\"her-MASE\",\"dic_mod\":\"are-MASE\"},\"see\":[\"G2046\"]}', 'Hermes, Mercury'),
(2061, 2061, 'Ἑρμογένης', '{\"def\":{\"lit\":\"lucky born, born of Mercury\",\"short\":\"born of Hermes; Hermogenes, an apostate Christian\",\"long\":[\"a certain Christian mentioned in 2 Timothy 1:15\"]},\"deriv\":\"from G2060 and G1096\",\"pronun\":{\"ipa\":\"hɛr.moˈɣɛ.nes\",\"ipa_mod\":\"e̞r.mowˈɣe̞.ne̞s\",\"sbl\":\"hermogenēs\",\"dic\":\"her-moh-GEH-nase\",\"dic_mod\":\"are-moh-GAY-nase\"},\"see\":[\"G1096\",\"G2060\"]}', 'Hermogenes'),
(2062, 2062, 'ἑρπετόν', '{\"def\":{\"short\":\"a reptile, i.e., (by Hebraism (compare H7431)) a small animal\",\"long\":[\"a creeping animal, reptile\",[\"used chiefly of snakes\"],\"an animal of any sort\",[\"four-legged animals and birds\",\"marine animals\"]]},\"deriv\":\"neuter of a derivative of ἕρπω (to creep)\",\"pronun\":{\"ipa\":\"hɛr.pɛˈton\",\"ipa_mod\":\"e̞r.pe̞ˈtown\",\"sbl\":\"herpeton\",\"dic\":\"her-peh-TONE\",\"dic_mod\":\"are-pay-TONE\"},\"see\":[\"H7431\"]}', 'creeping thing, serpent'),
(2063, 2063, 'ἐρυθρός', '{\"def\":{\"short\":\"red, i.e., (with G2281) the Red Sea\",\"long\":[\"the Red Sea\",[\"the upper part of the Arabian Gulf (perhaps also called the Heropolitie Gulf or Gulf of Suez)\"]]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ɛ.ryˈθros\",\"ipa_mod\":\"e̞.rjuˈθrows\",\"sbl\":\"erythros\",\"dic\":\"eh-roo-THROSE\",\"dic_mod\":\"ay-ryoo-THROSE\"},\"see\":[\"G2281\"],\"comment\":\"The sea through which the Israelites made their passage out of Egypt to the shore of Arabia.\"}', 'red'),
