(2067, 2067, 'ἔσθησις', '{\"def\":{\"short\":\"clothing (concretely)\",\"long\":[\"clothing, apparel\"]},\"deriv\":\"from a derivative of G2066\",\"pronun\":{\"ipa\":\"ˈɛ.sθe.sis\",\"ipa_mod\":\"ˈe̞.sθe̞.sis\",\"sbl\":\"esthēsis\",\"dic\":\"EH-sthay-sees\",\"dic_mod\":\"A-sthay-sees\"},\"see\":[\"G2066\"]}', 'government'),
