(2080, 2080, 'ἔσω', '{\"def\":{\"short\":\"inside (as preposition or adjective)\",\"long\":[\"to within, into\",\"within\",[\"the internal inner man\",\"the soul, conscience\"]]},\"deriv\":\"from G1519\",\"pronun\":{\"ipa\":\"ˈɛ.so\",\"ipa_mod\":\"ˈe̞.sow\",\"sbl\":\"esō\",\"dic\":\"EH-soh\",\"dic_mod\":\"A-soh\"},\"see\":[\"G1519\"]}', '(with-)in(-ner, -to, -ward)'),
