(2623, 2623, 'κατακλείω', '{\"def\":{\"short\":\"to shut down (in a dungeon), i.e., incarcerate\",\"long\":[\"to shut up, confine\"]},\"deriv\":\"from G2596 and G2808\",\"pronun\":{\"ipa\":\"kɑ.tɑˈkli.o\",\"ipa_mod\":\"kɑ.tɑˈkli.ow\",\"sbl\":\"katakleiō\",\"dic\":\"ka-ta-KLEE-oh\",\"dic_mod\":\"ka-ta-KLEE-oh\"},\"see\":[\"G2596\",\"G2808\"]}', 'shut up'),
(2624, 2624, 'κατακληροδοτέω', '{\"def\":{\"short\":\"to be a giver of lots to each, i.e., (by implication) to apportion an estate\",\"long\":[\"to distribute by lot\",\"to distribute as an inheritance\"]},\"deriv\":\"from G2596 and a derivative of a compound of G2819 and G1325\",\"pronun\":{\"ipa\":\"kɑ.tɑ.kle.ro.ðoˈtɛ.o\",\"ipa_mod\":\"kɑ.tɑ.kle̞.row.ðowˈte̞.ow\",\"sbl\":\"kataklērodoteō\",\"dic\":\"ka-ta-klay-roh-thoh-TEH-oh\",\"dic_mod\":\"ka-ta-klay-roh-thoh-TAY-oh\"},\"see\":[\"G1325\",\"G2596\",\"G2819\"]}', 'divide by lot'),
(2625, 2625, 'κατακλίνω', '{\"def\":{\"short\":\"to recline down, i.e., (specially) to take a place at table\",\"long\":[\"in the New Testament in reference to eating, to make to recline\",\"to recline (at a table)\"]},\"deriv\":\"from G2596 and G2827\",\"pronun\":{\"ipa\":\"kɑ.tɑˈkli.no\",\"ipa_mod\":\"kɑ.tɑˈkli.now\",\"sbl\":\"kataklinō\",\"dic\":\"ka-ta-KLEE-noh\",\"dic_mod\":\"ka-ta-KLEE-noh\"},\"see\":[\"G2596\",\"G2827\"]}', '(make) sit down (at meat)'),
(2626, 2626, 'κατακλύζω', '{\"def\":{\"short\":\"to dash (wash) down, i.e., (by implication) to deluge\",\"long\":[\"to overwhelm with water, to submerge, deluge\"]},\"deriv\":\"from G2596 and the base of G2830\",\"pronun\":{\"ipa\":\"kɑ.tɑˈkly.zo\",\"ipa_mod\":\"kɑ.tɑˈklju.zow\",\"sbl\":\"kataklyzō\",\"dic\":\"ka-ta-KLOO-zoh\",\"dic_mod\":\"ka-ta-KLYOO-zoh\"},\"see\":[\"G2596\",\"G2830\"]}', 'overflow'),
(2627, 2627, 'κατακλυσμός', '{\"def\":{\"short\":\"an inundation\",\"long\":[\"inundation, deluge\",[\"of Noah\'s deluge\"]]},\"deriv\":\"from G2626\",\"pronun\":{\"ipa\":\"kɑ.tɑ.klyˈsmos\",\"ipa_mod\":\"kɑ.tɑ.kljuˈsmows\",\"sbl\":\"kataklysmos\",\"dic\":\"ka-ta-kloo-SMOSE\",\"dic_mod\":\"ka-ta-klyoo-SMOSE\"},\"see\":[\"G2626\"]}', 'flood'),
(2628, 2628, 'κατακολουθέω', '{\"def\":{\"short\":\"to accompany closely\",\"long\":[\"to follow after\"]},\"deriv\":\"from G2596 and G0190\",\"pronun\":{\"ipa\":\"kɑ.tɑ.ko.luˈθɛ.o\",\"ipa_mod\":\"kɑ.tɑ.kow.luˈθe̞.ow\",\"sbl\":\"katakoloutheō\",\"dic\":\"ka-ta-koh-loo-THEH-oh\",\"dic_mod\":\"ka-ta-koh-loo-THAY-oh\"},\"see\":[\"G0190\",\"G2596\"]}', 'follow (after)'),
(2629, 2629, 'κατακόπτω', '{\"def\":{\"short\":\"to chop down, i.e., mangle\",\"long\":[\"to cut up in pieces\",\"to slay\",\"to beat, bruise\",\"to cut, gash, mangle\"]},\"deriv\":\"from G2596 and G2875\",\"pronun\":{\"ipa\":\"kɑ.tɑˈko.pto\",\"ipa_mod\":\"kɑ.tɑˈkow.ptow\",\"sbl\":\"katakoptō\",\"dic\":\"ka-ta-KOH-ptoh\",\"dic_mod\":\"ka-ta-KOH-ptoh\"},\"see\":[\"G2596\",\"G2875\"]}', 'cut'),
(2630, 2630, 'κατακρημνίζω', '{\"def\":{\"short\":\"to precipitate down\",\"long\":[\"to cast down a precipice\",\"to through down headlong\"]},\"deriv\":\"from G2596 and a derivative of G2911\",\"pronun\":{\"ipa\":\"kɑ.tɑ.kremˈni.zo\",\"ipa_mod\":\"kɑ.tɑ.kre̞mˈni.zow\",\"sbl\":\"katakrēmnizō\",\"dic\":\"ka-ta-krame-NEE-zoh\",\"dic_mod\":\"ka-ta-krame-NEE-zoh\"},\"see\":[\"G2596\",\"G2911\"]}', 'cast down headlong'),
(2631, 2631, 'κατάκριμα', '{\"def\":{\"short\":\"an adverse sentence (the verdict)\",\"long\":[\"damnatory sentence, condemnation\"]},\"deriv\":\"from G2632\",\"pronun\":{\"ipa\":\"kɑˈtɑ.kri.mɑ\",\"ipa_mod\":\"kɑˈtɑ.kri.mɑ\",\"sbl\":\"katakrima\",\"dic\":\"ka-TA-kree-ma\",\"dic_mod\":\"ka-TA-kree-ma\"},\"see\":[\"G2632\"]}', 'condemnation'),
(2632, 2632, 'κατακρίνω', '{\"def\":{\"short\":\"to judge against, i.e., sentence\",\"long\":[\"to give judgment against, to judge worthy of punishment\",[\"to condemn\",\"by one\'s good example to render another\'s wickedness the more evident and censurable\"]]},\"deriv\":\"from G2596 and G2919\",\"pronun\":{\"ipa\":\"kɑ.tɑˈkri.no\",\"ipa_mod\":\"kɑ.tɑˈkri.now\",\"sbl\":\"katakrinō\",\"dic\":\"ka-ta-KREE-noh\",\"dic_mod\":\"ka-ta-KREE-noh\"},\"see\":[\"G2596\",\"G2919\"]}', 'condemn, damn'),
(2633, 2633, 'κατάκρισις', '{\"def\":{\"short\":\"sentencing adversely (the act)\",\"long\":[\"condemnation\"]},\"deriv\":\"from G2632\",\"pronun\":{\"ipa\":\"kɑˈtɑ.kri.sis\",\"ipa_mod\":\"kɑˈtɑ.kri.sis\",\"sbl\":\"katakrisis\",\"dic\":\"ka-TA-kree-sees\",\"dic_mod\":\"ka-TA-kree-sees\"},\"see\":[\"G2632\"]}', 'condemn(-ation)'),
(2634, 2634, 'κατακυριεύω', '{\"def\":{\"short\":\"to lord against, i.e., control, subjugate\",\"long\":[\"to bring under one\'s power, to subject one\'s self, to subdue, master\",\"to hold in subjection, to be master of, exercise lordship over\"]},\"deriv\":\"from G2596 and G2961\",\"pronun\":{\"ipa\":\"kɑ.tɑ.ky.riˈɛβ.o\",\"ipa_mod\":\"kɑ.tɑ.cju.riˈev.ow\",\"sbl\":\"katakyrieuō\",\"dic\":\"ka-ta-koo-ree-EV-oh\",\"dic_mod\":\"ka-ta-kyoo-ree-AVE-oh\"},\"see\":[\"G2596\",\"G2961\"]}', 'exercise dominion over (lordship), be lord over, overcome');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(2635, 2635, 'καταλαλέω', '{\"def\":{\"short\":\"to be a traducer, i.e., to slander\",\"long\":[\"to speak against one, to criminate, traduce\"]},\"deriv\":\"from G2637\",\"pronun\":{\"ipa\":\"kɑ.tɑ.lɑˈlɛ.o\",\"ipa_mod\":\"kɑ.tɑ.lɑˈle̞.ow\",\"sbl\":\"katalaleō\",\"dic\":\"ka-ta-la-LEH-oh\",\"dic_mod\":\"ka-ta-la-LAY-oh\"},\"see\":[\"G2637\"]}', 'speak against (evil of)'),
(2636, 2636, 'καταλαλία', '{\"def\":{\"short\":\"defamation\",\"long\":[\"defamation, evil speaking\"]},\"deriv\":\"from G2637\",\"pronun\":{\"ipa\":\"kɑ.tɑ.lɑˈli.ɑ\",\"ipa_mod\":\"kɑ.tɑ.lɑˈli.ɑ\",\"sbl\":\"katalalia\",\"dic\":\"ka-ta-la-LEE-ah\",\"dic_mod\":\"ka-ta-la-LEE-ah\"},\"see\":[\"G2637\"]}', 'backbiting, evil speaking'),
(2637, 2637, 'κατάλαλος', '{\"def\":{\"short\":\"talkative against, i.e., a slanderer\",\"long\":[\"a defamer, evil speaker\"]},\"deriv\":\"from G2596 and the base of G2980\",\"pronun\":{\"ipa\":\"kɑˈtɑ.lɑ.los\",\"ipa_mod\":\"kɑˈtɑ.lɑ.lows\",\"sbl\":\"katalalos\",\"dic\":\"ka-TA-la-lose\",\"dic_mod\":\"ka-TA-la-lose\"},\"see\":[\"G2596\",\"G2980\"]}', 'backbiter'),
(2638, 2638, 'καταλαμβάνω', '{\"def\":{\"short\":\"to take eagerly, i.e., seize, possess, etc. (literally or figuratively)\",\"long\":[\"to lay hold of\",[\"to lay hold of so as to make one\'s own, to obtain, attain to, to make one\'s own, to take into one\'s self, appropriate\",\"to seize upon, take possession of\",[\"of evils overtaking one, of the last day overtaking the wicked with destruction, of a demon about to torment one\",\"in a good sense, of Christ by his holy power and influence laying hold of the human mind and will, in order to prompt and govern it\"],\"to detect, catch\",\"to lay hold of with the mind\",[\"to understand, perceive, learn, comprehend\"]]]},\"deriv\":\"from G2596 and G2983\",\"pronun\":{\"ipa\":\"kɑ.tɑ.lɑmˈβɑ.no\",\"ipa_mod\":\"kɑ.tɑ.lɑɱˈvɑ.now\",\"sbl\":\"katalambanō\",\"dic\":\"ka-ta-lahm-VA-noh\",\"dic_mod\":\"ka-ta-lahm-VA-noh\"},\"see\":[\"G2596\",\"G2983\"]}', 'apprehend, attain, come upon, comprehend, find, obtain, perceive, (over-)take'),
(2639, 2639, 'καταλέγω', '{\"def\":{\"short\":\"to lay down, i.e., (figuratively) to enrol\",\"long\":[\"to lay down, to lie down\",\"to narrate at length, recount, set forth\",\"to set down in a list or register, to enrol\",[\"of soldiers\",\"of those widows who held a prominent place in the church and exercised a certain superintendence over the rest of the women, and had charge of the widows and orphans supported at the public expense\"]]},\"deriv\":\"from G2596 and G3004 (in its original meaning)\",\"pronun\":{\"ipa\":\"kɑ.tɑˈlɛ.ɣo\",\"ipa_mod\":\"kɑ.tɑˈle̞.ɣow\",\"sbl\":\"katalegō\",\"dic\":\"ka-ta-LEH-goh\",\"dic_mod\":\"ka-ta-LAY-goh\"},\"see\":[\"G2596\",\"G3004\"]}', 'take into the number'),
(2640, 2640, 'κατάλειμμα', '{\"def\":{\"short\":\"a remainder, i.e., (by implication) a few\",\"long\":[\"remnant, remains\"]},\"deriv\":\"from G2641\",\"pronun\":{\"ipa\":\"kɑˈtɑ.lim.mɑ\",\"ipa_mod\":\"kɑˈtɑ.lim.mɑ\",\"sbl\":\"kataleimma\",\"dic\":\"ka-TA-leem-ma\",\"dic_mod\":\"ka-TA-leem-ma\"},\"see\":[\"G2641\"]}', 'remnant'),
(2641, 2641, 'καταλείπω', '{\"def\":{\"short\":\"to leave down, i.e., behind; by implication, to abandon, have remaining\",\"long\":[\"to leave behind\",[\"to depart from, leave\",[\"to be left\"],\"to bid (one) to remain\",\"to forsake, leave to one\'s self a person or thing by ceasing to care for it, to abandon, leave in the lurch\",[\"to be abandoned, forsaken\"],\"to cause to be left over, to reserve, to leave remaining\",\"like our \\\"leave behind\\\", it is used of one who on being called away cannot take another with him\",[\"especially of the dying (to leave behind)\"],\"like our \\\"leave\\\", leave alone, disregard\",[\"of those who sail past a place without stopping\"]]]},\"deriv\":\"from G2596 and G3007\",\"pronun\":{\"ipa\":\"kɑ.tɑˈli.po\",\"ipa_mod\":\"kɑ.tɑˈli.pow\",\"sbl\":\"kataleipō\",\"dic\":\"ka-ta-LEE-poh\",\"dic_mod\":\"ka-ta-LEE-poh\"},\"see\":[\"G2596\",\"G3007\"]}', 'forsake, leave, reserve'),
(2642, 2642, 'καταλιθάζω', '{\"def\":{\"short\":\"to stone down, i.e., to death\",\"long\":[\"to overwhelm with stones, to stone\"]},\"deriv\":\"from G2596 and G3034\",\"pronun\":{\"ipa\":\"kɑ.tɑ.liˈθɑ.zo\",\"ipa_mod\":\"kɑ.tɑ.liˈθɑ.zow\",\"sbl\":\"katalithazō\",\"dic\":\"ka-ta-lee-THA-zoh\",\"dic_mod\":\"ka-ta-lee-THA-zoh\"},\"see\":[\"G2596\",\"G3034\"]}', 'stone'),
(2643, 2643, 'καταλλαγή', '{\"def\":{\"short\":\"exchange (figuratively, adjustment), i.e., restoration to (the divine) favor\",\"long\":[\"exchange\",[\"of the business of money changers, exchanging equivalent values\"],\"adjustment of a difference, reconciliation, restoration to favor\",[\"in the New Testament of the restoration of the favor of God to sinners that repent and put their trust in the expiatory death of Christ\"]]},\"deriv\":\"from G2644\",\"pronun\":{\"ipa\":\"kɑ.tɑl.lɑˈɣe\",\"ipa_mod\":\"kɑ.tɑl.lɑˈɣe̞\",\"sbl\":\"katallagē\",\"dic\":\"ka-tahl-la-GAY\",\"dic_mod\":\"ka-tahl-la-GAY\"},\"see\":[\"G2644\"]}', 'atonement, reconciliation(-ing)'),
(2644, 2644, 'καταλλάσσω', '{\"def\":{\"short\":\"to change mutually, i.e., (figuratively) to compound a difference\",\"long\":[\"to change, exchange, as coins for others of equivalent value\",[\"to reconcile (those who are at variance)\",\"return to favor with, be reconciled to one\",\"to receive one into favor\"]]},\"deriv\":\"from G2596 and G0236\",\"pronun\":{\"ipa\":\"kɑ.tɑlˈlɑs.so\",\"ipa_mod\":\"kɑ.tɑlˈlɑs.sow\",\"sbl\":\"katallassō\",\"dic\":\"ka-tahl-LAHS-soh\",\"dic_mod\":\"ka-tahl-LAHS-soh\"},\"see\":[\"G0236\",\"G2596\"]}', 'reconcile'),
(2645, 2645, 'κατάλοιπος', '{\"def\":{\"short\":\"left down (behind), i.e remaining (plural the rest)\",\"long\":[\"left remaining\"]},\"deriv\":\"from G2596 and G3062\",\"pronun\":{\"ipa\":\"kɑˈtɑ.ly.pos\",\"ipa_mod\":\"kɑˈtɑ.ly.pows\",\"sbl\":\"kataloipos\",\"dic\":\"ka-TA-loo-pose\",\"dic_mod\":\"ka-TA-loo-pose\"},\"see\":[\"G2596\",\"G3062\"]}', 'residue'),
(2646, 2646, 'κατάλυμα', '{\"def\":{\"short\":\"properly, a dissolution (breaking up of a journey), i.e., (by implication) a lodging-place\",\"long\":[\"an inn, lodging place\",\"an eating room, dining room\"]},\"deriv\":\"from G2647\",\"pronun\":{\"ipa\":\"kɑˈtɑ.ly.mɑ\",\"ipa_mod\":\"kɑˈtɑ.lju.mɑ\",\"sbl\":\"katalyma\",\"dic\":\"ka-TA-loo-ma\",\"dic_mod\":\"ka-TA-lyoo-ma\"},\"see\":[\"G2647\"]}', 'guestchamber, inn'),
(2647, 2647, 'καταλύω', '{\"def\":{\"short\":\"to loosen down (disintegrate), i.e., (by implication) to demolish (literally or figuratively); specially (compare G2646) to halt for the night\",\"long\":[\"to dissolve, disunite\",[\"(what has been joined together), to destroy, demolish\",\"metaphorically to overthrow i.e., render vain, deprive of success, bring to naught\",[\"to subvert, overthrow\",[\"of institutions, forms of government, laws, etc., to deprive of force, annul, abrogate, discard\"]],\"of travellers, to halt on a journey, to put up, lodge (the figurative expression originating in the circumstance that, to put up for the night, the straps and packs of the beasts of burden are unbound and taken off; or, more correctly from the fact that the traveller\'s garments, tied up when he is on the journey, are unloosed at it end)\"]]},\"deriv\":\"from G2596 and G3089\",\"pronun\":{\"ipa\":\"kɑ.tɑˈly.o\",\"ipa_mod\":\"kɑ.tɑˈlju.ow\",\"sbl\":\"katalyō\",\"dic\":\"ka-ta-LOO-oh\",\"dic_mod\":\"ka-ta-LYOO-oh\"},\"see\":[\"G2596\",\"G2646\",\"G3089\"]}', 'destroy, dissolve, be guest, lodge, come to nought, overthrow, throw down'),
(2648, 2648, 'καταμανθάνω', '{\"def\":{\"short\":\"to learn thoroughly, i.e., (by implication) to note carefully\",\"long\":[\"to learn thoroughly, examine carefully\",\"to consider well\"]},\"deriv\":\"from G2596 and G3129\",\"pronun\":{\"ipa\":\"kɑ.tɑ.mɑnˈθɑ.no\",\"ipa_mod\":\"kɑ.tɑ.mɑn̪ˈθɑ.now\",\"sbl\":\"katamanthanō\",\"dic\":\"ka-ta-mahn-THA-noh\",\"dic_mod\":\"ka-ta-mahn-THA-noh\"},\"see\":[\"G2596\",\"G3129\"]}', 'consider'),
(2649, 2649, 'καταμαρτυρέω', '{\"def\":{\"short\":\"to testify against\",\"long\":[\"to bear witness against, to testify against one\"]},\"deriv\":\"from G2596 and G3140\",\"pronun\":{\"ipa\":\"kɑ.tɑ.mɑr.tyˈrɛ.o\",\"ipa_mod\":\"kɑ.tɑ.mɑr.tjuˈre̞.ow\",\"sbl\":\"katamartyreō\",\"dic\":\"ka-ta-mahr-too-REH-oh\",\"dic_mod\":\"ka-ta-mahr-tyoo-RAY-oh\"},\"see\":[\"G2596\",\"G3140\"]}', 'witness against'),
(2650, 2650, 'καταμένω', '{\"def\":{\"short\":\"to stay fully, i.e., reside\",\"long\":[\"to remain permanently, to abide\"]},\"deriv\":\"from G2596 and G3306\",\"pronun\":{\"ipa\":\"kɑ.tɑˈmɛ.no\",\"ipa_mod\":\"kɑ.tɑˈme̞.now\",\"sbl\":\"katamenō\",\"dic\":\"ka-ta-MEH-noh\",\"dic_mod\":\"ka-ta-MAY-noh\"},\"see\":[\"G2596\",\"G3306\"]}', 'abide'),
(2651, 2651, 'καταμόνας', '{\"def\":{\"short\":\"according to sole places, i.e., (adverbially) separately\",\"long\":[\"apart, alone\"]},\"deriv\":\"from G2596 and accusative case plural feminine of G3441 (with G5561 implied)\",\"pronun\":{\"ipa\":\"kɑ.tɑˈmo.nɑs\",\"ipa_mod\":\"kɑ.tɑˈmow.nɑs\",\"sbl\":\"katamonas\",\"dic\":\"ka-ta-MOH-nahs\",\"dic_mod\":\"ka-ta-MOH-nahs\"},\"see\":[\"G2596\",\"G3441\",\"G5561\"]}', 'alone'),
(2652, 2652, 'κατανάθεμα', '{\"def\":{\"short\":\"an imprecation\",\"long\":[\"curse\"]},\"deriv\":\"from G2596 (intensive) and G0331\",\"pronun\":{\"ipa\":\"kɑ.tɑˈnɑ.θɛ.mɑ\",\"ipa_mod\":\"kɑ.tɑˈnɑ.θe̞.mɑ\",\"sbl\":\"katanathema\",\"dic\":\"ka-ta-NA-theh-ma\",\"dic_mod\":\"ka-ta-NA-thay-ma\"},\"see\":[\"G0331\",\"G2596\"]}', 'curse'),
(2653, 2653, 'καταναθεματίζω', '{\"def\":{\"short\":\"to imprecate\",\"long\":[\"to curse\"]},\"deriv\":\"from G2596 (intensive) and G0332\",\"pronun\":{\"ipa\":\"kɑ.tɑ.nɑ.θɛ.mɑˈti.zo\",\"ipa_mod\":\"kɑ.tɑ.nɑ.θe̞.mɑˈti.zow\",\"sbl\":\"katanathematizō\",\"dic\":\"ka-ta-na-theh-ma-TEE-zoh\",\"dic_mod\":\"ka-ta-na-thay-ma-TEE-zoh\"},\"see\":[\"G0332\",\"G2596\"]}', 'curse'),
(2654, 2654, 'καταναλίσκω', '{\"def\":{\"short\":\"to consume utterly\",\"long\":[\"to consume\",[\"of fire\"]]},\"deriv\":\"from G2596 and G0355\",\"pronun\":{\"ipa\":\"kɑ.tɑ.nɑˈli.sko\",\"ipa_mod\":\"kɑ.tɑ.nɑˈli.skow\",\"sbl\":\"katanaliskō\",\"dic\":\"ka-ta-na-LEE-skoh\",\"dic_mod\":\"ka-ta-na-LEE-skoh\"},\"see\":[\"G0355\",\"G2596\"]}', 'consume'),
(2655, 2655, 'καταναρκάω', '{\"def\":{\"short\":\"to grow utterly torpid, i.e., (by implication) slothful (figuratively, expensive)\",\"long\":[\"to cause to grow numb or torpid\",\"to be torpid, inactive, to be to the detriment of one\",\"to weigh heavily upon, be burdensome to\"]},\"deriv\":\"from G2596 and ναρκάω (to be numb)\",\"pronun\":{\"ipa\":\"kɑ.tɑ.nɑrˈkɑ.o\",\"ipa_mod\":\"kɑ.tɑ.nɑrˈkɑ.ow\",\"sbl\":\"katanarkaō\",\"dic\":\"ka-ta-nahr-KA-oh\",\"dic_mod\":\"ka-ta-nahr-KA-oh\"},\"see\":[\"G2596\"]}', 'be burdensome (chargeable)'),
(2656, 2656, 'κατανεύω', '{\"def\":{\"short\":\"to nod down (towards), i.e., (by analogy) to make signs to\",\"long\":[\"to nod to, make a sign\",\"to indicate to another by a nod or sign what one wishes him to do\"]},\"deriv\":\"from G2596 and G3506\",\"pronun\":{\"ipa\":\"kɑ.tɑˈnɛβ.o\",\"ipa_mod\":\"kɑ.tɑˈnev.ow\",\"sbl\":\"kataneuō\",\"dic\":\"ka-ta-NEV-oh\",\"dic_mod\":\"ka-ta-NAVE-oh\"},\"see\":[\"G2596\",\"G3506\"]}', 'beckon'),
(2657, 2657, 'κατανοέω', '{\"def\":{\"short\":\"to observe fully\",\"long\":[\"to perceive, remark, observe, understand\",\"to consider attentively, fix one\'s eyes or mind upon\"]},\"deriv\":\"from G2596 and G3539\",\"pronun\":{\"ipa\":\"kɑ.tɑ.noˈɛ.o\",\"ipa_mod\":\"kɑ.tɑ.nowˈe̞.ow\",\"sbl\":\"katanoeō\",\"dic\":\"ka-ta-noh-EH-oh\",\"dic_mod\":\"ka-ta-noh-A-oh\"},\"see\":[\"G2596\",\"G3539\"]}', 'behold, consider, discover, perceive'),
(2658, 2658, 'καταντάω', '{\"def\":{\"short\":\"to meet against, i.e., arrive at (literally or figuratively)\",\"long\":[\"to come to, arrive\",[\"to come to a place over against, opposite another\",\"metaphorically to attain to a thing\"]]},\"deriv\":\"from G2596 and a derivative of G0473\",\"pronun\":{\"ipa\":\"kɑ.tɑnˈtɑ.o\",\"ipa_mod\":\"kɑ.tɑnˈtɑ.ow\",\"sbl\":\"katantaō\",\"dic\":\"ka-tahn-TA-oh\",\"dic_mod\":\"ka-tahn-TA-oh\"},\"see\":[\"G0473\",\"G2596\"]}', 'attain, come'),
(2659, 2659, 'κατάνυξις', '{\"def\":{\"short\":\"a prickling (sensation, as of the limbs asleep), i.e., (by implication, (perhaps by some confusion with G3506 or even with G3571)) stupor (lethargy)\",\"long\":[\"a pricking, piercing\",\"severe sorrow, extreme grief\",\"insensibility or torpor of mind, such as extreme grief easily produces\",[\"hence a \\\"spirit of stupor\\\", which renders their souls torpid so insensible that they are not affected at all by the offer made them of salvation through the Messiah\"]]},\"deriv\":\"from G2660\",\"pronun\":{\"ipa\":\"kɑˈtɑ.ny.k͡sis\",\"ipa_mod\":\"kɑˈtɑ.nju.k͡sis\",\"sbl\":\"katanyxis\",\"dic\":\"ka-TA-noo-ksees\",\"dic_mod\":\"ka-TA-nyoo-ksees\"},\"see\":[\"G2660\",\"G3506\",\"G3571\"]}', 'slumber'),
(2660, 2660, 'κατανύσσω', '{\"def\":{\"short\":\"to pierce thoroughly, i.e., (figuratively) to agitate violently (\\\"sting to the quick\\\")\",\"long\":[\"to prick, pierce\",\"metaphorically to pain the mind sharply, agitate it vehemently\",[\"especially of the emotion of sorrow\"]]},\"deriv\":\"from G2596 and G3572\",\"pronun\":{\"ipa\":\"kɑ.tɑˈnys.so\",\"ipa_mod\":\"kɑ.tɑˈnjus.sow\",\"sbl\":\"katanyssō\",\"dic\":\"ka-ta-NOOS-soh\",\"dic_mod\":\"ka-ta-NYOOS-soh\"},\"see\":[\"G2596\",\"G3572\"]}', 'prick'),
(2661, 2661, 'καταξιόω', '{\"def\":{\"short\":\"to deem entirely deserving\",\"long\":[\"to account worthy, judge worthy\"]},\"deriv\":\"from G2596 and G0515\",\"pronun\":{\"ipa\":\"kɑ.tɑ.k͡siˈo.o\",\"ipa_mod\":\"kɑ.tɑ.k͡siˈow.ow\",\"sbl\":\"kataxioō\",\"dic\":\"ka-ta-ksee-OH-oh\",\"dic_mod\":\"ka-ta-ksee-OH-oh\"},\"see\":[\"G0515\",\"G2596\"]}', '(ac-)count worthy'),
(2662, 2662, 'καταπατέω', '{\"def\":{\"short\":\"to trample down; figuratively, to reject with disdain\",\"long\":[\"to tread down, trample under foot, to trample on\",\"metaphorically to treat with rudeness and insult\",[\"to spurn, treat with insulting neglect\"]]},\"deriv\":\"from G2596 and G3961\",\"pronun\":{\"ipa\":\"kɑ.tɑ.pɑˈtɛ.o\",\"ipa_mod\":\"kɑ.tɑ.pɑˈte̞.ow\",\"sbl\":\"katapateō\",\"dic\":\"ka-ta-pa-TEH-oh\",\"dic_mod\":\"ka-ta-pa-TAY-oh\"},\"see\":[\"G2596\",\"G3961\"]}', 'trample, tread (down, underfoot)'),
(2663, 2663, 'κατάπαυσις', '{\"def\":{\"short\":\"reposing down, i.e., (by Hebraism) abode\",\"long\":[\"a putting to rest\",[\"calming of the winds\"],\"a resting place\",[\"metaphorically the heavenly blessedness in which God dwells, and of which he has promised to make persevering believers in Christ partakers after the toils and trials of life on earth are ended\"]]},\"deriv\":\"from G2664\",\"pronun\":{\"ipa\":\"kɑˈtɑ.pɑβ.sis\",\"ipa_mod\":\"kɑˈtɑ.pɑf.sis\",\"sbl\":\"katapausis\",\"dic\":\"ka-TA-pav-sees\",\"dic_mod\":\"ka-TA-paf-sees\"},\"see\":[\"G2664\"]}', 'rest'),
(2664, 2664, 'καταπαύω', '{\"def\":{\"short\":\"to settle down, i.e., (literally) to colonize, or (figuratively) to (cause to) desist\",\"long\":[\"to make quiet, to cause to be at rest, to grant rest\",[\"to lead to a quiet abode\",\"to still, restrain, to cause (one striving to do something) to desist\"],\"to rest, take rest\"]},\"deriv\":\"from G2596 and G3973\",\"pronun\":{\"ipa\":\"kɑ.tɑˈpɛβ.o\",\"ipa_mod\":\"kɑ.tɑˈpa.ow\",\"sbl\":\"katapauō\",\"dic\":\"ka-ta-PEV-oh\",\"dic_mod\":\"ka-ta-PA-oh\"},\"see\":[\"G2596\",\"G3973\"]}', 'cease, (give) rest(-rain)'),
(2665, 2665, 'καταπέτασμα', '{\"def\":{\"short\":\"something spread thoroughly, i.e., (specially) the door screen (to the Most Holy Place) in the Jewish Temple\",\"long\":[\"a veil spread out, a curtain\",[\"the name given to the two curtains in the temple at Jerusalem, one of them at the entrance to the temple separated the Holy Place from the outer court, the other veiled the Holy of Holies from the Holy Place\"]]},\"deriv\":\"from a compound of G2596 and a congener of G4072\",\"pronun\":{\"ipa\":\"kɑ.tɑˈpɛ.tɑ.smɑ\",\"ipa_mod\":\"kɑ.tɑˈpe̞.tɑ.smɑ\",\"sbl\":\"katapetasma\",\"dic\":\"ka-ta-PEH-ta-sma\",\"dic_mod\":\"ka-ta-PAY-ta-sma\"},\"see\":[\"G2596\",\"G4072\"]}', 'vail'),
(2666, 2666, 'καταπίνω', '{\"def\":{\"short\":\"to drink down, i.e., gulp entire (literally or figuratively)\",\"long\":[\"to drink down, swallow down\",\"to devour\",\"to swallow up, destroy\"]},\"deriv\":\"from G2596 and G4095\",\"pronun\":{\"ipa\":\"kɑ.tɑˈpi.no\",\"ipa_mod\":\"kɑ.tɑˈpi.now\",\"sbl\":\"katapinō\",\"dic\":\"ka-ta-PEE-noh\",\"dic_mod\":\"ka-ta-PEE-noh\"},\"see\":[\"G2596\",\"G4095\"]}', 'devour, drown, swallow (up)'),
(2667, 2667, 'καταπίπτω', '{\"def\":{\"short\":\"to fall down\",\"long\":[\"to fall down\"]},\"deriv\":\"from G2596 and G4098\",\"pronun\":{\"ipa\":\"kɑ.tɑˈpi.pto\",\"ipa_mod\":\"kɑ.tɑˈpi.ptow\",\"sbl\":\"katapiptō\",\"dic\":\"ka-ta-PEE-ptoh\",\"dic_mod\":\"ka-ta-PEE-ptoh\"},\"see\":[\"G2596\",\"G4098\"]}', 'fall (down)'),
