(2674, 2674, 'καταριθμέω', '{\"def\":{\"short\":\"to reckon among\",\"long\":[\"to number with\"]},\"deriv\":\"from G2596 and G0705\",\"pronun\":{\"ipa\":\"kɑ.tɑ.riθˈmɛ.o\",\"ipa_mod\":\"kɑ.tɑ.riθˈme̞.ow\",\"sbl\":\"katarithmeō\",\"dic\":\"ka-ta-reeth-MEH-oh\",\"dic_mod\":\"ka-ta-reeth-MAY-oh\"},\"see\":[\"G0705\",\"G2596\"]}', 'number with'),
