(2681, 2681, 'κατασκηνόω', '{\"def\":{\"short\":\"to camp down, i.e., haunt; figuratively, to remain\",\"long\":[\"to pitch one\'s tent, to fix one\'s abode, to dwell\"]},\"deriv\":\"from G2596 and G4637\",\"pronun\":{\"ipa\":\"kɑ.tɑ.skeˈno.o\",\"ipa_mod\":\"kɑ.tɑ.ske̞ˈnow.ow\",\"sbl\":\"kataskēnoō\",\"dic\":\"ka-ta-skay-NOH-oh\",\"dic_mod\":\"ka-ta-skay-NOH-oh\"},\"see\":[\"G2596\",\"G4637\"]}', 'lodge, rest'),
(2682, 2682, 'κατασκήνωσις', '{\"def\":{\"short\":\"an encamping, i.e., (figuratively) a perch\",\"long\":[\"the pitching of tents, encamping\",\"place of tarrying, encampment, abode\",[\"of the nest of birds\"]]},\"deriv\":\"from G2681\",\"pronun\":{\"ipa\":\"kɑ.tɑˈske.no.sis\",\"ipa_mod\":\"kɑ.tɑˈske̞.now.sis\",\"sbl\":\"kataskēnōsis\",\"dic\":\"ka-ta-SKAY-noh-sees\",\"dic_mod\":\"ka-ta-SKAY-noh-sees\"},\"see\":[\"G2681\"]}', 'nest'),
(2683, 2683, 'κατασκιάζω', '{\"def\":{\"short\":\"to overshade, i.e., cover\",\"long\":[\"to overshadow, cover with shade\"]},\"deriv\":\"from G2596 and a derivative of G4639\",\"pronun\":{\"ipa\":\"kɑ.tɑ.skiˈɑ.zo\",\"ipa_mod\":\"kɑ.tɑ.sciˈɑ.zow\",\"sbl\":\"kataskiazō\",\"dic\":\"ka-ta-skee-AH-zoh\",\"dic_mod\":\"ka-ta-skee-AH-zoh\"},\"see\":[\"G2596\",\"G4639\"]}', 'shadow'),
