(2732, 2733, 'κατοικία', '{\"def\":{\"short\":\"residence (properly, the condition; but by implication, the abode itself)\",\"long\":[\"dwelling, habitation\",\"to cause to dwell, to send or bring into an abode\",\"to give a dwelling to\"]},\"pronun\":{\"ipa\":\"kɑ.tyˈki.ɑ\",\"ipa_mod\":\"kɑ.tyˈki.ɑ\",\"sbl\":\"katoikia\",\"dic\":\"ka-too-KEE-ah\",\"dic_mod\":\"ka-too-KEE-ah\"}}', 'habitation'),
(2733, 2734, 'κατοπτρίζομαι', '{\"def\":{\"short\":\"to mirror oneself, i.e., to see reflected (figuratively)\",\"long\":[\"to show in a mirror, to make to reflect, to mirror\",\"to look at one\'s self in a mirror\",\"to behold one\'s self in a mirror\"]},\"deriv\":\"middle voice from a compound of G2596 and a derivative of G3700 (compare G2072)\",\"pronun\":{\"ipa\":\"kɑ.toptˈri.zo.mɛ\",\"ipa_mod\":\"kɑ.towptˈri.zow.me\",\"sbl\":\"katoptrizomai\",\"dic\":\"ka-tohpt-REE-zoh-meh\",\"dic_mod\":\"ka-tohpt-REE-zoh-may\"},\"see\":[\"G2072\",\"G2596\",\"G3700\"]}', 'behold as in a glass'),
(2734, 2735, 'κατόρθωμα', '{\"def\":{\"short\":\"something made fully upright, i.e., (figuratively) rectification (specially, good public administration)\",\"long\":[\"a right action, a successful achievement\",[\"of wholesome public measures or institutions\"]]},\"deriv\":\"from a compound of G2596 and a derivative of G3717 (compare G1357)\",\"pronun\":{\"ipa\":\"kɑˈtor.θo.mɑ\",\"ipa_mod\":\"kɑˈtowr.θow.mɑ\",\"sbl\":\"katorthōma\",\"dic\":\"ka-TORE-thoh-ma\",\"dic_mod\":\"ka-TORE-thoh-ma\"},\"see\":[\"G1357\",\"G2596\",\"G3717\"]}', 'very worthy deed'),
(2735, 2736, 'κάτω', '{\"def\":{\"short\":\"downwards\",\"long\":[\"down, downwards\",\"below, beneath\",[\"of place, below\",\"of temporal succession\"]]},\"deriv\":\"(compare G2737); adverb from G2596\",\"pronun\":{\"ipa\":\"ˈkɑ.to\",\"ipa_mod\":\"ˈkɑ.tow\",\"sbl\":\"katō\",\"dic\":\"KA-toh\",\"dic_mod\":\"KA-toh\"},\"see\":[\"G2596\",\"G2737\"]}', 'beneath, bottom, down, under'),
(2736, 2737, 'κατώτερος', '{\"def\":{\"short\":\"inferior (locally, of Hades)\",\"long\":[\"lower\"]},\"deriv\":\"comparative from G2736\",\"pronun\":{\"ipa\":\"kɑˈto.tɛ.ros\",\"ipa_mod\":\"kɑˈtow.te̞.rows\",\"sbl\":\"katōteros\",\"dic\":\"ka-TOH-teh-rose\",\"dic_mod\":\"ka-TOH-tay-rose\"},\"see\":[\"G2736\"]}', 'lower'),
(2737, 2738, 'καῦμα', '{\"def\":{\"short\":\"properly, a burn (concretely), but used (abstractly) of a glow\",\"long\":[\"heat\",[\"of painful and burning heat\"]]},\"deriv\":\"from G2545\",\"pronun\":{\"ipa\":\"ˈkɛβ.mɑ\",\"ipa_mod\":\"ˈka.mɑ\",\"sbl\":\"kauma\",\"dic\":\"KEV-ma\",\"dic_mod\":\"KA-ma\"},\"see\":[\"G2545\"]}', 'heat'),
(2738, 2739, 'καυματίζω', '{\"def\":{\"short\":\"to burn\",\"long\":[\"to burn with heat, to scorch\",\"to be tortured with intense heat\"]},\"deriv\":\"from G2738\",\"pronun\":{\"ipa\":\"kɛβ.mɑˈti.zo\",\"ipa_mod\":\"ka.mɑˈti.zow\",\"sbl\":\"kaumatizō\",\"dic\":\"kev-ma-TEE-zoh\",\"dic_mod\":\"ka-ma-TEE-zoh\"},\"see\":[\"G2738\"]}', 'scorch'),
(2739, 2740, 'καῦσις', '{\"def\":{\"short\":\"burning (the act)\",\"long\":[\"burning, burning up\"]},\"deriv\":\"from G2545\",\"pronun\":{\"ipa\":\"ˈkɑβ.sis\",\"ipa_mod\":\"ˈkɑf.sis\",\"sbl\":\"kausis\",\"dic\":\"KAV-sees\",\"dic_mod\":\"KAF-sees\"},\"see\":[\"G2545\"]}', 'be burned'),
(2740, 2741, 'καυσόω', '{\"def\":{\"short\":\"to set on fire\",\"long\":[\"to burn up, set fire to\",\"to suffer with feverish burning, be parched with fever\"]},\"deriv\":\"from G2740\",\"pronun\":{\"ipa\":\"kɑβˈso.o\",\"ipa_mod\":\"kɑfˈsow.ow\",\"sbl\":\"kausoō\",\"dic\":\"kav-SOH-oh\",\"dic_mod\":\"kaf-SOH-oh\"},\"see\":[\"G2740\"]}', 'fervent heat'),
(2741, 2742, 'καύσων', '{\"def\":{\"short\":\"a glare\",\"long\":[\"burning heat\",[\"of the sun\"],\"Eurus, a very dry hot, east wind, scorching and drying up everything\"]},\"deriv\":\"from G2741\",\"pronun\":{\"ipa\":\"ˈkɑβ.son\",\"ipa_mod\":\"ˈkɑf.sown\",\"sbl\":\"kausōn\",\"dic\":\"KAV-sone\",\"dic_mod\":\"KAF-sone\"},\"see\":[\"G2741\"]}', '(burning) heat');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(2742, 2743, 'καυτηριάζω', '{\"def\":{\"short\":\"to brand (\\\"cauterize\\\"), i.e., (by implication) to render unsensitive (figuratively)\",\"long\":[\"to mark by branding, to brand, branded with their own consciences\",[\"whose souls are branded with the marks of sin\",\"who carry about with them the perpetual consciousness of sin\"],\"seared\",\"in a medical sense, to cauterise, remover by cautery\"]},\"deriv\":\"from a derivative of G2545\",\"pronun\":{\"ipa\":\"kɑβ.te.riˈɑ.zo\",\"ipa_mod\":\"kɑf.te̞.riˈɑ.zow\",\"sbl\":\"kautēriazō\",\"dic\":\"kav-tay-ree-AH-zoh\",\"dic_mod\":\"kaf-tay-ree-AH-zoh\"},\"see\":[\"G2545\"]}', 'sear with a hot iron'),
(2743, 2744, 'καυχάομαι', '{\"def\":{\"short\":\"to vaunt (in a good or a bad sense)\",\"long\":[\"to glory (whether with reason or without)\",\"to glory on account of a thing\",\"to glory in a thing\"]},\"deriv\":\"from some (obsolete) base akin to that of αὐχέω (to boast) and G2172\",\"pronun\":{\"ipa\":\"kɑβˈxɑ.o.mɛ\",\"ipa_mod\":\"kɑfˈxɑ.ow.me\",\"sbl\":\"kauchaomai\",\"dic\":\"kav-HA-oh-meh\",\"dic_mod\":\"kaf-HA-oh-may\"},\"see\":[\"G2172\"]}', '(make) boast, glory, joy, rejoice'),
(2744, 2745, 'καύχημα', '{\"def\":{\"short\":\"a boast (properly, the object; by implication, the act) in a good or a bad sense\",\"long\":[\"that of which one glories or can glory, matter or ground of glorying\",\"a glorying or boasting\"]},\"deriv\":\"from G2744\",\"pronun\":{\"ipa\":\"ˈkɑβ.xe.mɑ\",\"ipa_mod\":\"ˈkɑf.xe̞.mɑ\",\"sbl\":\"kauchēma\",\"dic\":\"KAV-hay-ma\",\"dic_mod\":\"KAF-hay-ma\"},\"see\":[\"G2744\"]}', 'boasting, (whereof) to glory (of), glorying, rejoice(-ing)'),
(2745, 2746, 'καύχησις', '{\"def\":{\"short\":\"boasting (properly, the act; by implication, the object), in a good or a bad sense\",\"long\":[\"the act of glorying\"]},\"deriv\":\"from G2744\",\"pronun\":{\"ipa\":\"ˈkɑβ.xe.sis\",\"ipa_mod\":\"ˈkɑf.xe̞.sis\",\"sbl\":\"kauchēsis\",\"dic\":\"KAV-hay-sees\",\"dic_mod\":\"KAF-hay-sees\"},\"see\":[\"G2744\"]}', 'boasting, whereof I may glory, glorying, rejoicing'),
(2746, 2747, 'Κεγχρεαί', '{\"def\":{\"lit\":\"millet\",\"short\":\"Cenchreae, a port of Corinth\",\"long\":[\"the eastern harbor of Corinth (i.e., its harbor on the Saronic Gulf) and the emporium of its trade with the Asiatic shores of the Mediterranean, as Lechaeum on the Corinthian Gulf connected it with Italy and the west\"]},\"deriv\":\"probably from kegchros (millet)\",\"pronun\":{\"ipa\":\"kɛŋ.xrɛˈɛ\",\"ipa_mod\":\"ce̞ŋ.xre̞ˈe\",\"sbl\":\"kenchreai\",\"dic\":\"keng-hreh-EH\",\"dic_mod\":\"kayng-hray-A\"}}', 'Cencrea'),
(2747, 2748, 'Κεδρών', '{\"def\":{\"lit\":\"turbid\",\"short\":\"Cedron (i.e., Kidron), a brook near Jerusalem\",\"long\":[\"the name of a winter torrent, rising near Jerusalem and flowing down through a valley of Kidron, having the Mount of Olives on the east, into the Dead Sea\"]},\"deriv\":\"of Hebrew origin (H6939)\",\"pronun\":{\"ipa\":\"kɛˈðron\",\"ipa_mod\":\"ce̞ˈðrown\",\"sbl\":\"kedrōn\",\"dic\":\"keh-THRONE\",\"dic_mod\":\"kay-THRONE\"},\"see\":[\"H6939\"]}', 'Cedron'),
(2748, 2749, 'κεῖμαι', '{\"def\":{\"short\":\"to lie outstretched (literally or figuratively)\",\"long\":[\"to lie\",[\"of an infant\",\"of one buried\",\"of things that quietly cover some spot\",[\"of a city situated on a hill\"],\"of things put or set in any place, in ref. to which English often uses \\\"to stand\\\"\",[\"of vessels, of a throne, of the site of a city, of grain and other things laid up together, of a foundation\"]],\"metaphorically\",[\"to be (by God\'s intent) set, i.e., destined, appointed\",\"of laws, to be made, laid down\",\"lies in the power of the evil one, i.e., is held in subjection by the devil\"]]},\"deriv\":\"middle voice of a primary verb\",\"pronun\":{\"ipa\":\"ˈki.mɛ\",\"ipa_mod\":\"ˈci.me\",\"sbl\":\"keimai\",\"dic\":\"KEE-meh\",\"dic_mod\":\"KEE-may\"},\"see\":[\"G5087\"],\"comment\":\"Compare G5087.\"}', 'be (appointed, laid up, made, set), lay, lie'),
(2749, 2750, 'κειρία', '{\"def\":{\"short\":\"a swathe, i.e., winding-sheet\",\"long\":[\"a band, either for bed-girth, or for tying up a corpse after it has been swathed in linen\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"kiˈri.ɑ\",\"ipa_mod\":\"ciˈri.ɑ\",\"sbl\":\"keiria\",\"dic\":\"kee-REE-ah\",\"dic_mod\":\"kee-REE-ah\"}}', 'graveclothes'),
(2750, 2751, 'κείρω', '{\"def\":{\"short\":\"to shear\",\"long\":[\"to sheer: a sheep\",\"to get or let be shorn\",\"of shearing or cutting short the hair of the head\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈki.ro\",\"ipa_mod\":\"ˈci.row\",\"sbl\":\"keirō\",\"dic\":\"KEE-roh\",\"dic_mod\":\"KEE-roh\"}}', 'shear(-er)'),
(2751, 2752, 'κέλευμα', '{\"def\":{\"short\":\"a cry of incitement\",\"long\":[\"an order, command, spec. a stimulating cry, either that by which animals are roused and urged on by man, as horses by charioteers, hounds by hunters, etc., or that by which a signal is given to men, e.g., to rowers by the master of a ship, to soldiers by a commander (with a loud summons, a trumpet call)\"]},\"deriv\":\"from G2753\",\"pronun\":{\"ipa\":\"ˈkɛ.lɛβ.mɑ\",\"ipa_mod\":\"ˈke̞.lev.mɑ\",\"sbl\":\"keleuma\",\"dic\":\"KEH-lev-ma\",\"dic_mod\":\"KAY-lave-ma\"},\"see\":[\"G2753\"]}', 'shout'),
(2752, 2753, 'κελεύω', '{\"def\":{\"short\":\"\\\"hail\\\"; to incite by word, i.e., order\",\"long\":[\"to command, to order\"]},\"deriv\":\"from a primary κέλλω (to urge on)\",\"pronun\":{\"ipa\":\"kɛˈlɛβ.o\",\"ipa_mod\":\"ce̞ˈlev.ow\",\"sbl\":\"keleuō\",\"dic\":\"keh-LEV-oh\",\"dic_mod\":\"kay-LAVE-oh\"}}', 'bid, (at, give) command(-ment)'),
(2753, 2754, 'κενοδοξία', '{\"def\":{\"short\":\"empty glorying, i.e., self-conceit\",\"long\":[\"vain glory, groundless, self esteem, empty pride\",\"a vain opinion, error\"]},\"deriv\":\"from G2755\",\"pronun\":{\"ipa\":\"kɛ.no.ðoˈk͡si.ɑ\",\"ipa_mod\":\"ce̞.now.ðowˈk͡si.ɑ\",\"sbl\":\"kenodoxia\",\"dic\":\"keh-noh-thoh-KSEE-ah\",\"dic_mod\":\"kay-noh-thoh-KSEE-ah\"},\"see\":[\"G2755\"]}', 'vain-glory'),
(2754, 2755, 'κενόδοξος', '{\"def\":{\"short\":\"vainly glorifying, i.e., self-conceited\",\"long\":[\"glorying without reason, conceited, vain glorious, eager for empty glory\"]},\"deriv\":\"from G2756 and G1391\",\"pronun\":{\"ipa\":\"kɛˈno.ðo.k͡sos\",\"ipa_mod\":\"ce̞ˈnow.ðow.k͡sows\",\"sbl\":\"kenodoxos\",\"dic\":\"keh-NOH-thoh-ksose\",\"dic_mod\":\"kay-NOH-thoh-ksose\"},\"see\":[\"G1391\",\"G2756\"]}', 'desirous of vain-glory'),
(2755, 2756, 'κενός', '{\"def\":{\"short\":\"empty (literally or figuratively)\",\"long\":[\"empty, vain, devoid of truth\",[\"of places, vessels, etc. which contain nothing\",\"of men\",[\"empty handed\",\"without a gift\"],\"metaphorically destitute of spiritual wealth, of one who boasts of his faith as a transcendent possession, yet is without the fruits of faith\",\"metaphorically of endeavors, labors, acts, which result in nothing, vain, fruitless, without effect\",[\"vain of no purpose\"]]]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"kɛˈnos\",\"ipa_mod\":\"ce̞ˈnows\",\"sbl\":\"kenos\",\"dic\":\"keh-NOSE\",\"dic_mod\":\"kay-NOSE\"}}', 'empty, (in) vain'),
(2756, 2757, 'κενοφωνία', '{\"def\":{\"short\":\"empty sounding, i.e., fruitless discussion\",\"long\":[\"empty discussion, discussion of vain and useless matters\"]},\"deriv\":\"from a presumed compound of G2756 and G5456\",\"pronun\":{\"ipa\":\"kɛ.no.foˈni.ɑ\",\"ipa_mod\":\"ce̞.now.fowˈni.ɑ\",\"sbl\":\"kenophōnia\",\"dic\":\"keh-noh-foh-NEE-ah\",\"dic_mod\":\"kay-noh-foh-NEE-ah\"},\"see\":[\"G2756\",\"G5456\"]}', 'vain'),
(2757, 2758, 'κενόω', '{\"def\":{\"short\":\"to make empty, i.e., (figuratively) to abase, neutralize, falsify\",\"long\":[\"to empty, make empty\",[\"of Christ, he laid aside equality with or the form of God\"],\"to make void\",[\"deprive of force, render vain, useless, of no effect\"],\"to make void\",[\"cause a thing to be seen to be empty, hollow, false\"]]},\"deriv\":\"from G2756\",\"pronun\":{\"ipa\":\"kɛˈno.o\",\"ipa_mod\":\"ce̞ˈnow.ow\",\"sbl\":\"kenoō\",\"dic\":\"keh-NOH-oh\",\"dic_mod\":\"kay-NOH-oh\"},\"see\":[\"G2756\"]}', 'make (of none effect, of no reputation, void), be in vain'),
(2758, 2759, 'κέντρον', '{\"def\":{\"short\":\"a point (\\\"center\\\"), i.e., a sting (figuratively, poison) or goad (figuratively, divine impulse)\",\"long\":[\"a sting, as that of bees, scorpions, locusts\",\"an iron goad, for urging on oxen, horses and other beasts of burden\",[\"hence the proverb, \\\"to kick against the goad\\\", i.e., to offer vain and perilous or ruinous resistance\"]]},\"deriv\":\"from κεντέω (to prick)\",\"pronun\":{\"ipa\":\"ˈkɛn.tron\",\"ipa_mod\":\"ˈke̞n.trown\",\"sbl\":\"kentron\",\"dic\":\"KEN-trone\",\"dic_mod\":\"KANE-trone\"},\"comment\":\"Since animals wound by their sting and even cause death, Paul attributes death, personified as a sting, i.e., a deadly weapon.\"}', 'prick, sting'),
(2759, 2760, 'κεντυρίων', '{\"def\":{\"short\":\"a centurion, i.e., captain of one hundred soldiers\",\"long\":[\"centurion, an officer in the Roman army\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"kɛn.tyˈri.on\",\"ipa_mod\":\"ce̞n.tjuˈri.own\",\"sbl\":\"kentyriōn\",\"dic\":\"ken-too-REE-one\",\"dic_mod\":\"kane-tyoo-REE-one\"}}', 'centurion'),
(2760, 2761, 'κενῶς', '{\"def\":{\"short\":\"vainly, i.e., to no purpose\",\"long\":[\"vainly, in vain\"]},\"deriv\":\"adverb from G2756\",\"pronun\":{\"ipa\":\"kɛˈnos\",\"ipa_mod\":\"ce̞ˈnows\",\"sbl\":\"kenōs\",\"dic\":\"keh-NOSE\",\"dic_mod\":\"kay-NOSE\"},\"see\":[\"G2756\"]}', 'in vain'),
(2761, 2762, 'κεραία', '{\"def\":{\"short\":\"something horn-like, i.e., (specially) the apex of a Hebrew letter (figuratively, the least particle)\",\"long\":[\"a little horn\",\"extremity, apex, point\",[\"used by grammarians of the accents and diacritical points, i.e., the smallest distinguishing stroke of a Hebrew letter\"]]},\"deriv\":\"feminine of a presumed derivative of the base of G2768\",\"pronun\":{\"ipa\":\"kɛˈrɛ.ɑ\",\"ipa_mod\":\"ce̞ˈre.ɑ\",\"sbl\":\"keraia\",\"dic\":\"keh-REH-ah\",\"dic_mod\":\"kay-RAY-ah\"},\"see\":[\"G2768\"],\"comment\":\"The title is analogous to the dot of an \\\"i\\\" in English. Jesus used it to refer to the little lines by which the Hebrew letters, in other respects similar, differ from one another; the meaning is, \\\"not even the minutest part of the law shall perish.\\\"\"}', 'tittle'),
(2762, 2763, 'κεραμεύς', '{\"def\":{\"short\":\"a potter\",\"long\":[\"a potter\"]},\"deriv\":\"from G2766\",\"pronun\":{\"ipa\":\"kɛ.rɑˈmɛβs\",\"ipa_mod\":\"ce̞.rɑˈmefs\",\"sbl\":\"kerameus\",\"dic\":\"keh-ra-MEVS\",\"dic_mod\":\"kay-ra-MAYFS\"},\"see\":[\"G2766\"]}', 'potter'),
(2763, 2764, 'κεραμικός', '{\"def\":{\"short\":\"made of clay, i.e., earthen\",\"long\":[\"of or belonging to a potter\",\"made of clay, earthen\"]},\"deriv\":\"from G2766\",\"pronun\":{\"ipa\":\"kɛ.rɑ.miˈkos\",\"ipa_mod\":\"ce̞.rɑ.miˈkows\",\"sbl\":\"keramikos\",\"dic\":\"keh-ra-mee-KOSE\",\"dic_mod\":\"kay-ra-mee-KOSE\"},\"see\":[\"G2766\"]}', 'of a potter'),
(2764, 2765, 'κεράμιον', '{\"def\":{\"short\":\"an earthenware vessel, i.e., jar\",\"long\":[\"an earthen vessel, a pot, jar\",\"a jug or pitcher\",\"a water pitcher\"]},\"deriv\":\"neuter of a presumed derivative of G2766\",\"pronun\":{\"ipa\":\"kɛˈrɑ.mi.on\",\"ipa_mod\":\"ce̞ˈrɑ.mi.own\",\"sbl\":\"keramion\",\"dic\":\"keh-RA-mee-one\",\"dic_mod\":\"kay-RA-mee-one\"},\"see\":[\"G2766\"]}', 'pitcher'),
(2765, 2766, 'κέραμος', '{\"def\":{\"short\":\"earthenware, i.e., a tile (by analogy, a thin roof or awning)\",\"long\":[\"clay, potter\'s earth\",\"anything made of clay, earthen ware\",\"a roofing tile\",[\"the roof itself\",\"the phrase \\\"through the roof\\\", means through the door in the roof to which a ladder or stairway led up from the street (according to the Rabbis distinguish two ways of entering a house, \\\"the way through the door\\\" and \\\"the way through the roof\\\"\"]]},\"deriv\":\"probably from the base of G2767 (through the idea of mixing clay and water)\",\"pronun\":{\"ipa\":\"ˈkɛ.rɑ.mos\",\"ipa_mod\":\"ˈke̞.rɑ.mows\",\"sbl\":\"keramos\",\"dic\":\"KEH-ra-mose\",\"dic_mod\":\"KAY-ra-mose\"},\"see\":[\"G2767\"]}', 'tiling'),
(2766, 2767, 'κεράννυμι', '{\"def\":{\"short\":\"to mingle, i.e., (by implication) to pour out (for drinking)\",\"long\":[\"to mix, mingle\",\"to mix wine, water\",\"to pour out for drinking\"]},\"deriv\":\"prolonged form of a more primary κεράω <pronunciation strongs=\\\"ker-ah\'-o\\\"/> (which is used in certain tenses)\",\"pronun\":{\"ipa\":\"kɛˈrɑn.ny.mi\",\"ipa_mod\":\"ce̞ˈrɑn.nju.mi\",\"sbl\":\"kerannymi\",\"dic\":\"keh-RAHN-noo-mee\",\"dic_mod\":\"kay-RAHN-nyoo-mee\"},\"see\":[\"G3396\"],\"comment\":\"Compare G3396.\"}', 'fill, pour out'),
(2767, 2768, 'κέρας', '{\"def\":{\"short\":\"a horn (literally or figuratively)\",\"long\":[\"a horn\",[\"of animals\",\"since animals (especially bulls) defend themselves with their horns, the horn with the Hebrews (and other nations) is a symbol of strength and courage, and used as such in a variety of phrases\",[\"a mighty and valiant helper, the author of deliverance, of the Messiah\"],\"a projecting extremity in a shape like a horn, a point, apex: as of an altar\"]]},\"deriv\":\"from a primary κάρ (the hair of the head)\",\"pronun\":{\"ipa\":\"ˈkɛ.rɑs\",\"ipa_mod\":\"ˈke̞.rɑs\",\"sbl\":\"keras\",\"dic\":\"KEH-rahs\",\"dic_mod\":\"KAY-rahs\"}}', 'horn'),
(2768, 2769, 'κεράτιον', '{\"def\":{\"short\":\"something horned, i.e., (specially) the pod of the carob-tree\",\"long\":[\"a little horn\",\"the husk of a fruit, perhaps the name of the fruit\"]},\"deriv\":\"neuter of a presumed derivative of G2768\",\"pronun\":{\"ipa\":\"kɛˈrɑ.ti.on\",\"ipa_mod\":\"ce̞ˈrɑ.ti.own\",\"sbl\":\"keration\",\"dic\":\"keh-RA-tee-one\",\"dic_mod\":\"kay-RA-tee-one\"},\"see\":[\"G2768\"],\"comment\":\"This fruit is believed to be the Ceratonia silqua or carobtree species, also called John\'s Bread (from the notion that its pods, which resemble those of the \\\"locust\\\" constituted the food of the Baptist). This fruit was shaped like a horn and has a sweet taste; it was and is used not only for fattening swine, but as food by the lower classes.\"}', 'husk'),
(2769, 2770, 'κερδαίνω', '{\"def\":{\"short\":\"to gain (literally or figuratively)\",\"long\":[\"to gain, acquire, to get gain\",\"metaphorically\",[\"of gain arising from shunning or escaping from evil (where we say \\\"to spare one\'s self\\\", \\\"be spared\\\")\",\"to gain any one i.e., to win him over to the kingdom of God, to gain one to faith in Christ\",\"to gain Christ\'s favor and fellowship\"]]},\"deriv\":\"from G2771\",\"pronun\":{\"ipa\":\"kɛrˈðɛ.no\",\"ipa_mod\":\"ce̞rˈðe.now\",\"sbl\":\"kerdainō\",\"dic\":\"ker-THEH-noh\",\"dic_mod\":\"kare-THAY-noh\"},\"see\":[\"G2771\"]}', '(get) gain, win'),
(2770, 2771, 'κέρδος', '{\"def\":{\"short\":\"gain (pecuniary or genitive case)\",\"long\":[\"gain, advantage\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈkɛr.ðos\",\"ipa_mod\":\"ˈke̞r.ðows\",\"sbl\":\"kerdos\",\"dic\":\"KER-those\",\"dic_mod\":\"KARE-those\"}}', 'gain, lucre'),
(2771, 2772, 'κέρμα', '{\"def\":{\"short\":\"a clipping (bit), i.e., (specially) a coin\",\"long\":[\"small pieces of money, small coin, change, money\"]},\"deriv\":\"from G2751\",\"pronun\":{\"ipa\":\"ˈkɛr.mɑ\",\"ipa_mod\":\"ˈke̞r.mɑ\",\"sbl\":\"kerma\",\"dic\":\"KER-ma\",\"dic_mod\":\"KARE-ma\"},\"see\":[\"G2751\"]}', 'money'),
(2772, 2773, 'κερματιστής', '{\"def\":{\"short\":\"a handler of coins, i.e., money-broker\",\"long\":[\"a money changer, money broker\"]},\"deriv\":\"from a derivative of G2772\",\"pronun\":{\"ipa\":\"kɛr.mɑ.tiˈstes\",\"ipa_mod\":\"ce̞r.mɑ.tiˈste̞s\",\"sbl\":\"kermatistēs\",\"dic\":\"ker-ma-tee-STASE\",\"dic_mod\":\"kare-ma-tee-STASE\"},\"see\":[\"G2772\"],\"comment\":\"In the court of the Gentiles in the temple of Jerusalem were the seats of those who sold such animals for sacrifice as had been selected, examined, and approved, together with incense, oil, and other things needed in making offerings and in worship; and the magnitude of this traffic had introduced the banker\'s or broker\'s business.\"}', 'changer of money'),
(2773, 2774, 'κεφάλαιον', '{\"def\":{\"short\":\"a principal thing, i.e., main point; specially, an amount (of money)\",\"long\":[\"the chief or main point, the principal thing\",\"the pecuniary sum total of a reckoning, amount\",[\"the principal, capital, as distinguished from the interest\",\"a sum of money, sum\"]]},\"deriv\":\"neuter of a derivative of G2776\",\"pronun\":{\"ipa\":\"kɛˈfɑ.lɛ.on\",\"ipa_mod\":\"ce̞ˈfɑ.le.own\",\"sbl\":\"kephalaion\",\"dic\":\"keh-FA-leh-one\",\"dic_mod\":\"kay-FA-lay-one\"},\"see\":[\"G2776\"]}', 'sum'),
(2774, 2775, 'κεφαλαιόω', '{\"def\":{\"short\":\"(specially) to strike on the head\",\"long\":[\"to bring under headings, to sum up, to summarise\",\"to smite or wound in the head, to smite on the cheek\"]},\"deriv\":\"from the same as G2774\",\"pronun\":{\"ipa\":\"kɛ.fɑ.lɛˈo.o\",\"ipa_mod\":\"ce̞.fɑ.leˈow.ow\",\"sbl\":\"kephalaioō\",\"dic\":\"keh-fa-leh-OH-oh\",\"dic_mod\":\"kay-fa-lay-OH-oh\"},\"see\":[\"G2774\"]}', 'wound in the head'),
(2775, 2776, 'κεφαλή', '{\"def\":{\"short\":\"the head (as the part most readily taken hold of), literally or figuratively\",\"long\":[\"the head, both of men and often of animals\",\"metaphorically anything supreme, chief, prominent\",[\"of persons, master lord: of a husband in relation to his wife\",\"of Christ: the Lord of the husband and of the Church\",\"of things: the corner stone\"]]},\"deriv\":\"from the primary κάπτω (in the sense of seizing)\",\"pronun\":{\"ipa\":\"kɛ.fɑˈle\",\"ipa_mod\":\"ce̞.fɑˈle̞\",\"sbl\":\"kephalē\",\"dic\":\"keh-fa-LAY\",\"dic_mod\":\"kay-fa-LAY\"},\"comment\":\"Since the loss of the head destroys life, this word is used in the phrases relating to capital and extreme punishment.\"}', 'head'),
(2776, 2777, 'κεφαλίς', '{\"def\":{\"short\":\"properly, a knob, i.e., (by implication) a roll (by extension from the end of a stick on which the manuscript was rolled)\",\"long\":[\"a little head\",\"the highest part, extremity of anything\",[\"as the capital of a column\",\"the tips or knobs of the wooden rod around which parchments were rolled were called by this word, because they resembled little heads\"],\"the Alexandrian writers transferred the name to the roll or volume itself\",[\"in the roll of the book\"]]},\"deriv\":\"from G2776\",\"pronun\":{\"ipa\":\"kɛ.fɑˈlis\",\"ipa_mod\":\"ce̞.fɑˈlis\",\"sbl\":\"kephalis\",\"dic\":\"keh-fa-LEES\",\"dic_mod\":\"kay-fa-LEES\"},\"see\":[\"G2776\"]}', 'volume'),
(2777, 2778, 'κῆνσος', '{\"def\":{\"short\":\"properly, an enrollment (\\\"census\\\"), i.e., (by implication) a tax\",\"long\":[\"census (among the Romans, denoting a register and valuation of property in accordance with which taxes were paid), in the New Testament the tax or tribute levied on individuals and to be paid yearly\",\"the coin with which the tax is paid, tribute money\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈken.sos\",\"ipa_mod\":\"ˈke̞n.sows\",\"sbl\":\"kēnsos\",\"dic\":\"KANE-sose\",\"dic_mod\":\"KANE-sose\"}}', 'tribute'),
(2778, 2779, 'κῆπος', '{\"def\":{\"short\":\"a garden\",\"long\":[\"a garden\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈke.pos\",\"ipa_mod\":\"ˈke̞.pows\",\"sbl\":\"kēpos\",\"dic\":\"KAY-pose\",\"dic_mod\":\"KAY-pose\"}}', 'garden'),
(2779, 2780, 'κηπουρός', '{\"def\":{\"short\":\"a garden-keeper, i.e., gardener\",\"long\":[\"a keeper of a garden, a gardener\"]},\"deriv\":\"from G2779 and οὖρος (a warden)\",\"pronun\":{\"ipa\":\"ke.puˈros\",\"ipa_mod\":\"ke̞.puˈrows\",\"sbl\":\"kēpouros\",\"dic\":\"kay-poo-ROSE\",\"dic_mod\":\"kay-poo-ROSE\"},\"see\":[\"G2779\"]}', 'gardener'),
(2780, 2781, 'κηρίον', '{\"def\":{\"short\":\"a cell for honey, i.e., (collectively) the comb\",\"long\":[\"honeycomb\"]},\"deriv\":\"diminutive from κηός (wax)\",\"pronun\":{\"ipa\":\"keˈri.on\",\"ipa_mod\":\"ke̞ˈri.own\",\"sbl\":\"kērion\",\"dic\":\"kay-REE-one\",\"dic_mod\":\"kay-REE-one\"}}', '(honey-)comb'),
(2781, 2782, 'κήρυγμα', '{\"def\":{\"short\":\"a proclamation (especially of the gospel; by implication, the gospel itself)\",\"long\":[\"that which is proclaimed by a herald or public crier, a proclamation by herald\",\"in the New Testament the message or proclamation of the heralds of God or Christ\"]},\"deriv\":\"from G2784\",\"pronun\":{\"ipa\":\"ˈke.ryɣ.mɑ\",\"ipa_mod\":\"ˈke̞.rjuɣ.mɑ\",\"sbl\":\"kērygma\",\"dic\":\"KAY-roog-ma\",\"dic_mod\":\"KAY-ryoog-ma\"},\"see\":[\"G2784\"]}', 'preaching'),
(2782, 2783, 'κῆρυξ', '{\"def\":{\"short\":\"a herald, i.e., of divine truth (especially of the gospel)\",\"long\":[\"a herald or messenger vested with public authority, who conveyed the official messages of kings, magistrates, princes, military commanders, or who gave a public summons or demand, and performed various other duties, God\'s ambassador, the herald or proclaimer of the divine word\"]},\"deriv\":\"from G2784\",\"pronun\":{\"ipa\":\"ˈke.ryk͡s\",\"ipa_mod\":\"ˈke̞.rjuk͡s\",\"sbl\":\"kēryx\",\"dic\":\"KAY-rooks\",\"dic_mod\":\"KAY-ryooks\"},\"see\":[\"G2784\"]}', 'preacher'),
(2783, 2784, 'κηρύσσω', '{\"def\":{\"short\":\"to herald (as a public crier), especially divine truth (the gospel)\",\"long\":[\"to be a herald, to officiate as a herald\",[\"to proclaim after the manner of a herald\",\"always with the suggestion of formality, gravity and an authority which must be listened to and obeyed\"],\"to publish, proclaim openly: something which has been done\",\"used of the public proclamation of the gospel and matters pertaining to it, made by John the Baptist, by Jesus, by the apostles and other Christian teachers\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"keˈrys.so\",\"ipa_mod\":\"ke̞ˈrjus.sow\",\"sbl\":\"kēryssō\",\"dic\":\"kay-ROOS-soh\",\"dic_mod\":\"kay-RYOOS-soh\"}}', 'preacher(-er), proclaim, publish'),
(2784, 2785, 'κῆτος', '{\"def\":{\"short\":\"a huge fish (as gaping for prey)\",\"long\":[\"a sea monster, whale, huge fish\"]},\"deriv\":\"probably from the base of G5490\",\"pronun\":{\"ipa\":\"ˈke.tos\",\"ipa_mod\":\"ˈke̞.tows\",\"sbl\":\"kētos\",\"dic\":\"KAY-tose\",\"dic_mod\":\"KAY-tose\"},\"see\":[\"G5490\"]}', 'whale'),
(2785, 2786, 'Κηφᾶς', '{\"def\":{\"lit\":\"stone\",\"short\":\"the Rock; Cephas (i.e., Kepha), a surname of Peter\",\"long\":[\"another name for the apostle Peter\"]},\"deriv\":\"of Chaldee origin (compare H3710)\",\"pronun\":{\"ipa\":\"keˈfɑs\",\"ipa_mod\":\"ke̞ˈfɑs\",\"sbl\":\"kēphas\",\"dic\":\"kay-FAHS\",\"dic_mod\":\"kay-FAHS\"},\"see\":[\"H3710\"]}', 'Cephas'),
(2786, 2787, 'κιβωτός', '{\"def\":{\"short\":\"a box, i.e., the sacred ark and that of Noah\",\"long\":[\"a wooden chest or box\",\"in the New Testament the ark of the covenant, in the temple at Jerusalem\",\"of Noah\'s vessel built in the form of an ark\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ki.βoˈtos\",\"ipa_mod\":\"ci.vowˈtows\",\"sbl\":\"kibōtos\",\"dic\":\"kee-voh-TOSE\",\"dic_mod\":\"kee-voh-TOSE\"}}', 'ark'),
(2787, 2788, 'κιθάρα', '{\"def\":{\"short\":\"a lyre\",\"long\":[\"a harp to which praises of God are sung in heaven\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"kiˈθɑ.rɑ\",\"ipa_mod\":\"ciˈθɑ.rɑ\",\"sbl\":\"kithara\",\"dic\":\"kee-THA-ra\",\"dic_mod\":\"kee-THA-ra\"}}', 'harp'),
(2788, 2789, 'κιθαρίζω', '{\"def\":{\"short\":\"to play on a lyre\",\"long\":[\"to play upon the harp\"]},\"deriv\":\"from G2788\",\"pronun\":{\"ipa\":\"ki.θɑˈri.zo\",\"ipa_mod\":\"ci.θɑˈri.zow\",\"sbl\":\"kitharizō\",\"dic\":\"kee-tha-REE-zoh\",\"dic_mod\":\"kee-tha-REE-zoh\"},\"see\":[\"G2788\"]}', 'harp'),
(2789, 2790, 'κιθαρῳδός', '{\"def\":{\"short\":\"a lyre-singer(-player), i.e., harpist\",\"long\":[\"a harper, one who plays the harp and accompanies it with his voice\"]},\"deriv\":\"from G2788 and a derivative of the same as G5603\",\"pronun\":{\"ipa\":\"ki.θɑ.roˈðos\",\"ipa_mod\":\"ci.θɑ.rowˈðows\",\"sbl\":\"kitharōdos\",\"dic\":\"kee-tha-roh-THOSE\",\"dic_mod\":\"kee-tha-roh-THOSE\"},\"see\":[\"G2788\",\"G5603\"]}', 'harper'),
(2790, 2791, 'Κιλικία', '{\"def\":{\"lit\":\"the land of Celix\",\"short\":\"Cilicia, a region of Asia Minor\",\"long\":[\"a maritime province in the southeast of Asia Minor, boarding on Pamphylia in the west, Lycaonia and Cappadocia in the north and Syria in the east\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ki.liˈki.ɑ\",\"ipa_mod\":\"ci.liˈki.ɑ\",\"sbl\":\"kilikia\",\"dic\":\"kee-lee-KEE-ah\",\"dic_mod\":\"kee-lee-KEE-ah\"},\"comment\":\"The capital of Cilicia was Tarsus, where Paul was born.\"}', 'Cilicia'),
(2791, 2792, 'κινάμωμον', '{\"def\":{\"short\":\"cinnamon\",\"long\":[\"cinnamon was a well known aromatic substance, the rind of \\\"Laurus cinnamonum\\\" called \\\"Korunda-gauhah\\\" in Ceylon\"]},\"deriv\":\"of foreign origin (compare H7076)\",\"pronun\":{\"ipa\":\"kiˈnɑ.mo.mon\",\"ipa_mod\":\"ciˈnɑ.mow.mown\",\"sbl\":\"kinamōmon\",\"dic\":\"kee-NA-moh-mone\",\"dic_mod\":\"kee-NA-moh-mone\"},\"see\":[\"H7076\"]}', 'cinnamon'),
(2792, 2793, 'κινδυνεύω', '{\"def\":{\"short\":\"to undergo peril\",\"long\":[\"to be in jeopardy, to be in danger, to put in peril\"]},\"deriv\":\"from G2794\",\"pronun\":{\"ipa\":\"kin.ðyˈnɛβ.o\",\"ipa_mod\":\"cin̪.ðjuˈnev.ow\",\"sbl\":\"kindyneuō\",\"dic\":\"keen-thoo-NEV-oh\",\"dic_mod\":\"keen-thyoo-NAVE-oh\"},\"see\":[\"G2794\"]}', 'be in danger, be (stand) in jeopardy'),
(2793, 2794, 'κίνδυνος', '{\"def\":{\"short\":\"danger\",\"long\":[\"a danger, a peril\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈkin.ðy.nos\",\"ipa_mod\":\"ˈkin̪.ðju.nows\",\"sbl\":\"kindynos\",\"dic\":\"KEEN-thoo-nose\",\"dic_mod\":\"KEEN-thyoo-nose\"}}', 'peril'),
(2794, 2795, 'κινέω', '{\"def\":{\"short\":\"to stir (transitively), literally or figuratively\",\"long\":[\"to cause to go, i.e., to move, set in motion\",[\"to be moved, move: of that motion which is evident in life\",\"to move from a place, to remove\"],\"metaphorically\",[\"to move excite\",\"a riot, disturbance\",\"to throw into commotion\"]]},\"deriv\":\"from κίω (poetic for εἶμι, to go)\",\"pronun\":{\"ipa\":\"kiˈnɛ.o\",\"ipa_mod\":\"ciˈne̞.ow\",\"sbl\":\"kineō\",\"dic\":\"kee-NEH-oh\",\"dic_mod\":\"kee-NAY-oh\"}}', '(re-)move(-r), way'),
(2795, 2796, 'κίνησις', '{\"def\":{\"short\":\"a stirring\",\"long\":[\"a moving, agitation\"]},\"deriv\":\"from G2795\",\"pronun\":{\"ipa\":\"ˈki.ne.sis\",\"ipa_mod\":\"ˈki.ne̞.sis\",\"sbl\":\"kinēsis\",\"dic\":\"KEE-nay-sees\",\"dic_mod\":\"KEE-nay-sees\"},\"see\":[\"G2795\"]}', 'moving'),
(2796, 2797, 'Κίς', '{\"def\":{\"lit\":\"a snare\",\"short\":\"Cis (i.e., Kish), an Israelite\",\"long\":[\"the father of Saul, the first king of Israel\"]},\"deriv\":\"of Hebrew origin (H7027)\",\"pronun\":{\"ipa\":\"kis\",\"ipa_mod\":\"kis\",\"sbl\":\"kis\",\"dic\":\"kees\",\"dic_mod\":\"kees\"},\"see\":[\"H7027\"]}', 'Cis'),
(2797, 2798, 'κλάδος', '{\"def\":{\"short\":\"a twig or bough (as if broken off)\",\"long\":[\"a young tender shoot, broken off for grafting\",\"a branch\",[\"as the Jewish patriarchs are likened to a root, so their posterity are likened to branches\"]]},\"deriv\":\"from G2806\",\"pronun\":{\"ipa\":\"ˈklɑ.ðos\",\"ipa_mod\":\"ˈklɑ.ðows\",\"sbl\":\"klados\",\"dic\":\"KLA-those\",\"dic_mod\":\"KLA-those\"},\"see\":[\"G2806\"]}', 'branch'),
(2798, 2799, 'κλαίω', '{\"def\":{\"short\":\"to sob, i.e., wail aloud (whereas 1145 is rather to cry silently)\",\"long\":[\"to mourn, weep, lament\",[\"weeping as the sign of pain and grief for the thing signified (i.e., for the pain and grief)\",\"of those who mourn for the dead\"],\"to weep for, mourn for, bewail, one\"]},\"deriv\":\"of uncertain affinity\",\"pronun\":{\"ipa\":\"ˈklɛ.o\",\"ipa_mod\":\"ˈkle.ow\",\"sbl\":\"klaiō\",\"dic\":\"KLEH-oh\",\"dic_mod\":\"KLAY-oh\"}}', 'bewail, weep'),
(2799, 2800, 'κλάσις', '{\"def\":{\"short\":\"fracture (the act)\",\"long\":[\"a breaking\"]},\"deriv\":\"from G2806\",\"pronun\":{\"ipa\":\"ˈklɑ.sis\",\"ipa_mod\":\"ˈklɑ.sis\",\"sbl\":\"klasis\",\"dic\":\"KLA-sees\",\"dic_mod\":\"KLA-sees\"},\"see\":[\"G2806\"]}', 'breaking'),
(2800, 2801, 'κλάσμα', '{\"def\":{\"short\":\"a piece (bit)\",\"long\":[\"a fragment, broken piece\",[\"remnants of food\"]]},\"deriv\":\"from G2806\",\"pronun\":{\"ipa\":\"ˈklɑ.smɑ\",\"ipa_mod\":\"ˈklɑ.smɑ\",\"sbl\":\"klasma\",\"dic\":\"KLA-sma\",\"dic_mod\":\"KLA-sma\"},\"see\":[\"G2806\"]}', 'broken, fragment'),
(2801, 2802, 'Κλαύδη', '{\"def\":{\"lit\":\"lame\",\"short\":\"Claude, an island near Crete\",\"long\":[\"a small island nearly due west of Cape Malta on the south coast of Crete, and nearly due south of Phoenice\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈklɛβ.ðe\",\"ipa_mod\":\"ˈkla.ðe̞\",\"sbl\":\"klaudē\",\"dic\":\"KLEV-thay\",\"dic_mod\":\"KLA-thay\"}}', 'Clauda'),
(2802, 2803, 'Κλαυδία', '{\"def\":{\"lit\":\"lame\",\"short\":\"Claudia, a Christian woman\",\"long\":[\"a Christian woman\"]},\"deriv\":\"feminine of G2804\",\"pronun\":{\"ipa\":\"klɛβˈði.ɑ\",\"ipa_mod\":\"klaˈði.ɑ\",\"sbl\":\"klaudia\",\"dic\":\"klev-THEE-ah\",\"dic_mod\":\"kla-THEE-ah\"},\"see\":[\"G2804\"]}', 'Claudia'),
(2803, 2804, 'Κλαύδιος', '{\"def\":{\"lit\":\"lame\",\"short\":\"Claudius, the name of two Romans\",\"long\":[\"C. Caesar the name of the fourth Roman emperor, who came to power in 41 AD and was poisoned by his wife Agrippina, in 54 AD\",\"C. Lysias a tribune of the Roman cohort who rescued Paul from the hands of the mob at Jerusalem\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈklɛβ.ði.os\",\"ipa_mod\":\"ˈkla.ði.ows\",\"sbl\":\"klaudios\",\"dic\":\"KLEV-thee-ose\",\"dic_mod\":\"KLA-thee-ose\"}}', 'Claudius'),
(2804, 2805, 'κλαυθμός', '{\"def\":{\"short\":\"lamentation\",\"long\":[\"weeping, lamentation\"]},\"deriv\":\"from G2799\",\"pronun\":{\"ipa\":\"klɑβθˈmos\",\"ipa_mod\":\"klɑfθˈmows\",\"sbl\":\"klauthmos\",\"dic\":\"klavth-MOSE\",\"dic_mod\":\"klafth-MOSE\"},\"see\":[\"G2799\"]}', 'wailing, weeping, X wept'),
(2805, 2806, 'κλάω', '{\"def\":{\"short\":\"to break (specially, of bread)\",\"long\":[\"to break\",[\"used in the New Testament of the breaking of bread or communion\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈklɑ.o\",\"ipa_mod\":\"ˈklɑ.ow\",\"sbl\":\"klaō\",\"dic\":\"KLA-oh\",\"dic_mod\":\"KLA-oh\"}}', 'break'),
(2806, 2807, 'κλείς', '{\"def\":{\"short\":\"a key (as shutting a lock), literally or figuratively\",\"long\":[\"a key\",[\"since the keeper of the keys has the power to open and to shut\",\"metaphorically in the New Testament to denote power and authority of various kinds\"]]},\"deriv\":\"from G2808\",\"pronun\":{\"ipa\":\"klis\",\"ipa_mod\":\"klis\",\"sbl\":\"kleis\",\"dic\":\"klees\",\"dic_mod\":\"klees\"},\"see\":[\"G2808\"]}', 'key'),
(2807, 2808, 'κλείω', '{\"def\":{\"short\":\"to close (literally or figuratively)\",\"long\":[\"to shut, shut up\",\"metaphorically\",[\"to cause the heavens to withhold rain\",\"to shut up compassion so that it is like a thing inaccessible to one, to be devoid of pity towards one\",\"to obstruct the entrance into the kingdom of heaven\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈkli.o\",\"ipa_mod\":\"ˈkli.ow\",\"sbl\":\"kleiō\",\"dic\":\"KLEE-oh\",\"dic_mod\":\"KLEE-oh\"}}', 'shut (up)'),
(2808, 2809, 'κλέμμα', '{\"def\":{\"short\":\"stealing (properly, the thing stolen, but used of the act)\",\"long\":[\"thing stolen\",\"the act of theft\"]},\"deriv\":\"from G2813\",\"pronun\":{\"ipa\":\"ˈklɛm.mɑ\",\"ipa_mod\":\"ˈkle̞m.mɑ\",\"sbl\":\"klemma\",\"dic\":\"KLEM-ma\",\"dic_mod\":\"KLAME-ma\"},\"see\":[\"G2813\"]}', 'theft'),
(2809, 2810, 'Κλεόπας', '{\"def\":{\"lit\":\"of a renowned father\",\"short\":\"Cleopas, a Christian\",\"long\":[\"one of the two disciples who were going to Emmaus on the day of the resurrection\"]},\"deriv\":\"probably contracted from Κλεόπατρος (compound of G2811 and G3962)\",\"pronun\":{\"ipa\":\"klɛˈo.pɑs\",\"ipa_mod\":\"kle̞ˈow.pɑs\",\"sbl\":\"kleopas\",\"dic\":\"kleh-OH-pahs\",\"dic_mod\":\"klay-OH-pahs\"},\"see\":[\"G2811\",\"G3962\"]}', 'Cleopas'),
(2810, 2811, 'κλέος', '{\"def\":{\"short\":\"renown (as if being called)\",\"long\":[\"rumour, report\",\"glory, praise\"]},\"deriv\":\"from a shorter form of G2564\",\"pronun\":{\"ipa\":\"ˈklɛ.os\",\"ipa_mod\":\"ˈkle̞.ows\",\"sbl\":\"kleos\",\"dic\":\"KLEH-ose\",\"dic_mod\":\"KLAY-ose\"},\"see\":[\"G2564\"]}', 'glory'),
(2811, 2812, 'κλέπτης', '{\"def\":{\"short\":\"a stealer (literally or figuratively)\",\"long\":[\"an embezzler, pilferer\",[\"the name is transferred to false teachers, who do not care to instruct men, but abuse their confidence for their own gain\"]]},\"deriv\":\"from G2813\",\"pronun\":{\"ipa\":\"ˈklɛ.ptes\",\"ipa_mod\":\"ˈkle̞.pte̞s\",\"sbl\":\"kleptēs\",\"dic\":\"KLEH-ptase\",\"dic_mod\":\"KLAY-ptase\"},\"see\":[\"G2813\",\"G3027\"],\"comment\":\"Compare G3027.\"}', 'thief'),
(2812, 2813, 'κλέπτω', '{\"def\":{\"short\":\"to filch\",\"long\":[\"to steal\",[\"to commit a theft\",\"take away by theft i.e., take away by stealth\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈklɛ.pto\",\"ipa_mod\":\"ˈkle̞.ptow\",\"sbl\":\"kleptō\",\"dic\":\"KLEH-ptoh\",\"dic_mod\":\"KLAY-ptoh\"}}', 'steal'),
(2813, 2814, 'κλῆμα', '{\"def\":{\"short\":\"a limb or shoot (as if broken off)\",\"long\":[\"a tender and flexible branch\",\"specifically the shoot or branch of a vine, a vine sprout\"]},\"deriv\":\"from G2806\",\"pronun\":{\"ipa\":\"ˈkle.mɑ\",\"ipa_mod\":\"ˈkle̞.mɑ\",\"sbl\":\"klēma\",\"dic\":\"KLAY-ma\",\"dic_mod\":\"KLAY-ma\"},\"see\":[\"G2806\"]}', 'branch'),
(2814, 2815, 'Κλήμης', '{\"def\":{\"lit\":\"mild, merciful\",\"short\":\"merciful; Clemes (i.e., Clemens), a Christian\",\"long\":[\"a companion of Paul and apparently a member of the church of Philippi\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈkle.mes\",\"ipa_mod\":\"ˈkle̞.me̞s\",\"sbl\":\"klēmēs\",\"dic\":\"KLAY-mase\",\"dic_mod\":\"KLAY-mase\"},\"comment\":\"According to tradition, he is identical with that Clement, who was bishop of Rome towards the close of the 1st century.\"}', 'Clement'),
(2815, 2816, 'κληρονομέω', '{\"def\":{\"short\":\"to be an heir to (literally or figuratively)\",\"long\":[\"to receive a lot, receive by lot\",[\"especially to receive a part of an inheritance, receive as an inheritance, obtain by right of inheritance\",\"to be an heir, to inherit\"],\"to receive the portion assigned to one, receive an allotted portion, receive as one\'s own or as a possession\",\"to become partaker of, to obtain\"]},\"deriv\":\"from G2818\",\"pronun\":{\"ipa\":\"kle.ro.noˈmɛ.o\",\"ipa_mod\":\"kle̞.row.nowˈme̞.ow\",\"sbl\":\"klēronomeō\",\"dic\":\"klay-roh-noh-MEH-oh\",\"dic_mod\":\"klay-roh-noh-MAY-oh\"},\"see\":[\"G2818\"]}', 'be heir, (obtain by) inherit(-ance)'),
(2816, 2817, 'κληρονομία', '{\"def\":{\"short\":\"heirship, i.e., (concretely) a patrimony or (genitive case) a possession\",\"long\":[\"an inheritance, property received (or to be received) by inheritance\",\"what is given to one as a possession\",[\"the eternal blessedness of the consummated kingdom of God which is to be expected after the visible return of Christ\",\"the share which an individual will have in that eternal blessedness\"]]},\"deriv\":\"from G2818\",\"pronun\":{\"ipa\":\"kle.ro.noˈmi.ɑ\",\"ipa_mod\":\"kle̞.row.nowˈmi.ɑ\",\"sbl\":\"klēronomia\",\"dic\":\"klay-roh-noh-MEE-ah\",\"dic_mod\":\"klay-roh-noh-MEE-ah\"},\"see\":[\"G2818\"]}', 'inheritance'),
(2817, 2818, 'κληρονόμος', '{\"def\":{\"short\":\"a sharer by lot, i.e., inheritor (literally or figuratively); by implication, a possessor\",\"long\":[\"one who receives by lot, an heir\",[\"an heir\",\"in Messianic usage, one who receives his allotted possession by right of sonship\"],\"one who has acquired or obtained the portion allotted to him\"]},\"deriv\":\"from G2819 and the base of G3551 (in its original sense of partitioning, i.e. (reflexively) getting by apportionment)\",\"pronun\":{\"ipa\":\"kle.roˈno.mos\",\"ipa_mod\":\"kle̞.rowˈnow.mows\",\"sbl\":\"klēronomos\",\"dic\":\"klay-roh-NOH-mose\",\"dic_mod\":\"klay-roh-NOH-mose\"},\"see\":[\"G2819\",\"G3551\"]}', 'heir'),
(2818, 2819, 'κλῆρος', '{\"def\":{\"short\":\"a die (for drawing chances); by implication, a portion (as if so secured); by extension, an acquisition (especially a patrimony, figuratively)\",\"long\":[\"an object used in casting or drawing lots, which was either a pebble, or a potsherd, or a bit of wood\",[\"the lots of several persons concerned, inscribed with their names, were thrown together into a vase, which was then shaken, and he whose lot fell out first upon the ground was the one chosen\"],\"what is obtained by lot, allotted portion\",[\"a portion of the ministry common to the apostles\",\"used of the part which one will have in eternal salvation\",[\"of salvation itself\",\"the eternal salvation which God has assigned to the saints\"],\"of persons\",[\"those whose care and oversight has been assigned to one [allotted charge], used of Christian churches, the administration of which falls to the lot of presbyters\"]]]},\"deriv\":\"probably from G2806 (through the idea of using bits of wood, etc., for the purpose\",\"pronun\":{\"ipa\":\"ˈkle.ros\",\"ipa_mod\":\"ˈkle̞.rows\",\"sbl\":\"klēros\",\"dic\":\"KLAY-rose\",\"dic_mod\":\"KLAY-rose\"},\"see\":[\"G2806\"]}', 'heritage, inheritance, lot, part'),
(2819, 2820, 'κληρόω', '{\"def\":{\"short\":\"to allot, i.e., (figuratively) to assign (a privilege)\",\"long\":[\"to cast lots, determine by lot\",\"to choose by lot\",\"to allot, assign by lot\",[\"on to another as a possession\"],\"in New Testament: to make a lot, i.e., a heritage, private possession\"]},\"deriv\":\"from G2819\",\"pronun\":{\"ipa\":\"kleˈro.o\",\"ipa_mod\":\"kle̞ˈrow.ow\",\"sbl\":\"klēroō\",\"dic\":\"klay-ROH-oh\",\"dic_mod\":\"klay-ROH-oh\"},\"see\":[\"G2819\"]}', 'obtain an inheritance'),
(2820, 2821, 'κλῆσις', '{\"def\":{\"short\":\"an invitation (figuratively)\",\"long\":[\"a calling, calling to\",\"a call, invitation\",[\"to a feast\",\"of the divine invitation to embrace salvation of God\"]]},\"deriv\":\"from a shorter form of G2564\",\"pronun\":{\"ipa\":\"ˈkle.sis\",\"ipa_mod\":\"ˈkle̞.sis\",\"sbl\":\"klēsis\",\"dic\":\"KLAY-sees\",\"dic_mod\":\"KLAY-sees\"},\"see\":[\"G2564\"]}', 'calling'),
(2821, 2822, 'κλητός', '{\"def\":{\"short\":\"invited, i.e., appointed, or (specially), a saint\",\"long\":[\"called, invited (to a banquet)\",[\"invited (by God in the proclamation of the Gospel) to obtain eternal salvation in the kingdom through Christ\",\"called to (the discharge of) some office\",[\"divinely selected and appointed\"]]]},\"deriv\":\"from the same as G2821\",\"pronun\":{\"ipa\":\"kleˈtos\",\"ipa_mod\":\"kle̞ˈtows\",\"sbl\":\"klētos\",\"dic\":\"klay-TOSE\",\"dic_mod\":\"klay-TOSE\"},\"see\":[\"G2821\"]}', 'called'),
(2822, 2823, 'κλίβανος', '{\"def\":{\"short\":\"an earthen pot used for baking in\",\"long\":[\"a clibanus, a earthen vessel for baking bread\",\"a furnace, oven\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈkli.βɑ.nos\",\"ipa_mod\":\"ˈkli.vɑ.nows\",\"sbl\":\"klibanos\",\"dic\":\"KLEE-va-nose\",\"dic_mod\":\"KLEE-va-nose\"},\"comment\":\"It was broader at the bottom than above at the orifice, and when sufficiently heated by a fire kindled within, the dough was baked by being spread upon the outside. But according to others, the dough was baked by being placed inside and the fire or coals outside, the vessel being perforated with small holes that the heat might better penetrate.\"}', 'oven'),
(2823, 2824, 'κλίμα', '{\"def\":{\"short\":\"a slope, i.e., (specially) a \\\"clime\\\" or tract of country\",\"long\":[\"an inclination, slope, declivity\",\"the [supposed] sloping of the earth from the equator towards the poles, a zone\",\"a tract of land, a region\"]},\"deriv\":\"from G2827\",\"pronun\":{\"ipa\":\"ˈkli.mɑ\",\"ipa_mod\":\"ˈkli.mɑ\",\"sbl\":\"klima\",\"dic\":\"KLEE-ma\",\"dic_mod\":\"KLEE-ma\"},\"see\":[\"G2827\"]}', 'part, region'),
(2824, 2825, 'κλίνη', '{\"def\":{\"short\":\"a couch (for sleep, sickness, sitting or eating)\",\"long\":[\"a small bed, a couch\",\"a couch to recline on at meals\",\"a couch on which a sick man is carried\"]},\"deriv\":\"from G2827\",\"pronun\":{\"ipa\":\"ˈkli.ne\",\"ipa_mod\":\"ˈkli.ne̞\",\"sbl\":\"klinē\",\"dic\":\"KLEE-nay\",\"dic_mod\":\"KLEE-nay\"},\"see\":[\"G2827\"]}', 'bed, table'),
(2825, 2826, 'κλινίδιον', '{\"def\":{\"short\":\"a pallet or little couch\",\"long\":[\"a small bed, a couch\"]},\"deriv\":\"neuter of a presumed derivative of G2825\",\"pronun\":{\"ipa\":\"kliˈni.ði.on\",\"ipa_mod\":\"kliˈni.ði.own\",\"sbl\":\"klinidion\",\"dic\":\"klee-NEE-thee-one\",\"dic_mod\":\"klee-NEE-thee-one\"},\"see\":[\"G2825\"]}', 'bed'),
(2826, 2827, 'κλίνω', '{\"def\":{\"short\":\"to slant or slope, i.e., incline or recline (literally or figuratively)\",\"long\":[\"transitively\",[\"to incline, bow\",\"to cause to fall back\",\"to recline\",[\"in a place for repose\"]],\"intransitively\",[\"to incline one\'s self\",[\"of the declining of the day\"]]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈkli.no\",\"ipa_mod\":\"ˈkli.now\",\"sbl\":\"klinō\",\"dic\":\"KLEE-noh\",\"dic_mod\":\"KLEE-noh\"}}', 'bow (down), be far spent, lay, turn to flight, wear away'),
(2827, 2828, 'κλισία', '{\"def\":{\"short\":\"properly, reclination, i.e., (concretely and specially), a party at a meal\",\"long\":[\"a hut erected to pass the night in\",\"a tent\",\"anything to recline on\",[\"a chair in which to lean back the head\",\"a reclining chair\"],\"a company reclining\",[\"a row or party of persons reclining at meal\"]]},\"deriv\":\"from a derivative of G2827\",\"pronun\":{\"ipa\":\"kliˈsi.ɑ\",\"ipa_mod\":\"kliˈsi.ɑ\",\"sbl\":\"klisia\",\"dic\":\"klee-SEE-ah\",\"dic_mod\":\"klee-SEE-ah\"},\"see\":[\"G2827\"]}', 'company'),
(2828, 2829, 'κλοπή', '{\"def\":{\"short\":\"stealing\",\"long\":[\"theft\"]},\"deriv\":\"from G2813\",\"pronun\":{\"ipa\":\"kloˈpe\",\"ipa_mod\":\"klowˈpe̞\",\"sbl\":\"klopē\",\"dic\":\"kloh-PAY\",\"dic_mod\":\"kloh-PAY\"},\"see\":[\"G2813\"]}', 'theft'),
(2829, 2830, 'κλύδων', '{\"def\":{\"short\":\"a surge of the sea (literally or figuratively)\",\"long\":[\"a dashing or surging wave, a surge, a violent agitation of the sea\"]},\"deriv\":\"from κλύζω (to billow or dash over)\",\"pronun\":{\"ipa\":\"ˈkly.ðon\",\"ipa_mod\":\"ˈklju.ðown\",\"sbl\":\"klydōn\",\"dic\":\"KLOO-thone\",\"dic_mod\":\"KLYOO-thone\"}}', 'raging, wave'),
(2830, 2831, 'κλυδωνίζομαι', '{\"def\":{\"short\":\"to surge, i.e., (figuratively) to fluctuate\",\"long\":[\"to be tossed by the waves\",\"metaphorically to be agitated (like the waves) mentally\"]},\"deriv\":\"middle voice from G2830\",\"pronun\":{\"ipa\":\"kly.ðoˈni.zo.mɛ\",\"ipa_mod\":\"klju.ðowˈni.zow.me\",\"sbl\":\"klydōnizomai\",\"dic\":\"kloo-thoh-NEE-zoh-meh\",\"dic_mod\":\"klyoo-thoh-NEE-zoh-may\"},\"see\":[\"G2830\"]}', 'toss to and fro'),
(2831, 2832, 'Κλωπᾶς', '{\"def\":{\"lit\":\"my exchanges\",\"short\":\"Clopas, an Israelite\",\"long\":[\"the father of James the less, the husband of Mary the sister of the mother of Jesus\"]},\"deriv\":\"of Chaldee origin (corresponding to H0256)\",\"pronun\":{\"ipa\":\"kloˈpɑs\",\"ipa_mod\":\"klowˈpɑs\",\"sbl\":\"klōpas\",\"dic\":\"kloh-PAHS\",\"dic_mod\":\"kloh-PAHS\"},\"see\":[\"H0256\"]}', 'Cleophas'),
(2832, 2833, 'κνήθω', '{\"def\":{\"short\":\"to scratch, i.e., (by implication) to tickle\",\"long\":[\"to scratch, tickle, make to itch\",\"to itch\",\"desirous of hearing something pleasant\"]},\"deriv\":\"from a primary κνάω (to scrape)\",\"pronun\":{\"ipa\":\"ˈkne.θo\",\"ipa_mod\":\"ˈkne̞.θow\",\"sbl\":\"knēthō\",\"dic\":\"KNAY-thoh\",\"dic_mod\":\"KNAY-thoh\"}}', 'X itching'),
(2833, 2834, 'Κνίδος', '{\"def\":{\"lit\":\"nettled\",\"short\":\"Cnidus, a place in Asia Minor\",\"long\":[\"a peninsula [now Cape Crio] and a city by the same name, situated at the extreme south west of the peninsula of Asia Minor, on a promontory now called Cape Crio, which projects between the islands of Cos and Rhodes\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ˈkni.ðos\",\"ipa_mod\":\"ˈkni.ðows\",\"sbl\":\"knidos\",\"dic\":\"KNEE-those\",\"dic_mod\":\"KNEE-those\"}}', 'Cnidus'),
(2834, 2835, 'κοδράντης', '{\"def\":{\"short\":\"a quadrans, i.e., the fourth part of an as\",\"long\":[\"a quadrans (about the fourth part of an \\\"as\\\"); in the New Testament a coin equal to one half the Attic chalcus worth about 3/8 of a cent\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"koˈðrɑn.tes\",\"ipa_mod\":\"kowˈðrɑn.te̞s\",\"sbl\":\"kodrantēs\",\"dic\":\"koh-THRAHN-tase\",\"dic_mod\":\"koh-THRAHN-tase\"}}', 'farthing'),
(2835, 2836, 'κοιλία', '{\"def\":{\"short\":\"a cavity, i.e., (especially) the abdomen; by implication, the matrix; figuratively, the heart\",\"long\":[\"the whole belly, the entire cavity\",[\"the upper [i.e., stomach] and the lower belly are distinguished\"],\"the lower belly, the lower region, the receptacle of the excrement\",\"the gullet\",[\"to be given up to the pleasures of the palate, to gluttony\"],\"the womb, the place where the foetus is conceived and nourished until birth\",[\"of the uterus of animals\"],\"the innermost part of a man, the soul, heart as the seat of thought, feeling, choice\"]},\"deriv\":\"from κοῖλος (\\\"hollow\\\")\",\"pronun\":{\"ipa\":\"kyˈli.ɑ\",\"ipa_mod\":\"kyˈli.ɑ\",\"sbl\":\"koilia\",\"dic\":\"koo-LEE-ah\",\"dic_mod\":\"koo-LEE-ah\"}}', 'belly, womb'),
(2836, 2837, 'κοιμάω', '{\"def\":{\"short\":\"to put to sleep, i.e., (passively or reflexively) to slumber; figuratively, to decease\",\"long\":[\"to cause to sleep, put to sleep\",\"metaphorically\",[\"to still, calm, quiet\",\"to fall asleep, to sleep\",\"to die\"]]},\"deriv\":\"from G2749\",\"pronun\":{\"ipa\":\"kyˈmɑ.o\",\"ipa_mod\":\"kyˈmɑ.ow\",\"sbl\":\"koimaō\",\"dic\":\"koo-MA-oh\",\"dic_mod\":\"koo-MA-oh\"},\"see\":[\"G2749\"]}', '(be a-, fall a-, fall on) sleep, be dead'),
(2837, 2838, 'κοίμησις', '{\"def\":{\"short\":\"sleeping, i.e., (by implication) repose\",\"long\":[\"a reposing, taking rest\",\"a lying, reclining\"]},\"deriv\":\"from G2837\",\"pronun\":{\"ipa\":\"ˈky.me.sis\",\"ipa_mod\":\"ˈky.me̞.sis\",\"sbl\":\"koimēsis\",\"dic\":\"KOO-may-sees\",\"dic_mod\":\"KOO-may-sees\"},\"see\":[\"G2837\"]}', 'taking of rest'),
(2838, 2839, 'κοινός', '{\"def\":{\"short\":\"common, i.e., (literally) shared by all or several, or (ceremonially) profane\",\"long\":[\"common\",\"common i.e., ordinary, belonging to generality\",[\"by the Jews, unhallowed, profane, Levitically unclean\"]]},\"deriv\":\"probably from G4862\",\"pronun\":{\"ipa\":\"kyˈnos\",\"ipa_mod\":\"kyˈnows\",\"sbl\":\"koinos\",\"dic\":\"koo-NOSE\",\"dic_mod\":\"koo-NOSE\"},\"see\":[\"G4862\"]}', 'common, defiled, unclean, unholy'),
(2839, 2840, 'κοινόω', '{\"def\":{\"short\":\"to make (or consider) profane (ceremonially)\",\"long\":[\"to make common\",[\"to make (Levitically) unclean, render unhallowed, defile, profane\",\"to declare or count unclean\"]]},\"deriv\":\"from G2839\",\"pronun\":{\"ipa\":\"kyˈno.o\",\"ipa_mod\":\"kyˈnow.ow\",\"sbl\":\"koinoō\",\"dic\":\"koo-NOH-oh\",\"dic_mod\":\"koo-NOH-oh\"},\"see\":[\"G2839\"]}', 'call common, defile, pollute, unclean'),
(2840, 2841, 'κοινωνέω', '{\"def\":{\"short\":\"to share with others (objectively or subjectively)\",\"long\":[\"to come into communion or fellowship with, to become a sharer, be made a partner\",\"to enter into fellowship, join one\'s self to an associate, make one\'s self a sharer or partner\"]},\"deriv\":\"from G2844\",\"pronun\":{\"ipa\":\"ky.noˈnɛ.o\",\"ipa_mod\":\"ky.nowˈne̞.ow\",\"sbl\":\"koinōneō\",\"dic\":\"koo-noh-NEH-oh\",\"dic_mod\":\"koo-noh-NAY-oh\"},\"see\":[\"G2844\"]}', 'communicate, distribute, be partaker'),
(2841, 2842, 'κοινωνία', '{\"def\":{\"short\":\"partnership, i.e., (literally) participation, or (social) intercourse, or (pecuniary) benefaction\",\"long\":[\"fellowship, association, community, communion, joint participation, intercourse\",[\"the share which one has in anything, participation\",\"intercourse, fellowship, intimacy\",[\"the right hand as a sign and pledge of fellowship (in fulfilling the apostolic office)\"],\"a gift jointly contributed, a collection, a contribution, as exhibiting an embodiment and proof of fellowship\"]]},\"deriv\":\"from G2844\",\"pronun\":{\"ipa\":\"ky.noˈni.ɑ\",\"ipa_mod\":\"ky.nowˈni.ɑ\",\"sbl\":\"koinōnia\",\"dic\":\"koo-noh-NEE-ah\",\"dic_mod\":\"koo-noh-NEE-ah\"},\"see\":[\"G2844\"]}', '(to) communicate(-ation), communion, (contri-)distribution, fellowship'),
(2842, 2843, 'κοινωνικός', '{\"def\":{\"short\":\"communicative, i.e., (pecuniarily) liberal\",\"long\":[\"social, sociable, ready and apt to form and maintain communion and fellowship\",\"inclined to make others sharers in one\'s possessions, inclined to impart, free in giving, liberal\"]},\"deriv\":\"from G2844\",\"pronun\":{\"ipa\":\"ky.no.niˈkos\",\"ipa_mod\":\"ky.now.niˈkows\",\"sbl\":\"koinōnikos\",\"dic\":\"koo-noh-nee-KOSE\",\"dic_mod\":\"koo-noh-nee-KOSE\"},\"see\":[\"G2844\"]}', 'willing to communicate'),
(2843, 2844, 'κοινωνός', '{\"def\":{\"short\":\"a sharer, i.e., associate\",\"long\":[\"a partner, associate, comrade, companion\",\"a partner, sharer, in anything\",[\"of the altar in Jerusalem on which the sacrifices are offered\",[\"sharing in the worship of the Jews\"],\"partakers of (or with) demons\",[\"brought into fellowship with them, because they are the authors of heathen worship\"]]]},\"deriv\":\"from G2839\",\"pronun\":{\"ipa\":\"ky.noˈnos\",\"ipa_mod\":\"ky.nowˈnows\",\"sbl\":\"koinōnos\",\"dic\":\"koo-noh-NOSE\",\"dic_mod\":\"koo-noh-NOSE\"},\"see\":[\"G2839\"]}', 'companion, X fellowship, partaker, partner');
INSERT INTO `lexicon_greek` (`id`, `strongs`, `word`, `data`, `usage`) VALUES
(2844, 2845, 'κοίτη', '{\"def\":{\"short\":\"a couch; by extension, cohabitation; by implication, the male sperm\",\"long\":[\"a place for laying down, resting, sleeping in\",[\"a bed, couch\"],\"the marriage bed\",[\"of adultery\"],\"cohabitation, whether lawful or unlawful\",[\"sexual intercourse\"]]},\"deriv\":\"from G2749\",\"pronun\":{\"ipa\":\"ˈky.te\",\"ipa_mod\":\"ˈky.te̞\",\"sbl\":\"koitē\",\"dic\":\"KOO-tay\",\"dic_mod\":\"KOO-tay\"},\"see\":[\"G2749\"]}', 'bed, chambering, X conceive'),
(2845, 2846, 'κοιτών', '{\"def\":{\"short\":\"a bedroom\",\"long\":[\"a sleeping room, bed chamber\",[\"the officer who is over the bed chamber, the chamberlain\"]]},\"deriv\":\"from G2845\",\"pronun\":{\"ipa\":\"kyˈton\",\"ipa_mod\":\"kyˈtown\",\"sbl\":\"koitōn\",\"dic\":\"koo-TONE\",\"dic_mod\":\"koo-TONE\"},\"see\":[\"G2845\"]}', '+ chamberlain'),
(2846, 2847, 'κόκκινος', '{\"def\":{\"short\":\"crimson-colored\",\"long\":[\"crimson, scarlet colored. A kernel, the grain or berry of the \\\"ilex coccifera\\\"; these berries are the clusters of the eggs of a female insect, the \\\"kermes\\\" (resembling the cochineal), and when collected and pulverised produces a red which was used in dyeing (Pliny)\",\"scarlet cloth or clothing\"]},\"deriv\":\"from G2848 (from the kernel-shape of the insect)\",\"pronun\":{\"ipa\":\"ˈkok.ki.nos\",\"ipa_mod\":\"ˈkowk.ci.nows\",\"sbl\":\"kokkinos\",\"dic\":\"KOKE-kee-nose\",\"dic_mod\":\"KOKE-kee-nose\"},\"see\":[\"G2848\"]}', 'scarlet (colour, coloured)'),
(2847, 2848, 'κόκκος', '{\"def\":{\"short\":\"a kernel of seed\",\"long\":[\"a grain\"]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"ˈkok.kos\",\"ipa_mod\":\"ˈkowk.kows\",\"sbl\":\"kokkos\",\"dic\":\"KOKE-kose\",\"dic_mod\":\"KOKE-kose\"}}', 'corn, grain'),
(2848, 2849, 'κολάζω', '{\"def\":{\"short\":\"properly, to curtail, i.e., (figuratively) to chastise (or reserve for infliction)\",\"long\":[\"to lop or prune, as trees and wings\",\"to curb, check, restrain\",\"to chastise, correct, punishment\",\"to cause to be punished\"]},\"deriv\":\"from κόλος (dwarf)\",\"pronun\":{\"ipa\":\"koˈlɑ.zo\",\"ipa_mod\":\"kowˈlɑ.zow\",\"sbl\":\"kolazō\",\"dic\":\"koh-LA-zoh\",\"dic_mod\":\"koh-LA-zoh\"}}', 'punish'),
(2849, 2850, 'κολακεία', '{\"def\":{\"short\":\"flattery\",\"long\":[\"flattery, flattering discourse\"]},\"deriv\":\"from a derivative of κόλαξ (a fawner)\",\"pronun\":{\"ipa\":\"ko.lɑˈki.ɑ\",\"ipa_mod\":\"kow.lɑˈci.ɑ\",\"sbl\":\"kolakeia\",\"dic\":\"koh-la-KEE-ah\",\"dic_mod\":\"koh-la-KEE-ah\"}}', 'X flattering'),
(2850, 2851, 'κόλασις', '{\"def\":{\"short\":\"penal infliction\",\"long\":[\"correction, punishment, penalty\"]},\"deriv\":\"from G2849\",\"pronun\":{\"ipa\":\"ˈko.lɑ.sis\",\"ipa_mod\":\"ˈkow.lɑ.sis\",\"sbl\":\"kolasis\",\"dic\":\"KOH-la-sees\",\"dic_mod\":\"KOH-la-sees\"},\"see\":[\"G2849\"]}', 'punishment, torment'),
(2851, 2852, 'κολαφίζω', '{\"def\":{\"short\":\"to rap with the fist\",\"long\":[\"to strike with the fist, give one a blow with the fist\",\"to maltreat, treat with violence and contumely\"]},\"deriv\":\"from a derivative of the base of G2849\",\"pronun\":{\"ipa\":\"ko.lɑˈfi.zo\",\"ipa_mod\":\"kow.lɑˈfi.zow\",\"sbl\":\"kolaphizō\",\"dic\":\"koh-la-FEE-zoh\",\"dic_mod\":\"koh-la-FEE-zoh\"},\"see\":[\"G2849\"]}', 'buffet'),
(2852, 2853, 'κολλάω', '{\"def\":{\"short\":\"to glue, i.e., (passively or reflexively) to stick (figuratively)\",\"long\":[\"to glue, to glue together, cement, fasten together\",\"to join or fasten firmly together\",\"to join one\'s self to, cleave to\"]},\"deriv\":\"from κόλλα (\\\"glue\\\")\",\"pronun\":{\"ipa\":\"kolˈlɑ.o\",\"ipa_mod\":\"kowlˈlɑ.ow\",\"sbl\":\"kollaō\",\"dic\":\"kole-LA-oh\",\"dic_mod\":\"kole-LA-oh\"}}', 'cleave, join (self), keep company'),
(2853, 2854, 'κολλούριον', '{\"def\":{\"short\":\"properly, a poultice (as made of or in the form of crackers), i.e., (by analogy) a plaster\",\"long\":[\"a preparation composed of various materials and used as a remedy for tender eyelids\"]},\"deriv\":\"neuter of a presumed derivative of κολλύρα (a cake; prob akin to the base of G2853)\",\"pronun\":{\"ipa\":\"kolˈlu.ri.on\",\"ipa_mod\":\"kowlˈlu.ri.own\",\"sbl\":\"kollourion\",\"dic\":\"kole-LOO-ree-one\",\"dic_mod\":\"kole-LOO-ree-one\"},\"see\":[\"G2853\"]}', 'eyesalve'),
(2854, 2855, 'κολλυβιστής', '{\"def\":{\"short\":\"a coin-dealer\",\"long\":[\"a money-changer, banker\"]},\"deriv\":\"from a presumed derivative of κόλλυβος (a small coin; probably akin to G2854)\",\"pronun\":{\"ipa\":\"kol.ly.βiˈstes\",\"ipa_mod\":\"kowl.lju.viˈste̞s\",\"sbl\":\"kollybistēs\",\"dic\":\"kole-loo-vee-STASE\",\"dic_mod\":\"kole-lyoo-vee-STASE\"},\"see\":[\"G2854\"]}', '(money-)changer'),
(2855, 2856, 'κολοβόω', '{\"def\":{\"short\":\"to dock, i.e., (figuratively) abridge\",\"long\":[\"to mutilate\",\"in New Testament: to shorten, abridge, curtail\"]},\"deriv\":\"from a derivative of the base of G2849\",\"pronun\":{\"ipa\":\"ko.loˈβo.o\",\"ipa_mod\":\"kow.lowˈvow.ow\",\"sbl\":\"koloboō\",\"dic\":\"koh-loh-VOH-oh\",\"dic_mod\":\"koh-loh-VOH-oh\"},\"see\":[\"G2849\"]}', 'shorten'),
(2856, 2857, 'Κολοσσαί', '{\"def\":{\"lit\":\"monstrosities\",\"short\":\"Colossae, a place in Asia Minor\",\"long\":[\"anciently a large and flourishing city, but in Strabo\'s time a small town of Phrygia Major situated on the Lycus not far from its junction with the Maeander and in the neighbourhood of Laodicea and Hierapolis\"]},\"deriv\":\"apparently feminine plural of kolossos (\\\"colossal\\\")\",\"pronun\":{\"ipa\":\"ko.losˈsɛ\",\"ipa_mod\":\"kow.lowsˈse\",\"sbl\":\"kolossai\",\"dic\":\"koh-lose-SEH\",\"dic_mod\":\"koh-lose-SAY\"}}', 'Colosse'),
(2857, 2858, 'Κολοσσαεύς', '{\"def\":{\"short\":\"a Colossaean, (i.e., inhabitant of Colossae\",\"long\":[\"a Colossian\"]},\"deriv\":\"from G2857\",\"pronun\":{\"ipa\":\"ko.los.sɑˈɛβs\",\"ipa_mod\":\"kow.lows.sɑˈefs\",\"sbl\":\"kolossaeus\",\"dic\":\"koh-lose-sa-EVS\",\"dic_mod\":\"koh-lose-sa-AYFS\"},\"see\":[\"G2857\"]}', 'Colossian'),
(2858, 2859, 'κόλπος', '{\"def\":{\"short\":\"the bosom; by analogy, a bay\",\"long\":[\"the front of the body between the arms\",\"the bosom of a garment, i.e., the hollow formed by the upper forepart of a rather loose garment bound by a girdle or sash, used for keeping and carrying things (the fold or pocket)\",\"a bay of the sea\"]},\"deriv\":\"apparently a primary word\",\"pronun\":{\"ipa\":\"ˈkol.pos\",\"ipa_mod\":\"ˈkowl.pows\",\"sbl\":\"kolpos\",\"dic\":\"KOLE-pose\",\"dic_mod\":\"KOLE-pose\"}}', 'bosom, creek'),
(2859, 2860, 'κολυμβάω', '{\"def\":{\"short\":\"to plunge into water\",\"long\":[\"to dive, to swim\"]},\"deriv\":\"from κόλυμβος (a diver)\",\"pronun\":{\"ipa\":\"ko.lymˈβɑ.o\",\"ipa_mod\":\"kow.ljuɱˈvɑ.ow\",\"sbl\":\"kolymbaō\",\"dic\":\"koh-loom-VA-oh\",\"dic_mod\":\"koh-lyoom-VA-oh\"}}', 'swim'),
(2860, 2861, 'κολυμβήθρα', '{\"def\":{\"short\":\"a diving-place, i.e., pond for bathing (or swimming)\",\"long\":[\"a place for diving, a swimming hole, a reservoir or pool for bathing\"]},\"deriv\":\"from G2860\",\"pronun\":{\"ipa\":\"ko.lymˈβe.θrɑ\",\"ipa_mod\":\"kow.ljuɱˈve̞.θrɑ\",\"sbl\":\"kolymbēthra\",\"dic\":\"koh-loom-VAY-thra\",\"dic_mod\":\"koh-lyoom-VAY-thra\"},\"see\":[\"G2860\"]}', 'pool'),
(2861, 2862, 'κολωνία', '{\"def\":{\"short\":\"a Roman \\\"colony\\\" for veterans\",\"long\":[\"a colony\",[\"the city of Philippi is so called, where Octavius had planted a Roman colony\"]]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ko.loˈni.ɑ\",\"ipa_mod\":\"kow.lowˈni.ɑ\",\"sbl\":\"kolōnia\",\"dic\":\"koh-loh-NEE-ah\",\"dic_mod\":\"koh-loh-NEE-ah\"}}', 'colony'),
(2862, 2863, 'κομάω', '{\"def\":{\"short\":\"to wear tresses of hair\",\"long\":[\"to let the hair grow, have long hair\"]},\"deriv\":\"from G2864\",\"pronun\":{\"ipa\":\"koˈmɑ.o\",\"ipa_mod\":\"kowˈmɑ.ow\",\"sbl\":\"komaō\",\"dic\":\"koh-MA-oh\",\"dic_mod\":\"koh-MA-oh\"},\"see\":[\"G2864\"]}', 'have long hair'),
(2863, 2864, 'κόμη', '{\"def\":{\"short\":\"the hair of the head (locks, as ornamental, and thus differing from G2359; which properly denotes merely the scalp)\",\"long\":[\"hair, head of hair\"]},\"deriv\":\"apparently from the same as G2865\",\"pronun\":{\"ipa\":\"ˈko.me\",\"ipa_mod\":\"ˈkow.me̞\",\"sbl\":\"komē\",\"dic\":\"KOH-may\",\"dic_mod\":\"KOH-may\"},\"see\":[\"G2359\",\"G2865\"]}', 'hair'),
(2864, 2865, 'κομίζω', '{\"def\":{\"short\":\"properly, to provide for, i.e., (by implication) to carry off (as if from harm; genitive case obtain)\",\"long\":[\"to care for, take care of, provide for\",\"to take up or carry away in order to care for and preserve\",\"to carry away, bear off\",\"to carry, bear, bring to, to carry away for one\'s self, to carry off what is one\'s own, to bring back\",[\"to receive, obtain: the promised blessing\",\"to receive what was previously one\'s own, to get back, receive back, recover\"]]},\"deriv\":\"from a primary κομέω (to tend, i.e. take care of)\",\"pronun\":{\"ipa\":\"koˈmi.zo\",\"ipa_mod\":\"kowˈmi.zow\",\"sbl\":\"komizō\",\"dic\":\"koh-MEE-zoh\",\"dic_mod\":\"koh-MEE-zoh\"}}', 'bring, receive'),
(2865, 2866, 'κομψότερον', '{\"def\":{\"short\":\"figuratively, convalescent\",\"long\":[\"more finely, better\"]},\"deriv\":\"neuter comparative of a derivative of the base of 2865 (meaning, properly, well dressed, i.e. nice)\",\"pronun\":{\"ipa\":\"komˈp͡so.tɛ.ron\",\"ipa_mod\":\"kowmˈp͡sow.te̞.rown\",\"sbl\":\"kompsoteron\",\"dic\":\"kome-PSOH-teh-rone\",\"dic_mod\":\"kome-PSOH-tay-rone\"}}', '+ began to amend'),
(2866, 2867, 'κονιάω', '{\"def\":{\"short\":\"to whitewash\",\"long\":[\"to cover with lime, plaster over, whitewash\",[\"the Jews were accustomed to whitewash the entrances to their sepulchers, as a warning against defilement by touching them\",\"term applied to a hypocrite who conceals his malice under an outward assumption of piety\"]]},\"deriv\":\"from κονία (dust; by analogy, lime)\",\"pronun\":{\"ipa\":\"ko.niˈɑ.o\",\"ipa_mod\":\"kow.niˈɑ.ow\",\"sbl\":\"koniaō\",\"dic\":\"koh-nee-AH-oh\",\"dic_mod\":\"koh-nee-AH-oh\"}}', 'whiten'),
(2867, 2868, 'κονιορτός', '{\"def\":{\"short\":\"pulverulence (as blown about)\",\"long\":[\"raised dust, flying dust\",\"dust\"]},\"deriv\":\"from the base of G2867 and ornumi (to \\\"rouse\\\")\",\"pronun\":{\"ipa\":\"ko.ni.orˈtos\",\"ipa_mod\":\"kow.ni.owrˈtows\",\"sbl\":\"koniortos\",\"dic\":\"koh-nee-ore-TOSE\",\"dic_mod\":\"koh-nee-ore-TOSE\"},\"see\":[\"G2867\"]}', 'dust'),
(2868, 2869, 'κοπάζω', '{\"def\":{\"short\":\"to tire, i.e., (figuratively) to relax\",\"long\":[\"to grow weary or tired\",\"to cease from violence, cease raging\"]},\"deriv\":\"from G2873\",\"pronun\":{\"ipa\":\"koˈpɑ.zo\",\"ipa_mod\":\"kowˈpɑ.zow\",\"sbl\":\"kopazō\",\"dic\":\"koh-PA-zoh\",\"dic_mod\":\"koh-PA-zoh\"},\"see\":[\"G2873\"]}', 'cease'),
(2869, 2870, 'κοπετός', '{\"def\":{\"short\":\"mourning (properly, by beating the breast)\",\"long\":[\"lamentation with beating of the breast as a sign of grief\"]},\"deriv\":\"from G2875\",\"pronun\":{\"ipa\":\"ko.pɛˈtos\",\"ipa_mod\":\"kow.pe̞ˈtows\",\"sbl\":\"kopetos\",\"dic\":\"koh-peh-TOSE\",\"dic_mod\":\"koh-pay-TOSE\"},\"see\":[\"G2875\"]}', 'lamentation'),
(2870, 2871, 'κοπή', '{\"def\":{\"short\":\"cutting, i.e., carnage\",\"long\":[\"the act of cutting, a cut\",\"a cutting in pieces, slaughter\"]},\"deriv\":\"from G2875\",\"pronun\":{\"ipa\":\"koˈpe\",\"ipa_mod\":\"kowˈpe̞\",\"sbl\":\"kopē\",\"dic\":\"koh-PAY\",\"dic_mod\":\"koh-PAY\"},\"see\":[\"G2875\"]}', 'slaughter'),
(2871, 2872, 'κοπιάω', '{\"def\":{\"short\":\"to feel fatigue; by implication, to work hard\",\"long\":[\"to grow weary, tired, exhausted (with toil or burdens or grief)\",\"to labor with wearisome effort, to toil\",[\"of bodily labor\"]]},\"deriv\":\"from a derivative of G2873\",\"pronun\":{\"ipa\":\"ko.piˈɑ.o\",\"ipa_mod\":\"kow.piˈɑ.ow\",\"sbl\":\"kopiaō\",\"dic\":\"koh-pee-AH-oh\",\"dic_mod\":\"koh-pee-AH-oh\"},\"see\":[\"G2873\"]}', '(bestow) labour, toil, be wearied'),
(2872, 2873, 'κόπος', '{\"def\":{\"short\":\"a cut, i.e., (by analogy) toil (as reducing the strength), literally or figuratively; by implication, pains\",\"long\":[\"a beating\",\"a beating of the breast with grief, sorrow\",\"labor\",[\"trouble\",[\"to cause one trouble, make work for him\"],\"intense labor united with trouble and toil\"]]},\"deriv\":\"from G2875\",\"pronun\":{\"ipa\":\"ˈko.pos\",\"ipa_mod\":\"ˈkow.pows\",\"sbl\":\"kopos\",\"dic\":\"KOH-pose\",\"dic_mod\":\"KOH-pose\"},\"see\":[\"G2875\"]}', 'labour, + trouble, weariness'),
(2873, 2874, 'κοπρία', '{\"def\":{\"short\":\"manure\",\"long\":[\"dung, manure\"]},\"deriv\":\"from κόπρος (ordure; perhaps akin to G2875)\",\"pronun\":{\"ipa\":\"koˈpri.ɑ\",\"ipa_mod\":\"kowˈpri.ɑ\",\"sbl\":\"kopria\",\"dic\":\"koh-PREE-ah\",\"dic_mod\":\"koh-PREE-ah\"},\"see\":[\"G2875\"]}', 'dung(-hill)'),
(2874, 2875, 'κόπτω', '{\"def\":{\"short\":\"to \\\"chop\\\"; specially, to beat the breast in grief\",\"long\":[\"to cut, strike, smite\",\"to cut from, cut off\",\"to beat one\'s breast for grief\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈko.pto\",\"ipa_mod\":\"ˈkow.ptow\",\"sbl\":\"koptō\",\"dic\":\"KOH-ptoh\",\"dic_mod\":\"KOH-ptoh\"},\"see\":[\"G5114\"],\"comment\":\"Compare the base of G5114.\"}', 'cut down, lament, mourn, (be-)wail'),
(2875, 2876, 'κόραξ', '{\"def\":{\"short\":\"a crow (from its voracity)\",\"long\":[\"a raven\"]},\"deriv\":\"perhaps from G2880\",\"pronun\":{\"ipa\":\"ˈko.rɑk͡s\",\"ipa_mod\":\"ˈkow.rɑk͡s\",\"sbl\":\"korax\",\"dic\":\"KOH-rahks\",\"dic_mod\":\"KOH-rahks\"},\"see\":[\"G2880\"]}', 'raven'),
(2876, 2877, 'κοράσιον', '{\"def\":{\"short\":\"a (little) girl\",\"long\":[\"a girl, damsel, maiden\"]},\"deriv\":\"neuter of a presumed derivative of κόρη (a maiden)\",\"pronun\":{\"ipa\":\"koˈrɑ.si.on\",\"ipa_mod\":\"kowˈrɑ.si.own\",\"sbl\":\"korasion\",\"dic\":\"koh-RA-see-one\",\"dic_mod\":\"koh-RA-see-one\"}}', 'damsel, maid'),
(2877, 2878, 'κορβᾶν', '{\"def\":{\"short\":\"a votive offering and the offering; a consecrated present (to the Temple fund); by extension (the latter term) the Treasury itself, i.e., the room where the contribution boxes stood\",\"long\":[\"a gift offered (or to be offered) to God\",\"the sacred treasury\"]},\"deriv\":\"of Hebrew and Chaldee origin respectively (H7133)\",\"pronun\":{\"ipa\":\"korˈβɑn\",\"ipa_mod\":\"kowrˈvɑn\",\"sbl\":\"korban\",\"dic\":\"kore-VAHN\",\"dic_mod\":\"kore-VAHN\"},\"see\":[\"H7133\"]}', 'Corban, treasury'),
(2878, 2879, 'Κορέ', '{\"def\":{\"lit\":\"baldness\",\"short\":\"Core (i.e., Korach), an Israelite\",\"long\":[\"a man who, with others, rebelled against Moses\"]},\"deriv\":\"of Hebrew origin (H7141)\",\"pronun\":{\"ipa\":\"koˈrɛ\",\"ipa_mod\":\"kowˈre̞\",\"sbl\":\"kore\",\"dic\":\"koh-REH\",\"dic_mod\":\"koh-RAY\"},\"see\":[\"H7141\"]}', 'Core'),
(2879, 2880, 'κορέννυμι', '{\"def\":{\"short\":\"to cram, i.e., glut or sate\",\"long\":[\"to satiate, sate, satisfy\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"koˈrɛn.ny.mi\",\"ipa_mod\":\"kowˈre̞n.nju.mi\",\"sbl\":\"korennymi\",\"dic\":\"koh-REN-noo-mee\",\"dic_mod\":\"koh-RANE-nyoo-mee\"}}', 'eat enough, full'),
(2880, 2881, 'Κορίνθιος', '{\"def\":{\"short\":\"a Corinthian, i.e., inhabitant of Corinth\",\"long\":[\"a Corinthian, and inhabitant of Corinth\"]},\"deriv\":\"from G2882\",\"pronun\":{\"ipa\":\"koˈrin.θi.os\",\"ipa_mod\":\"kowˈrin̪.θi.ows\",\"sbl\":\"korinthios\",\"dic\":\"koh-REEN-thee-ose\",\"dic_mod\":\"koh-REEN-thee-ose\"},\"see\":[\"G2882\"]}', 'Corinthian'),
(2881, 2882, 'Κόρινθος', '{\"def\":{\"lit\":\"satiated\",\"short\":\"Corinthus, a city of Greece\",\"long\":[\"an ancient and famous city of Greece, on the Isthmus of Corinth, and about 40 miles (65 km) west of Athens\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈko.rin.θos\",\"ipa_mod\":\"ˈkow.rin̪.θows\",\"sbl\":\"korinthos\",\"dic\":\"KOH-reen-those\",\"dic_mod\":\"KOH-reen-those\"}}', 'Corinth'),
(2882, 2883, 'Κορνήλιος', '{\"def\":{\"lit\":\"of a horn\",\"short\":\"Cornelius, a Roman\",\"long\":[\"a Roman centurion of the Italian cohort stationed in Caesarea who converted to Christianity\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"korˈne.li.os\",\"ipa_mod\":\"kowrˈne̞.li.ows\",\"sbl\":\"kornēlios\",\"dic\":\"kore-NAY-lee-ose\",\"dic_mod\":\"kore-NAY-lee-ose\"}}', 'Cornelius'),
(2883, 2884, 'κόρος', '{\"def\":{\"short\":\"a cor, i.e., a specific measure\",\"long\":[\"a corus or cor, the largest Hebrew dry measure (i.e., for wheat, meal etc.) about 10 to 11 bushels (350 to 400 liters)\"]},\"deriv\":\"of Hebrew origin (H3734)\",\"pronun\":{\"ipa\":\"ˈko.ros\",\"ipa_mod\":\"ˈkow.rows\",\"sbl\":\"koros\",\"dic\":\"KOH-rose\",\"dic_mod\":\"KOH-rose\"},\"see\":[\"H3734\"]}', 'measure'),
(2884, 2885, 'κοσμέω', '{\"def\":{\"short\":\"to put in proper order, i.e., decorate (literally or figuratively); specially, to snuff (a wick)\",\"long\":[\"to put in order, arrange, make ready, prepare\",\"to ornament, adore\",\"metaphorically to embellish with honor, gain honor\"]},\"deriv\":\"from G2889\",\"pronun\":{\"ipa\":\"koˈsmɛ.o\",\"ipa_mod\":\"kowˈsme̞.ow\",\"sbl\":\"kosmeō\",\"dic\":\"koh-SMEH-oh\",\"dic_mod\":\"koh-SMAY-oh\"},\"see\":[\"G2889\"]}', 'adorn, garnish, trim'),
(2885, 2886, 'κοσμικός', '{\"def\":{\"short\":\"terrene (\\\"cosmic\\\"), literally (mundane) or figuratively (corrupt)\",\"long\":[\"of or belonging to the world\",[\"relating to the universe\",\"earthly\",\"worldly, i.e., having the character of this present corrupt age\"]]},\"deriv\":\"from G2889 (in its secondary sense)\",\"pronun\":{\"ipa\":\"ko.smiˈkos\",\"ipa_mod\":\"kow.smiˈkows\",\"sbl\":\"kosmikos\",\"dic\":\"koh-smee-KOSE\",\"dic_mod\":\"koh-smee-KOSE\"},\"see\":[\"G2889\"]}', 'worldly'),
(2886, 2887, 'κόσμιος', '{\"def\":{\"short\":\"orderly, i.e., decorous\",\"long\":[\"well arranged, seemly, modest\"]},\"deriv\":\"from G2889 (in its primary sense)\",\"pronun\":{\"ipa\":\"ˈko.smi.os\",\"ipa_mod\":\"ˈkow.smi.ows\",\"sbl\":\"kosmios\",\"dic\":\"KOH-smee-ose\",\"dic_mod\":\"KOH-smee-ose\"},\"see\":[\"G2889\"]}', 'of good behaviour, modest'),
(2887, 2888, 'κοσμοκράτωρ', '{\"def\":{\"short\":\"a world-ruler, an epithet of Satan\",\"long\":[\"lord of the world, prince of this age\",[\"the devil and his demons\"]]},\"deriv\":\"from G2889 and G2902\",\"pronun\":{\"ipa\":\"ko.smoˈkrɑ.tor\",\"ipa_mod\":\"kow.smowˈkrɑ.towr\",\"sbl\":\"kosmokratōr\",\"dic\":\"koh-smoh-KRA-tore\",\"dic_mod\":\"koh-smoh-KRA-tore\"},\"see\":[\"G2889\",\"G2902\"]}', 'ruler'),
(2888, 2889, 'κόσμος', '{\"def\":{\"short\":\"orderly arrangement, i.e., decoration; by implication, the world (including its inhabitants, literally or figuratively (morally))\",\"long\":[\"an apt and harmonious arrangement or constitution, order, government\",\"ornament, decoration, adornment, i.e., the arrangement of the stars, \'the heavenly hosts\', as the ornament of the heavens (see 1 Peter 3:3)\",\"the world, the universe\",\"the circle of the earth, the earth\",\"the inhabitants of the earth, men, the human race\",\"the ungodly multitude; the whole mass of men alienated from God, and therefore hostile to the cause of Christ\",\"world affairs, the aggregate of things earthly\",[\"the whole circle of earthly goods, endowments riches, advantages, pleasures, etc, which although hollow and frail and fleeting, stir desire, seduce from God and are obstacles to the cause of Christ\"]]},\"deriv\":\"probably from the base of G2865\",\"pronun\":{\"ipa\":\"ˈko.smos\",\"ipa_mod\":\"ˈkow.smows\",\"sbl\":\"kosmos\",\"dic\":\"KOH-smose\",\"dic_mod\":\"KOH-smose\"},\"see\":[\"G2865\"]}', 'adorning, world'),
(2889, 2890, 'Κούαρτος', '{\"def\":{\"lit\":\"fourth\",\"short\":\"Quartus, a Christian\",\"long\":[\"a Roman Christian, perhaps one of the 70 disciples and the Bishop of Beirut (Berytus)\"]},\"deriv\":\"of Latin origin (fourth)\",\"pronun\":{\"ipa\":\"ˈku.ɑr.tos\",\"ipa_mod\":\"ˈku.ɑr.tows\",\"sbl\":\"kouartos\",\"dic\":\"KOO-ar-tose\",\"dic_mod\":\"KOO-ar-tose\"}}', 'Quartus'),
(2890, 2891, 'κοῦμι', '{\"def\":{\"short\":\"cumi (i.e., rise!)\",\"long\":[\"arise\"]},\"deriv\":\"of Chaldee origin (H6966)\",\"pronun\":{\"ipa\":\"ˈku.mi\",\"ipa_mod\":\"ˈku.mi\",\"sbl\":\"koumi\",\"dic\":\"KOO-mee\",\"dic_mod\":\"KOO-mee\"},\"see\":[\"H6966\"]}', 'cumi'),
(2891, 2892, 'κουστωδία', '{\"def\":{\"short\":\"\\\"custody\\\", i.e., a Roman sentry\",\"long\":[\"a guard, used of Roman soldiers guarding the sepulcher of Christ\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ku.stoˈði.ɑ\",\"ipa_mod\":\"ku.stowˈði.ɑ\",\"sbl\":\"koustōdia\",\"dic\":\"koo-stoh-THEE-ah\",\"dic_mod\":\"koo-stoh-THEE-ah\"},\"comment\":\"A Roman guard was made up of four to sixteen solders. In combat, they would form a square, and were able to hold off a much larger force.\"}', 'watch'),
(2892, 2893, 'κουφίζω', '{\"def\":{\"short\":\"to unload\",\"long\":[\"to be light\",\"to lighten\",[\"a ship by throwing the cargo overboard\"]]},\"deriv\":\"from κοῦφος (light in weight)\",\"pronun\":{\"ipa\":\"kuˈfi.zo\",\"ipa_mod\":\"kuˈfi.zow\",\"sbl\":\"kouphizō\",\"dic\":\"koo-FEE-zoh\",\"dic_mod\":\"koo-FEE-zoh\"}}', 'lighten'),
(2893, 2894, 'κόφινος', '{\"def\":{\"short\":\"a (small) basket\",\"long\":[\"a basket, wicker basket\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈko.fi.nos\",\"ipa_mod\":\"ˈkow.fi.nows\",\"sbl\":\"kophinos\",\"dic\":\"KOH-fee-nose\",\"dic_mod\":\"KOH-fee-nose\"}}', 'basket'),
(2894, 2895, 'κράββατος', '{\"def\":{\"short\":\"a mattress\",\"long\":[\"a pallet, camp bed (a rather simple bed holding only one person)\"]},\"deriv\":\"probably of foreign origin\",\"pronun\":{\"ipa\":\"ˈkrɑβ.βɑ.tos\",\"ipa_mod\":\"ˈkrɑv.vɑ.tows\",\"sbl\":\"krabbatos\",\"dic\":\"KRAHV-va-tose\",\"dic_mod\":\"KRAHV-va-tose\"}}', 'bed'),
(2895, 2896, 'κράζω', '{\"def\":{\"short\":\"properly, to \\\"croak\\\" (as a raven) or scream, i.e., (genitive case) to call aloud (shriek, exclaim, intreat)\",\"long\":[\"to croak\",[\"of the cry of a raven\",\"hence, to cry out, cry aloud, vociferate\",\"to cry or pray for vengeance\"],\"to cry\",[\"cry out aloud, speak with a loud voice\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈkrɑ.zo\",\"ipa_mod\":\"ˈkrɑ.zow\",\"sbl\":\"krazō\",\"dic\":\"KRA-zoh\",\"dic_mod\":\"KRA-zoh\"}}', 'cry (out)'),
(2896, 2897, 'κραιπάλη', '{\"def\":{\"short\":\"properly, a headache (as a seizure of pain) from drunkenness, i.e., (by implication) a debauch (by analogy, a glut)\",\"long\":[\"the giddiness and headache caused by drinking wine to excess\"]},\"deriv\":\"probably from the same as G0726\",\"pronun\":{\"ipa\":\"krɛˈpɑ.le\",\"ipa_mod\":\"kreˈpɑ.le̞\",\"sbl\":\"kraipalē\",\"dic\":\"kreh-PA-lay\",\"dic_mod\":\"kray-PA-lay\"},\"see\":[\"G0726\"]}', 'surfeiting'),
(2897, 2898, 'κρανίον', '{\"def\":{\"short\":\"a skull (\\\"cranium\\\")\",\"long\":[\"a skull\"]},\"deriv\":\"diminutive of a derivative of the base of G2768\",\"pronun\":{\"ipa\":\"krɑˈni.on\",\"ipa_mod\":\"krɑˈni.own\",\"sbl\":\"kranion\",\"dic\":\"kra-NEE-one\",\"dic_mod\":\"kra-NEE-one\"},\"see\":[\"G2768\"]}', 'Calvary, skull'),
(2898, 2899, 'κράσπεδον', '{\"def\":{\"short\":\"a margin, i.e., (specially), a fringe or tassel\",\"long\":[\"the extremity or prominent part of a thing, edge, skirt, margin\",[\"the fringe of a garment\",\"in the New Testament a little appendage hanging down from the edge of the mantle or cloak, made of twisted wool\",\"a tassel, tuft: the Jews had such appendages attached to their mantles to remind them of the law\"]]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈkrɑ.spɛ.ðon\",\"ipa_mod\":\"ˈkrɑ.spe̞.ðown\",\"sbl\":\"kraspedon\",\"dic\":\"KRA-speh-thone\",\"dic_mod\":\"KRA-spay-thone\"}}', 'border, hem'),
(2899, 2900, 'κραταιός', '{\"def\":{\"short\":\"powerful\",\"long\":[\"mighty\",[\"of the mighty power of God\"]]},\"deriv\":\"from G2904\",\"pronun\":{\"ipa\":\"krɑ.tɛˈos\",\"ipa_mod\":\"krɑ.teˈows\",\"sbl\":\"krataios\",\"dic\":\"kra-teh-OSE\",\"dic_mod\":\"kra-tay-OSE\"},\"see\":[\"G2904\"]}', 'mighty'),
(2900, 2901, 'κραταιόω', '{\"def\":{\"short\":\"to empower, i.e., (passively) increase in vigor\",\"long\":[\"to strengthen, make strong\",\"to be made strong, to increase in strength, to grow strong\"]},\"deriv\":\"from G2900\",\"pronun\":{\"ipa\":\"krɑ.tɛˈo.o\",\"ipa_mod\":\"krɑ.teˈow.ow\",\"sbl\":\"krataioō\",\"dic\":\"kra-teh-OH-oh\",\"dic_mod\":\"kra-tay-OH-oh\"},\"see\":[\"G2900\"]}', 'be strengthened, be (wax) strong'),
(2901, 2902, 'κρατέω', '{\"def\":{\"short\":\"to use strength, i.e., seize or retain (literally or figuratively)\",\"long\":[\"to have power, be powerful\",[\"to be chief, be master of, to rule\"],\"to get possession of\",[\"to become master of, to obtain\",\"to take hold of\",\"to take hold of, take, seize\",[\"to lay hands on one in order to get him into one\'s power\"]],\"to hold\",[\"to hold in the hand\",\"to hold fast, i.e., not discard or let go\",[\"to keep carefully and faithfully\"],\"to continue to hold, to retain\",[\"of death continuing to hold one\",\"to hold in check, restrain\"]]]},\"deriv\":\"from G2904\",\"pronun\":{\"ipa\":\"krɑˈtɛ.o\",\"ipa_mod\":\"krɑˈte̞.ow\",\"sbl\":\"krateō\",\"dic\":\"kra-TEH-oh\",\"dic_mod\":\"kra-TAY-oh\"},\"see\":[\"G2904\"]}', 'hold (by, fast), keep, lay hand (hold) on, obtain, retain, take (by)'),
(2902, 2903, 'κράτιστος', '{\"def\":{\"short\":\"strongest, i.e., (in dignity) very honorable\",\"long\":[\"mightiest, strongest, noblest, most illustrious, best, most excellent\",[\"used in addressing men of prominent rank or office\"]]},\"deriv\":\"superlative of a derivative of G2904\",\"pronun\":{\"ipa\":\"ˈkrɑ.ti.stos\",\"ipa_mod\":\"ˈkrɑ.ti.stows\",\"sbl\":\"kratistos\",\"dic\":\"KRA-tee-stose\",\"dic_mod\":\"KRA-tee-stose\"},\"see\":[\"G2904\"]}', 'most excellent (noble)'),
(2903, 2904, 'κράτος', '{\"def\":{\"short\":\"vigor (\\\"great\\\") (literally or figuratively)\",\"long\":[\"force, strength\",\"power, might: mighty with great power\",[\"a mighty deed, a work of power\"],\"dominion\"]},\"deriv\":\"perhaps a primary word\",\"pronun\":{\"ipa\":\"ˈkrɑ.tos\",\"ipa_mod\":\"ˈkrɑ.tows\",\"sbl\":\"kratos\",\"dic\":\"KRA-tose\",\"dic_mod\":\"KRA-tose\"}}', 'dominion, might(-ily), power, strength'),
