(2905, 2906, 'κραυγή', '{\"def\":{\"short\":\"an outcry (in notification, tumult or grief)\",\"long\":[\"a crying, outcry, clamour\"]},\"deriv\":\"from G2896\",\"pronun\":{\"ipa\":\"krɛβˈɣe\",\"ipa_mod\":\"kraˈɣe̞\",\"sbl\":\"kraugē\",\"dic\":\"krev-GAY\",\"dic_mod\":\"kra-GAY\"},\"see\":[\"G2896\"]}', 'clamour, cry(-ing)'),
(2906, 2907, 'κρέας', '{\"def\":{\"short\":\"(butcher\'s) meat\",\"long\":[\"(the) flesh (of a sacrificed animal)\"]},\"deriv\":\"perhaps a primary word\",\"pronun\":{\"ipa\":\"ˈkrɛ.ɑs\",\"ipa_mod\":\"ˈkre̞.ɑs\",\"sbl\":\"kreas\",\"dic\":\"KREH-as\",\"dic_mod\":\"KRAY-as\"}}', 'flesh'),
(2907, 2908, 'κρεῖσσον', '{\"def\":{\"short\":\"(as noun) better, i.e., greater advantage\",\"long\":[\"better\"]},\"deriv\":\"neuter of an alternate form of G2909\",\"pronun\":{\"ipa\":\"ˈkris.son\",\"ipa_mod\":\"ˈkris.sown\",\"sbl\":\"kreisson\",\"dic\":\"KREES-sone\",\"dic_mod\":\"KREES-sone\"},\"see\":[\"G2909\"]}', 'better'),
(2908, 2909, 'κρείττων', '{\"def\":{\"short\":\"stronger, i.e., (figuratively) better, i.e., nobler\",\"long\":[\"more useful, more serviceable, more advantageous\",\"more excellent\"]},\"deriv\":\"comparative of a derivative of G2904\",\"pronun\":{\"ipa\":\"ˈkrit.ton\",\"ipa_mod\":\"ˈkrit.town\",\"sbl\":\"kreittōn\",\"dic\":\"KREET-tone\",\"dic_mod\":\"KREET-tone\"},\"see\":[\"G2904\"]}', 'best, better'),
(2909, 2910, 'κρεμάννυμι', '{\"def\":{\"short\":\"to hang\",\"long\":[\"to hang up, suspend\",\"to be suspended, to hang\",[\"used of one hanging on a cross\",\"used of the Law and the Prophets, they is summed up or hanging on two precepts\"]]},\"deriv\":\"a prolonged form of a primary verb\",\"pronun\":{\"ipa\":\"krɛˈmɑn.ny.mi\",\"ipa_mod\":\"kre̞ˈmɑn.nju.mi\",\"sbl\":\"kremannymi\",\"dic\":\"kreh-MAHN-noo-mee\",\"dic_mod\":\"kray-MAHN-nyoo-mee\"}}', 'hang'),
(2910, 2911, 'κρημνός', '{\"def\":{\"short\":\"overhanging, i.e., a precipice\",\"long\":[\"a steep place, a precipice\"]},\"deriv\":\"from G2910\",\"pronun\":{\"ipa\":\"kremˈnos\",\"ipa_mod\":\"kre̞mˈnows\",\"sbl\":\"krēmnos\",\"dic\":\"krame-NOSE\",\"dic_mod\":\"krame-NOSE\"},\"see\":[\"G2910\"]}', 'steep place'),
(2911, 2912, 'Κρής', '{\"def\":{\"short\":\"a Cretan, i.e., inhabitant of Crete\",\"long\":[\"a Cretan, an inhabitant of the island of Crete\"]},\"deriv\":\"from G2914\",\"pronun\":{\"ipa\":\"kres\",\"ipa_mod\":\"kre̞s\",\"sbl\":\"krēs\",\"dic\":\"krase\",\"dic_mod\":\"krase\"},\"see\":[\"G2914\"]}', 'Crete, Cretian'),
(2912, 2913, 'Κρήσκης', '{\"def\":{\"lit\":\"growing\",\"short\":\"growing; Cresces (i.e., Crescens), a Christian\",\"long\":[\"an assistant of Paul, said to be one of the seventy disciples\"]},\"deriv\":\"of Latin origin\",\"pronun\":{\"ipa\":\"ˈkre.skes\",\"ipa_mod\":\"ˈkre̞.ske̞s\",\"sbl\":\"krēskēs\",\"dic\":\"KRAY-skase\",\"dic_mod\":\"KRAY-skase\"}}', 'Crescens'),
(2913, 2914, 'Κρήτη', '{\"def\":{\"lit\":\"fleshy\",\"short\":\"Crete, an island in the Mediterranean\",\"long\":[\"the largest and most fertile island of the Mediterranean archipelago or Aegean Sea, now the modern Candia\"]},\"deriv\":\"of uncertain derivation\",\"pronun\":{\"ipa\":\"ˈkre.te\",\"ipa_mod\":\"ˈkre̞.te̞\",\"sbl\":\"krētē\",\"dic\":\"KRAY-tay\",\"dic_mod\":\"KRAY-tay\"}}', 'Crete'),
