(2927, 2928, 'κρύπτω', '{\"def\":{\"short\":\"to conceal (properly, by covering)\",\"long\":[\"to hide, conceal, to be hid\",\"escape notice\",\"metaphorically to conceal (that it may not become known)\"]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"ˈkry.pto\",\"ipa_mod\":\"ˈkrju.ptow\",\"sbl\":\"kryptō\",\"dic\":\"KROO-ptoh\",\"dic_mod\":\"KRYOO-ptoh\"}}', 'hide (self), keep secret, secret(-ly)'),
(2928, 2929, 'κρυσταλλίζω', '{\"def\":{\"short\":\"to make (i.e., intransitively, resemble) ice (\\\"crystallize\\\")\",\"long\":[\"to be of crystalline brightness and transparency\",\"to shine like crystal\"]},\"deriv\":\"from G2930\",\"pronun\":{\"ipa\":\"kry.stɑlˈli.zo\",\"ipa_mod\":\"krju.stɑlˈli.zow\",\"sbl\":\"krystallizō\",\"dic\":\"kroo-stahl-LEE-zoh\",\"dic_mod\":\"kryoo-stahl-LEE-zoh\"},\"see\":[\"G2930\"]}', 'be clear as crystal'),
(2929, 2930, 'κρύσταλλος', '{\"def\":{\"short\":\"ice, i.e., (by analogy) rock \\\"crystal\\\"\",\"long\":[\"crystal, a kind of precious stone\"]},\"deriv\":\"from a derivative of κρύος (frost)\",\"pronun\":{\"ipa\":\"ˈkry.stɑl.los\",\"ipa_mod\":\"ˈkrju.stɑl.lows\",\"sbl\":\"krystallos\",\"dic\":\"KROO-stahl-lose\",\"dic_mod\":\"KRYOO-stahl-lose\"}}', 'crystal'),
