(2931, 2932, 'κτάομαι', '{\"def\":{\"short\":\"to get, i.e., acquire (by any means; own)\",\"long\":[\"to acquire, get, or procure a thing for one\'s self, to possess\",[\"to marry a wife\"]]},\"deriv\":\"a primary verb\",\"pronun\":{\"ipa\":\"kˈtɑ.o.mɛ\",\"ipa_mod\":\"kˈtɑ.ow.me\",\"sbl\":\"ktaomai\",\"dic\":\"k-TA-oh-meh\",\"dic_mod\":\"k-TA-oh-may\"}}', 'obtain, possess, provide, purchase'),
(2932, 2933, 'κτῆμα', '{\"def\":{\"short\":\"an acquirement, i.e., estate\",\"long\":[\"a possession\",[\"of property, lands, estates\"]]},\"deriv\":\"from G2932\",\"pronun\":{\"ipa\":\"kˈte.mɑ\",\"ipa_mod\":\"kˈte̞.mɑ\",\"sbl\":\"ktēma\",\"dic\":\"k-TAY-ma\",\"dic_mod\":\"k-TAY-ma\"},\"see\":[\"G2932\"]}', 'possession'),
(2933, 2934, 'κτῆνος', '{\"def\":{\"short\":\"property, i.e., (specially) a domestic animal\",\"long\":[\"a beast\",[\"especially a beast of burden\",\"used for four legged animals as opposed to fishes and birds\"]]},\"deriv\":\"from G2932\",\"pronun\":{\"ipa\":\"kˈte.nos\",\"ipa_mod\":\"kˈte̞.nows\",\"sbl\":\"ktēnos\",\"dic\":\"k-TAY-nose\",\"dic_mod\":\"k-TAY-nose\"},\"see\":[\"G2932\"]}', 'beast'),
