(2989, 2990, 'λανθάνω', '{\"def\":{\"short\":\"to lie hid (literally or figuratively); often used adverbially, unwittingly\",\"long\":[\"to be hidden, to be hidden from one, secretly, unawares, without knowing\"]},\"deriv\":\"a prolonged form of a primary verb, which is used only as an alternate in certain tenses\",\"pronun\":{\"ipa\":\"lɑnˈθɑ.no\",\"ipa_mod\":\"lɑn̪ˈθɑ.now\",\"sbl\":\"lanthanō\",\"dic\":\"lahn-THA-noh\",\"dic_mod\":\"lahn-THA-noh\"}}', 'be hid, be ignorant of, unawares'),
(2990, 2991, 'λαξευτός', '{\"def\":{\"short\":\"rock-quarried\",\"long\":[\"cut out of stone\"]},\"deriv\":\"from a compound of λᾶς (a stone) and the base of G3584 (in its original sense of scraping)\",\"pronun\":{\"ipa\":\"lɑ.k͡sɛβˈtos\",\"ipa_mod\":\"lɑ.k͡sefˈtows\",\"sbl\":\"laxeutos\",\"dic\":\"la-ksev-TOSE\",\"dic_mod\":\"la-ksayf-TOSE\"},\"see\":[\"G3584\"]}', 'hewn in stone'),
