(2992, 2993, 'Λαοδίκεια', '{\"def\":{\"lit\":\"justice of the people\",\"short\":\"Laodicia, a place in Asia Minor\",\"long\":[\"a city of Phrygia, situated on the river Lycus not far from Colosse\"]},\"deriv\":\"from a compound of G2992 and G1349\",\"pronun\":{\"ipa\":\"lɑ.oˈði.ki.ɑ\",\"ipa_mod\":\"lɑ.owˈði.ci.ɑ\",\"sbl\":\"laodikeia\",\"dic\":\"la-oh-THEE-kee-ah\",\"dic_mod\":\"la-oh-THEE-kee-ah\"},\"see\":[\"G1349\",\"G2992\"],\"comment\":\"Laodicea was destroyed by an earthquake in AD 66 and rebuilt by Marcus Aurelius. It was the location of a Christian church.\"}', 'Laodicea'),
(2993, 2994, 'Λαοδικεύς', '{\"def\":{\"short\":\"a Laodicean, i.e., inhabitant of Laodicia\",\"long\":[\"a Laodicean, an inhabitant of Laodicea\"]},\"deriv\":\"from G2993\",\"pronun\":{\"ipa\":\"lɑ.o.ðiˈkɛβs\",\"ipa_mod\":\"lɑ.ow.ðiˈcefs\",\"sbl\":\"laodikeus\",\"dic\":\"la-oh-thee-KEVS\",\"dic_mod\":\"la-oh-thee-KAYFS\"},\"see\":[\"G2993\"]}', 'Laodicean'),
