(2997, 2998, 'λατομέω', '{\"def\":{\"short\":\"to quarry\",\"long\":[\"to cut stones, to hew out stones\"]},\"deriv\":\"from the same as the first part of G2991 and the base of G5114\",\"pronun\":{\"ipa\":\"lɑ.toˈmɛ.o\",\"ipa_mod\":\"lɑ.towˈme̞.ow\",\"sbl\":\"latomeō\",\"dic\":\"la-toh-MEH-oh\",\"dic_mod\":\"la-toh-MAY-oh\"},\"see\":[\"G2991\",\"G5114\"]}', 'hew'),
(2998, 2999, 'λατρεία', '{\"def\":{\"short\":\"ministration of God, i.e., worship\",\"long\":[\"service rendered for hire\",[\"any service or ministration: the service of God\"],\"the service and worship of God according to the requirements of the Levitical law\",\"to perform sacred services\"]},\"deriv\":\"from G3000\",\"pronun\":{\"ipa\":\"lɑˈtri.ɑ\",\"ipa_mod\":\"lɑˈtri.ɑ\",\"sbl\":\"latreia\",\"dic\":\"la-TREE-ah\",\"dic_mod\":\"la-TREE-ah\"},\"see\":[\"G3000\"]}', '(divine) service'),
(2999, 3000, 'λατρεύω', '{\"def\":{\"short\":\"to minister (to God), i.e., render religious homage\",\"long\":[\"to serve for hire\",\"to serve, minister to, either to the gods or men and used alike of slaves and freemen\",[\"in the New Testament, to render religious service or homage, to worship\",\"to perform sacred services, to offer gifts, to worship God in the observance of the rites instituted for his worship\",[\"of priests, to officiate, to discharge the sacred office\"]]]},\"deriv\":\"from λάτρις (a hired menial)\",\"pronun\":{\"ipa\":\"lɑˈtrɛβ.o\",\"ipa_mod\":\"lɑˈtrev.ow\",\"sbl\":\"latreuō\",\"dic\":\"la-TREV-oh\",\"dic_mod\":\"la-TRAVE-oh\"}}', 'serve, do the service, worship(-per)'),
